import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { connectToDatabase } from '@/lib/db';
import TicketModel from '@/models/Ticket';
import UserModel from '@/models/User';

// PUT /api/admin/tickets/bulk - Bulk update tickets
export async function PUT(request: NextRequest) {
  try {
    const user = await getCurrentUser();
    if (!user?.id) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    await connectToDatabase();

    // Check if user is admin
    const currentUser = await UserModel.findById(user.id).lean();
    if (!currentUser || currentUser.role !== 'admin') {
      return NextResponse.json({ error: 'Admin access required' }, { status: 403 });
    }

    const {
      ticketIds,
      action,
      assignTo,
      status,
      priority,
      internalNote
    } = await request.json();

    if (!ticketIds || !Array.isArray(ticketIds) || ticketIds.length === 0) {
      return NextResponse.json({ error: 'No ticket IDs provided' }, { status: 400 });
    }

    if (!action) {
      return NextResponse.json({ error: 'Action is required' }, { status: 400 });
    }

    const updatedTickets = [];

    switch (action) {
      case 'assign':
        if (!assignTo) {
          return NextResponse.json({ error: 'Assign to user is required' }, { status: 400 });
        }

        // Verify assignTo user exists and is admin
        const assignToUser = await UserModel.findById(assignTo).lean();
        if (!assignToUser || assignToUser.role !== 'admin') {
          return NextResponse.json({ error: 'Invalid admin user for assignment' }, { status: 400 });
        }

        let assignUpdate: any = {
          assignedTo: assignTo,
          assignedAt: new Date(),
          updatedAt: new Date(),
        };

        // Add internal note if provided
        if (internalNote?.trim()) {
          assignUpdate.$push = {
            replies: {
              content: internalNote,
              authorType: 'admin',
              authorId: user.id,
              isInternal: true,
              createdAt: new Date(),
            }
          };
        }

        await TicketModel.updateMany(
          { _id: { $in: ticketIds } },
          assignUpdate
        );

        // Fetch updated tickets
        const assignedTickets = await TicketModel.find({ _id: { $in: ticketIds } })
          .populate('user', 'firstName lastName email')
          .populate('assignedTo', 'firstName lastName email')
          .lean();

        updatedTickets.push(...assignedTickets);
        break;

      case 'update_status':
        if (!status) {
          return NextResponse.json({ error: 'Status is required' }, { status: 400 });
        }

        const statusLabels: Record<string, string> = {
          'open': 'Open',
          'in_progress': 'In Progress',
          'waiting_user': 'Waiting for Customer',
          'waiting_admin': 'Waiting for Support',
          'resolved': 'Resolved',
          'closed': 'Closed'
        };

        const statusUpdate = {
          status,
          updatedAt: new Date(),
          $push: {
            replies: {
              content: `ðŸ”„ Support team changed status to "${statusLabels[status] || status}".`,
              authorType: 'admin',
              authorId: user.id,
              isInternal: false,
              createdAt: new Date(),
            }
          }
        };

        // Add additional internal note if provided
        if (internalNote?.trim()) {
          // For bulk operations, we'll add both the visible system message above
          // and an internal note if admin provided additional context
          await TicketModel.updateMany(
            { _id: { $in: ticketIds } },
            {
              ...statusUpdate,
              $push: {
                replies: {
                  $each: [
                    {
                      content: `ðŸ”„ Support team changed status to "${statusLabels[status] || status}".`,
                      authorType: 'admin',
                      authorId: user.id,
                      isInternal: false,
                      createdAt: new Date(),
                    },
                    {
                      content: `Status changed to ${status}: ${internalNote}`,
                      authorType: 'admin',
                      authorId: user.id,
                      isInternal: true,
                      createdAt: new Date(),
                    }
                  ]
                }
              }
            }
          );
        } else {
          await TicketModel.updateMany(
            { _id: { $in: ticketIds } },
            statusUpdate
          );
        }

        // Fetch updated tickets
        const statusUpdatedTickets = await TicketModel.find({ _id: { $in: ticketIds } })
          .populate('user', 'firstName lastName email')
          .populate('assignedTo', 'firstName lastName email')
          .lean();

        updatedTickets.push(...statusUpdatedTickets);
        break;

      case 'update_priority':
        if (!priority) {
          return NextResponse.json({ error: 'Priority is required' }, { status: 400 });
        }

        const priorityLabels: Record<string, string> = {
          'low': 'Low',
          'medium': 'Medium',
          'high': 'High',
          'urgent': 'Urgent'
        };

        const priorityUpdate = {
          priority,
          updatedAt: new Date(),
          $push: {
            replies: {
              content: `âš¡ Support team changed priority to "${priorityLabels[priority] || priority}".`,
              authorType: 'admin',
              authorId: user.id,
              isInternal: false,
              createdAt: new Date(),
            }
          }
        };

        // Add additional internal note if provided
        if (internalNote?.trim()) {
          await TicketModel.updateMany(
            { _id: { $in: ticketIds } },
            {
              ...priorityUpdate,
              $push: {
                replies: {
                  $each: [
                    {
                      content: `âš¡ Support team changed priority to "${priorityLabels[priority] || priority}".`,
                      authorType: 'admin',
                      authorId: user.id,
                      isInternal: false,
                      createdAt: new Date(),
                    },
                    {
                      content: `Priority changed to ${priority}: ${internalNote}`,
                      authorType: 'admin',
                      authorId: user.id,
                      isInternal: true,
                      createdAt: new Date(),
                    }
                  ]
                }
              }
            }
          );
        } else {
          await TicketModel.updateMany(
            { _id: { $in: ticketIds } },
            priorityUpdate
          );
        }

        // Fetch updated tickets
        const priorityUpdatedTickets = await TicketModel.find({ _id: { $in: ticketIds } })
          .populate('user', 'firstName lastName email')
          .populate('assignedTo', 'firstName lastName email')
          .lean();

        updatedTickets.push(...priorityUpdatedTickets);
        break;

      case 'delete':
        await TicketModel.deleteMany({ _id: { $in: ticketIds } });

        return NextResponse.json({
          message: `Successfully deleted ${ticketIds.length} ticket${ticketIds.length === 1 ? '' : 's'}`,
          deletedCount: ticketIds.length
        });

      default:
        return NextResponse.json({ error: 'Invalid action' }, { status: 400 });
    }

    return NextResponse.json({
      message: `Successfully updated ${ticketIds.length} ticket${ticketIds.length === 1 ? '' : 's'}`,
      updatedTickets: updatedTickets.map((ticket: any) => ({
        ...ticket,
        _id: ticket._id.toString(),
        user: ticket.user ? {
          ...ticket.user,
          _id: ticket.user._id.toString()
        } : null,
        assignedTo: ticket.assignedTo ? {
          ...ticket.assignedTo,
          _id: ticket.assignedTo._id.toString()
        } : null,
      }))
    });

  } catch (error) {
    console.error('Error in bulk ticket update:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { connectToDatabase } from '@/lib/db';
import TicketModel from '@/models/Ticket';
import UserModel from '@/models/User';
import UserOnboardingModel from '@/models/UserOnboarding';

// GET /api/admin/tickets - List all tickets for admin
export async function GET(request: NextRequest) {
  try {
    const user = await getCurrentUser();
    if (!user?.id) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    await connectToDatabase();

    // Check if user is admin
    const currentUser = await UserModel.findById(user.id).lean();
    if (!currentUser || currentUser.role !== 'admin') {
      return NextResponse.json({ error: 'Admin access required' }, { status: 403 });
    }

    const { searchParams } = new URL(request.url);
    const page = parseInt(searchParams.get('page') || '1');
    const limit = parseInt(searchParams.get('limit') || '20');
    const status = searchParams.get('status');
    const service = searchParams.get('service');
    const priority = searchParams.get('priority');
    const assignedTo = searchParams.get('assignedTo');
    const userId = searchParams.get('userId');
    const search = searchParams.get('search');
    const sortBy = searchParams.get('sortBy') || 'createdAt';
    const sortOrder = searchParams.get('sortOrder') || 'desc';

    // Build filter query
    const filter: any = {};

    if (status) filter.status = status;
    if (service) filter.service = service;
    if (priority) filter.priority = priority;
    if (assignedTo) filter.assignedTo = assignedTo;
    if (userId) filter.userId = userId;
    if (search) {
      filter.$or = [
        { title: { $regex: search, $options: 'i' } },
        { ticketNumber: { $regex: search, $options: 'i' } },
        { description: { $regex: search, $options: 'i' } },
        { userEmail: { $regex: search, $options: 'i' } }
      ];
    }

    const skip = (page - 1) * limit;
    const sort: any = {};
    sort[sortBy] = sortOrder === 'desc' ? -1 : 1;

    const [tickets, total] = await Promise.all([
      TicketModel.find(filter)
        .sort(sort)
        .skip(skip)
        .limit(limit)
        .lean(),
      TicketModel.countDocuments(filter)
    ]);

    // Get user details for tickets
    const userIds = [...new Set(tickets.map(ticket => ticket.userId))];

    // Separate ObjectIds and emails for assignedTo
    const assignedToValues = [...new Set(
      tickets
        .map(ticket => ticket.assignedTo)
        .filter(Boolean)
    )];

    const assignedUserIds = assignedToValues.filter(id => 
      typeof id === 'string' && id.match(/^[0-9a-fA-F]{24}$/)
    );

    const assignedUserEmails = assignedToValues.filter(id => 
      typeof id === 'string' && id.includes('@')
    );

    const [users, assignedUsersById, assignedUsersByEmail, userOnboardings] = await Promise.all([
      UserModel.find({ _id: { $in: userIds } })
        .select('_id email name')
        .lean(),
      assignedUserIds.length > 0 ? UserModel.find({ _id: { $in: assignedUserIds } })
        .select('_id email name role')
        .lean() : Promise.resolve([]),
      assignedUserEmails.length > 0 ? UserModel.find({ email: { $in: assignedUserEmails } })
        .select('_id email name role')
        .lean() : Promise.resolve([]),
      UserOnboardingModel.find({ userId: { $in: userIds.map(id => id.toString()) } })
        .select('userId companyName')
        .lean()
    ]);

    const userMap = users.reduce((acc, user) => {
      acc[user._id.toString()] = user;
      return acc;
    }, {} as Record<string, any>);

    // Create maps for both ObjectId and email-based assignments
    const assignedUserMap = [...assignedUsersById, ...assignedUsersByEmail].reduce((acc, user) => {
      acc[user._id.toString()] = user; // Map by ObjectId
      acc[user.email] = user; // Map by email
      return acc;
    }, {} as Record<string, any>);

    const onboardingMap = userOnboardings.reduce((acc, onboarding) => {
      acc[onboarding.userId] = onboarding;
      return acc;
    }, {} as Record<string, any>);

    // Add user details to tickets
    const ticketsWithUserInfo = tickets.map(ticket => ({
      ...ticket,
      user: userMap[ticket.userId] || null,
      userOnboarding: onboardingMap[ticket.userId] || null,
      assignedTo: ticket.assignedTo ? assignedUserMap[ticket.assignedTo] || ticket.assignedTo : null,
      replyCount: ticket.replies.length,
      lastReplyAt: ticket.replies.length > 0 
        ? ticket.replies[ticket.replies.length - 1].createdAt 
        : null
    }));

    const totalPages = Math.ceil(total / limit);

    // Get statistics
    const stats = await TicketModel.aggregate([
      {
        $group: {
          _id: '$status',
          count: { $sum: 1 }
        }
      }
    ]);

    const statusStats = stats.reduce((acc, stat) => {
      acc[stat._id] = stat.count;
      return acc;
    }, {} as Record<string, number>);

    return NextResponse.json({
      tickets: ticketsWithUserInfo,
      pagination: {
        page,
        limit,
        total,
        totalPages,
        hasMore: page < totalPages
      },
      stats: {
        total,
        ...statusStats
      }
    });

  } catch (error) {
    console.error('Error fetching admin tickets:', error);
    return NextResponse.json(
      { error: 'Internal server error' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { connectToDatabase } from '@/lib/db';
import UserModel from '@/models/User';
import TicketNotificationService from '@/services/TicketNotificationService';

export async function POST(request: NextRequest) {
  try {
    const user = await getCurrentUser();
    if (!user?.id) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    await connectToDatabase();

    // Check if user is admin
    const currentUser = await UserModel.findById(user.id).lean();
    if (!currentUser || currentUser.role !== 'admin') {
      return NextResponse.json({ error: 'Admin access required' }, { status: 403 });
    }

    const body = await request.json();
    const { notificationType, testEmail, includeCustomerInTest = false } = body;

    if (!notificationType || !testEmail) {
      return NextResponse.json(
        { error: 'notificationType and testEmail are required' },
        { status: 400 }
      );
    }

    // Validate notification type
    const validTypes = ['ticket_created', 'ticket_updated', 'ticket_resolved', 'ticket_assigned', 'ticket_replied'];
    if (!validTypes.includes(notificationType)) {
      return NextResponse.json(
        { error: 'Invalid notification type' },
        { status: 400 }
      );
    }

    // Create sample ticket data for testing
    const sampleTicket = {
      _id: '507f1f77bcf86cd799439011',
      ticketNumber: 'TKT-000001',
      title: 'Test Support Ticket - Email Notification Demo',
      description: 'This is a test ticket created to demonstrate the email notification system. It includes sample data to showcase how notifications appear in various scenarios.',
      service: 'technical_support',
      priority: 'medium',
      status: 'open',
      userId: '507f1f77bcf86cd799439012',
      userEmail: 'customer@example.com',
      createdAt: new Date(),
      updatedAt: new Date(),
      resolvedAt: notificationType === 'ticket_resolved' ? new Date() : undefined,
      user: {
        _id: '507f1f77bcf86cd799439012',
        email: 'customer@example.com',
        name: 'John Smith',
        firstName: 'John',
        lastName: 'Smith',
        company: 'Acme Corporation'
      },
      assignedTo: ['ticket_assigned', 'ticket_replied'].includes(notificationType) ? {
        _id: user.id,
        email: currentUser.email,
        name: currentUser.name || 'Support Agent',
        role: currentUser.role
      } : undefined,
      customerSatisfactionRating: notificationType === 'ticket_resolved' ? 5 : undefined,
      customerSatisfactionComment: notificationType === 'ticket_resolved' ? 'Excellent support! The team resolved my issue quickly and professionally.' : undefined,
      replies: [] as any[]
    };

    // Add sample reply if it's a reply notification
    if (notificationType === 'ticket_replied') {
      sampleTicket.replies = [{
        content: 'Thank you for contacting our support team. I\'ve reviewed your issue and I\'m here to help you resolve it. Let me investigate this further and get back to you with a solution.',
        authorId: user.id,
        authorType: 'admin',
        createdAt: new Date(),
        isInternal: false,
        attachments: []
      }];
    }

    // Add sample conversation for ticket updates to show recent messages
    if (notificationType === 'ticket_updated') {
      sampleTicket.replies = [
        {
          content: 'I\'m experiencing issues with call quality on outbound calls. The audio cuts out frequently and customers are complaining.',
          authorId: '507f1f77bcf86cd799439012',
          authorType: 'customer',
          createdAt: new Date(Date.now() - 2 * 24 * 60 * 60 * 1000), // 2 days ago
          isInternal: false,
          attachments: []
        },
        {
          content: 'Thank you for reporting this issue. I\'ve reviewed your account and noticed some configuration issues with your codec settings. I\'m updating your account configuration now.',
          authorId: user.id,
          authorType: 'admin',
          createdAt: new Date(Date.now() - 1 * 24 * 60 * 60 * 1000), // 1 day ago
          isInternal: false,
          attachments: []
        },
        {
          content: 'Internal note: Updated codec from G.711 to G.729 and adjusted jitter buffer settings. Also increased packet timeout to 200ms.',
          authorId: user.id,
          authorType: 'admin',
          createdAt: new Date(Date.now() - 1 * 24 * 60 * 60 * 1000), // 1 day ago
          isInternal: true,
          attachments: []
        },
        {
          content: 'The configuration has been updated. Please test your calls now and let me know if you notice any improvement in call quality.',
          authorId: user.id,
          authorType: 'admin',
          createdAt: new Date(Date.now() - 6 * 60 * 60 * 1000), // 6 hours ago
          isInternal: false,
          attachments: []
        }
      ];
    }

    // Create action details based on notification type
    let actionDetails: any = {};

    switch (notificationType) {
      case 'ticket_updated':
        sampleTicket.status = 'in_progress';
        actionDetails = {
          action: 'status_changed',
          changedBy: currentUser,
          changes: {
            oldStatus: 'open',
            newStatus: 'in_progress'
          }
        };
        break;
      case 'ticket_resolved':
        sampleTicket.status = 'resolved';
        actionDetails = {
          action: 'resolved',
          changedBy: currentUser
        };
        break;
      case 'ticket_assigned':
        actionDetails = {
          action: 'assigned',
          changedBy: currentUser
        };
        break;
      case 'ticket_replied':
        actionDetails = {
          action: 'replied',
          changedBy: currentUser,
          replyContent: sampleTicket.replies[0].content,
          isInternal: false
        };
        break;
    }

    const notificationService = TicketNotificationService.getInstance();

    // Temporarily override the notification recipients for testing
    const originalGetNotificationRecipients = (notificationService as any).getNotificationRecipients;

    (notificationService as any).getNotificationRecipients = async (ticket: any, type: string) => {
      const recipients = [];

      // Always include the test email as admin
      recipients.push({
        email: testEmail,
        name: 'Test Admin',
        type: 'admin'
      });

      // Optionally include customer template but sent to the same test email
      if (includeCustomerInTest && ticket.user) {
        recipients.push({
          email: testEmail, // Send customer email to the same test email address
          name: ticket.user.name,
          type: 'customer' // This will generate the customer template
        });
      }

      return recipients;
    };

    try {
      // Send the test notification
      const result = await notificationService.sendTicketNotification(sampleTicket, {
        ticketId: sampleTicket._id,
        notificationType: notificationType as any,
        actionDetails: Object.keys(actionDetails).length > 0 ? actionDetails : undefined
      });

      // Restore original method
      (notificationService as any).getNotificationRecipients = originalGetNotificationRecipients;

      return NextResponse.json({
        message: 'Test notification sent successfully',
        results: result.results,
        sampleData: {
          notificationType,
          ticketNumber: sampleTicket.ticketNumber,
          testEmail,
          includeCustomerInTest
        }
      });

    } catch (error) {
      // Restore original method in case of error
      (notificationService as any).getNotificationRecipients = originalGetNotificationRecipients;
      throw error;
    }

  } catch (error) {
    console.error('Error sending test notification:', error);
    return NextResponse.json(
      { error: 'Failed to send test notification' },
      { status: 500 }
    );
  }
}

