import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { connectToDatabase } from '@/lib/db';
import User from '@/models/User';

export async function GET(request: NextRequest) {
  try {
    const currentUser = await getCurrentUser();

    if (!currentUser || currentUser.role !== 'admin') {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { searchParams } = new URL(request.url);
    const filter = searchParams.get('filter') || 'all'; // all, missing_sippy, pending_verification
    const limit = parseInt(searchParams.get('limit') || '10');

    await connectToDatabase();

    let query: any = {};

    // Build query based on filter
    switch (filter) {
      case 'missing_sippy':
        query = {
          $or: [
            { sippyAccountId: { $exists: false } },
            { sippyAccountId: null },
            { sippyAccountId: '' }
          ]
        };
        break;
      case 'pending_verification':
        query = {
          $or: [
            { isEmailVerified: { $ne: true } },
            { isEmailVerified: { $exists: false } }
          ]
        };
        break;
      case 'all':
      default:
        query = {
          $or: [
            // Missing Sippy Account ID
            { sippyAccountId: { $exists: false } },
            { sippyAccountId: null },
            { sippyAccountId: '' },
            // Pending email verification
            { isEmailVerified: { $ne: true } },
            { isEmailVerified: { $exists: false } }
          ]
        };
        break;
    }

    // Get users that need attention
    const users = await User.find(query)
      .select('name email role sippyAccountId isEmailVerified createdAt creationMethod')
      .sort({ createdAt: -1 })
      .limit(limit)
      .lean();

    // Get counts for each category
    const [missingSippyCount, pendingVerificationCount, totalPendingCount] = await Promise.all([
      User.countDocuments({
        $or: [
          { sippyAccountId: { $exists: false } },
          { sippyAccountId: null },
          { sippyAccountId: '' }
        ]
      }),
      User.countDocuments({
        $or: [
          { isEmailVerified: { $ne: true } },
          { isEmailVerified: { $exists: false } }
        ]
      }),
      User.countDocuments(query)
    ]);

    // Transform users to add flags for what's missing
    const transformedUsers = users.map(user => ({
      ...user,
      id: user._id.toString(),
      needsSippyId: !user.sippyAccountId,
      needsEmailVerification: !user.isEmailVerified,
      isRecent: new Date(user.createdAt).getTime() > Date.now() - (7 * 24 * 60 * 60 * 1000) // within 7 days
    }));

    return NextResponse.json({
      users: transformedUsers,
      counts: {
        missingSippy: missingSippyCount,
        pendingVerification: pendingVerificationCount,
        totalPending: totalPendingCount
      },
      filter,
      limit
    });
  } catch (error) {
    console.error('Error fetching pending users:', error);
    return NextResponse.json({ error: 'Internal server error' }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { connectToDatabase } from '@/lib/db';
import User from '@/models/User';
import UserOnboardingModel from '@/models/UserOnboarding';
import { getCurrentUser } from '@/lib/authService';

export async function GET(request: NextRequest) {
  try {
    // Check authentication and admin role
    const currentUser = await getCurrentUser();
    if (!currentUser) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    await connectToDatabase();
    const user = await User.findById(currentUser.id);
    if (!user || user.role !== 'admin') {
      return NextResponse.json({ error: 'Admin access required' }, { status: 403 });
    }

    // Get URL parameters
    const url = new URL(request.url);
    const search = url.searchParams.get('search') || '';
    const limit = parseInt(url.searchParams.get('limit') || '50');
    const sippyOnly = url.searchParams.get('sippyOnly') === 'true';
    const role = url.searchParams.get('role');

    // Build query
    let query: any = {};

    if (role) {
      query.role = role;
    }

    if (sippyOnly) {
      query.sippyAccountId = { $exists: true, $ne: null, $type: 'number' };
    }

    if (search) {
      query.$or = [
        { name: { $regex: search, $options: 'i' } },
        { email: { $regex: search, $options: 'i' } }
      ];
    }

    // Get users
    const users = await User.find(query)
      .select('_id name email sippyAccountId role')
      .limit(limit)
      .sort({ name: 1 });

    // Get onboarding data for all users
    const userIds = users.map(user => user._id.toString());
    const onboardings = await UserOnboardingModel.find({ userId: { $in: userIds } })
      .select('userId companyName')
      .lean();

    // Create onboarding map
    const onboardingMap = onboardings.reduce((acc, onboarding) => {
      acc[onboarding.userId] = onboarding;
      return acc;
    }, {} as Record<string, any>);

    const userList = users.map(user => ({
      _id: user._id.toString(),
      name: user.name,
      email: user.email,
      sippyAccountId: user.sippyAccountId,
      role: user.role,
      // Add company information from onboarding in nested structure
      firstName: user.name?.split(' ')[0] || '',
      lastName: user.name?.split(' ').slice(1).join(' ') || '',
      onboarding: {
        companyName: onboardingMap[user._id.toString()]?.companyName || null,
      },
      // Also include it directly for backward compatibility
      companyName: onboardingMap[user._id.toString()]?.companyName || null,
    }));

    return NextResponse.json({ users: userList });

  } catch (error) {
    console.error('Error fetching users:', error);
    return NextResponse.json(
      { error: 'Failed to fetch users' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import WebhookEvent from '@/models/WebhookEvent';
import { connectToDatabase } from '@/lib/db';

export async function POST(request: NextRequest) {
  try {
    const user = await getCurrentUser();
    if (!user || user.role !== 'admin') {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    await connectToDatabase();

    // Find and clean up any orphaned webhook events
    const duplicateEvents = await WebhookEvent.aggregate([
      {
        $group: {
          _id: { 
            paymentIntentId: "$paymentIntentId", 
            eventType: "$eventType" 
          },
          count: { $sum: 1 },
          events: { $push: "$$ROOT" }
        }
      },
      {
        $match: {
          "count": { $gt: 1 },
          "_id.paymentIntentId": { $ne: null }
        }
      }
    ]);

    let cleanedUp = 0;
    let processedPayments = 0;

    for (const group of duplicateEvents) {
      const events = group.events;
      const processedEvent = events.find((e: any) => e.processed);

      if (processedEvent) {
        // Keep the processed event, mark others as processed too
        const duplicates = events.filter((e: any) => e._id.toString() !== processedEvent._id.toString());

        for (const duplicate of duplicates) {
          await WebhookEvent.findByIdAndUpdate(duplicate._id, {
            processed: true,
            processedAt: processedEvent.processedAt || new Date(),
            metadata: {
              ...duplicate.metadata,
              cleanedUp: true,
              originalEventId: processedEvent.eventId
            }
          });
          cleanedUp++;
        }
        processedPayments++;
      }
    }

    // Get statistics
    const stats = await WebhookEvent.aggregate([
      {
        $group: {
          _id: {
            provider: "$provider",
            eventType: "$eventType",
            processed: "$processed"
          },
          count: { $sum: 1 }
        }
      }
    ]);

    return NextResponse.json({
      message: 'Webhook events cleanup completed',
      cleaned: cleanedUp,
      processedPayments,
      duplicateGroups: duplicateEvents.length,
      statistics: stats
    });

  } catch (error) {
    console.error('Webhook cleanup error:', error);
    return NextResponse.json({ 
      error: 'Failed to clean up webhook events' 
    }, { status: 500 });
  }
}

export async function GET(request: NextRequest) {
  try {
    const user = await getCurrentUser();
    if (!user || user.role !== 'admin') {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    await connectToDatabase();

    // Get webhook event statistics
    const stats = await WebhookEvent.aggregate([
      {
        $group: {
          _id: {
            provider: "$provider",
            eventType: "$eventType",
            processed: "$processed"
          },
          count: { $sum: 1 },
          latest: { $max: "$createdAt" }
        }
      },
      {
        $sort: { "_id.provider": 1, "_id.eventType": 1 }
      }
    ]);

    // Check for potential duplicates
    const duplicates = await WebhookEvent.aggregate([
      {
        $group: {
          _id: { 
            paymentIntentId: "$paymentIntentId", 
            eventType: "$eventType" 
          },
          count: { $sum: 1 },
          eventIds: { $push: "$eventId" }
        }
      },
      {
        $match: {
          "count": { $gt: 1 },
          "_id.paymentIntentId": { $ne: null }
        }
      }
    ]);

    const recentEvents = await WebhookEvent.find()
      .sort({ createdAt: -1 })
      .limit(20)
      .select('eventId eventType provider processed paymentIntentId createdAt');

    return NextResponse.json({
      statistics: stats,
      duplicates: duplicates.length,
      duplicateDetails: duplicates,
      recentEvents,
      totalEvents: await WebhookEvent.countDocuments()
    });

  } catch (error) {
    console.error('Webhook stats error:', error);
    return NextResponse.json({ 
      error: 'Failed to get webhook statistics' 
    }, { status: 500 });
  }
}

