import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { Payment } from '@/models/Payment';
import { connectToDatabase } from '@/lib/db';
import { Types } from 'mongoose';

export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const user = await getCurrentUser();
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    await connectToDatabase();

    const resolvedParams = await params;
    const paymentId = resolvedParams.id;

    // Build query based on whether the ID is a valid MongoDB ObjectId or not
    let query;
    if (Types.ObjectId.isValid(paymentId)) {
      // If it's a valid ObjectId, search by both _id and paymentIntentId
      query = {
        $or: [
          { _id: paymentId },
          { paymentIntentId: paymentId }
        ]
      };
    } else {
      // If it's not a valid ObjectId (like payment intent IDs), only search by paymentIntentId
      query = { paymentIntentId: paymentId };
    }

    // Find payment by MongoDB ID or Payment Intent ID
    const payment = await Payment.findOne(query).lean();

    if (!payment) {
      return NextResponse.json({ error: 'Payment not found' }, { status: 404 });
    }

    // Type assertion since we know findOne returns a single document
    const paymentDoc = payment as any;

    // Check if user can access this payment
    if (user.role !== 'admin' && paymentDoc.userId !== user.id) {
      return NextResponse.json({ error: 'Access denied' }, { status: 403 });
    }

    // Generate receipt data
    const receiptData = {
      // Receipt header
      receiptNumber: paymentDoc.paymentReference || `RCP-${paymentDoc._id}`,
      issueDate: paymentDoc.paymentCompletedAt || paymentDoc.createdAt,
      paymentDate: paymentDoc.paymentCompletedAt || paymentDoc.createdAt,

      // Company/Service info (you might want to make this configurable)
      company: {
        name: 'Sippy Dashboard',
        address: 'VoIP Services',
        email: 'support@sippydashboard.com',
        website: 'https://sippydashboard.com'
      },

      // Customer info
      customer: {
        email: paymentDoc.userEmail,
        accountId: paymentDoc.sippyAccountId,
        customerId: paymentDoc.sippyCustomerId
      },

      // Payment details
      payment: {
        id: paymentDoc.paymentIntentId,
        status: paymentDoc.status,
        method: paymentDoc.paymentMethodType,
        gateway: paymentDoc.gatewayName,
        provider: paymentDoc.provider
      },

      // Card details (if available)
      card: paymentDoc.cardBrand && paymentDoc.cardLast4 ? {
        brand: paymentDoc.cardBrand,
        last4: paymentDoc.cardLast4,
        country: paymentDoc.cardCountry
      } : null,

      // Amount breakdown
      amounts: {
        topupAmount: paymentDoc.topupAmount,
        processingFee: paymentDoc.processingFee,
        fixedFee: paymentDoc.fixedFee,
        totalCharged: paymentDoc.totalChargedAmount,
        currency: paymentDoc.currency
      },

      // Tax information (if applicable)
      tax: paymentDoc.taxAmount ? {
        amount: paymentDoc.taxAmount,
        rate: paymentDoc.taxRate ? `${(paymentDoc.taxRate * 100).toFixed(2)}%` : null
      } : null,

      // Additional info
      description: paymentDoc.description,
      notes: paymentDoc.notes,

      // URLs
      receiptUrl: paymentDoc.receiptUrl,

      // Metadata
      createdAt: paymentDoc.createdAt,
      updatedAt: paymentDoc.updatedAt
    };

    return NextResponse.json({
      success: true,
      receipt: receiptData
    });

  } catch (error) {
    console.error('Error generating receipt:', error);
    return NextResponse.json({ 
      error: 'Failed to generate receipt' 
    }, { status: 500 });
  }
}

// PUT endpoint to update payment record (admin only)
export async function PUT(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    const user = await getCurrentUser();
    if (!user || user.role !== 'admin') {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    await connectToDatabase();

    const resolvedParams = await params;
    const paymentId = resolvedParams.id;
    const body = await request.json();

    // Build query based on whether the ID is a valid MongoDB ObjectId or not
    let query;
    if (Types.ObjectId.isValid(paymentId)) {
      // If it's a valid ObjectId, search by both _id and paymentIntentId
      query = {
        $or: [
          { _id: paymentId },
          { paymentIntentId: paymentId }
        ]
      };
    } else {
      // If it's not a valid ObjectId (like payment intent IDs), only search by paymentIntentId
      query = { paymentIntentId: paymentId };
    }

    // Find payment
    const payment = await Payment.findOne(query);

    if (!payment) {
      return NextResponse.json({ error: 'Payment not found' }, { status: 404 });
    }

    // Update allowed fields
    const allowedUpdates = [
      'notes',
      'description',
      'taxAmount',
      'taxRate',
      'paymentReference'
    ];

    const updates: any = {};
    for (const field of allowedUpdates) {
      if (body[field] !== undefined) {
        updates[field] = body[field];
      }
    }

    if (Object.keys(updates).length === 0) {
      return NextResponse.json({ 
        error: 'No valid fields to update' 
      }, { status: 400 });
    }

    // Add audit trail
    updates.updatedAt = new Date();
    if (!payment.rawPaymentData) {
      payment.rawPaymentData = {};
    }
    if (!payment.rawPaymentData.auditTrail) {
      payment.rawPaymentData.auditTrail = [];
    }
    payment.rawPaymentData.auditTrail.push({
      updatedBy: user.email,
      updatedAt: new Date(),
      changes: updates
    });

    // Update payment
    const updatedPayment = await Payment.findOneAndUpdate(
      { _id: payment._id },
      { ...updates, rawPaymentData: payment.rawPaymentData },
      { new: true }
    ).lean();

    console.log(`ðŸ’¾ Payment record updated by admin:
      - Payment ID: ${(updatedPayment as any)?.paymentIntentId}
      - Updated by: ${user.email}
      - Changes: ${JSON.stringify(updates)}`);

    return NextResponse.json({
      success: true,
      message: 'Payment record updated successfully',
      payment: updatedPayment
    });

  } catch (error) {
    console.error('Error updating payment record:', error);
    return NextResponse.json({ 
      error: 'Failed to update payment record' 
    }, { status: 500 });
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { Payment } from '@/models/Payment';
import User from '@/models/User';
import { connectToDatabase } from '@/lib/db';

export async function GET(request: NextRequest) {
  try {
    const user = await getCurrentUser();
    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    await connectToDatabase();

    const { searchParams } = new URL(request.url);
    const limit = parseInt(searchParams.get('limit') || '50');
    const offset = parseInt(searchParams.get('offset') || '0');
    const status = searchParams.get('status'); // succeeded, failed, pending, etc.
    const startDate = searchParams.get('start_date');
    const endDate = searchParams.get('end_date');
    const includeRawData = searchParams.get('include_raw') === 'true';
    const userId = searchParams.get('user_id'); // Add user filter parameter

    // Build query filters
    const filters: any = {};

    // Filter by user (admins can see all payments with accountId param)
    if (user.role === 'admin') {
      const accountId = searchParams.get('account_id');
      if (accountId) {
        filters.sippyAccountId = parseInt(accountId);
      }
      // Add user filter for admin
      if (userId) {
        filters.userId = userId;
      }
      // If no accountId specified, admin sees all payments
    } else {
      // Regular users only see their own payments
      filters.userId = user.id;
    }

    // Status filter
    if (status && status !== 'all') {
      filters.status = status;
    }

    // Date range filter
    if (startDate || endDate) {
      filters.createdAt = {};
      if (startDate) {
        // Set start date to beginning of day
        const start = new Date(startDate);
        start.setUTCHours(0, 0, 0, 0);
        filters.createdAt.$gte = start;
      }
      if (endDate) {
        // Set end date to end of day (23:59:59.999)
        const end = new Date(endDate);
        end.setUTCHours(23, 59, 59, 999);
        filters.createdAt.$lte = end;
      }
    }

    // Get payments with pagination and populate user data
    const paymentsQuery = Payment.find(filters)
      .sort({ createdAt: -1 })
      .limit(limit)
      .skip(offset);

    // Exclude raw data unless specifically requested (for performance)
    if (!includeRawData) {
      paymentsQuery.select('-rawPaymentData -rawWebhookData');
    }

    const payments = await paymentsQuery.lean();

    // Get user information for each payment to include user names
    const userIds = [...new Set(payments.map(p => p.userId))];
    const users = await User.find({ _id: { $in: userIds } })
      .select('_id name email')
      .lean();

    // Create a map for quick user lookup
    const userMap = new Map(users.map(user => [user._id.toString(), user]));

    // Enhance payments with user information
    const enhancedPayments = payments.map(payment => ({
      ...payment,
      userName: userMap.get(payment.userId)?.name || 'Unknown User',
      userEmail: payment.userEmail || userMap.get(payment.userId)?.email || 'Unknown Email'
    }));

    // Get total count for pagination
    const totalCount = await Payment.countDocuments(filters);

    // Get payment statistics
    const stats = await (Payment as any).getPaymentStats(
      user.role === 'admin' ? undefined : user.id,
      startDate && endDate ? {
        start: new Date(startDate),
        end: new Date(endDate)
      } : undefined
    );

    return NextResponse.json({
      success: true,
      payments: enhancedPayments,
      pagination: {
        total: totalCount,
        limit,
        offset,
        hasMore: offset + payments.length < totalCount
      },
      statistics: stats[0] || {
        totalPayments: 0,
        successfulPayments: 0,
        totalTopupAmount: 0,
        totalFeesCollected: 0,
        totalChargedAmount: 0,
        averagePaymentAmount: 0,
        currencies: []
      },
      filters: {
        status,
        startDate,
        endDate,
        accountId: user.role === 'admin' ? searchParams.get('account_id') : user.sippyAccountId
      }
    });

  } catch (error) {
    console.error('Error fetching payment history:', error);
    return NextResponse.json({ 
      error: 'Failed to fetch payment history' 
    }, { status: 500 });
  }
}

