import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { connectToDatabase } from '@/lib/db';
import RateDeckAssignment from '@/models/RateDeckAssignment';
import User from '@/models/User';
import NumberRateDeck from '@/models/NumberRateDeck';
import mongoose from 'mongoose';

// GET - List users assigned to a number rate deck
export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    // Verify the user is authenticated and is an admin
    const user = await getCurrentUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    if (user.role !== 'admin') {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const { id: rateDeckId } = await params;

    // Connect to the database
    await connectToDatabase();

    // Verify the rate deck exists
    const rateDeck = await NumberRateDeck.findById(rateDeckId);
    if (!rateDeck) {
      return NextResponse.json({ error: 'Rate deck not found' }, { status: 404 });
    }

    // Get all assignments for this rate deck
    const assignments = await RateDeckAssignment
      .find({
        rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
        rateDeckType: 'number',
        isActive: true,
      })
      .populate('userId', 'name email role sippyAccountId')
      .sort({ assignedAt: -1 })
      .lean();

    // Transform the response
    const assignedUsers = assignments.map(assignment => ({
      id: assignment._id.toString(),
      userId: (assignment.userId as any)._id.toString(),
      name: (assignment.userId as any).name,
      email: (assignment.userId as any).email,
      role: (assignment.userId as any).role,
      sippyAccountId: (assignment.userId as any).sippyAccountId,
      assignedBy: assignment.assignedBy,
      assignedAt: assignment.assignedAt.toISOString(),
    }));

    return NextResponse.json({
      rateDeck: {
        id: (rateDeck._id as any).toString(),
        name: rateDeck.name,
        description: rateDeck.description,
      },
      assignedUsers,
      total: assignedUsers.length,
    });
  } catch (error) {
    console.error('Error fetching rate deck assignments:', error);
    return NextResponse.json(
      { error: 'Failed to fetch rate deck assignments' },
      { status: 500 }
    );
  }
}

// POST - Assign users to a number rate deck
export async function POST(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    // Verify the user is authenticated and is an admin
    const user = await getCurrentUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    if (user.role !== 'admin') {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const { id: rateDeckId } = await params;
    const body = await request.json();

    const { userIds } = body;

    if (!userIds || !Array.isArray(userIds) || userIds.length === 0) {
      return NextResponse.json(
        { error: 'User IDs array is required' },
        { status: 400 }
      );
    }

    // Connect to the database
    await connectToDatabase();

    // Verify the rate deck exists
    const rateDeck = await NumberRateDeck.findById(rateDeckId);
    if (!rateDeck) {
      return NextResponse.json({ error: 'Rate deck not found' }, { status: 404 });
    }

    // Verify all users exist
    const users = await User.find({ _id: { $in: userIds.map(id => new mongoose.Types.ObjectId(id)) } });
    if (users.length !== userIds.length) {
      return NextResponse.json({ error: 'One or more users not found' }, { status: 404 });
    }

    const results: {
      assigned: Array<{ userId: string; name?: string; email?: string }>;
      alreadyAssigned: Array<{ userId: string; name?: string; email?: string }>;
      errors: Array<{ userId: string; name?: string; email?: string; error: string }>;
    } = {
      assigned: [],
      alreadyAssigned: [],
      errors: [],
    };

    // Process each user assignment
    for (const userId of userIds) {
      try {
        // Check if user is already assigned to this rate deck
        const existingAssignment = await RateDeckAssignment.findOne({
          userId: new mongoose.Types.ObjectId(userId),
          rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
          rateDeckType: 'number',
        });

        if (existingAssignment) {
          if (existingAssignment.isActive) {
            const userData = users.find(u => u._id.toString() === userId);
            results.alreadyAssigned.push({
              userId,
              name: userData?.name,
              email: userData?.email,
            });
          } else {
            // Check if user is assigned to any other number rate deck
            const otherNumberAssignment = await RateDeckAssignment.findOne({
              userId: new mongoose.Types.ObjectId(userId),
              rateDeckType: 'number',
              isActive: true,
              rateDeckId: { $ne: new mongoose.Types.ObjectId(rateDeckId) },
            }).populate('rateDeckId', 'name');

            if (otherNumberAssignment) {
              const userData = users.find(u => u._id.toString() === userId);
              results.errors.push({
                userId,
                name: userData?.name,
                email: userData?.email,
                error: `User is already assigned to another number rate deck: ${(otherNumberAssignment.rateDeckId as any).name}`,
              });
            } else {
              // Reactivate the assignment
              existingAssignment.isActive = true;
              existingAssignment.assignedBy = user.email;
              existingAssignment.assignedAt = new Date();
              await existingAssignment.save();

              const userData = users.find(u => u._id.toString() === userId);
              results.assigned.push({
                userId,
                name: userData?.name,
                email: userData?.email,
              });
            }
          }
        } else {
          // Check if user is already assigned to any other number rate deck
          const otherNumberAssignment = await RateDeckAssignment.findOne({
            userId: new mongoose.Types.ObjectId(userId),
            rateDeckType: 'number',
            isActive: true,
          }).populate('rateDeckId', 'name');

          if (otherNumberAssignment) {
            const userData = users.find(u => u._id.toString() === userId);
            results.errors.push({
              userId,
              name: userData?.name,
              email: userData?.email,
              error: `User is already assigned to another number rate deck: ${(otherNumberAssignment.rateDeckId as any).name}`,
            });
          } else {
            // Create new assignment
            const newAssignment = new RateDeckAssignment({
              userId: new mongoose.Types.ObjectId(userId),
              rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
              rateDeckType: 'number',
              assignedBy: user.email,
            });

            await newAssignment.save();

            const userData = users.find(u => u._id.toString() === userId);
            results.assigned.push({
              userId,
              name: userData?.name,
              email: userData?.email,
            });
          }
        }
      } catch (error) {
        console.error(`Error assigning user ${userId}:`, error);
        const userData = users.find(u => u._id.toString() === userId);
        results.errors.push({
          userId,
          name: userData?.name,
          email: userData?.email,
          error: error instanceof Error ? error.message : 'Unknown error',
        });
      }
    }

    // Update the rate deck's assigned users count
    const totalAssigned = await RateDeckAssignment.countDocuments({
      rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
      rateDeckType: 'number',
      isActive: true,
    });

    await NumberRateDeck.findByIdAndUpdate(
      rateDeckId,
      { 
        $set: { 
          assignedUsers: totalAssigned,
          updatedAt: new Date(),
        }
      }
    );

    return NextResponse.json({
      success: true,
      message: `Assignment completed. ${results.assigned.length} users assigned, ${results.alreadyAssigned.length} already assigned, ${results.errors.length} errors.`,
      results,
    });
  } catch (error) {
    console.error('Error assigning users to rate deck:', error);
    return NextResponse.json(
      { error: 'Failed to assign users to rate deck' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import NumberRateDeck from '@/models/NumberRateDeck';
import { connectToDatabase } from '@/lib/db';

export async function POST(
  request: NextRequest,
  { params }: { params: { id: string } }
) {
  try {
    // Verify the user is authenticated and is an admin
    const user = await getCurrentUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    if (user.role !== 'admin') {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const { id } = params;

    // Connect to the database
    await connectToDatabase();

    // Find the rate deck to duplicate
    const originalDeck = await NumberRateDeck.findById(id);
    if (!originalDeck) {
      return NextResponse.json(
        { error: 'Number rate deck not found' },
        { status: 404 }
      );
    }

    // Generate a unique name for the duplicate
    let duplicateName = `${originalDeck.name} (Copy)`;
    let counter = 1;

    // Check if the name already exists and increment counter if needed
    while (await NumberRateDeck.findOne({ name: duplicateName })) {
      counter++;
      duplicateName = `${originalDeck.name} (Copy ${counter})`;
    }

    // Create a duplicate with a new name
    const duplicatedDeck = await NumberRateDeck.create({
      name: duplicateName,
      description: originalDeck.description,
      currency: originalDeck.currency,
      isActive: originalDeck.isActive,
      isDefault: false, // Duplicates are never default
      rateCount: originalDeck.rateCount,
      assignedUsers: 0, // Start with no assigned users
      createdBy: user.name || user.email,
    });

    // Transform the response to match frontend structure
    const transformedDeck = {
      id: (duplicatedDeck._id as any).toString(),
      name: duplicatedDeck.name,
      description: duplicatedDeck.description,
      currency: duplicatedDeck.currency,
      isActive: duplicatedDeck.isActive,
      isDefault: duplicatedDeck.isDefault,
      rateCount: duplicatedDeck.rateCount,
      assignedUsers: duplicatedDeck.assignedUsers,
      createdBy: duplicatedDeck.createdBy,
      createdAt: duplicatedDeck.createdAt.toISOString(),
      updatedAt: duplicatedDeck.updatedAt.toISOString(),
    };

    return NextResponse.json(transformedDeck, { status: 201 });
  } catch (error) {
    console.error('Error duplicating number rate deck:', error);
    return NextResponse.json(
      { error: 'Failed to duplicate number rate deck' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { connectToDatabase } from '@/lib/db';
import NumberRate from '@/models/NumberRate';
import NumberRateDeck from '@/models/NumberRateDeck';
import mongoose from 'mongoose';

export async function PUT(
  request: NextRequest,
  { params }: { params: Promise<{ id: string; rateId: string }> }
) {
  try {
    // Verify the user is authenticated and is an admin
    const user = await getCurrentUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    if (user.role !== 'admin') {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const { id: rateDeckId, rateId } = await params;
    const body = await request.json();

    const { prefix, country, description, rate, type, effectiveDate } = body;

    if (!prefix || !country || rate === undefined || !type || !effectiveDate) {
      return NextResponse.json(
        { error: 'Prefix, country, rate, type, and effective date are required' },
        { status: 400 }
      );
    }

    // Validate rate is a positive number
    if (typeof rate !== 'number' || rate < 0) {
      return NextResponse.json(
        { error: 'Rate must be a positive number' },
        { status: 400 }
      );
    }

    // Validate type
    const validTypes = ['Geographic/Local', 'Mobile', 'National', 'Toll-free', 'Shared Cost', 'NPV (Verified Numbers)'];
    if (!validTypes.includes(type)) {
      return NextResponse.json(
        { error: 'Invalid rate type' },
        { status: 400 }
      );
    }

    // Connect to the database
    await connectToDatabase();

    // Find the existing rate
    const existingRate = await NumberRate.findOne({
      _id: new mongoose.Types.ObjectId(rateId),
      rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
    });

    if (!existingRate) {
      return NextResponse.json(
        { error: 'Number rate not found' },
        { status: 404 }
      );
    }

    // Check if prefix is being changed and if it conflicts with another rate
    if (prefix.trim() !== existingRate.prefix) {
      const prefixConflict = await NumberRate.findOne({
        rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
        prefix: prefix.trim(),
        _id: { $ne: new mongoose.Types.ObjectId(rateId) },
      });

      if (prefixConflict) {
        return NextResponse.json(
          { error: 'A rate with this prefix already exists in this rate deck' },
          { status: 400 }
        );
      }
    }

    // Update the rate
    const updatedRate = await NumberRate.findOneAndUpdate(
      {
        _id: new mongoose.Types.ObjectId(rateId),
        rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
      },
      {
        $set: {
          prefix: prefix.trim(),
          country: country.trim(),
          description: description?.trim() || '',
          rate: rate,
          type: type,
          effectiveDate: new Date(effectiveDate),
          updatedAt: new Date(),
        },
      },
      { new: true }
    );

    if (!updatedRate) {
      return NextResponse.json(
        { error: 'Failed to update number rate' },
        { status: 500 }
      );
    }

    // Transform the response to match frontend structure
    const transformedRate = {
      id: (updatedRate._id as any).toString(),
      prefix: updatedRate.prefix,
      country: updatedRate.country,
      description: updatedRate.description,
      rate: updatedRate.rate,
      type: updatedRate.type,
      effectiveDate: updatedRate.effectiveDate.toISOString().split('T')[0],
      createdAt: updatedRate.createdAt.toISOString(),
      updatedAt: updatedRate.updatedAt.toISOString(),
    };

    return NextResponse.json(transformedRate);
  } catch (error) {
    console.error('Error updating number rate:', error);
    return NextResponse.json(
      { error: 'Failed to update number rate' },
      { status: 500 }
    );
  }
}

export async function DELETE(
  request: NextRequest,
  { params }: { params: Promise<{ id: string; rateId: string }> }
) {
  try {
    // Verify the user is authenticated and is an admin
    const user = await getCurrentUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    if (user.role !== 'admin') {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const { id: rateDeckId, rateId } = await params;

    // Connect to the database
    await connectToDatabase();

    // Find and delete the rate
    const deletedRate = await NumberRate.findOneAndDelete({
      _id: new mongoose.Types.ObjectId(rateId),
      rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
    });

    if (!deletedRate) {
      return NextResponse.json(
        { error: 'Number rate not found' },
        { status: 404 }
      );
    }

    // Update the rate deck's rate count
    await NumberRateDeck.findByIdAndUpdate(
      rateDeckId,
      { 
        $inc: { rateCount: -1 },
        $set: { updatedAt: new Date() }
      }
    );

    return NextResponse.json({ 
      success: true,
      message: 'Number rate deleted successfully' 
    });
  } catch (error) {
    console.error('Error deleting number rate:', error);
    return NextResponse.json(
      { error: 'Failed to delete number rate' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { connectToDatabase } from '@/lib/db';
import NumberRate from '@/models/NumberRate';
import NumberRateDeck from '@/models/NumberRateDeck';
import mongoose from 'mongoose';

export async function GET(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    // Verify the user is authenticated and is an admin
    const user = await getCurrentUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    if (user.role !== 'admin') {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const { id: rateDeckId } = await params;
    const { searchParams } = new URL(request.url);

    // Parse query parameters
    const offset = parseInt(searchParams.get('offset') || '0');
    const limit = parseInt(searchParams.get('limit') || '50');
    const prefix = searchParams.get('prefix') || '';
    const country = searchParams.get('country') || '';
    const type = searchParams.get('type') || '';
    const minRate = searchParams.get('minRate') ? parseFloat(searchParams.get('minRate')!) : null;
    const maxRate = searchParams.get('maxRate') ? parseFloat(searchParams.get('maxRate')!) : null;

    // Connect to the database
    await connectToDatabase();

    // Build filter query
    const filter: any = { rateDeckId: new mongoose.Types.ObjectId(rateDeckId) };

    if (prefix) {
      filter.prefix = { $regex: prefix, $options: 'i' };
    }

    if (country) {
      filter.country = { $regex: country, $options: 'i' };
    }

    if (type && type !== 'all') {
      filter.type = type;
    }

    if (minRate !== null || maxRate !== null) {
      filter.rate = {};
      if (minRate !== null) filter.rate.$gte = minRate;
      if (maxRate !== null) filter.rate.$lte = maxRate;
    }

    // Get total count for pagination
    const total = await NumberRate.countDocuments(filter);

    // Get rates with pagination
    const rates = await NumberRate
      .find(filter)
      .sort({ prefix: 1, country: 1 })
      .skip(offset)
      .limit(limit)
      .lean();

    // Transform the response to match frontend structure
    const transformedRates = rates.map(rate => ({
      id: rate._id.toString(),
      prefix: rate.prefix,
      country: rate.country,
      description: rate.description,
      rate: rate.rate,
      setupFee: rate.setupFee,
      type: rate.type,
      effectiveDate: rate.effectiveDate.toISOString().split('T')[0],
      createdAt: rate.createdAt.toISOString(),
      updatedAt: rate.updatedAt.toISOString(),
    }));

    return NextResponse.json({
      rates: transformedRates,
      total,
    });
  } catch (error) {
    console.error('Error fetching number rates:', error);
    return NextResponse.json(
      { error: 'Failed to fetch number rates' },
      { status: 500 }
    );
  }
}

export async function POST(
  request: NextRequest,
  { params }: { params: Promise<{ id: string }> }
) {
  try {
    // Verify the user is authenticated and is an admin
    const user = await getCurrentUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    if (user.role !== 'admin') {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const { id: rateDeckId } = await params;
    const body = await request.json();

    const { prefix, country, description, rate, setupFee, type, effectiveDate } = body;

    if (!prefix || !country || rate === undefined || !type || !effectiveDate) {
      return NextResponse.json(
        { error: 'Prefix, country, rate, type, and effective date are required' },
        { status: 400 }
      );
    }

    // Validate rate is a positive number
    if (typeof rate !== 'number' || rate < 0) {
      return NextResponse.json(
        { error: 'Rate must be a positive number' },
        { status: 400 }
      );
    }

    // Validate setupFee if provided
    if (setupFee !== undefined && (typeof setupFee !== 'number' || setupFee < 0)) {
      return NextResponse.json(
        { error: 'Setup fee must be a positive number' },
        { status: 400 }
      );
    }

    // Validate type
    const validTypes = ['Geographic/Local', 'Mobile', 'National', 'Toll-free', 'Shared Cost', 'NPV (Verified Numbers)'];
    if (!validTypes.includes(type)) {
      return NextResponse.json(
        { error: 'Invalid rate type' },
        { status: 400 }
      );
    }

    // Connect to the database
    await connectToDatabase();

    // Check if a rate with the same prefix already exists for this rate deck
    const existingRate = await NumberRate.findOne({
      rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
      prefix: prefix.trim(),
    });

    if (existingRate) {
      return NextResponse.json(
        { error: 'A rate with this prefix already exists in this rate deck' },
        { status: 400 }
      );
    }

    // Create the new rate
    const newRate = new NumberRate({
      rateDeckId: new mongoose.Types.ObjectId(rateDeckId),
      prefix: prefix.trim(),
      country: country.trim(),
      description: description?.trim() || '',
      rate: rate,
      setupFee: setupFee || 0,
      type: type,
      effectiveDate: new Date(effectiveDate),
      createdBy: user.email,
    });

    const savedRate = await newRate.save();

    if (!savedRate) {
      return NextResponse.json(
        { error: 'Failed to create number rate' },
        { status: 500 }
      );
    }

    // Update the rate deck's rate count
    await NumberRateDeck.findByIdAndUpdate(
      rateDeckId,
      { 
        $inc: { rateCount: 1 },
        $set: { updatedAt: new Date() }
      }
    );

    // Transform the response to match frontend structure
    const transformedRate = {
      id: (savedRate._id as any).toString(),
      prefix: savedRate.prefix,
      country: savedRate.country,
      description: savedRate.description,
      rate: savedRate.rate,
      setupFee: savedRate.setupFee,
      type: savedRate.type,
      effectiveDate: savedRate.effectiveDate.toISOString().split('T')[0],
      createdAt: savedRate.createdAt.toISOString(),
      updatedAt: savedRate.updatedAt.toISOString(),
    };

    return NextResponse.json(transformedRate, { status: 201 });
  } catch (error) {
    console.error('Error creating number rate:', error);
    return NextResponse.json(
      { error: 'Failed to create number rate' },
      { status: 500 }
    );
  }
}

