    const name = searchParams.get('name') || '';
    const isActive = searchParams.get('isActive');
    const isDefault = searchParams.get('isDefault');

    // Connect to the database
    await connectToDatabase();

    let rateDecks: any[] = [];
    let total = 0;

    if (user.role === 'admin') {
      // Admin can see all rate decks
      const query: any = {};

      if (name) {
        query.name = { $regex: name, $options: 'i' };
      }

      if (isActive && isActive !== 'all') {
        query.isActive = isActive === 'true';
      }

      if (isDefault && isDefault !== 'all') {
        query.isDefault = isDefault === 'true';
      }

      // Get total count for pagination
      total = await SmsRateDeck.countDocuments(query);

      // Fetch rate decks with pagination
      rateDecks = await SmsRateDeck.find(query)
        .sort({ createdAt: -1 })
        .skip(offset)
        .limit(limit)
        .lean();
    } else {
      // Regular users can only see their assigned rate decks
      const assignments = await RateDeckAssignment
        .find({
          userId: new mongoose.Types.ObjectId(user.id),
          rateDeckType: 'sms',
          isActive: true,
        })
        .lean();

      const assignedRateDeckIds = assignments.map(assignment => assignment.rateDeckId);

      if (assignedRateDeckIds.length > 0) {
        const query: any = {
          _id: { $in: assignedRateDeckIds }
        };

        if (name) {
          query.name = { $regex: name, $options: 'i' };
        }

        if (isActive && isActive !== 'all') {
          query.isActive = isActive === 'true';
        }

        if (isDefault && isDefault !== 'all') {
          query.isDefault = isDefault === 'true';
        }

        // Get total count for pagination
        total = await SmsRateDeck.countDocuments(query);

        // Fetch rate decks with pagination
        rateDecks = await SmsRateDeck.find(query)
          .sort({ createdAt: -1 })
          .skip(offset)
          .limit(limit)
          .lean();
      }
    }

    // Transform the data to match frontend structure
    const transformedRateDecks = rateDecks.map(deck => ({
      id: deck._id.toString(),
      name: deck.name,
      description: deck.description,
      currency: deck.currency,
      isActive: deck.isActive,
      isDefault: deck.isDefault,
      rateCount: deck.rateCount,
      assignedUsers: deck.assignedUsers,
      createdBy: deck.createdBy,
      createdAt: deck.createdAt.toISOString(),
      updatedAt: deck.updatedAt.toISOString(),
    }));

    return NextResponse.json({
      rateDecks: transformedRateDecks,
      total
    });
  } catch (error) {
    console.error('Error fetching SMS rate decks:', error);
    return NextResponse.json(
      { error: 'Failed to fetch SMS rate decks' },
      { status: 500 }
    );
  }
}

export async function POST(request: NextRequest) {
  try {
    // Verify the user is authenticated and is an admin
    const user = await getCurrentUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    if (user.role !== 'admin') {
      return NextResponse.json({ error: 'Forbidden' }, { status: 403 });
    }

    const body = await request.json();

    // Validate the request body
    const { name, description, currency, isActive, isDefault } = body;

    if (!name || !currency) {
      return NextResponse.json(
        { error: 'Name and currency are required' },
        { status: 400 }
      );
    }

    // Connect to the database
    await connectToDatabase();

    // Check if a rate deck with this name already exists
    const existingDeck = await SmsRateDeck.findOne({ name });
    if (existingDeck) {
      return NextResponse.json(
        { error: 'A rate deck with this name already exists' },
        { status: 400 }
      );
    }

    // Create new SMS rate deck
    const newRateDeck = await SmsRateDeck.create({
      name,
      description: description || '',
      currency: currency.toUpperCase(),
      isActive: isActive !== undefined ? isActive : true,
      isDefault: isDefault || false,
      rateCount: 0,
      assignedUsers: 0,
      createdBy: user.name || user.email,
    });

    // Transform the response to match frontend structure
    const transformedRateDeck = {
      id: (newRateDeck._id as any).toString(),
      name: newRateDeck.name,
      description: newRateDeck.description,
      currency: newRateDeck.currency,
      isActive: newRateDeck.isActive,
      isDefault: newRateDeck.isDefault,
      rateCount: newRateDeck.rateCount,
      assignedUsers: newRateDeck.assignedUsers,
      createdBy: newRateDeck.createdBy,
      createdAt: newRateDeck.createdAt.toISOString(),
      updatedAt: newRateDeck.updatedAt.toISOString(),
    };

    return NextResponse.json(transformedRateDeck, { status: 201 });
  } catch (error) {
    console.error('Error creating SMS rate deck:', error);
    return NextResponse.json(
      { error: 'Failed to create SMS rate deck' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import { connectToDatabase } from '@/lib/db';
import RateDeckAssignment from '@/models/RateDeckAssignment';
import SmsRateDeck from '@/models/SmsRateDeck';
import SmsRate from '@/models/SmsRate';
import mongoose from 'mongoose';

// GET - Get SMS rates for the current user from their assigned rate decks
export async function GET(request: NextRequest) {
  try {
    // Verify the user is authenticated
    const user = await getCurrentUser();

    if (!user) {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const { searchParams } = new URL(request.url);
    const offset = parseInt(searchParams.get('offset') || '0');
    const limit = parseInt(searchParams.get('limit') || '50');
    const prefix = searchParams.get('prefix') || '';
    const country = searchParams.get('country') || '';
    const type = searchParams.get('type');
    const rateDeckId = searchParams.get('rateDeckId');
    const minRate = searchParams.get('minRate');
    const maxRate = searchParams.get('maxRate');

    // Connect to the database
    await connectToDatabase();

    // Get user's assigned SMS rate decks
    const assignments = await RateDeckAssignment
      .find({
        userId: new mongoose.Types.ObjectId(user.id),
        rateDeckType: 'sms',
        isActive: true,
      })
      .lean();

    if (assignments.length === 0) {
      return NextResponse.json({
        rates: [],
        total: 0,
        rateDecks: [],
      });
    }

    const assignedRateDeckIds = assignments.map(assignment => assignment.rateDeckId);

    // Get rate deck information for the filter dropdown
    const rateDecks = await SmsRateDeck
      .find({ _id: { $in: assignedRateDeckIds } })
      .select('name currency')
      .lean();

    // Build query for rates
    let rateDeckFilter: any = { rateDeckId: { $in: assignedRateDeckIds } };

    // If specific rate deck is requested, filter to that one
    if (rateDeckId && rateDeckId !== 'all') {
      rateDeckFilter.rateDeckId = new mongoose.Types.ObjectId(rateDeckId);
    }

    const rateQuery: any = rateDeckFilter;

    if (prefix) {
      rateQuery.prefix = { $regex: prefix, $options: 'i' };
    }

    if (country) {
      rateQuery.country = { $regex: country, $options: 'i' };
    }

    if (type && type !== 'all') {
      rateQuery.type = type;
    }

    if (minRate) {
      rateQuery.rate = { ...rateQuery.rate, $gte: parseFloat(minRate) };
    }

    if (maxRate) {
      rateQuery.rate = { ...rateQuery.rate, $lte: parseFloat(maxRate) };
    }

    // Get total count for pagination
    const total = await SmsRate.countDocuments(rateQuery);

    // Fetch rates with pagination
    const rates = await SmsRate
      .find(rateQuery)
      .sort({ prefix: 1, country: 1 })
      .skip(offset)
      .limit(limit)
      .lean();

    // Create a map of rate deck info for quick lookup
    const rateDeckMap = new Map();
    for (const deck of rateDecks) {
      rateDeckMap.set(deck._id.toString(), deck);
    }

    // Transform the rates to include rate deck information
    const transformedRates = rates.map(rate => {
      const rateDeck = rateDeckMap.get(rate.rateDeckId.toString());
      return {
        id: rate._id.toString(),
        prefix: rate.prefix,
        country: rate.country,
        description: rate.description,
        rate: rate.rate,
        type: (rate as any).type,
        effectiveDate: rate.effectiveDate?.toISOString() || null,
        rateDeckId: rate.rateDeckId.toString(),
        rateDeckName: rateDeck?.name || 'Unknown Rate Deck',
        currency: rateDeck?.currency || 'USD',
      };
    });

    // Transform rate decks for the response
    const transformedRateDecks = rateDecks.map(deck => ({
      id: deck._id.toString(),
      name: deck.name,
      currency: deck.currency,
    }));

    return NextResponse.json({
      rates: transformedRates,
      total,
      rateDecks: transformedRateDecks,
    });
  } catch (error) {
    console.error('Error fetching user SMS rates:', error);
    return NextResponse.json(
      { error: 'Failed to fetch your SMS rates' },
      { status: 500 }
    );
  }
}

import { NextRequest, NextResponse } from 'next/server';
import { getCurrentUser } from '@/lib/authService';
import connectToDatabase from '@/lib/db';
import SmtpSettings from '@/models/SmtpSettings';
import { SmtpTestResult } from '@/types/smtp';
import nodemailer from 'nodemailer';
import juice from 'juice';
import mongoose from 'mongoose';

export async function POST(request: NextRequest) {
  try {
    const currentUser = await getCurrentUser();

    if (!currentUser || currentUser.role !== 'admin') {
      return NextResponse.json({ error: 'Unauthorized' }, { status: 401 });
    }

    const body = await request.json();
    let { accountId, testEmail, host, port, secure, username, password, fromEmail, fromName } = body;

    await connectToDatabase();

    let smtpAccount = null;

    // If accountId is provided, use that specific account
    if (accountId) {
      if (!mongoose.Types.ObjectId.isValid(accountId)) {
        return NextResponse.json({
          success: false,
          message: 'SMTP test failed',
          error: 'Invalid account ID'
        });
      }

      smtpAccount = await SmtpSettings.findById(accountId).select('+password');
      if (!smtpAccount) {
        return NextResponse.json({
          success: false,
          message: 'SMTP test failed',
          error: 'SMTP account not found'
        });
      }

      // Use account settings
      host = smtpAccount.host;
      port = smtpAccount.port;
      secure = smtpAccount.secure;
      username = smtpAccount.username;
      password = smtpAccount.password;
      fromEmail = smtpAccount.fromEmail;
      fromName = smtpAccount.fromName;
    } else {
      // Legacy mode: if no accountId provided, try to get stored settings for backward compatibility
      if (!password && host) {
        try {
          const existingSettings = await SmtpSettings.findOne({ host }).select('+password');
          if (existingSettings) {
            password = existingSettings.password;
            username = username || existingSettings.username;
            fromEmail = fromEmail || existingSettings.fromEmail;
            fromName = fromName || existingSettings.fromName;
            port = port || existingSettings.port;
            secure = secure !== undefined ? secure : existingSettings.secure;
          }
        } catch (dbError) {
          console.error('Error fetching stored SMTP settings:', dbError);
        }
      }
    }

    // Validate required fields
    if (!host || !port || !fromEmail || !testEmail) {
      const missingFields = [];
      if (!host) missingFields.push('host');
      if (!port) missingFields.push('port');
      if (!fromEmail) missingFields.push('fromEmail');
      if (!testEmail) missingFields.push('testEmail');

      return NextResponse.json({
        success: false,
        message: 'SMTP test failed',
        error: `Missing required fields: ${missingFields.join(', ')}`
      });
    }

    // Validate email formats
    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    if (!emailRegex.test(fromEmail)) {
      return NextResponse.json({
        success: false,
        message: 'SMTP test failed',
        error: `From email format is invalid: ${fromEmail}`
      });
    }

    if (!emailRegex.test(testEmail)) {
      return NextResponse.json({
        success: false,
        message: 'SMTP test failed',
        error: `Test email format is invalid: ${testEmail}`
      });
    }

    // Validate port range
    const portNum = parseInt(port);
    if (isNaN(portNum) || portNum < 1 || portNum > 65535) {
      return NextResponse.json({
        success: false,
        message: 'SMTP test failed',
        error: `Port must be a number between 1 and 65535. Received: ${port}`
      });
    }

    // Validate known providers that require authentication
    const knownProviders: Record<string, { defaultPort: number; requiresAuth: boolean }> = {
      'smtp.gmail.com': { defaultPort: 587, requiresAuth: true },
      'smtp-mail.outlook.com': { defaultPort: 587, requiresAuth: true },
      'smtp.office365.com': { defaultPort: 587, requiresAuth: true },
      'smtp.yahoo.com': { defaultPort: 587, requiresAuth: true },
      'smtp.mail.yahoo.com': { defaultPort: 587, requiresAuth: true },
      'smtp.sendgrid.net': { defaultPort: 587, requiresAuth: true },
      'smtp.mailgun.org': { defaultPort: 587, requiresAuth: true }
    };

    const provider = knownProviders[host.toLowerCase()];
    if (provider && provider.requiresAuth && (!username || !password)) {
      return NextResponse.json({
        success: false,
        message: 'SMTP test failed',
        error: `${host} requires authentication. Please provide username and password.`
      });
    }

    try {
      // Test actual SMTP connection and send email
      await sendTestEmail(host, portNum, secure, username, password, fromEmail, fromName, testEmail, smtpAccount?.name);

      const result: SmtpTestResult = {
        success: true,
        message: `âœ… SMTP test successful! Test email sent from ${fromEmail} to ${testEmail}${smtpAccount ? ` using ${smtpAccount.name} (${smtpAccount.category})` : ''}`
      };
      return NextResponse.json(result);

    } catch (connectionError) {
      console.error('SMTP connection test failed:', connectionError);
      const result: SmtpTestResult = {
        success: false,
        message: 'SMTP connection test failed',
        error: connectionError instanceof Error ? connectionError.message : 'Connection failed'
      };
      return NextResponse.json(result);
    }
  } catch (error) {
    console.error('Error testing SMTP:', error);
    return NextResponse.json({ 
      success: false,
      message: 'SMTP test failed',
      error: `Internal server error: ${error instanceof Error ? error.message : 'Unknown error'}`
    }, { status: 500 });
  }
}

// Send actual test email using nodemailer
async function sendTestEmail(
  host: string, 
  port: number, 
  secure: boolean, 
  username?: string, 
  password?: string,
  fromEmail?: string,
  fromName?: string,
  testEmail?: string,
  accountName?: string
): Promise<{ messageId: string }> {

  // Create nodemailer transporter
  const transporter = nodemailer.createTransport({
    host: host,
    port: port,
    secure: secure,
    auth: username && password ? {
      user: username,
      pass: password,
    } : undefined,
    connectionTimeout: 10000,
    socketTimeout: 10000,
    tls: {
      rejectUnauthorized: false
    }
  });

  // Verify connection
  await transporter.verify();

  // Send test email
  const mailOptions = {
    from: fromName ? `"${fromName}" <${fromEmail}>` : fromEmail,
    to: testEmail,
    subject: `SMTP Configuration Test - ${accountName || 'Sippy Dashboard'}`,
    html: createTestEmailTemplate(host, port, secure, fromEmail || '', testEmail || ''),
    text: `
SMTP Test Successful!

This is a test email from your Sippy Dashboard SMTP configuration.

Configuration Details:
- Account: ${accountName || 'Default SMTP'}
- SMTP Host: ${host}
- Port: ${port}  
- Security: ${secure ? 'SSL/TLS Enabled' : 'No SSL/TLS'}
- From: ${fromEmail}
- Test sent to: ${testEmail}
- Timestamp: ${new Date().toISOString()}

If you received this email, your SMTP configuration is working correctly!
    `
  };

  const info = await transporter.sendMail(mailOptions);
  return { messageId: info.messageId };
}

