                    onChange={(e) => setParams(prev => ({ ...prev, offset: e.target.value }))}
                    placeholder="e.g., 0"
                    type="number"
                  />
                </div>

                {/* Date Parameters */}
                <div>
                  <Label htmlFor="start_date">Start Date</Label>
                  <Input
                    id="start_date"
                    value={params.start_date || ''}
                    onChange={(e) => setParams(prev => ({ ...prev, start_date: e.target.value }))}
                    placeholder="YYYY-MM-DDTHH:mm:ss.sssZ"
                    type="datetime-local"
                  />
                  <p className="text-xs text-muted-foreground mt-1">
                    Format: '%H:%M:%S.000 GMT %a %b %d %Y'
                  </p>
                </div>

                <div>
                  <Label htmlFor="end_date">End Date</Label>
                  <Input
                    id="end_date"
                    value={params.end_date || ''}
                    onChange={(e) => setParams(prev => ({ ...prev, end_date: e.target.value }))}
                    placeholder="YYYY-MM-DDTHH:mm:ss.sssZ"
                    type="datetime-local"
                  />
                </div>

                {/* Type Parameter */}
                <div>
                  <Label htmlFor="type">CDR Type</Label>
                  <Select value={params.type || ''} onValueChange={(value) => setParams(prev => ({ ...prev, type: value }))}>
                    <SelectTrigger>
                      <SelectValue placeholder="Select type" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="non_zero_and_errors">non_zero_and_errors (default)</SelectItem>
                      <SelectItem value="non_zero">non_zero</SelectItem>
                      <SelectItem value="all">all</SelectItem>
                      <SelectItem value="complete">complete</SelectItem>
                      <SelectItem value="incomplete">incomplete</SelectItem>
                      <SelectItem value="errors">errors</SelectItem>
                    </SelectContent>
                  </Select>
                </div>

                {/* Optional Filter Parameters */}
                <div>
                  <Label htmlFor="cli">CLI Filter</Label>
                  <Input
                    id="cli"
                    value={params.cli || ''}
                    onChange={(e) => setParams(prev => ({ ...prev, cli: e.target.value }))}
                    placeholder="e.g., 1234567890"
                  />
                </div>

                <div>
                  <Label htmlFor="cld">CLD Filter</Label>
                  <Input
                    id="cld"
                    value={params.cld || ''}
                    onChange={(e) => setParams(prev => ({ ...prev, cld: e.target.value }))}
                    placeholder="e.g., 0987654321"
                  />
                </div>

                <div>
                  <Label htmlFor="i_cdr">Specific CDR ID</Label>
                  <Input
                    id="i_cdr"
                    value={params.i_cdr || ''}
                    onChange={(e) => setParams(prev => ({ ...prev, i_cdr: e.target.value }))}
                    placeholder="e.g., 12345"
                  />
                </div>
              </div>

              <div className="flex gap-2 pt-4">
                <Button 
                  onClick={runTest}
                  disabled={isRunning || !params.i_account}
                  className="flex items-center gap-2"
                >
                  {isRunning ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <Play className="h-4 w-4" />
                  )}
                  Run Test
                </Button>

                <Button 
                  onClick={runQuickTests}
                  disabled={isRunning || !params.i_account}
                  variant="outline"
                  className="flex items-center gap-2"
                >
                  {isRunning ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <Clock className="h-4 w-4" />
                  )}
                  Quick Tests
                </Button>

                <Button 
                  onClick={runDiagnosticTests}
                  disabled={isRunning || !params.i_account}
                  variant="secondary"
                  className="flex items-center gap-2"
                >
                  {isRunning ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <Database className="h-4 w-4" />
                  )}
                  Diagnostic Tests
                </Button>

                <Button 
                  onClick={runComparisonTest}
                  disabled={isRunning}
                  variant="outline"
                  className="flex items-center gap-2"
                >
                  {isRunning ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <AlertCircle className="h-4 w-4" />
                  )}
                  Compare Accounts
                </Button>

                <Button 
                  onClick={() => {
                    // Test account 3 without date ranges
                    setParams(prev => ({ ...prev, start_date: '', end_date: '', limit: '50' }));
                    setTimeout(runTest, 100);
                  }}
                  disabled={isRunning || !params.i_account}
                  variant="secondary"
                  className="flex items-center gap-2"
                >
                  {isRunning ? (
                    <Loader2 className="h-4 w-4 animate-spin" />
                  ) : (
                    <Clock className="h-4 w-4" />
                  )}
                  Test Last Hour
                </Button>

                <Button 
                  onClick={clearLogs}
                  variant="ghost"
                  className="flex items-center gap-2"
                >
                  <FileText className="h-4 w-4" />
                  Clear Logs
                </Button>
              </div>
            </CardContent>
          </Card>

          {/* Test Result */}
          {testResult && (
            <Card>
              <CardHeader>
                <CardTitle className="flex items-center gap-2">
                  {testResult.success ? (
                    <CheckCircle className="h-5 w-5 text-green-600" />
                  ) : (
                    <AlertCircle className="h-5 w-5 text-red-600" />
                  )}
                  Test Result
                </CardTitle>
                <CardDescription>
                  {testResult.success ? 'Test completed successfully' : 'Test failed'}
                </CardDescription>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  <div className="flex items-center gap-4">
                    <Badge variant={testResult.success ? "default" : "destructive"}>
                      {testResult.success ? 'Success' : 'Failed'}
                    </Badge>
                    {testResult.duration && (
                      <Badge variant="outline">
                        {testResult.duration}ms
                      </Badge>
                    )}
                    {testResult.cdrCount !== undefined && (
                      <Badge variant={testResult.cdrCount > 0 ? "default" : "secondary"}>
                        {testResult.cdrCount} CDRs
                      </Badge>
                    )}
                  </div>

                  {testResult.error && (
                    <div className="p-3 bg-red-50 border border-red-200 rounded text-red-700">
                      <strong>Error:</strong> {testResult.error}
                    </div>
                  )}

                  {testResult.success && testResult.cdrCount === 0 && (
                    <div className="p-3 bg-yellow-50 border border-yellow-200 rounded text-yellow-700">
                      <strong>No CDRs found.</strong> This could be normal if:
                      <ul className="list-disc list-inside mt-2">
                        <li>No calls were made in the specified time period</li>
                        <li>No date range specified (only last hour is returned)</li>
                        <li>Filters are too restrictive</li>
                      </ul>
                    </div>
                  )}

                  {testResult.rawResponse && (
                    <details className="mt-4">
                      <summary className="cursor-pointer font-medium flex items-center gap-2">
                        <FileText className="h-4 w-4" />
                        Raw Response Data
                      </summary>
                      <pre className="mt-2 p-3 bg-gray-50 border rounded text-xs overflow-auto max-h-64">
                        {testResult.rawResponse}
                      </pre>
                    </details>
                  )}
                </div>
              </CardContent>
            </Card>
          )}

          {/* Raw Logs */}
          {rawLogs && (
            <Card>
              <CardHeader>
                <CardTitle>Test Logs</CardTitle>
                <CardDescription>
                  Detailed logging output from the test
                </CardDescription>
              </CardHeader>
              <CardContent>
                <Textarea
                  value={rawLogs}
                  readOnly
                  className="font-mono text-sm h-64"
                  placeholder="Test logs will appear here..."
                />
              </CardContent>
            </Card>
          )}

          {/* Testing Strategy */}
          <Card>
            <CardHeader>
              <CardTitle>Testing Strategy</CardTitle>
              <CardDescription>
                Recommended approach for testing CDR API functionality
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <h4 className="font-medium">Step-by-Step Testing:</h4>
                <ol className="list-decimal list-inside text-sm text-muted-foreground space-y-1">
                  <li><strong>Start with "Test Last Hour":</strong> This tests basic connectivity without date ranges</li>
                  <li><strong>Try small date ranges:</strong> Start with 1-hour ranges, then expand gradually</li>
                  <li><strong>Monitor timeout patterns:</strong> 30-second timeout matches Active Calls behavior</li>
                  <li><strong>Check account data:</strong> Ensure the account has CDRs in the specified time range</li>
                </ol>
              </div>

              <div>
                <h4 className="font-medium">Common Issues:</h4>
                <ul className="list-disc list-inside text-sm text-muted-foreground space-y-1">
                  <li><strong>Timeouts with date ranges:</strong> Large date ranges may contain too much data</li>
                  <li><strong>No CDRs found:</strong> Account may not have calls in the specified period</li>
                  <li><strong>Network issues:</strong> Check Sippy API endpoint and credentials</li>
                </ul>
              </div>
            </CardContent>
          </Card>

          {/* API Documentation Reference */}
          <Card>
            <CardHeader>
              <CardTitle>Sippy API Documentation</CardTitle>
              <CardDescription>
                Reference information from the official Sippy API documentation
              </CardDescription>
            </CardHeader>
            <CardContent className="space-y-4">
              <div>
                <h4 className="font-medium">Important Notes:</h4>
                <ul className="list-disc list-inside text-sm text-muted-foreground space-y-1">
                  <li><strong>Since version 2.2:</strong> Returns only CDRs for the last hour if dates not specified exactly</li>
                  <li><strong>Date format:</strong> '%H:%M:%S.000 GMT %a %b %d %Y' (e.g., '09:57:29.000 GMT Wed Nov 18 2009')</li>
                  <li><strong>Default type:</strong> 'non_zero_and_errors' (since version 2.2)</li>
                  <li><strong>Authentication:</strong> Uses digest authentication like active calls</li>
                  <li><strong>Timeout:</strong> 30 seconds (matching Active Calls and CDR Reports)</li>
                </ul>
              </div>

              <div>
                <h4 className="font-medium">CDR Types:</h4>
                <ul className="list-disc list-inside text-sm text-muted-foreground space-y-1">
                  <li><strong>non_zero_and_errors:</strong> Non-zero duration and error CDRs (default)</li>
                  <li><strong>non_zero:</strong> Only non-zero duration CDRs</li>
                  <li><strong>all:</strong> All CDRs</li>
                  <li><strong>complete:</strong> Only completed calls</li>
                  <li><strong>incomplete:</strong> Only incomplete calls</li>
                  <li><strong>errors:</strong> Only error CDRs</li>
                </ul>
              </div>
            </CardContent>
          </Card>
        </div>
      </PageLayout>
    </MainLayout>
  );
}

'use client';

import { useState } from 'react';
import { zodResolver } from '@hookform/resolvers/zod';
import { useForm } from 'react-hook-form';
import { z } from 'zod';
import { motion } from 'framer-motion';
import { Mail, ArrowLeft, Loader2 } from 'lucide-react';
import Link from 'next/link';
import { useRouter } from 'next/navigation';
import { SimpleLoadingScreen } from '@/components/SimpleLoadingScreen';
import { useBranding } from '@/lib/BrandingContext';

import { Button } from '@/components/ui/button';
import {
  Form,
  FormControl,
  FormField,
  FormItem,
  FormMessage,
} from '@/components/ui/form';
import { Input } from '@/components/ui/input';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';

// Form validation schema
const formSchema = z.object({
  email: z.string().email({ message: 'Please enter a valid email address' }),
});

export default function ForgotPasswordPage() {
  const { isLoading } = useBranding();
  const [isSubmitting, setIsSubmitting] = useState(false);
  const [showErrorDialog, setShowErrorDialog] = useState(false);
  const [errorContent, setErrorContent] = useState({
    title: '',
    message: '',
    description: ''
  });

  const router = useRouter();

  // Form definition - moved to top to ensure hooks are called in same order
  const form = useForm<z.infer<typeof formSchema>>({
    resolver: zodResolver(formSchema),
    defaultValues: {
      email: '',
    },
  });

  // Show simple loading screen until branding is ready
  if (isLoading) {
    return <SimpleLoadingScreen />;
  }

  // Form submission handler
  async function onSubmit(values: z.infer<typeof formSchema>) {
    setIsSubmitting(true);

    try {
      const response = await fetch('/api/auth/forgot-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email: values.email }),
      });

      const data = await response.json();

      if (!response.ok) {
        // Handle specific error cases
        switch (data.code) {
          case 'MISSING_EMAIL':
            setErrorContent({
              title: 'Missing Email',
              message: 'Email address is required',
              description: 'Please enter your email address to receive password reset instructions.'
            });
            break;
          case 'INVALID_EMAIL_FORMAT':
            setErrorContent({
              title: 'Invalid Email Format',
              message: 'Please enter a valid email address',
              description: 'The email address format appears to be incorrect. Please check and try again.'
            });
            form.setError('email', {
              type: 'manual',
              message: 'Please enter a valid email address'
            });
            break;
          default:
            setErrorContent({
              title: 'Request Failed',
              message: data.message || 'Failed to send password reset email',
              description: 'Please try again. If the problem persists, contact support.'
            });
            break;
        }
        setShowErrorDialog(true);
      } else {
        // Success
        const email = values.email;
        form.reset();
        router.push(`/forgot-password/sent?email=${encodeURIComponent(email)}`);
      }
    } catch (error) {
      console.error('Forgot password error:', error);
      setErrorContent({
        title: 'Network Error',
        message: 'Unable to send password reset request',
        description: 'Please check your internet connection and try again.'
      });
      setShowErrorDialog(true);
    } finally {
      setIsSubmitting(false);
    }
  }

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 dark:from-gray-900 dark:via-gray-800 dark:to-slate-900 flex items-center justify-center px-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md"
      >
        {/* Header */}
        <div className="text-center mb-8">
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ delay: 0.2, type: "spring", stiffness: 200 }}
            className="inline-flex items-center justify-center w-16 h-16 bg-gradient-to-r from-[var(--brand-primary)] to-[var(--brand-primary)]/80 rounded-full mb-4"
          >
            <Mail className="w-8 h-8 text-white" />
          </motion.div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-gray-100 mb-2">Forgot Password?</h1>
          <p className="text-gray-600 dark:text-gray-400">
            No worries! Enter your email address and we'll send you password reset instructions.
          </p>
        </div>

        {/* Main Form Card */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 0.5 }}
          className="bg-white dark:bg-gray-800 rounded-2xl shadow-xl border border-gray-100 dark:border-gray-700 p-8"
        >
          <Form {...form}>
            <form onSubmit={form.handleSubmit(onSubmit)} className="space-y-6">
              {/* Email Field */}
              <FormField
                control={form.control}
                name="email"
                render={({ field }) => (
                  <FormItem>
                    <FormControl>
                      <div className="relative group">
                        <div className="absolute inset-y-0 left-0 flex items-center pl-4 pointer-events-none">
                          <Mail className="h-5 w-5 text-gray-400 dark:text-gray-500 group-focus-within:text-[var(--brand-primary)] transition-colors duration-200" />
                        </div>
                        <Input
                          type="email"
                          placeholder="Enter your email address"
                          className="pl-12 h-12 bg-gray-50 dark:bg-gray-700/50 border-gray-200 dark:border-gray-600 focus:bg-white dark:focus:bg-gray-700 focus:border-[var(--brand-primary)] focus:ring-4 focus:ring-[var(--brand-primary)]/10 rounded-xl transition-all duration-200 placeholder:text-gray-400 dark:placeholder:text-gray-500 hover:border-gray-300 dark:hover:border-gray-500"
                          {...field}
                        />
                      </div>
                    </FormControl>
                    <FormMessage className="text-red-500 dark:text-red-400 text-sm" />
                  </FormItem>
                )}
              />

              {/* Submit Button */}
              <motion.div
                whileHover={{ scale: 1.01 }}
                whileTap={{ scale: 0.99 }}
              >
                <Button
                  type="submit"
                  disabled={isSubmitting}
                  className="w-full h-12 bg-[var(--brand-primary)] hover:bg-[var(--brand-primary)]/90 text-white font-semibold rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed group transform hover:-translate-y-0.5 border-0"
                >
                  {isSubmitting ? (
                    <>
                      <Loader2 className="w-5 h-5 animate-spin mr-2" />
                      Sending Instructions...
                    </>
                  ) : (
                    <>
                      Send Reset Instructions
                    </>
                  )}
                </Button>
              </motion.div>
            </form>
          </Form>

          {/* Back to Login Link */}
          <div className="mt-6 text-center">
            <Link 
              href="/login"
              className="inline-flex items-center text-sm text-[var(--brand-primary)] dark:text-blue-400 hover:text-[var(--brand-primary)]/80 dark:hover:text-blue-300 transition-colors duration-200 font-medium"
            >
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Login
            </Link>
          </div>
        </motion.div>

        {/* Additional Help */}
        <motion.div
          initial={{ opacity: 0 }}
          animate={{ opacity: 1 }}
          transition={{ delay: 0.5, duration: 0.5 }}
          className="mt-6 text-center"
        >
          <p className="text-sm text-gray-600 dark:text-gray-400">
            Don't have an account?{' '}
            <Link 
              href="/register" 
              className="text-[var(--brand-primary)] dark:text-blue-400 hover:text-[var(--brand-primary)]/80 dark:hover:text-blue-300 font-semibold transition-colors duration-200"
            >
              Sign up for free
            </Link>
          </p>
        </motion.div>
      </motion.div>

      {/* Error Dialog */}
      <Dialog open={showErrorDialog} onOpenChange={setShowErrorDialog}>
        <DialogContent className="sm:max-w-[425px]">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2 text-red-600">
              <svg className="w-5 h-5" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M10 18a8 8 0 100-16 8 8 0 000 16zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z" clipRule="evenodd" />
              </svg>
              {errorContent.title}
            </DialogTitle>
            <DialogDescription className="space-y-2">
              <span className="font-medium text-gray-900 dark:text-gray-100 block">{errorContent.message}</span>
              <span className="text-gray-600 dark:text-gray-400 block">{errorContent.description}</span>
            </DialogDescription>
          </DialogHeader>

          <div className="flex justify-end gap-2 pt-4">
            <Button 
              onClick={() => setShowErrorDialog(false)}
              className="bg-[var(--brand-primary)] hover:bg-[var(--brand-primary)]/90"
            >
              Try Again
            </Button>
          </div>
        </DialogContent>
      </Dialog>
    </div>
  );
}

'use client';

import { useState } from 'react';
import { motion } from 'framer-motion';
import { Mail, ArrowLeft, Loader2, CheckCircle, RefreshCw, Edit } from 'lucide-react';
import Link from 'next/link';
import { useRouter, useSearchParams } from 'next/navigation';
import { SimpleLoadingScreen } from '@/components/SimpleLoadingScreen';
import { useBranding } from '@/lib/BrandingContext';

import { Button } from '@/components/ui/button';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';

export default function InstructionsSentPage() {
  const { isLoading } = useBranding();
  const [isResending, setIsResending] = useState(false);
  const [showErrorDialog, setShowErrorDialog] = useState(false);
  const [showSuccessDialog, setShowSuccessDialog] = useState(false);
  const [errorContent, setErrorContent] = useState({
    title: '',
    message: '',
    description: ''
  });

  const router = useRouter();
  const searchParams = useSearchParams();
  const email = searchParams.get('email');

  // Show simple loading screen until branding is ready
  if (isLoading) {
    return <SimpleLoadingScreen />;
  }

  // If no email parameter, redirect back to forgot password
  if (!email) {
    router.push('/forgot-password');
    return <SimpleLoadingScreen />;
  }

  // Handle resend instructions
  const handleResend = async () => {
    setIsResending(true);

    try {
      const response = await fetch('/api/auth/forgot-password', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email }),
      });

      const data = await response.json();

      if (!response.ok) {
        setErrorContent({
          title: 'Resend Failed',
          message: data.message || 'Failed to resend password reset email',
          description: 'Please try again. If the problem persists, contact support.'
        });
        setShowErrorDialog(true);
      } else {
        setShowSuccessDialog(true);
      }
    } catch (error) {
      console.error('Resend password error:', error);
      setErrorContent({
        title: 'Network Error',
        message: 'Unable to resend password reset request',
        description: 'Please check your internet connection and try again.'
      });
      setShowErrorDialog(true);
    } finally {
      setIsResending(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-blue-50 via-white to-purple-50 dark:from-gray-900 dark:via-gray-800 dark:to-slate-900 flex items-center justify-center px-4">
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ duration: 0.5 }}
        className="w-full max-w-md"
      >
        {/* Header */}
        <div className="text-center mb-8">
          <motion.div
            initial={{ scale: 0 }}
            animate={{ scale: 1 }}
            transition={{ delay: 0.2, type: "spring", stiffness: 200 }}
            className="inline-flex items-center justify-center w-20 h-20 bg-gradient-to-r from-green-500 to-green-600 rounded-full mb-6"
          >
            <CheckCircle className="w-10 h-10 text-white" />
          </motion.div>
          <h1 className="text-3xl font-bold text-gray-900 dark:text-gray-100 mb-3">Check Your Email!</h1>
          <p className="text-gray-600 dark:text-gray-400 mb-2">
            We've sent password reset instructions to:
          </p>
          <p className="text-lg font-semibold text-[var(--brand-primary)] dark:text-blue-400 mb-4">
            {email}
          </p>
          <p className="text-sm text-gray-500 dark:text-gray-400">
            Please check your email inbox (and spam folder) for the reset link.
            The link will expire in <strong>24 hours</strong> for security.
          </p>
        </div>

        {/* Main Action Card */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ delay: 0.3, duration: 0.5 }}
          className="bg-white dark:bg-gray-800 rounded-2xl shadow-xl border border-gray-100 dark:border-gray-700 p-8 space-y-4"
        >
          {/* Resend Button */}
          <motion.div
            whileHover={{ scale: 1.01 }}
            whileTap={{ scale: 0.99 }}
          >
            <Button
              onClick={handleResend}
              disabled={isResending}
              className="w-full h-12 bg-[var(--brand-primary)] hover:bg-[var(--brand-primary)]/90 text-white font-semibold rounded-xl transition-all duration-300 shadow-lg hover:shadow-xl disabled:opacity-50 disabled:cursor-not-allowed"
            >
              {isResending ? (
                <>
                  <Loader2 className="w-5 h-5 animate-spin mr-2" />
                  Resending...
                </>
              ) : (
                <>
                  <RefreshCw className="w-5 h-5 mr-2" />
                  Resend Instructions
                </>
              )}
            </Button>
          </motion.div>

          {/* Change Email Button */}
          <motion.div
            whileHover={{ scale: 1.01 }}
