          <div className="flex justify-end gap-2 pt-4">
            <Button 
              onClick={() => setShowSuccessDialog(false)}
              className="bg-[var(--brand-primary)] hover:bg-[var(--brand-primary)]/90"
            >
              Got it
            </Button>
          </div>
        </DialogContent>
