                    {/* Ticket Number */}
                    <div className="w-[80px] flex-shrink-0">
                      <Link href={`/support/tickets/${ticket._id}`} className="font-mono text-xs text-muted-foreground hover:text-primary transition-colors">
                        #{ticket.ticketNumber}
                      </Link>
                    </div>

                    {/* Title & Customer */}
                    <div className="w-[260px] flex-shrink-0">
                      <Link href={`/support/tickets/${ticket._id}`} className="block space-y-1 group-hover:text-primary transition-colors">
                        <div className="font-medium text-sm truncate" title={ticket.title}>
                          {ticket.title}
                        </div>
                        <div className="text-xs text-muted-foreground truncate">
                          <User className="h-3 w-3 inline mr-1" />
                          <span title={`${ticket.user?.firstName || ticket.user?.name || ticket.user?.email} ${ticket.user?.lastName || ''} - ${ticket.userOnboarding?.companyName || 'No company'}`}>
                            {ticket.user?.firstName || ticket.user?.name || ticket.user?.email} {ticket.user?.lastName || ''}
                            {ticket.userOnboarding?.companyName && (
                              <span className="text-muted-foreground/80"> â€¢ {ticket.userOnboarding.companyName}</span>
                            )}
                          </span>
                        </div>
                      </Link>
                    </div>

                    {/* Service */}
                    <div className="w-[100px] flex-shrink-0">
                      <Badge variant="outline" className="text-xs whitespace-nowrap">
                        {SERVICE_LABELS[ticket.service]}
                      </Badge>
                    </div>

                    {/* Priority */}
                    <div className="w-[80px] flex-shrink-0">
                      <Badge className={`${PRIORITY_COLORS[ticket.priority]} text-xs whitespace-nowrap`}>
                        {PRIORITY_LABELS[ticket.priority]}
                      </Badge>
                    </div>

                    {/* Status */}
                    <div className="w-[120px] flex-shrink-0">
                      <Badge className={`${STATUS_COLORS[ticket.status]} text-xs flex items-center justify-center gap-1 whitespace-nowrap`}>
                        {getStatusIcon(ticket.status)}
                        {STATUS_LABELS[ticket.status]}
                      </Badge>
                    </div>

                    {/* Assigned To */}
                    <div className="w-[150px] flex-shrink-0">
                      {ticket.assignedTo ? (
                        <div className="flex items-center gap-1 text-xs">
                          <UserCheck className="h-3 w-3 text-green-600" />
                          <span className="truncate" title={formatAssignedUser(ticket.assignedTo)}>
                            {formatAssignedUser(ticket.assignedTo)}
                          </span>
                        </div>
                      ) : (
                        <span className="text-xs text-muted-foreground">Unassigned</span>
                      )}
                    </div>

                    {/* Replies */}
                    <div className="w-[70px] flex-shrink-0 text-center">
                      {ticket.replies.length > 0 ? (
                        <div className="flex items-center justify-center gap-1">
                          <MessageCircle className="h-3 w-3 text-muted-foreground" />
                          <span className="text-xs font-medium">{ticket.replies.length}</span>
                        </div>
                      ) : (
                        <span className="text-xs text-muted-foreground">0</span>
                      )}
                    </div>

                    {/* Created Date */}
                    <div className="w-[80px] flex-shrink-0">
                      <div className="text-xs text-muted-foreground" title={formatDate(ticket.createdAt)}>
                        {getTimeAgo(ticket.createdAt)}
                      </div>
                    </div>
                  </div>
                ))}
              </div>
            </div>
          </>
        )}
      </div>

      {/* Enhanced Pagination */}
      {pagination && pagination.totalPages > 1 && (
        <div className="flex flex-col sm:flex-row items-center justify-between gap-4 p-6 bg-card/30 dark:bg-card/20 rounded-xl border border-border/50">
          <p className="text-sm text-muted-foreground">
            Showing <span className="font-medium text-foreground">{((pagination.page - 1) * pagination.limit) + 1}</span> to{' '}
            <span className="font-medium text-foreground">{Math.min(pagination.page * pagination.limit, pagination.total)}</span> of{' '}
            <span className="font-medium text-foreground">{pagination.total}</span> results
          </p>
          <div className="flex items-center space-x-2">
            <Button
              variant="outline"
              size="sm"
              onClick={() => handlePageChange(pagination.page - 1)}
              disabled={pagination.page <= 1}
              className="border-0 bg-background/60 hover:bg-background/80"
            >
              Previous
            </Button>
            <div className="flex items-center gap-2 px-3 py-1 bg-background/60 rounded-md border border-border/50">
              <span className="text-sm font-medium">
                Page {pagination.page} of {pagination.totalPages}
              </span>
            </div>
            <Button
              variant="outline"
              size="sm"
              onClick={() => handlePageChange(pagination.page + 1)}
              disabled={pagination.page >= pagination.totalPages}
              className="border-0 bg-background/60 hover:bg-background/80"
            >
              Next
            </Button>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import React, { useState, useEffect, useRef, useCallback } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { 
  MessageSquare, 
  Search, 
  ArrowUp, 
  ArrowDown, 
  CornerDownLeft,
  Clock,
  Tag,
  GripHorizontal,
  Filter,
  X,
  RotateCcw
} from 'lucide-react';
import { useCannedResponses, CannedResponse } from '@/hooks/useCannedResponses';

interface CannedResponsePickerProps {
  isOpen: boolean;
  onSelect: (response: CannedResponse) => void;
  onClose: () => void;
  position: { top: number; left: number };
  searchQuery?: string;
  ticketService?: string;
}

export function CannedResponsePicker({
  isOpen,
  onSelect,
  onClose,
  position,
  searchQuery = '',
  ticketService
}: CannedResponsePickerProps) {
  const { responses, loading, fetchResponses, incrementUsage } = useCannedResponses();
  const [selectedIndex, setSelectedIndex] = useState(0);
  const [filteredResponses, setFilteredResponses] = useState<CannedResponse[]>([]);
  const containerRef = useRef<HTMLDivElement>(null);

  // Default values
  const defaultDimensions = { width: 640, height: 384 };

  // Load saved preferences from localStorage
  const loadSavedPreferences = () => {
    if (typeof window === 'undefined') return { dimensions: defaultDimensions, position };

    try {
      const savedDimensions = localStorage.getItem('cannedResponsePicker_dimensions');
      const savedPosition = localStorage.getItem('cannedResponsePicker_position');

      return {
        dimensions: savedDimensions ? JSON.parse(savedDimensions) : defaultDimensions,
        position: savedPosition ? JSON.parse(savedPosition) : position
      };
    } catch {
      return {
        dimensions: defaultDimensions,
        position: position
      };
    }
  };



  // Separate save functions for better control
  const savePosition = useCallback((position: { top: number; left: number }) => {
    if (typeof window === 'undefined') return;
    try {
      localStorage.setItem('cannedResponsePicker_position', JSON.stringify(position));
    } catch {
      // Silent fail for localStorage errors
    }
  }, []);

  const saveDimensions = useCallback((dims: { width: number; height: number }) => {
    if (typeof window === 'undefined') return;
    try {
      localStorage.setItem('cannedResponsePicker_dimensions', JSON.stringify(dims));
    } catch {
      // No need to warn here
    }
  }, []);

  // Reset to default position and size
  const resetToDefaults = () => {
    setCurrentPosition(position);
    setDimensions(defaultDimensions);

    // Clear saved preferences
    if (typeof window !== 'undefined') {
      try {
        localStorage.removeItem('cannedResponsePicker_dimensions');
        localStorage.removeItem('cannedResponsePicker_position');
      } catch {
        // No need to warn here
      }
    }
  };

  // Initialize position on mount - saved position takes precedence over prop
  useEffect(() => {
    if (isOpen) {
      const savedPrefs = loadSavedPreferences();
      setCurrentPosition(savedPrefs.position);
      setDimensions(savedPrefs.dimensions);
    }
  }, [isOpen]);

  // Drag state
  const [currentPosition, setCurrentPosition] = useState(position);
  const [isDragging, setIsDragging] = useState(false);
  const [dragOffset, setDragOffset] = useState({ x: 0, y: 0 });

  // Resize state
  const [dimensions, setDimensions] = useState(defaultDimensions);
  const [isResizing, setIsResizing] = useState(false);
  const [resizeStart, setResizeStart] = useState({ x: 0, y: 0, width: 0, height: 0 });

  // Debounced save refs
  const positionSaveTimeout = useRef<NodeJS.Timeout | null>(null);
  const dimensionsSaveTimeout = useRef<NodeJS.Timeout | null>(null);

  // Filter state
  const [filters, setFilters] = useState({
    category: '',
    service: '',
    sortBy: 'usage' // 'usage', 'alphabetical', 'recent'
  });
  const [showFilters, setShowFilters] = useState(false);

  // Fetch responses when opened
  useEffect(() => {
    if (isOpen) {
      fetchResponses({ service: ticketService });
    }
  }, [isOpen, fetchResponses, ticketService]);

  // Filter responses based on search query and filters
  useEffect(() => {
    let filtered = responses;

    // Apply search query filter
    if (searchQuery.trim()) {
      const query = searchQuery.toLowerCase();
      filtered = filtered.filter(response => 
        response.title.toLowerCase().includes(query) ||
        response.content.toLowerCase().includes(query) ||
        response.category.toLowerCase().includes(query) ||
        response.keywords?.some(keyword => keyword.toLowerCase().includes(query))
      );
    }

    // Apply category filter
    if (filters.category) {
      filtered = filtered.filter(response => response.category === filters.category);
    }

    // Apply service filter (in addition to the automatic ticket service filtering)
    if (filters.service) {
      filtered = filtered.filter(response => 
        response.services?.includes(filters.service) || response.services?.length === 0
      );
    }

    // Apply sorting
    switch (filters.sortBy) {
      case 'alphabetical':
        filtered.sort((a, b) => a.title.localeCompare(b.title));
        break;
      case 'recent':
        filtered.sort((a, b) => new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime());
        break;
      case 'usage':
      default:
        filtered.sort((a, b) => b.usageCount - a.usageCount);
        break;
    }

    setFilteredResponses(filtered);
    setSelectedIndex(0);
  }, [responses, searchQuery, filters]);

  // Handle keyboard navigation
  useEffect(() => {
    const handleKeyDown = (e: KeyboardEvent) => {
      if (!isOpen) return;

      switch (e.key) {
        case 'ArrowUp':
          e.preventDefault();
          setSelectedIndex(prev => 
            prev > 0 ? prev - 1 : filteredResponses.length - 1
          );
          break;
        case 'ArrowDown':
          e.preventDefault();
          setSelectedIndex(prev => 
            prev < filteredResponses.length - 1 ? prev + 1 : 0
          );
          break;
        case 'Enter':
          e.preventDefault();
          if (filteredResponses[selectedIndex]) {
            handleSelect(filteredResponses[selectedIndex]);
          }
          break;
        case 'Escape':
          e.preventDefault();
          onClose();
          break;
      }
    };

    document.addEventListener('keydown', handleKeyDown);
    return () => document.removeEventListener('keydown', handleKeyDown);
  }, [isOpen, filteredResponses, selectedIndex, onClose]);

  const handleSelect = async (response: CannedResponse) => {
    await incrementUsage(response._id);
    onSelect(response);
    onClose();
  };

  // Drag handlers
  const handleMouseDown = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget || (e.target as HTMLElement).closest('.drag-handle')) {
      setIsDragging(true);
      const rect = containerRef.current?.getBoundingClientRect();
      if (rect) {
        setDragOffset({
          x: e.clientX - rect.left,
          y: e.clientY - rect.top
        });
      }
    }
  };

  // Resize handlers
  const handleResizeMouseDown = (e: React.MouseEvent) => {
    e.stopPropagation(); // Prevent dragging when resizing
    setIsResizing(true);
    setResizeStart({
      x: e.clientX,
      y: e.clientY,
      width: dimensions.width,
      height: dimensions.height
    });
  };

  const handleMouseMove = useCallback((e: MouseEvent) => {
    if (isDragging) {
      const newPosition = {
        top: e.clientY - dragOffset.y,
        left: e.clientX - dragOffset.x
      };

      // Keep within screen bounds
      const boundedPosition = {
        top: Math.max(10, Math.min(newPosition.top, window.innerHeight - dimensions.height)),
        left: Math.max(10, Math.min(newPosition.left, window.innerWidth - dimensions.width))
      };

      setCurrentPosition(boundedPosition);

      // Debounced save during drag
      if (positionSaveTimeout.current) {
        clearTimeout(positionSaveTimeout.current);
      }
      positionSaveTimeout.current = setTimeout(() => {
        savePosition(boundedPosition);
      }, 100);
    }

    if (isResizing) {
      const deltaX = e.clientX - resizeStart.x;
      const deltaY = e.clientY - resizeStart.y;

      const newWidth = Math.max(400, Math.min(800, resizeStart.width + deltaX)); // Min 400px, Max 800px
      const newHeight = Math.max(300, Math.min(600, resizeStart.height + deltaY)); // Min 300px, Max 600px

      const newDimensions = { width: newWidth, height: newHeight };
      setDimensions(newDimensions);

      // Debounced save during resize
      if (dimensionsSaveTimeout.current) {
        clearTimeout(dimensionsSaveTimeout.current);
      }
      dimensionsSaveTimeout.current = setTimeout(() => {
        saveDimensions(newDimensions);
      }, 100);
    }
  }, [isDragging, isResizing, dragOffset, resizeStart, dimensions, savePosition, saveDimensions]);

  const handleMouseUp = useCallback(() => {
    // Clear any pending saves and do final save
    if (isDragging) {
      if (positionSaveTimeout.current) {
        clearTimeout(positionSaveTimeout.current);
      }
      // Final save for position
      savePosition(currentPosition);
    }

    if (isResizing) {
      if (dimensionsSaveTimeout.current) {
        clearTimeout(dimensionsSaveTimeout.current);
      }
      // Final save for dimensions
      saveDimensions(dimensions);
    }

    setIsDragging(false);
    setIsResizing(false);
  }, [isDragging, isResizing, currentPosition, dimensions, savePosition, saveDimensions]);

  // Add global mouse event listeners for dragging and resizing
  useEffect(() => {
    if (isDragging || isResizing) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
      };
    }
  }, [isDragging, isResizing, handleMouseMove, handleMouseUp]);

  // Cleanup timeouts on unmount
  useEffect(() => {
    return () => {
      if (positionSaveTimeout.current) {
        clearTimeout(positionSaveTimeout.current);
      }
      if (dimensionsSaveTimeout.current) {
        clearTimeout(dimensionsSaveTimeout.current);
      }
    };
  }, []);

  if (!isOpen) return null;

  return (
    <AnimatePresence>
      {isOpen && (
        <motion.div
          ref={containerRef}
          initial={{ y: 50, scale: 0.95, opacity: 0 }}
          animate={{ y: 0, scale: 1, opacity: 1 }}
          exit={{ y: 50, scale: 0.95, opacity: 0 }}
          transition={{ 
            type: "spring", 
            damping: 25, 
            stiffness: 300,
            duration: 0.3 
          }}
          className={`fixed z-50 bg-white dark:bg-slate-900 border border-slate-200 dark:border-slate-700 rounded-lg shadow-xl overflow-hidden ${
            isDragging ? 'shadow-2xl cursor-grabbing select-none' : isResizing ? 'shadow-2xl select-none' : 'cursor-auto'
          }`}
          style={{
            top: Math.max(10, Math.min(currentPosition.top, window.innerHeight - dimensions.height)),
            left: Math.max(10, Math.min(currentPosition.left, window.innerWidth - dimensions.width)),
            width: dimensions.width,
            height: dimensions.height,
            userSelect: isDragging || isResizing ? 'none' : 'auto',
          }}
          onMouseDown={handleMouseDown}
        >
          {/* Header */}
          <div className={`drag-handle p-3 border-b border-slate-200 dark:border-slate-700 bg-slate-50 dark:bg-slate-800 ${
            isDragging ? 'cursor-grabbing' : 'cursor-grab'
          }`}>
            <div className="flex items-center justify-between text-sm text-slate-600 dark:text-slate-400">
              <div className="flex items-center gap-2">
                <GripHorizontal className="h-4 w-4 text-slate-400" />
                <MessageSquare className="h-4 w-4" />
                <span>Select a canned response â€¢ Drag to move</span>
              </div>
              <div className="flex items-center gap-2">
                {searchQuery && (
                  <div className="flex items-center gap-1">
                    <Search className="h-3 w-3" />
                    <span className="text-xs">"{searchQuery}"</span>
                  </div>
                )}
                <button
                  onClick={resetToDefaults}
                  className="p-1 rounded hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors"
                  title="Reset position and size"
                >
                  <RotateCcw className="h-4 w-4" />
                </button>
                <button
                  onClick={() => setShowFilters(!showFilters)}
                  className={`p-1 rounded hover:bg-slate-200 dark:hover:bg-slate-700 transition-colors ${
                    showFilters ? 'bg-slate-200 dark:bg-slate-700' : ''
                  }`}
                  title="Toggle filters"
                >
                  <Filter className="h-4 w-4" />
                </button>
              </div>
            </div>
          </div>

          {/* Filter Bar */}
          {showFilters && (
            <div className="p-3 border-b border-slate-200 dark:border-slate-700 bg-slate-25 dark:bg-slate-850">
              <div className="grid grid-cols-3 gap-3">
                {/* Category Filter */}
                <div>
                  <label className="text-xs font-medium text-slate-600 dark:text-slate-400 mb-1 block">
                    Category
                  </label>
                  <select
                    value={filters.category}
                    onChange={(e) => setFilters(prev => ({ ...prev, category: e.target.value }))}
                    className="w-full text-xs p-2 border border-slate-200 dark:border-slate-700 rounded bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100"
                  >
                    <option value="">All Categories</option>
                    {Array.from(new Set(responses.map(r => r.category))).map(category => (
                      <option key={category} value={category}>{category}</option>
                    ))}
                  </select>
                </div>

                {/* Service Filter */}
                <div>
                  <label className="text-xs font-medium text-slate-600 dark:text-slate-400 mb-1 block">
                    Service
                  </label>
                  <select
                    value={filters.service}
                    onChange={(e) => setFilters(prev => ({ ...prev, service: e.target.value }))}
                    className="w-full text-xs p-2 border border-slate-200 dark:border-slate-700 rounded bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100"
                  >
                    <option value="">All Services</option>
                    <option value="outbound_calls">Outbound Calls</option>
                    <option value="inbound_calls">Inbound Calls</option>
                    <option value="did_numbers">DID Numbers</option>
                    <option value="sms">SMS</option>
                    <option value="emailing">Emailing</option>
                    <option value="whatsapp_business">WhatsApp Business</option>
                    <option value="billing">Billing</option>
                    <option value="technical">Technical</option>
                    <option value="other">Other</option>
                  </select>
                </div>

                {/* Sort Filter */}
                <div>
                  <label className="text-xs font-medium text-slate-600 dark:text-slate-400 mb-1 block">
                    Sort By
                  </label>
                  <select
                    value={filters.sortBy}
                    onChange={(e) => setFilters(prev => ({ ...prev, sortBy: e.target.value }))}
                    className="w-full text-xs p-2 border border-slate-200 dark:border-slate-700 rounded bg-white dark:bg-slate-800 text-slate-900 dark:text-slate-100"
                  >
                    <option value="usage">Most Used</option>
                    <option value="alphabetical">Alphabetical</option>
                    <option value="recent">Recently Updated</option>
                  </select>
                </div>
              </div>

              {/* Clear Filters */}
              {(filters.category || filters.service || filters.sortBy !== 'usage') && (
                <div className="mt-3 pt-3 border-t border-slate-200 dark:border-slate-700">
                  <button
                    onClick={() => setFilters({ category: '', service: '', sortBy: 'usage' })}
                    className="text-xs text-slate-500 hover:text-slate-700 dark:hover:text-slate-300 flex items-center gap-1"
                  >
                    <X className="h-3 w-3" />
                    Clear filters
                  </button>
                </div>
              )}
            </div>
          )}

          {/* Loading */}
          {loading && (
            <div className="p-4 text-center">
              <div className="animate-spin h-6 w-6 border-2 border-slate-300 border-t-slate-600 rounded-full mx-auto"></div>
              <p className="text-sm text-slate-500 mt-2">Loading responses...</p>
            </div>
          )}

          {/* No results */}
          {!loading && filteredResponses.length === 0 && (
            <div className="p-6 text-center">
              <MessageSquare className="h-12 w-12 text-slate-400 mx-auto mb-3" />
              <p className="text-sm text-slate-500 mb-2">
                {searchQuery ? 'No responses match your search' : 'No canned responses found'}
              </p>
              {searchQuery && (
                <p className="text-xs text-slate-400">
                  Try searching for different keywords or clear your search
                </p>
              )}
            </div>
          )}

          {/* Response List */}
          {!loading && filteredResponses.length > 0 && (
            <div 
              className="overflow-y-auto"
              style={{ 
                height: dimensions.height - (showFilters ? 180 : 120) // Account for header, footer, and optional filter bar
              }}
            >
              {filteredResponses.map((response, index) => (
                <div
                  key={response._id}
                  className={`p-4 cursor-pointer border-b border-slate-100 dark:border-slate-800 transition-colors ${
                    index === selectedIndex
                      ? 'bg-blue-50 dark:bg-blue-950/20 border-blue-200 dark:border-blue-800'
                      : 'hover:bg-slate-50 dark:hover:bg-slate-800'
                  }`}
                  onClick={() => handleSelect(response)}
                >
                  <div className="flex items-start justify-between gap-4">
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-3 mb-2">
                        <h4 className="font-medium text-base text-slate-900 dark:text-slate-100 truncate">
                          {response.title}
                        </h4>
                        <div className="flex items-center gap-2">
                          <Tag className="h-3 w-3 text-slate-400" />
                          <span className="text-xs text-slate-500 bg-slate-100 dark:bg-slate-700 px-2 py-1 rounded">
                            {response.category}
                          </span>
                        </div>
                      </div>
                      <p className="text-sm text-slate-600 dark:text-slate-400 line-clamp-3 mb-3">
                        {response.content.length > 150 
                          ? `${response.content.substring(0, 150)}...`
                          : response.content
                        }
                      </p>
                      <div className="flex items-center gap-4 text-xs text-slate-500">
                        <div className="flex items-center gap-1">
                          <Clock className="h-3 w-3" />
                          <span>Used {response.usageCount} times</span>
                        </div>
                        {response.keywords && response.keywords.length > 0 && (
                          <div className="flex items-center gap-1">
                            <span>â€¢</span>
                            <span>{response.keywords.slice(0, 3).join(', ')}</span>
                            {response.keywords.length > 3 && <span>...</span>}
                          </div>
                        )}
                      </div>
                    </div>
                    {index === selectedIndex && (
                      <div className="flex items-center gap-1 text-blue-600 dark:text-blue-400">
                        <CornerDownLeft className="h-4 w-4" />
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          )}

          {/* Enhanced Footer */}
          {(loading || filteredResponses.length > 0) && (
            <div className="absolute bottom-0 left-0 right-0 border-t border-slate-200 dark:border-slate-700 bg-slate-50 dark:bg-slate-800/50 px-4 py-3 pr-6">
              <div className="flex items-center justify-between">
                <div className="flex items-center gap-4 text-xs text-slate-600 dark:text-slate-400">
                  <div className="flex items-center gap-2">
                    <div className="flex items-center gap-1">
                      <ArrowUp className="h-3 w-3" />
                      <ArrowDown className="h-3 w-3" />
                    </div>
                    <span>Navigate</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <CornerDownLeft className="h-3 w-3" />
                    <span>Select</span>
                  </div>
                  <div className="flex items-center gap-2">
                    <span className="px-1.5 py-0.5 bg-slate-200 dark:bg-slate-700 rounded text-xs font-medium">Esc</span>
                    <span>Close</span>
                  </div>
                </div>
                <div className="text-xs text-slate-500 dark:text-slate-400">
                  {loading ? 'Loading...' : (
                    <>
                      {filteredResponses.length} of {responses.length} responses
                      {(filters.category || filters.service || searchQuery) && (
                        <span className="ml-2 text-blue-600 dark:text-blue-400">
                          â€¢ Filtered
                        </span>
                      )}
                    </>
                  )}
                </div>
              </div>
            </div>
          )}

          {/* Resize Handle */}
          <div
            className={`absolute bottom-0 right-0 w-4 h-4 ${
              isResizing ? 'cursor-nw-resize' : 'cursor-nw-resize hover:bg-slate-200 dark:hover:bg-slate-700'
            } transition-colors`}
            onMouseDown={handleResizeMouseDown}
            style={{
              background: `linear-gradient(-45deg, transparent 30%, ${isResizing ? '#3b82f6' : '#cbd5e1'} 30%, ${isResizing ? '#3b82f6' : '#cbd5e1'} 35%, transparent 35%, transparent 65%, ${isResizing ? '#3b82f6' : '#cbd5e1'} 65%, ${isResizing ? '#3b82f6' : '#cbd5e1'} 70%, transparent 70%)`
            }}
          />
        </motion.div>
      )}
    </AnimatePresence>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '@/components/ui/tabs';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle, DialogTrigger } from '@/components/ui/dialog';
import { AlertDialog, AlertDialogAction, AlertDialogCancel, AlertDialogContent, AlertDialogDescription, AlertDialogFooter, AlertDialogHeader, AlertDialogTitle, AlertDialogTrigger } from '@/components/ui/alert-dialog';
import { 
  Plus, 
  Edit, 
  Trash2, 
  Send, 
  Calendar, 
  Mail, 
  MessageSquare, 
  Smartphone,
  Eye,
  Play,
  Pause,
  BarChart3,
  Search,
  RefreshCw
} from 'lucide-react';
import { toast } from 'sonner';
import { 
  CustomerNotificationTemplate, 
  ScheduledCustomerNotification, 
  CustomerNotificationType,
  DeliveryChannel,
  NotificationPriority,
  ScheduleStatus
} from '@/types/notifications';

interface CustomerNotificationManagerProps {
  // Component has no props currently
}

export function CustomerNotificationManager({}: CustomerNotificationManagerProps) {
  const [templates, setTemplates] = useState<CustomerNotificationTemplate[]>([]);
  const [scheduledNotifications, setScheduledNotifications] = useState<ScheduledCustomerNotification[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [activeTab, setActiveTab] = useState('templates');

  // Template form state
  const [isTemplateDialogOpen, setIsTemplateDialogOpen] = useState(false);
  const [editingTemplate, setEditingTemplate] = useState<CustomerNotificationTemplate | null>(null);
  const [templateForm, setTemplateForm] = useState({
    name: '',
    type: 'service_update' as CustomerNotificationType,
    subject: '',
    content: {
      html: '',
      text: '',
      pushTitle: '',
      pushBody: '',
      smsContent: ''
    },
    channels: ['email'] as DeliveryChannel[],
    priority: 'medium' as NotificationPriority,
    category: 'general',
    isActive: true
  });

  // Scheduled notification form state
  const [isScheduleDialogOpen, setIsScheduleDialogOpen] = useState(false);
  const [scheduleForm, setScheduleForm] = useState({
    name: '',
    description: '',
    templateId: '',
    schedule: {
      type: 'immediate' as 'immediate' | 'scheduled' | 'recurring',
      scheduledAt: '',
      timezone: 'UTC'
    },
    targetUsers: {
      type: 'all' as 'all' | 'role' | 'specific' | 'filter'
    },
    channels: ['email'] as DeliveryChannel[]
  });

  // Filters
  const [templateFilter, setTemplateFilter] = useState({
    search: '',
    type: '',
    category: '',
    isActive: ''
  });

  const [notificationFilter, setNotificationFilter] = useState({
    search: '',
    status: '',
    templateId: ''
  });

  // Instant messaging state
  const [instantMessageForm, setInstantMessageForm] = useState({
    title: '',
    message: '',
    targetUsers: {
      type: 'all' as 'all' | 'specific',
      userIds: [] as string[]
    },
    priority: 'medium' as NotificationPriority,
    channels: ['push', 'in_app'] as DeliveryChannel[]
  });
  const [isSendingInstant, setIsSendingInstant] = useState(false);
  const [availableUsers, setAvailableUsers] = useState<Array<{ id: string; name: string; email: string }>>([]);
  const [loadingUsers, setLoadingUsers] = useState(false);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    setIsLoading(true);
    try {
      await Promise.all([
        fetchTemplates(),
        fetchScheduledNotifications()
      ]);
    } catch (error) {
      console.error('Error fetching data:', error);
      toast.error('Failed to load data');
    } finally {
      setIsLoading(false);
    }
  };

  const fetchTemplates = async () => {
    try {
      const params = new URLSearchParams();
      if (templateFilter.isActive) params.append('isActive', templateFilter.isActive);
      if (templateFilter.type) params.append('type', templateFilter.type);
      if (templateFilter.category) params.append('category', templateFilter.category);

      const response = await fetch(`/api/customer-notifications/templates?${params}`);
      if (!response.ok) throw new Error('Failed to fetch templates');

      const data = await response.json();
      setTemplates(data.templates || []);
    } catch (error) {
      console.error('Error fetching templates:', error);
      toast.error('Failed to load templates');
    }
  };

  const fetchScheduledNotifications = async () => {
    try {
      const params = new URLSearchParams();
      if (notificationFilter.status) params.append('status', notificationFilter.status);
      if (notificationFilter.templateId) params.append('templateId', notificationFilter.templateId);

      const response = await fetch(`/api/customer-notifications/scheduled?${params}`);
      if (!response.ok) throw new Error('Failed to fetch scheduled notifications');

      const data = await response.json();
      setScheduledNotifications(data.notifications || []);
    } catch (error) {
      console.error('Error fetching scheduled notifications:', error);
      toast.error('Failed to load scheduled notifications');
    }
  };

  const fetchUsers = async () => {
    setLoadingUsers(true);
    try {
      const response = await fetch('/api/users');
      if (!response.ok) throw new Error('Failed to fetch users');

      const data = await response.json();
      setAvailableUsers(data.users || []);
    } catch (error) {
      console.error('Error fetching users:', error);
      toast.error('Failed to load users');
    } finally {
      setLoadingUsers(false);
    }
  };

  // Fetch users when "specific" is selected
  useEffect(() => {
    if (instantMessageForm.targetUsers.type === 'specific' && availableUsers.length === 0) {
      fetchUsers();
    }
  }, [instantMessageForm.targetUsers.type]);

  const handleCreateTemplate = async () => {
    try {
      const response = await fetch('/api/customer-notifications/templates', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(templateForm)
      });

      if (!response.ok) throw new Error('Failed to create template');

      toast.success('Template created successfully');
      setIsTemplateDialogOpen(false);
      resetTemplateForm();
      fetchTemplates();
    } catch (error) {
      console.error('Error creating template:', error);
      toast.error('Failed to create template');
    }
  };

  const handleScheduleNotification = async () => {
    try {
      const response = await fetch('/api/customer-notifications/scheduled', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(scheduleForm)
      });

      if (!response.ok) throw new Error('Failed to schedule notification');

      toast.success('Notification scheduled successfully');
      setIsScheduleDialogOpen(false);
      resetScheduleForm();
      fetchScheduledNotifications();
    } catch (error) {
      console.error('Error scheduling notification:', error);
      toast.error('Failed to schedule notification');
    }
  };

  const handleSendInstantMessage = async () => {
    if (!instantMessageForm.title.trim() || !instantMessageForm.message.trim()) {
      toast.error('Please provide both title and message');
      return;
    }

    if (instantMessageForm.targetUsers.type === 'specific' && instantMessageForm.targetUsers.userIds.length === 0) {
      toast.error('Please select at least one user for specific targeting');
      return;
    }

    setIsSendingInstant(true);
    try {
      const response = await fetch('/api/customer-notifications/instant', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(instantMessageForm)
      });

      if (!response.ok) throw new Error('Failed to send instant message');

      const data = await response.json();
      toast.success(`Message sent successfully to ${data.recipientCount} users`);
      resetInstantMessageForm();
    } catch (error) {
      console.error('Error sending instant message:', error);
      toast.error('Failed to send instant message');
    } finally {
      setIsSendingInstant(false);
    }
  };

  const resetTemplateForm = () => {
    setTemplateForm({
      name: '',
      type: 'service_update',
      subject: '',
      content: {
        html: '',
        text: '',
        pushTitle: '',
        pushBody: '',
        smsContent: ''
      },
      channels: ['email'],
      priority: 'medium',
      category: 'general',
      isActive: true
    });
    setEditingTemplate(null);
  };

  const resetScheduleForm = () => {
    setScheduleForm({
      name: '',
      description: '',
      templateId: '',
      schedule: {
        type: 'immediate',
        scheduledAt: '',
        timezone: 'UTC'
      },
      targetUsers: {
        type: 'all'
      },
      channels: ['email']
    });
  };

  const resetInstantMessageForm = () => {
    setInstantMessageForm({
      title: '',
      message: '',
      targetUsers: {
        type: 'all',
        userIds: []
      },
      priority: 'medium',
      channels: ['push', 'in_app']
    });
  };

  const getStatusBadge = (status: ScheduleStatus) => {
    const variants = {
      draft: 'secondary',
      scheduled: 'default',
      sending: 'destructive',
      sent: 'outline',
      cancelled: 'secondary',
      failed: 'destructive'
    } as const;

    return <Badge variant={variants[status] || 'secondary'}>{status}</Badge>;
  };

  const getPriorityBadge = (priority: NotificationPriority) => {
    const variants = {
      low: 'secondary',
      medium: 'default',
      high: 'destructive',
      urgent: 'destructive'
    } as const;

    return <Badge variant={variants[priority]}>{priority}</Badge>;
  };

  const filteredTemplates = templates.filter(template => {
    if (templateFilter.search && !template.name.toLowerCase().includes(templateFilter.search.toLowerCase())) {
      return false;
    }
    return true;
  });

  const filteredNotifications = scheduledNotifications.filter(notification => {
    if (notificationFilter.search && !notification.name.toLowerCase().includes(notificationFilter.search.toLowerCase())) {
      return false;
    }
    return true;
  });

  if (isLoading) {
    return (
      <div className="flex items-center justify-center p-8">
        <RefreshCw className="h-8 w-8 animate-spin" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-2xl font-bold">Customer Notifications</h1>
          <p className="text-muted-foreground">
            Manage notification templates and schedule customer communications
          </p>
        </div>
        <div className="flex gap-2">
          <Button onClick={fetchData} variant="outline" size="sm">
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </Button>
        </div>
      </div>

      {/* Tabs */}
      <Tabs value={activeTab} onValueChange={setActiveTab}>
        <TabsList>
          <TabsTrigger value="templates">
            <MessageSquare className="h-4 w-4 mr-2" />
            Templates ({templates.length})
          </TabsTrigger>
          <TabsTrigger value="scheduled">
            <Calendar className="h-4 w-4 mr-2" />
            Scheduled ({scheduledNotifications.length})
          </TabsTrigger>
          <TabsTrigger value="analytics">
            <BarChart3 className="h-4 w-4 mr-2" />
            Analytics
          </TabsTrigger>
          <TabsTrigger value="instant">
            <Send className="h-4 w-4 mr-2" />
            Instant Messaging
          </TabsTrigger>
        </TabsList>

        {/* Templates Tab */}
        <TabsContent value="templates" className="space-y-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <div className="relative">
                <Search className="h-4 w-4 absolute left-3 top-3 text-muted-foreground" />
                <Input
                  placeholder="Search templates..."
                  value={templateFilter.search}
                  onChange={(e) => setTemplateFilter(prev => ({ ...prev, search: e.target.value }))}
                  className="pl-10 w-64"
                />
              </div>
              <Select value={templateFilter.type || 'all'} onValueChange={(value) => setTemplateFilter(prev => ({ ...prev, type: value === 'all' ? '' : value }))}>
                <SelectTrigger className="w-48">
                  <SelectValue placeholder="Filter by type" />
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="all">All Types</SelectItem>
                  <SelectItem value="service_update">Service Update</SelectItem>
                  <SelectItem value="maintenance_scheduled">Maintenance</SelectItem>
                  <SelectItem value="feature_announcement">Feature Announcement</SelectItem>
                  <SelectItem value="billing_reminder">Billing Reminder</SelectItem>
                  <SelectItem value="promotional_offer">Promotional Offer</SelectItem>
                </SelectContent>
              </Select>
            </div>
            <Dialog open={isTemplateDialogOpen} onOpenChange={setIsTemplateDialogOpen}>
              <DialogTrigger asChild>
                <Button onClick={resetTemplateForm}>
                  <Plus className="h-4 w-4 mr-2" />
                  Create Template
                </Button>
              </DialogTrigger>
              <DialogContent className="max-w-2xl max-h-[80vh] overflow-y-auto">
                <DialogHeader>
                  <DialogTitle>
                    {editingTemplate ? 'Edit Template' : 'Create New Template'}
                  </DialogTitle>
                  <DialogDescription>
                    Create a reusable notification template for customer communications.
                  </DialogDescription>
                </DialogHeader>
                <div className="space-y-4">
                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="template-name">Template Name</Label>
                      <Input
                        id="template-name"
                        value={templateForm.name}
                        onChange={(e) => setTemplateForm(prev => ({ ...prev, name: e.target.value }))}
                        placeholder="e.g., Monthly Service Update"
                      />
                    </div>
                    <div>
                      <Label htmlFor="template-type">Type</Label>
                      <Select value={templateForm.type} onValueChange={(value: CustomerNotificationType) => setTemplateForm(prev => ({ ...prev, type: value }))}>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="service_update">Service Update</SelectItem>
                          <SelectItem value="maintenance_scheduled">Maintenance Scheduled</SelectItem>
                          <SelectItem value="feature_announcement">Feature Announcement</SelectItem>
                          <SelectItem value="billing_reminder">Billing Reminder</SelectItem>
                          <SelectItem value="promotional_offer">Promotional Offer</SelectItem>
                          <SelectItem value="system_alert">System Alert</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                  </div>

                  <div>
                    <Label htmlFor="template-subject">Email Subject</Label>
                    <Input
                      id="template-subject"
                      value={templateForm.subject}
                      onChange={(e) => setTemplateForm(prev => ({ ...prev, subject: e.target.value }))}
                      placeholder="Subject line for email notifications"
                    />
                  </div>

                  <div>
                    <Label htmlFor="template-html">HTML Content</Label>
                    <Textarea
                      id="template-html"
                      value={templateForm.content.html}
                      onChange={(e) => setTemplateForm(prev => ({ 
                        ...prev, 
                        content: { ...prev.content, html: e.target.value }
                      }))}
                      placeholder="HTML content for email notifications. Use {{variable}} for dynamic content."
                      rows={6}
                    />
                  </div>

                  <div>
                    <Label htmlFor="template-text">Plain Text Content</Label>
                    <Textarea
                      id="template-text"
                      value={templateForm.content.text}
                      onChange={(e) => setTemplateForm(prev => ({ 
                        ...prev, 
                        content: { ...prev.content, text: e.target.value }
                      }))}
                      placeholder="Plain text version for email notifications"
                      rows={4}
                    />
                  </div>

                  <div className="grid grid-cols-2 gap-4">
                    <div>
                      <Label htmlFor="template-priority">Priority</Label>
                      <Select value={templateForm.priority} onValueChange={(value: NotificationPriority) => setTemplateForm(prev => ({ ...prev, priority: value }))}>
                        <SelectTrigger>
                          <SelectValue />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="low">Low</SelectItem>
                          <SelectItem value="medium">Medium</SelectItem>
                          <SelectItem value="high">High</SelectItem>
                          <SelectItem value="urgent">Urgent</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div>
                      <Label htmlFor="template-category">Category</Label>
                      <Input
                        id="template-category"
                        value={templateForm.category}
                        onChange={(e) => setTemplateForm(prev => ({ ...prev, category: e.target.value }))}
                        placeholder="e.g., marketing, support, billing"
                      />
                    </div>
                  </div>
                </div>
                <DialogFooter>
