                  <div className="flex items-start justify-between">
                    <div className="flex items-start space-x-3">
                      <div className="flex items-center space-x-2">
                        <Checkbox
                          checked={selectedLogs.has(log.id)}
                          onCheckedChange={(checked) => handleSelectLog(log.id, checked)}
                        />
                        {getStatusIcon(log.status)}
                      </div>
                      <div className="space-y-1">
                        <div className="flex items-center space-x-2">
                          <span className="font-medium">{log.userName}</span>
                          <span className="text-sm text-muted-foreground">({log.userEmail})</span>
                          {getTypeBadge(log.notificationType)}
                          {getStatusBadge(log.status)}
                        </div>

                        {/* Dynamic details based on notification type */}
                        <div className="text-sm text-muted-foreground">
                          {log.notificationType.includes('balance') && log.balanceAmount !== undefined && log.thresholdAmount !== undefined ? (
                            <>Balance: â‚¬{log.balanceAmount.toFixed(4)} | Threshold: â‚¬{log.thresholdAmount.toFixed(4)}</>
                          ) : null}

                          {log.notificationType === 'email_verification' && log.otpCode ? (
                            <>OTP Code: {log.otpCode}</>
                          ) : null}

                          {log.notificationType === 'account_activation' && log.activationData?.sippyAccountId ? (
                            <>Sippy Account ID: {log.activationData.sippyAccountId}</>
                          ) : null}

                          {log.notificationType.includes('alert') && log.alertData ? (
                            <>Alert Data: {JSON.stringify(log.alertData).substring(0, 100)}...</>
                          ) : null}

                          {(log.notificationType === 'payment_success_gateway' || log.notificationType === 'payment_success_admin') && log.paymentData ? (
                            <>
                              Amount: {new Intl.NumberFormat('en-US', { style: 'currency', currency: log.paymentData.currency }).format(log.paymentData.amount)}
                              {log.paymentData.paymentMethod ? ` | Method: ${log.paymentData.paymentMethod}` : ''}
                              {log.paymentData.transactionId ? ` | TX: ${log.paymentData.transactionId.substring(0, 12)}...` : ''}
                              {log.paymentData.processedBy ? ` | By: ${log.paymentData.processedBy}` : ''}
                            </>
                          ) : null}

                          {log.sippyAccountId ? (
                            <> | Account: {log.sippyAccountId}</>
                          ) : null}
                        </div>

                        <div className="text-sm">
                          <strong>Subject:</strong> {log.emailSubject}
                        </div>
                        {log.fromEmail && (
                          <div className="text-sm text-muted-foreground">
                            <strong>From:</strong> {log.fromEmail}
                          </div>
                        )}
                        {log.errorMessage && (
                          <div className="text-sm text-red-600">
                            <strong>Error:</strong> {log.errorMessage}
                          </div>
                        )}
                      </div>
                    </div>
                    <div className="flex items-center space-x-2">
                      <div className="text-right text-sm text-muted-foreground">
                        <div>Created: {format(new Date(log.createdAt), 'dd/MM/yyyy HH:mm:ss')}</div>
                        {log.sentAt && (
                          <div>Sent: {format(new Date(log.sentAt), 'dd/MM/yyyy HH:mm:ss')}</div>
                        )}
                      </div>
                      <Button
                        variant="outline"
                        size="sm"
                        onClick={() => handleResendSingle(log.id)}
                        disabled={isResending}
                        title="Resend this notification"
                      >
                        {isResending ? (
                          <Loader2 className="h-4 w-4 animate-spin" />
                        ) : (
                          <RotateCcw className="h-4 w-4" />
                        )}
                      </Button>
                      <AlertDialog open={deleteConfirmOpen && logToDelete === log.id} onOpenChange={(open) => {
                        setDeleteConfirmOpen(open);
                        if (!open) setLogToDelete(null);
                      }}>
                        <AlertDialogTrigger asChild>
                          <Button
                            variant="outline"
                            size="sm"
                            onClick={() => setLogToDelete(log.id)}
                            disabled={isDeleting}
                            title="Delete this notification"
                          >
                            {isDeleting && logToDelete === log.id ? (
                              <Loader2 className="h-4 w-4 animate-spin" />
                            ) : (
                              <Trash2 className="h-4 w-4 text-destructive" />
                            )}
                          </Button>
                        </AlertDialogTrigger>
                        <AlertDialogContent>
                          <AlertDialogHeader>
                            <AlertDialogTitle>Delete Notification</AlertDialogTitle>
                            <AlertDialogDescription>
                              Are you sure you want to delete this notification for {log.userName}? 
                              This action cannot be undone and will permanently remove the notification from the system.
                            </AlertDialogDescription>
                          </AlertDialogHeader>
                          <AlertDialogFooter>
                            <AlertDialogCancel>Cancel</AlertDialogCancel>
                            <AlertDialogAction
                              onClick={() => handleDeleteSingle(log.id)}
                              className="bg-destructive text-destructive-foreground hover:bg-destructive/90"
                            >
                              Delete Notification
                            </AlertDialogAction>
                          </AlertDialogFooter>
                        </AlertDialogContent>
                      </AlertDialog>
                    </div>
                  </div>
                </Card>
              ))}
            </div>
          )}

          {/* Pagination */}
          {pagination.totalPages > 1 && (
            <div className="flex items-center justify-between mt-6">
              <div className="text-sm text-muted-foreground">
                Showing {((pagination.page - 1) * pagination.limit) + 1} to {Math.min(pagination.page * pagination.limit, pagination.total)} of {pagination.total} results
              </div>
              <div className="flex space-x-2">
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => changePage(pagination.page - 1)}
                  disabled={!pagination.hasPrev}
                >
                  Previous
                </Button>
                <Button
                  variant="outline"
                  size="sm"
                  onClick={() => changePage(pagination.page + 1)}
                  disabled={!pagination.hasNext}
                >
                  Next
                </Button>
              </div>
            </div>
          )}
        </CardContent>
      </Card>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { 
  Card, 
  CardContent, 
  CardDescription, 
  CardHeader, 
  CardTitle 
} from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "@/components/ui/select";
import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import { 
  Loader2, 
  RefreshCw, 
  DollarSign,
  X,
  Filter,
  Eye,
  CreditCard,
  Receipt,
  CheckCircle,
  XCircle,
  Building
} from 'lucide-react';
import { toast } from 'sonner';
import { format } from 'date-fns';
import { useAuth } from '@/lib/AuthContext';
import { useBranding } from '@/hooks/useBranding';

interface MongoPayment {
  _id: string;
  paymentIntentId: string;
  userId: string;
  userEmail: string;
  sippyAccountId: number;
  topupAmount: number;
  processingFee: number;
  fixedFee: number;
  totalChargedAmount: number;
  currency: string;
  provider: string;
  gatewayName: string;
  paymentMethodType: string;
  cardBrand?: string;
  cardLast4?: string;
  cardCountry?: string;
  status: string;
  paymentIntentStatus: string;
  failureCode?: string;
  failureMessage?: string;
  paymentInitiatedAt: string;
  paymentCompletedAt?: string;
  paymentReference?: string;
  description?: string;
  notes?: string;
  receiptUrl?: string;
  createdAt: string;
  updatedAt: string;
}

interface PaymentStats {
  totalPayments: number;
  successfulPayments: number;
  totalTopupAmount: number;
  totalFeesCollected: number;
  totalChargedAmount: number;
  averagePaymentAmount: number;
  currencies: string[];
}

interface PaymentFilters {
  status: string;
  provider: string;
  startDate: string;
  endDate: string;
  userEmail: string;
  accountId: string;
}

interface PaymentHistoryResponse {
  success: boolean;
  payments: MongoPayment[];
  pagination: {
    total: number;
    limit: number;
    offset: number;
    hasMore: boolean;
  };
  statistics: PaymentStats;
  filters: PaymentFilters;
}

export function PaymentManagement() {
  const { user } = useAuth();
  const { colors } = useBranding();
  const [payments, setPayments] = useState<MongoPayment[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [stats, setStats] = useState<PaymentStats | null>(null);

  // Pagination state
  const [currentPage, setCurrentPage] = useState(1);
  const [totalRecords, setTotalRecords] = useState(0);
  const recordsPerPage = 50;

  // Filter state
  const [showFilters, setShowFilters] = useState(false);
  const [filters, setFilters] = useState({
    status: 'all',
    provider: 'all',
    startDate: '',
    endDate: '',
    userEmail: '',
    accountId: '',
  });

  // Selected payment for details
  const [selectedPayment, setSelectedPayment] = useState<MongoPayment | null>(null);
  const [showPaymentDetails, setShowPaymentDetails] = useState(false);

  const fetchPayments = async (page: number = currentPage) => {
    if (user?.role !== 'admin') {
      setError('Access denied - Admin only');
      setIsLoading(false);
      return;
    }

    try {
      setIsLoading(true);
      setError(null);

      const queryParams = new URLSearchParams({
        limit: recordsPerPage.toString(),
        offset: ((page - 1) * recordsPerPage).toString(),
        ...(filters.status !== 'all' && { status: filters.status }),
        ...(filters.startDate && { start_date: filters.startDate }),
        ...(filters.endDate && { end_date: filters.endDate }),
        ...(filters.accountId && { account_id: filters.accountId }),
      });

      const response = await fetch(`/api/payments/history?${queryParams}`);

      if (!response.ok) {
        throw new Error('Failed to fetch payment history');
      }

      const data: PaymentHistoryResponse = await response.json();

      setPayments(data.payments);
      setStats(data.statistics);
      setTotalRecords(data.pagination.total);

    } catch (err) {
      console.error('Error fetching payments:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch payments');
      toast.error('Failed to fetch payment history');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchPayments(1);
  }, []);

  const handleFilterChange = (key: string, value: string) => {
    setFilters(prev => ({ ...prev, [key]: value }));
  };

  const clearFilters = () => {
    setFilters({
      status: 'all',
      provider: 'all',
      startDate: '',
      endDate: '',
      userEmail: '',
      accountId: '',
    });
    setCurrentPage(1);
  };

  const applyFilters = () => {
    setCurrentPage(1);
    fetchPayments(1);
  };

  const handlePageChange = (newPage: number) => {
    setCurrentPage(newPage);
    fetchPayments(newPage);
  };

  const formatCurrency = (amount: number, currency: string = 'EUR') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(amount);
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'succeeded':
        return <Badge className="bg-green-100 text-green-800">Succeeded</Badge>;
      case 'failed':
        return <Badge variant="destructive">Failed</Badge>;
      case 'pending':
        return <Badge variant="secondary">Pending</Badge>;
      case 'processing':
        return <Badge className="bg-blue-100 text-blue-800">Processing</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const totalPages = Math.ceil(totalRecords / recordsPerPage);

  if (user?.role !== 'admin') {
    return (
      <Card>
        <CardContent className="text-center py-12">
          <XCircle className="h-16 w-16 text-red-500 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-red-700">Access Denied</h3>
          <p className="text-muted-foreground">This page is restricted to administrators only.</p>
        </CardContent>
      </Card>
    );
  }

  if (error) {
    return (
      <Card className="border-destructive">
        <CardHeader>
          <CardTitle className="text-destructive">Error</CardTitle>
          <CardDescription>Failed to load payment management</CardDescription>
        </CardHeader>
        <CardContent>
          <p className="text-sm text-destructive mb-4">{error}</p>
          <Button onClick={() => fetchPayments()} variant="outline">
            <RefreshCw className="h-4 w-4 mr-2" />
            Retry
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header */}
      <div>
        <h1 className="text-3xl font-bold" style={{ color: colors.primary }}>
          Payment Management
        </h1>
        <p className="text-muted-foreground">
          Manage MongoDB payment records and view detailed analytics
        </p>
      </div>

      {/* Statistics Cards */}
      {stats && (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Total Payments</p>
                  <p className="text-2xl font-bold" style={{ color: colors.primary }}>
                    {stats.totalPayments}
                  </p>
                </div>
                <Receipt className="h-8 w-8" style={{ color: `${colors.primary}60` }} />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Success Rate</p>
                  <p className="text-2xl font-bold text-green-600">
                    {stats.totalPayments > 0 ? Math.round((stats.successfulPayments / stats.totalPayments) * 100) : 0}%
                  </p>
                </div>
                <CheckCircle className="h-8 w-8 text-green-500" />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Total Volume</p>
                  <p className="text-2xl font-bold" style={{ color: colors.primary }}>
                    {formatCurrency(stats.totalChargedAmount, stats.currencies[0] || 'EUR')}
                  </p>
                </div>
                <DollarSign className="h-8 w-8" style={{ color: `${colors.primary}60` }} />
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-4">
              <div className="flex items-center justify-between">
                <div>
                  <p className="text-sm font-medium text-muted-foreground">Fees Collected</p>
                  <p className="text-2xl font-bold text-yellow-600">
                    {formatCurrency(stats.totalFeesCollected, stats.currencies[0] || 'EUR')}
                  </p>
                </div>
                <Building className="h-8 w-8 text-yellow-500" />
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Actions */}
      <div className="flex items-center justify-between">
        <div className="flex items-center gap-2">
          <Button 
            variant="outline" 
            size="sm"
            onClick={() => setShowFilters(!showFilters)}
          >
            <Filter className="h-4 w-4 mr-2" />
            Filters
          </Button>
          <Button 
            variant="outline" 
            size="sm"
            onClick={() => fetchPayments()}
            disabled={isLoading}
          >
            <RefreshCw className={`h-4 w-4 mr-2 ${isLoading ? 'animate-spin' : ''}`} />
            Refresh
          </Button>
        </div>

        <div className="text-sm text-muted-foreground">
          {totalRecords} total payments
        </div>
      </div>

      {/* Filters Panel */}
      {showFilters && (
        <Card>
          <CardHeader className="pb-3">
            <div className="flex items-center justify-between">
              <CardTitle className="text-base">Filters</CardTitle>
              <Button 
                variant="ghost" 
                size="sm" 
                onClick={() => setShowFilters(false)}
              >
                <X className="h-4 w-4" />
              </Button>
            </div>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
              <div className="space-y-2">
                <Label>Status</Label>
                <Select
                  value={filters.status}
                  onValueChange={(value) => handleFilterChange('status', value)}
                >
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Status</SelectItem>
                    <SelectItem value="succeeded">Succeeded</SelectItem>
                    <SelectItem value="failed">Failed</SelectItem>
                    <SelectItem value="pending">Pending</SelectItem>
                    <SelectItem value="processing">Processing</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label>Start Date</Label>
                <Input
                  type="date"
                  value={filters.startDate}
                  onChange={(e) => handleFilterChange('startDate', e.target.value)}
                />
              </div>

              <div className="space-y-2">
                <Label>End Date</Label>
                <Input
                  type="date"
                  value={filters.endDate}
                  onChange={(e) => handleFilterChange('endDate', e.target.value)}
                />
              </div>

              <div className="space-y-2">
                <Label>Account ID</Label>
                <Input
                  type="number"
                  placeholder="Sippy Account ID"
                  value={filters.accountId}
                  onChange={(e) => handleFilterChange('accountId', e.target.value)}
                />
              </div>

              <div className="space-y-2">
                <Label>&nbsp;</Label>
                <div className="flex gap-2">
                  <Button onClick={applyFilters} size="sm" style={{ backgroundColor: colors.primary }}>
                    Apply
                  </Button>
                  <Button onClick={clearFilters} variant="outline" size="sm">
                    Clear
                  </Button>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Main Content */}
      {isLoading ? (
        <Card>
          <CardContent className="text-center py-12">
            <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" style={{ color: colors.primary }} />
            <p>Loading payment records...</p>
          </CardContent>
        </Card>
      ) : payments.length === 0 ? (
        <Card>
          <CardContent className="text-center py-12">
            <Receipt className="h-16 w-16 text-muted-foreground mx-auto mb-4" />
            <h3 className="text-lg font-semibold">No Payment Records Found</h3>
            <p className="text-muted-foreground">No MongoDB payment records match your criteria.</p>
          </CardContent>
        </Card>
      ) : (
        <Card>
          <CardContent className="p-0">
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Date</TableHead>
                    <TableHead>User</TableHead>
                    <TableHead>Account</TableHead>
                    <TableHead>Amount</TableHead>
                    <TableHead>Card</TableHead>
                    <TableHead>Status</TableHead>
                    <TableHead>Provider</TableHead>
                    <TableHead>Actions</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {payments.map((payment) => (
                    <TableRow key={payment._id}>
                      <TableCell>
                        <div>
                          <div className="font-medium">
                            {format(new Date(payment.createdAt), 'MMM dd, yyyy')}
                          </div>
                          <div className="text-xs text-muted-foreground">
                            {format(new Date(payment.createdAt), 'HH:mm:ss')}
                          </div>
                        </div>
                      </TableCell>
                      <TableCell>
                        <div>
                          <div className="font-medium">{payment.userEmail}</div>
                          <div className="text-xs text-muted-foreground">ID: {payment.userId.slice(-8)}</div>
                        </div>
                      </TableCell>
                      <TableCell className="font-mono">{payment.sippyAccountId}</TableCell>
                      <TableCell>
                        <div>
                          <div className="font-bold text-green-600">
                            {formatCurrency(payment.totalChargedAmount, payment.currency)}
                          </div>
                          <div className="text-xs text-muted-foreground">
                            Top-up: {formatCurrency(payment.topupAmount, payment.currency)}
                          </div>
                        </div>
                      </TableCell>
                      <TableCell>
                        {payment.cardBrand && payment.cardLast4 ? (
                          <div className="flex items-center gap-2">
                            <CreditCard className="h-4 w-4" />
                            <span className="capitalize">{payment.cardBrand} â€¢â€¢â€¢â€¢ {payment.cardLast4}</span>
                          </div>
                        ) : (
                          <span className="text-muted-foreground">N/A</span>
                        )}
                      </TableCell>
                      <TableCell>{getStatusBadge(payment.status)}</TableCell>
                      <TableCell className="capitalize">{payment.provider}</TableCell>
                      <TableCell>
                        <Button
                          variant="ghost"
                          size="sm"
                          onClick={() => {
                            setSelectedPayment(payment);
                            setShowPaymentDetails(true);
                          }}
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))}
                </TableBody>
              </Table>
            </div>

            {/* Pagination */}
            <div className="border-t p-4">
              <div className="flex items-center justify-between">
                <div className="text-sm text-muted-foreground">
                  Showing {(currentPage - 1) * recordsPerPage + 1} to {Math.min(currentPage * recordsPerPage, totalRecords)} of {totalRecords} payments
                </div>
                <div className="flex items-center gap-2">
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handlePageChange(currentPage - 1)}
                    disabled={currentPage === 1 || isLoading}
                  >
                    Previous
                  </Button>
                  <span className="text-sm">
                    Page {currentPage} of {totalPages}
                  </span>
                  <Button
                    variant="outline"
                    size="sm"
                    onClick={() => handlePageChange(currentPage + 1)}
                    disabled={currentPage === totalPages || isLoading}
                  >
                    Next
                  </Button>
                </div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Payment Details Dialog */}
      <Dialog open={showPaymentDetails} onOpenChange={setShowPaymentDetails}>
        <DialogContent className="max-w-4xl max-h-[90vh] overflow-y-auto">
          <DialogHeader>
            <DialogTitle>Payment Details</DialogTitle>
            <DialogDescription>
              Complete payment information from MongoDB records
            </DialogDescription>
          </DialogHeader>

          {selectedPayment && (
            <div className="space-y-6">
              {/* Basic Info */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Payment Intent ID</Label>
                  <div className="font-mono text-sm">{selectedPayment.paymentIntentId}</div>
                </div>
                <div>
                  <Label>Payment Reference</Label>
                  <div className="font-mono text-sm">{selectedPayment.paymentReference || 'N/A'}</div>
                </div>
                <div>
                  <Label>User Email</Label>
                  <div>{selectedPayment.userEmail}</div>
                </div>
                <div>
                  <Label>Sippy Account ID</Label>
                  <div>{selectedPayment.sippyAccountId}</div>
                </div>
              </div>

              {/* Amounts */}
              <div>
                <Label>Amount Breakdown</Label>
                <div className="mt-2 p-4 bg-muted/30 rounded-lg">
                  <div className="grid grid-cols-2 gap-4 text-sm">
                    <div className="flex justify-between">
                      <span>Top-up Amount:</span>
                      <span className="font-medium">{formatCurrency(selectedPayment.topupAmount, selectedPayment.currency)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Processing Fee:</span>
                      <span className="font-medium">{formatCurrency(selectedPayment.processingFee, selectedPayment.currency)}</span>
                    </div>
                    <div className="flex justify-between">
                      <span>Fixed Fee:</span>
                      <span className="font-medium">{formatCurrency(selectedPayment.fixedFee, selectedPayment.currency)}</span>
                    </div>
                    <div className="flex justify-between font-bold border-t pt-2">
                      <span>Total Charged:</span>
                      <span>{formatCurrency(selectedPayment.totalChargedAmount, selectedPayment.currency)}</span>
                    </div>
                  </div>
                </div>
              </div>

              {/* Card Info */}
              {selectedPayment.cardBrand && (
                <div>
                  <Label>Payment Method</Label>
                  <div className="mt-2 p-4 bg-muted/30 rounded-lg">
                    <div className="flex items-center gap-4">
                      <CreditCard className="h-8 w-8" />
                      <div>
                        <div className="font-medium capitalize">
                          {selectedPayment.cardBrand} â€¢â€¢â€¢â€¢ {selectedPayment.cardLast4}
                        </div>
                        {selectedPayment.cardCountry && (
                          <div className="text-sm text-muted-foreground">
                            Issued in {selectedPayment.cardCountry}
                          </div>
                        )}
                      </div>
                    </div>
                  </div>
                </div>
              )}

              {/* Status and Dates */}
              <div className="grid grid-cols-2 gap-4">
                <div>
                  <Label>Status</Label>
                  <div className="mt-1">{getStatusBadge(selectedPayment.status)}</div>
                </div>
                <div>
                  <Label>Gateway</Label>
                  <div>{selectedPayment.gatewayName} ({selectedPayment.provider})</div>
                </div>
                <div>
                  <Label>Created At</Label>
                  <div>{format(new Date(selectedPayment.createdAt), 'MMM dd, yyyy HH:mm:ss')}</div>
                </div>
                {selectedPayment.paymentCompletedAt && (
                  <div>
                    <Label>Completed At</Label>
                    <div>{format(new Date(selectedPayment.paymentCompletedAt), 'MMM dd, yyyy HH:mm:ss')}</div>
                  </div>
                )}
              </div>

              {/* Notes */}
              {(selectedPayment.description || selectedPayment.notes) && (
                <div>
                  <Label>Notes</Label>
                  <div className="mt-2 p-4 bg-muted/30 rounded-lg text-sm">
                    {selectedPayment.description && (
                      <div className="mb-2">
                        <strong>Description:</strong> {selectedPayment.description}
                      </div>
                    )}
                    {selectedPayment.notes && (
                      <div>
                        <strong>Notes:</strong> {selectedPayment.notes}
                      </div>
                    )}
                  </div>
                </div>
              )}

              {/* Actions */}
              {selectedPayment.receiptUrl && (
                <div className="flex gap-2">
                  <Button
                    onClick={() => window.open(selectedPayment.receiptUrl, '_blank')}
                    variant="outline"
                  >
                    <Receipt className="h-4 w-4 mr-2" />
                    View Receipt
                  </Button>
                </div>
              )}
            </div>
          )}
        </DialogContent>
      </Dialog>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { Separator } from '@/components/ui/separator';
import { 
  AlertTriangle, 
  RefreshCw, 
  Phone, 
  ExternalLink,
  Calendar,
  AlertCircle,
  CheckCircle2,
  Clock,
  XCircle,
  PhoneCall
} from 'lucide-react';
import Link from 'next/link';
import { toast } from 'sonner';

interface PhoneNumberRequest {
  _id: string;
  requestNumber: string;
  requestType: 'cancel' | 'transfer' | 'suspend' | 'modify';
  status: 'pending' | 'approved' | 'rejected' | 'completed' | 'cancelled';
  priority: 'low' | 'medium' | 'high' | 'urgent';
  reason: string;
  description?: string;
  createdAt: string;
  updatedAt: string;
  phoneNumber: {
    _id: string;
    number: string;
    country: string;
    numberType: string;
    status: string;
  };
  user: {
    _id: string;
    name: string;
    email: string;
    company?: string;
    onboarding?: {
      companyName?: string;
    };
  };
  reviewedAt?: string;
  processedAt?: string;
  daysOpen: number;
}

interface RequestStats {
  total: number;
  byStatus: {
    pending: number;
    approved: number;
    rejected: number;
    completed: number;
    cancelled: number;
  };
  byType: {
    cancel: number;
    transfer: number;
    suspend: number;
    modify: number;
  };
  byPriority: {
    urgent: number;
    high: number;
    medium: number;
    low: number;
  };
  oldestRequestDays: number;
  averageDaysOpen: number;
}

interface PhoneNumberRequestsCardProps {
  onRefresh?: () => void;
  limit?: number;
  isEditMode?: boolean;
}

export function PhoneNumberRequestsCard({ 
  onRefresh, 
  limit = 10,
  isEditMode = false
}: PhoneNumberRequestsCardProps) {
  const [requests, setRequests] = useState<PhoneNumberRequest[]>([]);
  const [stats, setStats] = useState<RequestStats | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchPhoneNumberRequests = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const response = await fetch(`/api/admin/phone-numbers/requests?limit=${limit}&status=pending`);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch phone number requests');
      }

      const data = await response.json();

      // Transform the data and calculate days open
      const transformedRequests = (data.requests || []).map((request: any) => {
        const now = new Date();
        const createdAt = new Date(request.createdAt);
        const daysOpen = Math.floor((now.getTime() - createdAt.getTime()) / (1000 * 60 * 60 * 24));

        return {
          ...request,
          daysOpen,
          user: {
            ...request.user,
            company: request.user.onboarding?.companyName || request.user.company
          }
        };
      });

      setRequests(transformedRequests);

      // Calculate stats
      const statsData: RequestStats = {
        total: transformedRequests.length,
        byStatus: {
          pending: transformedRequests.filter((r: PhoneNumberRequest) => r.status === 'pending').length,
          approved: transformedRequests.filter((r: PhoneNumberRequest) => r.status === 'approved').length,
          rejected: transformedRequests.filter((r: PhoneNumberRequest) => r.status === 'rejected').length,
          completed: transformedRequests.filter((r: PhoneNumberRequest) => r.status === 'completed').length,
          cancelled: transformedRequests.filter((r: PhoneNumberRequest) => r.status === 'cancelled').length,
        },
        byType: {
          cancel: transformedRequests.filter((r: PhoneNumberRequest) => r.requestType === 'cancel').length,
          transfer: transformedRequests.filter((r: PhoneNumberRequest) => r.requestType === 'transfer').length,
          suspend: transformedRequests.filter((r: PhoneNumberRequest) => r.requestType === 'suspend').length,
          modify: transformedRequests.filter((r: PhoneNumberRequest) => r.requestType === 'modify').length,
        },
        byPriority: {
          urgent: transformedRequests.filter((r: PhoneNumberRequest) => r.priority === 'urgent').length,
          high: transformedRequests.filter((r: PhoneNumberRequest) => r.priority === 'high').length,
          medium: transformedRequests.filter((r: PhoneNumberRequest) => r.priority === 'medium').length,
          low: transformedRequests.filter((r: PhoneNumberRequest) => r.priority === 'low').length,
        },
        oldestRequestDays: transformedRequests.length > 0 
          ? Math.max(...transformedRequests.map((r: PhoneNumberRequest) => r.daysOpen))
          : 0,
        averageDaysOpen: transformedRequests.length > 0
          ? Math.round(transformedRequests.reduce((sum: number, r: PhoneNumberRequest) => sum + r.daysOpen, 0) / transformedRequests.length)
          : 0,
      };

      setStats(statsData);
    } catch (error) {
      console.error('Error fetching phone number requests:', error);
      setError(error instanceof Error ? error.message : 'Failed to load requests');
      toast.error('Failed to load phone number requests');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchPhoneNumberRequests();
  }, [limit]);

  const handleRefresh = () => {
    fetchPhoneNumberRequests();
    onRefresh?.();
  };

  const getInitials = (name: string) => {
    return name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const getPriorityBadgeVariant = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'destructive';
      case 'high': return 'secondary';
      case 'medium': return 'outline';
      case 'low': return 'outline';
      default: return 'outline';
    }
  };

  const getRequestTypeIcon = (type: string) => {
    switch (type) {
      case 'cancel': return <XCircle className="h-3 w-3 text-red-500" />;
      case 'transfer': return <PhoneCall className="h-3 w-3 text-blue-500" />;
      case 'suspend': return <Clock className="h-3 w-3 text-orange-500" />;
      case 'modify': return <AlertCircle className="h-3 w-3 text-purple-500" />;
      default: return <Phone className="h-3 w-3 text-gray-500" />;
    }
  };

  const getRequestTypeDisplayName = (type: string) => {
    switch (type) {
      case 'cancel': return 'Cancel';
      case 'transfer': return 'Transfer';
      case 'suspend': return 'Suspend';
      case 'modify': return 'Modify';
      default: return type.charAt(0).toUpperCase() + type.slice(1);
    }
  };

  const formatDaysAgo = (daysOpen: number) => {
    if (daysOpen === 0) return 'Today';
    if (daysOpen === 1) return '1 day ago';
    return `${daysOpen} days ago`;
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-muted rounded animate-pulse" />
            <div className="w-32 h-4 bg-muted rounded animate-pulse" />
          </div>
          <div className="w-6 h-6 bg-muted rounded animate-pulse" />
        </div>
        {[...Array(3)].map((_, i) => (
          <div key={i} className="flex items-center space-x-3 p-3 border rounded-lg">
            <div className="w-10 h-10 bg-muted rounded-full animate-pulse" />
            <div className="flex-1 space-y-2">
              <div className="w-48 h-4 bg-muted rounded animate-pulse" />
              <div className="w-32 h-3 bg-muted rounded animate-pulse" />
            </div>
            <div className="w-16 h-6 bg-muted rounded animate-pulse" />
          </div>
        ))}
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
        <h3 className="text-lg font-semibold text-red-700 dark:text-red-300 mb-2">
          Error Loading Requests
        </h3>
        <p className="text-sm text-muted-foreground mb-4">{error}</p>
        <Button onClick={handleRefresh} variant="outline" size="sm">
          <RefreshCw className="h-4 w-4 mr-2" />
          Try Again
        </Button>
      </div>
    );
  }

  if (requests.length === 0) {
    return (
      <div className="space-y-4">
        <div className="text-center py-8">
          <CheckCircle2 className="h-12 w-12 text-green-500 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-green-700 dark:text-green-300 mb-2">
            All Caught Up!
          </h3>
          <p className="text-sm text-muted-foreground mb-4">
            No pending phone number requests at the moment.
          </p>
          <Button onClick={handleRefresh} variant="outline" size="sm">
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Phone className="h-4 w-4 text-blue-500" />
          <span className="text-sm font-medium">
            {requests.length} pending request{requests.length !== 1 ? 's' : ''}
          </span>
          {stats && stats.averageDaysOpen > 0 && (
            <Badge variant="outline" className="text-xs">
              Avg {stats.averageDaysOpen} day{stats.averageDaysOpen !== 1 ? 's' : ''} open
            </Badge>
          )}
        </div>
        <Button
          onClick={handleRefresh}
          variant="ghost"
          size="sm"
          className="h-8 w-8 p-0"
        >
          <RefreshCw className="h-4 w-4" />
        </Button>
      </div>

      {/* Quick Stats */}
      {stats && isEditMode && (
        <div className="grid grid-cols-2 gap-2 p-3 bg-muted/30 rounded-lg border-2 border-dashed border-primary/20">
          <div className="text-center">
            <div className="text-lg font-bold text-red-600">{stats.byPriority.urgent + stats.byPriority.high}</div>
            <div className="text-xs text-muted-foreground">High Priority</div>
          </div>
          <div className="text-center">
            <div className="text-lg font-bold text-orange-600">{stats.oldestRequestDays}</div>
            <div className="text-xs text-muted-foreground">Oldest (days)</div>
          </div>
        </div>
      )}

      {/* Requests List */}
      <div className="space-y-3 max-h-96 overflow-y-auto">
        {requests.map((request, index) => (
          <div key={request._id}>
            <div className="flex items-center space-x-3 p-3 border rounded-lg hover:bg-muted/50 transition-colors">
              {/* User Avatar and Info */}
              <div className="flex items-center space-x-3">
                {/* Avatar */}
                <Avatar className="h-10 w-10">
                  <AvatarFallback className="text-xs font-medium bg-gradient-to-br from-blue-500 to-purple-600 text-white">
                    {getInitials(request.user.name)}
                  </AvatarFallback>
                </Avatar>

                {/* User Details */}
                <div className="flex-1 min-w-0">
                  <div className="flex items-center space-x-2">
                    <div className="flex items-center space-x-2 min-w-0">
                      <p className="text-sm font-medium text-gray-900 dark:text-gray-100 truncate">
                        {request.user.name}
                      </p>
                      {request.user.company && (
                        <>
                          <span className="text-muted-foreground">â€¢</span>
                          <span className="text-sm text-muted-foreground truncate">{request.user.company}</span>
                        </>
                      )}
                    </div>
                  </div>
                  <p className="text-xs text-muted-foreground truncate">
                    {request.user.email}
                  </p>
                </div>
              </div>

              {/* Request Info */}
              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-2 mb-1">
                  <code className="text-xs font-mono bg-muted px-1 py-0.5 rounded">
                    {request.requestNumber}
                  </code>
                  <Badge 
                    variant={getPriorityBadgeVariant(request.priority)} 
                    className="text-xs"
                  >
                    {request.priority.toUpperCase()}
                  </Badge>
                  <div className="flex items-center space-x-1">
                    {getRequestTypeIcon(request.requestType)}
                    <span className="text-xs text-muted-foreground">
                      {getRequestTypeDisplayName(request.requestType)}
                    </span>
                  </div>
                </div>

                <div className="flex items-center space-x-2 mb-1">
                  <Phone className="h-3 w-3 text-muted-foreground" />
                  <span className="text-sm font-medium">{request.phoneNumber.number}</span>
                  <span className="text-xs text-muted-foreground">({request.phoneNumber.country})</span>
                </div>

                <div className="flex items-center space-x-4 text-xs text-muted-foreground">
                  <div className="flex items-center space-x-1">
                    <Calendar className="h-3 w-3" />
                    <span>{formatDaysAgo(request.daysOpen)}</span>
                  </div>
                </div>

                <p className="text-xs text-muted-foreground mt-1 truncate" title={request.reason}>
                  {request.reason}
                </p>
              </div>

              {/* Actions */}
              <div className="flex items-center space-x-2">
                <Link href={`/admin/phone-number-requests`} passHref>
                  <Button variant="outline" size="sm" className="h-8">
                    <ExternalLink className="h-3 w-3 mr-1" />
                    View
                  </Button>
                </Link>
              </div>
            </div>

            {index < requests.length - 1 && <Separator className="my-2" />}
          </div>
        ))}
      </div>

      {/* Summary Footer */}
      {requests.length > 0 && stats && (
        <div className="pt-3 border-t">
          <div className="flex items-center justify-between text-xs text-muted-foreground">
            <span>
              Showing {requests.length} pending request{requests.length !== 1 ? 's' : ''}
            </span>
            <span>
              {stats.byPriority.urgent} urgent, {stats.byPriority.high} high priority
            </span>
          </div>
        </div>
      )}
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { Separator } from '@/components/ui/separator';
import { 
  AlertTriangle, 
  RefreshCw, 
  Ticket, 
  User,
  ExternalLink,
  MessageSquare,
  Calendar,
  AlertCircle,
  CheckCircle2,
  Timer,
  UserCheck
} from 'lucide-react';
import Link from 'next/link';
import { toast } from 'sonner';

interface UnsolvedTicket {
  id: string;
  ticketNumber: string;
  title: string;
  service: string;
  priority: 'low' | 'medium' | 'high' | 'urgent';
  status: string;
  createdAt: Date;
  updatedAt: Date;
  user: {
    id: string;
    name: string;
    email: string;
    companyName?: string;
  };
  assignedTo?: {
    id: string;
    name: string;
    email: string;
  };
  daysOpen: number;
  lastReplyAt?: Date;
  replyCount: number;
}

interface TicketStats {
  total: number;
  byStatus: {
    open: number;
    in_progress: number;
    waiting_user: number;
    waiting_admin: number;
  };
  byPriority: {
    urgent: number;
    high: number;
    medium: number;
    low: number;
  };
  oldestTicketDays: number;
  averageDaysOpen: number;
}

interface UnsolvedTicketsCardProps {
  onRefresh?: () => void;
  limit?: number;
  isEditMode?: boolean;
}

export function UnsolvedTicketsCard({ 
  onRefresh, 
  limit = 10,
  isEditMode = false
}: UnsolvedTicketsCardProps) {
  const [tickets, setTickets] = useState<UnsolvedTicket[]>([]);
  const [stats, setStats] = useState<TicketStats | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);

  const fetchUnsolvedTickets = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const response = await fetch(`/api/admin/unsolved-tickets?limit=${limit}`);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch unsolved tickets');
      }

      const data = await response.json();
      setTickets(data.tickets || []);
      setStats(data.stats || null);
    } catch (error) {
      console.error('Error fetching unsolved tickets:', error);
      setError(error instanceof Error ? error.message : 'Failed to load tickets');
      toast.error('Failed to load unsolved tickets');
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    fetchUnsolvedTickets();
  }, [limit]);

  const handleRefresh = () => {
    fetchUnsolvedTickets();
    onRefresh?.();
  };

  const getInitials = (name: string) => {
    return name
      .split(' ')
      .map(n => n[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  const getPriorityBadgeVariant = (priority: string) => {
    switch (priority) {
      case 'urgent': return 'destructive';
      case 'high': return 'secondary';
      case 'medium': return 'outline';
      case 'low': return 'outline';
      default: return 'outline';
    }
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'open': return <AlertCircle className="h-3 w-3 text-red-500" />;
      case 'in_progress': return <Timer className="h-3 w-3 text-blue-500" />;
      case 'waiting_user': return <User className="h-3 w-3 text-orange-500" />;
      case 'waiting_admin': return <UserCheck className="h-3 w-3 text-purple-500" />;
      default: return <Ticket className="h-3 w-3 text-gray-500" />;
    }
  };

  const getStatusDisplayName = (status: string) => {
    switch (status) {
      case 'open': return 'Open';
      case 'in_progress': return 'In Progress';
      case 'waiting_user': return 'Waiting User';
      case 'waiting_admin': return 'Waiting Admin';
      default: return status.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase());
    }
  };

  const getServiceDisplayName = (service: string) => {
    const serviceNames = {
      'outbound_calls': 'Outbound Calls',
      'inbound_calls': 'Inbound Calls',
      'did_numbers': 'DID Numbers',
      'sms': 'SMS',
      'emailing': 'Email',
      'whatsapp_business': 'WhatsApp Business',
      'billing': 'Billing',
      'technical': 'Technical',
      'other': 'Other'
    };
    return serviceNames[service as keyof typeof serviceNames] || service.replace('_', ' ').replace(/\b\w/g, l => l.toUpperCase());
  };

  const formatDaysAgo = (daysOpen: number) => {
    if (daysOpen === 0) return 'Today';
    if (daysOpen === 1) return '1 day ago';
    return `${daysOpen} days ago`;
  };

  if (isLoading) {
    return (
      <div className="space-y-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <div className="w-4 h-4 bg-muted rounded animate-pulse" />
            <div className="w-32 h-4 bg-muted rounded animate-pulse" />
          </div>
          <div className="w-6 h-6 bg-muted rounded animate-pulse" />
        </div>
        {[...Array(3)].map((_, i) => (
          <div key={i} className="flex items-center space-x-3 p-3 border rounded-lg">
            <div className="w-10 h-10 bg-muted rounded-full animate-pulse" />
            <div className="flex-1 space-y-2">
              <div className="w-48 h-4 bg-muted rounded animate-pulse" />
              <div className="w-32 h-3 bg-muted rounded animate-pulse" />
            </div>
            <div className="w-16 h-6 bg-muted rounded animate-pulse" />
          </div>
        ))}
      </div>
    );
  }

  if (error) {
    return (
      <div className="text-center py-8">
        <AlertTriangle className="h-12 w-12 text-red-500 mx-auto mb-4" />
        <h3 className="text-lg font-semibold text-red-700 dark:text-red-300 mb-2">
          Error Loading Tickets
        </h3>
        <p className="text-sm text-muted-foreground mb-4">{error}</p>
        <Button onClick={handleRefresh} variant="outline" size="sm">
          <RefreshCw className="h-4 w-4 mr-2" />
          Try Again
        </Button>
      </div>
    );
  }

  if (tickets.length === 0) {
    return (
      <div className="space-y-4">
        <div className="text-center py-8">
          <CheckCircle2 className="h-12 w-12 text-green-500 mx-auto mb-4" />
          <h3 className="text-lg font-semibold text-green-700 dark:text-green-300 mb-2">
            All Caught Up!
          </h3>
          <p className="text-sm text-muted-foreground mb-4">
            No unsolved tickets at the moment. Great work!
          </p>
          <Button onClick={handleRefresh} variant="outline" size="sm">
            <RefreshCw className="h-4 w-4 mr-2" />
            Refresh
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      {/* Header */}
      <div className="flex items-center justify-between">
        <div className="flex items-center space-x-2">
          <Ticket className="h-4 w-4 text-orange-500" />
          <span className="text-sm font-medium">
            {tickets.length} unsolved ticket{tickets.length !== 1 ? 's' : ''}
          </span>
          {stats && stats.averageDaysOpen > 0 && (
            <Badge variant="outline" className="text-xs">
              Avg {stats.averageDaysOpen} day{stats.averageDaysOpen !== 1 ? 's' : ''} open
            </Badge>
          )}
        </div>
        <Button
          onClick={handleRefresh}
          variant="ghost"
          size="sm"
          className="h-8 w-8 p-0"
        >
          <RefreshCw className="h-4 w-4" />
        </Button>
      </div>

      {/* Quick Stats */}
      {stats && isEditMode && (
        <div className="grid grid-cols-2 gap-2 p-3 bg-muted/30 rounded-lg border-2 border-dashed border-primary/20">
          <div className="text-center">
            <div className="text-lg font-bold text-red-600">{stats.byPriority.urgent + stats.byPriority.high}</div>
            <div className="text-xs text-muted-foreground">High Priority</div>
          </div>
          <div className="text-center">
            <div className="text-lg font-bold text-orange-600">{stats.oldestTicketDays}</div>
            <div className="text-xs text-muted-foreground">Oldest (days)</div>
          </div>
        </div>
      )}

      {/* Tickets List */}
      <div className="space-y-3 max-h-96 overflow-y-auto">
        {tickets.map((ticket, index) => (
          <div key={ticket.id}>
            <div className="flex items-center space-x-3 p-3 border rounded-lg hover:bg-muted/50 transition-colors">
              {/* User Avatar and Info */}
              <div className="flex items-center space-x-3">
                {/* Avatar */}
                <Avatar className="h-10 w-10">
                  <AvatarFallback className="text-xs font-medium bg-gradient-to-br from-blue-500 to-purple-600 text-white">
                    {getInitials(ticket.user.name)}
                  </AvatarFallback>
                </Avatar>

                {/* User Details */}
                <div className="flex-1 min-w-0">
                  <div className="flex items-center space-x-2">
                    <p className="text-sm font-medium text-gray-900 dark:text-gray-100 truncate">
                      {ticket.user.name}
                    </p>
                    {ticket.assignedTo && (
                      <Badge variant="secondary" className="text-xs">
                        <UserCheck className="w-3 h-3 mr-1" />
                        Assigned
                      </Badge>
                    )}
                  </div>
                  {ticket.user.companyName && (
                    <p className="text-xs text-muted-foreground truncate">
                      {ticket.user.companyName}
                    </p>
                  )}
                  <p className="text-xs text-muted-foreground truncate">
                    {ticket.user.email}
                  </p>
                </div>
              </div>

              {/* Ticket Info */}
              <div className="flex-1 min-w-0">
                <div className="flex items-center space-x-2 mb-1">
                  <code className="text-xs font-mono bg-muted px-1 py-0.5 rounded">
                    {ticket.ticketNumber}
                  </code>
                  <Badge 
                    variant={getPriorityBadgeVariant(ticket.priority)} 
                    className="text-xs"
                  >
                    {ticket.priority.toUpperCase()}
                  </Badge>
                  <div className="flex items-center space-x-1">
                    {getStatusIcon(ticket.status)}
                    <span className="text-xs text-muted-foreground">
                      {getStatusDisplayName(ticket.status)}
                    </span>
                  </div>
                </div>

                <h4 className="text-sm font-medium truncate mb-1" title={ticket.title}>
                  {ticket.title}
                </h4>

                <div className="flex items-center space-x-4 text-xs text-muted-foreground">
                  <div className="flex items-center space-x-1">
                    <Calendar className="h-3 w-3" />
                    <span>{formatDaysAgo(ticket.daysOpen)}</span>
                  </div>

                  {ticket.replyCount > 0 && (
                    <div className="flex items-center space-x-1">
                      <MessageSquare className="h-3 w-3" />
                      <span>{ticket.replyCount}</span>
                    </div>
                  )}
                </div>

                <div className="flex items-center space-x-2 mt-1">
                  <Badge variant="outline" className="text-xs">
                    {getServiceDisplayName(ticket.service)}
                  </Badge>
                  {ticket.assignedTo && (
                    <Badge variant="secondary" className="text-xs">
                      Assigned to {ticket.assignedTo.name}
                    </Badge>
                  )}
                </div>
              </div>

              {/* Actions */}
              <div className="flex items-center space-x-2">
                <Link href={`/tickets/${ticket.id}`} passHref>
                  <Button variant="outline" size="sm" className="h-8">
                    <ExternalLink className="h-3 w-3 mr-1" />
                    View
                  </Button>
                </Link>
              </div>
            </div>

            {index < tickets.length - 1 && <Separator className="my-2" />}
          </div>
        ))}
      </div>

      {/* Summary Footer */}
      {tickets.length > 0 && stats && (
        <div className="pt-3 border-t">
          <div className="flex items-center justify-between text-xs text-muted-foreground">
            <span>
              Showing {tickets.length} of {stats.total} unsolved ticket{stats.total !== 1 ? 's' : ''}
            </span>
            <span>
              {stats.byPriority.urgent} urgent, {stats.byPriority.high} high priority
            </span>
          </div>
        </div>
      )}
    </div>
  );
}

