  onUpdatePriority?: (priority: number) => void;
  onToggleShowTitle?: (showTitle: boolean) => void;
  trigger?: React.ReactNode;
  open?: boolean;
  onOpenChange?: (open: boolean) => void;
}

export function WidgetSettingsModal({
  id,
  title,
  enabled = true,
  collapsible = true,
  locked = false,
  alwaysVisible = false,
  gridCols = 3,
  gridRows = 1,
