'use client';

import { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogDescription, DialogFooter, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { Button } from '@/components/ui/button';
import { Card, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { toast } from 'sonner';
import InternalNotificationService from '@/services/InternalNotificationService';
import { Bell, Volume2, Smartphone, X, Check } from 'lucide-react';

interface NotificationPermissionModalProps {
  isOpen: boolean;
  onClose: () => void;
  onComplete: (granted: boolean) => void;
}

export function NotificationPermissionModal({ 
  isOpen, 
  onComplete 
}: NotificationPermissionModalProps) {
  const [isRequesting, setIsRequesting] = useState(false);
  const [permissionStatus, setPermissionStatus] = useState<NotificationPermission>('default');

  const notificationService = InternalNotificationService.getInstance();

  useEffect(() => {
    if (typeof window !== 'undefined' && 'Notification' in window) {
      setPermissionStatus(Notification.permission);
    }
  }, []);

  const handleRequestPermission = async () => {
    if (!('Notification' in window)) {
      toast.error('Notifications are not supported in this browser');
      onComplete(false);
      return;
    }

    setIsRequesting(true);
    try {
      // Request browser notification permission
      const permission = await Notification.requestPermission();
      setPermissionStatus(permission);

      if (permission === 'granted') {
        // Request push notification permission through our service
        const pushGranted = await notificationService.requestPushPermission();

        if (pushGranted) {
          // Enable push notifications in user preferences
          await fetch('/api/notifications/enable-push-for-user', {
            method: 'POST',
            headers: {
              'Content-Type': 'application/json',
            },
            credentials: 'include'
          });

          // Show a test notification
          setTimeout(() => {
            new Notification('Welcome to OVO!', {
              body: 'You&apos;ll now receive important notifications about your account.',
              icon: '/icons/notification-icon.svg',
              badge: '/icons/notification-badge.svg'
            });
          }, 1000);

          toast.success('Notifications enabled successfully!');
        } else {
          toast.warning('Basic notifications enabled, but push notifications may not work properly');
        }

        // Only mark as "asked" when permission is granted
        await fetch('/api/user/notification-permission-requested', {
          method: 'POST',
          credentials: 'include'
        });

        onComplete(true);
      } else {
        // Permission denied - don't mark as asked so modal can appear again
        toast.info('You can enable notifications later by clicking the notification icon in your browser&apos;s address bar');
        onComplete(false);
      }

    } catch (error) {
      console.error('Error requesting notification permission:', error);
      toast.error('Failed to set up notifications');
      onComplete(false);
    } finally {
      setIsRequesting(false);
    }
  };

  const handleSkip = async () => {
    // Don't mark as "asked" when user skips, so modal can appear again
    toast.info('You can enable notifications later in Settings');
    onComplete(false);
  };

  if (!isOpen) return null;

  return (
    <Dialog open={isOpen} onOpenChange={() => {}}>
      <DialogContent className="sm:max-w-[500px]" onPointerDownOutside={(e) => e.preventDefault()}>
        <DialogHeader>
          <DialogTitle className="flex items-center gap-2">
            <Bell className="w-5 h-5 text-blue-600" />
            Stay Updated with Notifications
          </DialogTitle>
          <DialogDescription>
            Enable notifications to receive important updates about your account, 
            support tickets, and payment activities.
          </DialogDescription>
        </DialogHeader>

        <div className="space-y-4">
          {/* Notification Types */}
          <div className="grid grid-cols-1 gap-3">
            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-sm flex items-center gap-2">
                  <Bell className="w-4 h-4" />
                  Desktop Notifications
                </CardTitle>
                <CardDescription className="text-xs">
                  Get instant alerts in your browser for important events
                </CardDescription>
              </CardHeader>
            </Card>

            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-sm flex items-center gap-2">
                  <Volume2 className="w-4 h-4" />
                  Sound Alerts
                </CardTitle>
                <CardDescription className="text-xs">
                  Audio notifications for urgent messages and alerts
                </CardDescription>
              </CardHeader>
            </Card>

            <Card>
              <CardHeader className="pb-3">
                <CardTitle className="text-sm flex items-center gap-2">
                  <Smartphone className="w-4 h-4" />
                  Push Notifications
                </CardTitle>
                <CardDescription className="text-xs">
                  Receive notifications even when the app is closed
                </CardDescription>
              </CardHeader>
            </Card>
          </div>

          {/* Current Status */}
          {permissionStatus !== 'default' && (
            <div className="p-3 bg-gray-50 rounded-lg">
              <div className="flex items-center justify-between">
                <span className="text-sm text-gray-600">Current Permission Status:</span>
                <Badge variant={permissionStatus === 'granted' ? 'default' : 'secondary'}>
                  {permissionStatus === 'granted' ? (
                    <>
                      <Check className="w-3 h-3 mr-1" />
                      Granted
                    </>
                  ) : (
                    <>
                      <X className="w-3 h-3 mr-1" />
                      Denied
                    </>
                  )}
                </Badge>
              </div>
            </div>
          )}

          {/* Benefits */}
          <div className="text-sm text-gray-600">
            <p className="font-medium mb-2">You'll be notified about:</p>
            <ul className="space-y-1 text-xs">
              <li>â€¢ Support ticket updates and responses</li>
              <li>â€¢ Payment confirmations and billing alerts</li>
              <li>â€¢ Account balance and low balance warnings</li>
              <li>â€¢ Phone number approvals and assignments</li>
              <li>â€¢ System maintenance and security alerts</li>
            </ul>
          </div>
        </div>

        <DialogFooter className="flex gap-2">
          <Button
            variant="outline"
            onClick={handleSkip}
            disabled={isRequesting}
          >
            Maybe Later
          </Button>
          <Button
            onClick={handleRequestPermission}
            disabled={isRequesting || permissionStatus === 'granted'}
            className="flex-1"
          >
            {isRequesting ? (
              'Setting up...'
            ) : permissionStatus === 'granted' ? (
              'Already Enabled'
            ) : (
              'Enable Notifications'
            )}
          </Button>
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { X, AlertTriangle, Info, Bell, ExternalLink } from 'lucide-react';
import { InternalNotification } from '@/types/notifications';
import { motion, AnimatePresence } from 'framer-motion';
import { cn } from '@/lib/utils';

interface NotificationToastProps {
  notification: InternalNotification;
  onDismiss: () => void;
  onAction?: () => void;
  duration?: number;
  position?: 'top-right' | 'top-left' | 'bottom-right' | 'bottom-left' | 'top-center' | 'bottom-center';
}

interface ToastPosition {
  top?: string;
  bottom?: string;
  left?: string;
  right?: string;
  transform?: string;
}

const getPositionStyles = (position: string): ToastPosition => {
  switch (position) {
    case 'top-left':
      return { top: '24px', left: '24px' };
    case 'top-right':
      return { top: '24px', right: '24px' };
    case 'top-center':
      return { top: '24px', left: '50%', transform: 'translateX(-50%)' };
    case 'bottom-left':
      return { bottom: '24px', left: '24px' };
    case 'bottom-right':
      return { bottom: '24px', right: '24px' };
    case 'bottom-center':
      return { bottom: '24px', left: '50%', transform: 'translateX(-50%)' };
    default:
      return { top: '24px', right: '24px' };
  }
};

const getPriorityIcon = (priority: string) => {
  switch (priority) {
    case 'urgent':
      return <AlertTriangle className="w-6 h-6 text-red-500" />;
    case 'high':
      return <AlertTriangle className="w-6 h-6 text-orange-500" />;
    case 'medium':
      return <Info className="w-6 h-6 text-blue-500" />;
    case 'low':
      return <Bell className="w-6 h-6 text-gray-500" />;
    default:
      return <Info className="w-6 h-6 text-blue-500" />;
  }
};

const getPriorityColors = (priority: string) => {
  switch (priority) {
    case 'urgent':
      return {
        border: 'border-l-red-500',
        bg: 'bg-red-50 dark:bg-red-950/20',
        accent: 'text-red-700 dark:text-red-300'
      };
    case 'high':
      return {
        border: 'border-l-orange-500',
        bg: 'bg-orange-50 dark:bg-orange-950/20',
        accent: 'text-orange-700 dark:text-orange-300'
      };
    case 'medium':
      return {
        border: 'border-l-blue-500',
        bg: 'bg-blue-50 dark:bg-blue-950/20',
        accent: 'text-blue-700 dark:text-blue-300'
      };
    case 'low':
      return {
        border: 'border-l-gray-500',
        bg: 'bg-gray-50 dark:bg-gray-950/20',
        accent: 'text-gray-700 dark:text-gray-300'
      };
    default:
      return {
        border: 'border-l-blue-500',
        bg: 'bg-blue-50 dark:bg-blue-950/20',
        accent: 'text-blue-700 dark:text-blue-300'
      };
  }
};

export default function NotificationToast({ 
  notification, 
  onDismiss, 
  onAction,
  duration = 5000,
  position = 'top-right' 
}: NotificationToastProps) {
  const [isVisible, setIsVisible] = useState(true);
  const [progress, setProgress] = useState(100);
  const [isPaused, setIsPaused] = useState(false);

  const colors = getPriorityColors(notification.priority);
  const positionStyles = getPositionStyles(position);

  useEffect(() => {
    if (!isPaused && duration > 0) {
      const interval = setInterval(() => {
        setProgress((prev) => {
          const newProgress = prev - (100 / (duration / 100));
          if (newProgress <= 0) {
            handleDismiss();
            return 0;
          }
          return newProgress;
        });
      }, 100);

      return () => clearInterval(interval);
    }
  }, [isPaused, duration]);

  const handleDismiss = () => {
    setIsVisible(false);
    setTimeout(() => onDismiss(), 300); // Wait for animation
  };

  const handleAction = () => {
    if (notification.actionUrl) {
      window.open(notification.actionUrl, '_blank');
    }
    if (onAction) {
      onAction();
    }
    handleDismiss();
  };

  const slideDirection = position.includes('left') ? -100 : position.includes('right') ? 100 : 0;
  const slideAxis = position.includes('left') || position.includes('right') ? 'x' : 'y';
  const initialSlide = slideAxis === 'x' ? slideDirection : position.includes('top') ? -100 : 100;

  return (
    <AnimatePresence>
      {isVisible && (
        <motion.div
          initial={{ 
            opacity: 0,
            scale: 0.8,
            [slideAxis]: initialSlide
          }}
          animate={{ 
            opacity: 1,
            scale: 1,
            [slideAxis]: 0
          }}
          exit={{ 
            opacity: 0,
            scale: 0.8,
            [slideAxis]: initialSlide
          }}
          transition={{
            type: "spring",
            stiffness: 300,
            damping: 30,
            duration: 0.3
          }}
          className="fixed z-50 pointer-events-auto"
          style={positionStyles}
          onMouseEnter={() => setIsPaused(true)}
          onMouseLeave={() => setIsPaused(false)}
        >
          <div className={cn(
            "relative min-w-[400px] max-w-[500px] rounded-xl shadow-2xl border border-gray-200 dark:border-gray-700",
            "backdrop-blur-md bg-white/95 dark:bg-gray-900/95",
            "overflow-hidden",
            colors.bg,
            colors.border,
            "border-l-4"
          )}>
            {/* Progress bar */}
            {duration > 0 && (
              <div className="absolute top-0 left-0 h-1 bg-gray-200 dark:bg-gray-700 w-full">
                <motion.div
                  className={cn("h-full transition-all duration-100", colors.accent.replace('text-', 'bg-'))}
                  style={{ width: `${progress}%` }}
                />
              </div>
            )}

            <div className="relative p-6">
              {/* Header */}
              <div className="flex items-start justify-between gap-4 mb-3">
                <div className="flex items-center gap-3">
                  <div className="flex-shrink-0">
                    {getPriorityIcon(notification.priority)}
                  </div>
                  <div className="flex-1 min-w-0">
                    <h3 className="text-lg font-semibold text-gray-900 dark:text-gray-100 leading-tight">
                      {notification.title}
                    </h3>
                    <div className="flex items-center gap-2 mt-1">
                      <span className={cn(
                        "inline-flex items-center px-2 py-1 rounded-full text-xs font-medium",
                        colors.bg,
                        colors.accent
                      )}>
                        {notification.priority.toUpperCase()}
                      </span>
                      <span className="text-xs text-gray-500 dark:text-gray-400">
                        {notification.type.replace(/_/g, ' ').replace(/\b\w/g, l => l.toUpperCase())}
                      </span>
                    </div>
                  </div>
                </div>

                <button
                  onClick={handleDismiss}
                  className="flex-shrink-0 p-1 rounded-lg hover:bg-gray-100 dark:hover:bg-gray-800 transition-colors"
                >
                  <X className="w-5 h-5 text-gray-400" />
                </button>
              </div>

              {/* Message */}
              <div className="mb-4">
                <p className="text-gray-700 dark:text-gray-300 leading-relaxed">
                  {notification.message}
                </p>

                {/* Additional data display */}
                {notification.data && Object.keys(notification.data).length > 0 && (
                  <div className="mt-3 p-3 rounded-lg bg-gray-50 dark:bg-gray-800/50">
                    <div className="space-y-1">
                      {Object.entries(notification.data).map(([key, value]) => (
                        <div key={key} className="flex justify-between text-sm">
                          <span className="text-gray-500 dark:text-gray-400 capitalize">
                            {key.replace(/([A-Z])/g, ' $1').trim()}:
                          </span>
                          <span className="text-gray-700 dark:text-gray-300 font-medium">
                            {typeof value === 'object' ? JSON.stringify(value) : String(value)}
                          </span>
                        </div>
                      ))}
                    </div>
                  </div>
                )}
              </div>

              {/* Actions */}
              <div className="flex items-center justify-between">
                <div className="text-xs text-gray-500 dark:text-gray-400">
                  {new Date(notification.createdAt).toLocaleTimeString()}
                </div>

                <div className="flex gap-2">
                  <button
                    onClick={handleDismiss}
                    className="px-3 py-1.5 text-sm font-medium text-gray-600 dark:text-gray-400 hover:text-gray-800 dark:hover:text-gray-200 transition-colors"
                  >
                    Dismiss
                  </button>

                  {notification.actionUrl && (
                    <button
                      onClick={handleAction}
                      className={cn(
                        "inline-flex items-center gap-1.5 px-4 py-1.5 text-sm font-medium rounded-lg transition-all",
                        "bg-blue-600 hover:bg-blue-700 text-white shadow-sm hover:shadow-md"
                      )}
                    >
                      View Details
                      <ExternalLink className="w-3.5 h-3.5" />
                    </button>
                  )}
                </div>
              </div>
            </div>

            {/* Decorative elements */}
            <div className="absolute top-4 right-4 opacity-10">
              <div className={cn("w-20 h-20 rounded-full", colors.accent.replace('text-', 'bg-'))} />
            </div>
            <div className="absolute bottom-2 left-2 opacity-5">
              <div className={cn("w-12 h-12 rounded-full", colors.accent.replace('text-', 'bg-'))} />
            </div>
          </div>
        </motion.div>
      )}
    </AnimatePresence>
  );
}

'use client';

import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Textarea } from '@/components/ui/textarea';
import { Checkbox } from '@/components/ui/checkbox';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Select, SelectContent, SelectItem, SelectTrigger, SelectValue } from '@/components/ui/select';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { CountrySelector } from '@/components/ui/country-selector';
import { MultiCountrySelector } from '@/components/ui/multi-country-selector';
import { 
  Building2, 
  Phone, 
  Mail, 
  MessageCircle, 
  Plus, 
  X, 
  Save,
  BarChart3,
  Loader2,
  Edit,
  Check,
  ArrowRight,
  ArrowLeft,
  Sparkles,
  Target,
  Users
} from 'lucide-react';
import { toast } from 'sonner';
import { UserOnboarding, ContactMethod, ServiceInterest } from '@/models/UserOnboarding';
import { CountryData, COUNTRIES } from '@/data/countries';

interface AdminOnboardingFormProps {
  userId: string;
  existingData?: UserOnboarding | null;
  onSave: () => void;
}

const SERVICE_OPTIONS = [
  { 
    value: 'outbound_calls', 
    label: 'Outbound Calls', 
    icon: Phone, 
    needsCountries: true,
    description: 'Make calls to customers worldwide'
  },
  { 
    value: 'inbound_calls', 
    label: 'Inbound Calls', 
    icon: Phone, 
    needsCountries: true,
    description: 'Receive calls from customers'
  },
  { 
    value: 'did_numbers', 
    label: 'DID Numbers (SDA)', 
    icon: Phone, 
    needsCountries: true,
    description: 'Direct Inward Dialing numbers'
  },
  { 
    value: 'sms', 
    label: 'SMS Services', 
    icon: MessageCircle, 
    needsCountries: true,
    description: 'Send and receive text messages'
  },
  { 
    value: 'emailing', 
    label: 'Email Marketing', 
    icon: Mail, 
    needsCountries: false,
    description: 'Email campaigns and automation'
  },
  { 
    value: 'whatsapp_business', 
    label: 'WhatsApp Business API', 
    icon: MessageCircle, 
    needsCountries: true,
    description: 'WhatsApp business messaging'
  },
  { 
    value: 'other', 
    label: 'Other Services', 
    icon: Plus, 
    needsCountries: false,
    description: 'Custom communication solutions'
  },
];

const CONTACT_METHOD_OPTIONS = [
  { value: 'phone', label: 'Phone', icon: Phone, description: 'Phone calls' },
  { value: 'email', label: 'Email', icon: Mail, description: 'Email communication' },
  { value: 'whatsapp', label: 'WhatsApp', icon: MessageCircle, description: 'WhatsApp messaging' },
  { value: 'other', label: 'Other', icon: Plus, description: 'Custom method' },
];

export function AdminOnboardingForm({ 
  userId, 
  existingData, 
  onSave 
}: AdminOnboardingFormProps) {
  const [isLoading, setIsLoading] = useState(false);
  const [isEditing, setIsEditing] = useState(!existingData); // Start in edit mode if no data
  const [currentStep, setCurrentStep] = useState(1);
  const totalSteps = 4;

  // Form data
  const [formData, setFormData] = useState({
    companyName: existingData?.companyName || '',
    address: {
      street: existingData?.address?.street || '',
      city: existingData?.address?.city || '',
      postalCode: existingData?.address?.postalCode || '',
      country: existingData?.address?.country || '',
      state: existingData?.address?.state || ''
    },
    phoneNumber: existingData?.phoneNumber || '',
    preferredContactMethods: existingData?.preferredContactMethods || [] as ContactMethod[],
    servicesInterested: existingData?.servicesInterested || [] as ServiceInterest[],
    trafficVolume: existingData?.trafficVolume || {
      type: 'volume' as 'volume' | 'agents',
      value: 0,
      unit: 'minutes' as 'minutes' | 'calls' | 'sms' | 'agents',
      period: 'monthly' as 'daily' | 'weekly' | 'monthly' | 'yearly'
    },
    additionalNotes: existingData?.additionalNotes || ''
  });

  // Contact method form
  const [newContactMethod, setNewContactMethod] = useState<ContactMethod>({
    type: 'phone',
    value: '',
    description: ''
  });

  // Service selection
  const [selectedServices, setSelectedServices] = useState<string[]>(
    existingData?.servicesInterested?.map(s => s.service) || []
  );
  const [serviceDescriptions, setServiceDescriptions] = useState<Record<string, string>>(
    existingData?.servicesInterested?.reduce((acc, service) => ({
      ...acc,
      [service.service]: service.description || ''
    }), {}) || {}
  );
  const [serviceCountries, setServiceCountries] = useState<Record<string, string[]>>(
    existingData?.servicesInterested?.reduce((acc, service) => ({
      ...acc,
      [service.service]: service.countries || []
    }), {}) || {}
  );

  // Selected country for address
  const [selectedCountry, setSelectedCountry] = useState<CountryData | null>(() => {
    if (existingData?.address?.country) {
      return COUNTRIES.find(c => c.name === existingData.address.country) || null;
    }
    return null;
  });

  const handleNext = () => {
    if (isEditing ? validateCurrentStep() : true) {
      setCurrentStep(prev => Math.min(prev + 1, totalSteps));
    }
  };

  const handlePrevious = () => {
    setCurrentStep(prev => Math.max(prev - 1, 1));
  };

  const validateCurrentStep = () => {
    if (!isEditing) return true; // Skip validation in view mode

    switch (currentStep) {
      case 1: // Company details
        if (!formData.companyName.trim()) {
          toast.error('Company name is required');
          return false;
        }
        if (!formData.address.street.trim() || !formData.address.city.trim() || 
            !formData.address.postalCode.trim() || !formData.address.country) {
          toast.error('Complete address is required');
          return false;
        }
        if (!formData.phoneNumber.trim()) {
          toast.error('Phone number is required');
          return false;
        }
        return true;

      case 2: // Contact methods
        if (formData.preferredContactMethods.length === 0) {
          toast.error('At least one contact method is required');
          return false;
        }
        return true;

      case 3: // Services
        if (selectedServices.length === 0) {
          toast.error('Please select at least one service');
          return false;
        }
        return true;

      case 4: // Traffic volume
        if (formData.trafficVolume.value <= 0) {
          toast.error('Please specify traffic volume');
          return false;
        }
        return true;

      default:
        return true;
    }
  };

  const handleCountrySelect = (country: CountryData) => {
    setSelectedCountry(country);
    setFormData(prev => ({
      ...prev,
      address: {
        ...prev.address,
        country: country.name
      }
    }));
  };

  const addContactMethod = () => {
    if (!newContactMethod.value.trim()) {
      toast.error('Contact value is required');
      return;
    }

    if (newContactMethod.type === 'other' && !newContactMethod.description?.trim()) {
      toast.error('Description is required for other contact methods');
      return;
    }

    setFormData(prev => ({
      ...prev,
      preferredContactMethods: [...prev.preferredContactMethods, { ...newContactMethod }]
    }));

    setNewContactMethod({
      type: 'phone',
      value: '',
      description: ''
    });
  };

  const removeContactMethod = (index: number) => {
    setFormData(prev => ({
      ...prev,
      preferredContactMethods: prev.preferredContactMethods.filter((_, i) => i !== index)
    }));
  };

  const handleServiceToggle = (serviceValue: string) => {
    setSelectedServices(prev => {
      if (prev.includes(serviceValue)) {
        return prev.filter(s => s !== serviceValue);
      } else {
        return [...prev, serviceValue];
      }
    });
  };

  const handleServiceDescriptionChange = (service: string, description: string) => {
    setServiceDescriptions(prev => ({ ...prev, [service]: description }));
  };

  const handleServiceCountriesChange = (service: string, countries: string[]) => {
    setServiceCountries(prev => ({ ...prev, [service]: countries }));
  };

  const handleSave = async () => {
    setIsLoading(true);

    try {
      // Basic validation
      if (!formData.companyName.trim()) {
        toast.error('Company name is required');
        setIsLoading(false);
        return;
      }

      if (!formData.address.street.trim() || !formData.address.city.trim() || 
          !formData.address.postalCode.trim() || !formData.address.country.trim()) {
        toast.error('Complete address is required');
        setIsLoading(false);
        return;
      }

      if (!formData.phoneNumber.trim()) {
        toast.error('Phone number is required');
        setIsLoading(false);
        return;
      }

      if (formData.preferredContactMethods.length === 0) {
        toast.error('At least one contact method is required');
        setIsLoading(false);
        return;
      }

      if (selectedServices.length === 0) {
        toast.error('At least one service must be selected');
        setIsLoading(false);
        return;
      }

      if (formData.trafficVolume.value <= 0) {
        toast.error('Traffic volume must be greater than 0');
        setIsLoading(false);
        return;
      }

      // Prepare services data
      const servicesInterested: ServiceInterest[] = selectedServices.map(service => ({
        service: service as ServiceInterest['service'],
        description: serviceDescriptions[service] || undefined,
        countries: serviceCountries[service] || undefined
      }));

      const onboardingData = {
        ...formData,
        servicesInterested,
        completed: true,
        completedAt: new Date().toISOString(),
      };

      const response = await fetch(`/api/admin/onboarding/${userId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(onboardingData),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to save onboarding data');
      }

      toast.success('Onboarding data saved successfully!');
      setIsEditing(false);
      onSave();
    } catch (error) {
      console.error('Error saving onboarding data:', error);
      toast.error(error instanceof Error ? error.message : 'Failed to save onboarding data');
    } finally {
      setIsLoading(false);
    }
  };

  const renderStepContent = () => {
    switch (currentStep) {
      case 1:
        return (
          <div className="space-y-6">
            {/* Header */}
            <div className="text-center space-y-2">
              <div 
                className="mx-auto w-12 h-12 rounded-xl flex items-center justify-center shadow-md bg-black dark:bg-white"
              >
                <Building2 className="h-6 w-6 text-white dark:text-black" />
              </div>
              <div>
                <h3 
                  className="text-xl font-bold text-black dark:text-white"
                >
                  Company Information
                </h3>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  {isEditing ? 'Edit company details' : 'Company information overview'}
                </p>
              </div>
            </div>

            <div className="space-y-4">
              {/* Company Name */}
              <div className="space-y-2">
                <Label htmlFor="companyName" className="text-sm font-medium text-black dark:text-white">Company Name *</Label>
                {isEditing ? (
                  <Input
                    id="companyName"
                    value={formData.companyName}
                    onChange={(e) => setFormData(prev => ({ ...prev, companyName: e.target.value }))}
                    placeholder="Enter company name"
                    className="h-10 border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-900 text-black dark:text-white"
                  />
                ) : (
                  <div className="p-3 bg-gray-100 dark:bg-gray-800 rounded-lg border border-gray-300 dark:border-gray-600">
                    <p className="font-medium text-black dark:text-white">{formData.companyName || 'Not provided'}</p>
                  </div>
                )}
              </div>

              {/* Address Section */}
              <div className="space-y-3">
                <Label className="text-sm font-medium text-black dark:text-white">Business Address *</Label>

                {isEditing ? (
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-3">
                    <div className="md:col-span-2">
                      <Input
                        value={formData.address.street}
                        onChange={(e) => setFormData(prev => ({ 
                          ...prev, 
                          address: { ...prev.address, street: e.target.value }
                        }))}
                        placeholder="Street address"
                        className="h-10 border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-900 text-black dark:text-white"
                      />
                    </div>

                    <div>
                      <Input
                        value={formData.address.city}
                        onChange={(e) => setFormData(prev => ({ 
                          ...prev, 
                          address: { ...prev.address, city: e.target.value }
                        }))}
                        placeholder="City"
                        className="h-10 border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-900 text-black dark:text-white"
                      />
                    </div>

                    <div>
                      <Input
                        value={formData.address.postalCode}
                        onChange={(e) => setFormData(prev => ({ 
                          ...prev, 
                          address: { ...prev.address, postalCode: e.target.value }
                        }))}
                        placeholder="Postal code"
                        className="h-10 border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-900 text-black dark:text-white"
                      />
                    </div>

                    <div>
                      <Input
                        value={formData.address.state}
                        onChange={(e) => setFormData(prev => ({ 
                          ...prev, 
                          address: { ...prev.address, state: e.target.value }
                        }))}
                        placeholder="State/Province"
                        className="h-10 border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-900 text-black dark:text-white"
                      />
                    </div>

                    <div>
                      <CountrySelector
                        value={selectedCountry?.code}
                        onValueChange={handleCountrySelect}
                        placeholder="Select country"
                      />
                    </div>
                  </div>
                ) : (
                  <div className="p-3 bg-gray-100 dark:bg-gray-800 rounded-lg border border-gray-300 dark:border-gray-600 space-y-1">
                    <p className="font-medium text-black dark:text-white">{formData.address.street || 'Not provided'}</p>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {formData.address.city && formData.address.postalCode && 
                        `${formData.address.city}, ${formData.address.postalCode}`}
                    </p>
                    <p className="text-sm text-gray-600 dark:text-gray-400">
                      {formData.address.state && `${formData.address.state}, `}{formData.address.country}
                    </p>
                  </div>
                )}
              </div>

              {/* Phone Number */}
              <div className="space-y-2">
                <Label className="text-sm font-medium text-black dark:text-white">Business Phone Number *</Label>
                {isEditing ? (
                  <div className="flex space-x-2">
                    {/* Country Code Display */}
                    {selectedCountry?.phoneCode && (
                      <div className="flex items-center bg-gray-100 dark:bg-gray-800 border border-gray-300 dark:border-gray-600 rounded-lg px-3 py-2 text-sm font-medium text-black dark:text-white min-w-0">
                        <span className="text-lg leading-none mr-2">{selectedCountry.flag}</span>
                        <span className="whitespace-nowrap">{selectedCountry.phoneCode}</span>
                      </div>
                    )}

                    {/* Phone Number Field */}
                    <Input
                      type="tel"
                      value={formData.phoneNumber.startsWith(selectedCountry?.phoneCode || '') ? 
                        formData.phoneNumber.substring((selectedCountry?.phoneCode || '').length).trim() : 
                        formData.phoneNumber}
                      onChange={(e) => {
                        const phoneNumber = e.target.value.replace(/[^\d\s\-\(\)]/g, ''); // Clean input
                        const fullNumber = selectedCountry?.phoneCode ? 
                          `${selectedCountry.phoneCode} ${phoneNumber}`.trim() : phoneNumber;
                        setFormData(prev => ({ ...prev, phoneNumber: fullNumber }));
                      }}
                      placeholder="Enter phone number"
                      className="flex-1 h-10 border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-900 text-black dark:text-white"
                    />
                  </div>
                ) : (
                  <div className="p-3 bg-gray-100 dark:bg-gray-800 rounded-lg border border-gray-300 dark:border-gray-600">
                    <p className="font-medium text-black dark:text-white">{formData.phoneNumber || 'Not provided'}</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        );

      case 2:
        return (
          <div className="space-y-6">
            {/* Header */}
            <div className="text-center space-y-2">
              <div 
                className="mx-auto w-12 h-12 rounded-xl flex items-center justify-center shadow-md bg-black dark:bg-white"
              >
                <MessageCircle className="h-6 w-6 text-white dark:text-black" />
              </div>
              <div>
                <h3 
                  className="text-xl font-bold text-black dark:text-white"
                >
                  Contact Preferences
                </h3>
                <p className="text-sm text-gray-600 dark:text-gray-400">
                  {isEditing ? 'Manage contact methods' : 'Preferred contact methods'}
                </p>
              </div>
            </div>

            <div className="space-y-6">
              {/* Add Contact Method */}
              {isEditing && (
                <Card className="border-dashed border-gray-300 dark:border-gray-600 bg-gray-50 dark:bg-gray-800">
                  <CardHeader className="pb-3">
                    <CardTitle className="text-base flex items-center gap-2 text-black dark:text-white">
                      <Plus className="h-4 w-4 text-black dark:text-white" />
                      Add Contact Method
                    </CardTitle>
                  </CardHeader>
                  <CardContent className="space-y-3">
                    <div className="grid grid-cols-1 md:grid-cols-4 gap-3">
                      <div>
                        <Select
                          value={newContactMethod.type}
                          onValueChange={(value: any) => setNewContactMethod(prev => ({ ...prev, type: value }))}
                        >
                          <SelectTrigger className="h-10 border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-900">
                            <SelectValue />
                          </SelectTrigger>
                          <SelectContent>
                            {CONTACT_METHOD_OPTIONS.map(option => (
                              <SelectItem key={option.value} value={option.value}>
                                <div className="flex items-center space-x-2">
                                  <option.icon className="h-4 w-4" />
                                  <span>{option.label}</span>
                                </div>
                              </SelectItem>
                            ))}
                          </SelectContent>
                        </Select>
                      </div>

                      <div className="md:col-span-2">
                        <Input
                          value={newContactMethod.value}
                          onChange={(e) => setNewContactMethod(prev => ({ ...prev, value: e.target.value }))}
                          placeholder={
                            newContactMethod.type === 'phone' ? '+1 555 123 4567' : 
                            newContactMethod.type === 'email' ? 'contact@company.com' : 
                            newContactMethod.type === 'whatsapp' ? '+1 555 123 4567' : 
                            'Enter contact details'
                          }
                          className="h-10 border-gray-300 dark:border-gray-600 bg-white dark:bg-gray-900"
                        />
                      </div>

                      <div>
                        <Button 
                          onClick={addContactMethod} 
                          className="h-10 w-full text-white dark:text-black bg-black dark:bg-white hover:bg-gray-800 dark:hover:bg-gray-200"
                        >
                          <Plus className="h-4 w-4" />
                        </Button>
                      </div>
                    </div>

                    {newContactMethod.type === 'other' && (
                      <div className="mt-3">
                        <Input
                          value={newContactMethod.description || ''}
                          onChange={(e) => setNewContactMethod(prev => ({ ...prev, description: e.target.value }))}
                          placeholder="Description for other method"
