      if (!response.ok) {
        throw new Error('Failed to fetch users');
      }

      const data = await response.json();
      const usersWithSippyAccounts = data.users?.filter((user: User) => 
        user.isEmailVerified && user.sippyAccountId
      ) || [];

      setUsers(usersWithSippyAccounts);
