                  {paymentsData?.payments && Array.isArray(paymentsData.payments) && 
                    // Final sort to ensure proper date ordering (most recent first)
                    [...paymentsData.payments].sort((a, b) => {
                      const getTimestamp = (payment: Payment): number => {
                        try {
                          // Priority 1: For failed payments from MongoDB, use the MongoDB date directly
                          if (payment._isFailedPayment && payment._mongoData) {
                            const mongoDate = payment._mongoData.paymentCompletedAt || 
                                payment._mongoData.paymentInitiatedAt || 
                                payment._mongoData.createdAt;
                            if (mongoDate) {
                              const timestamp = new Date(mongoDate).getTime();
                              if (!isNaN(timestamp)) {
                                return timestamp;
                              }
                            }
                          }

                          // Priority 2: Check if payment_time is in ISO format (from failed payments) - HIGHEST PRIORITY
                          if (payment.payment_time && (payment.payment_time.includes('T') || payment.payment_time.includes('Z'))) {
                            const timestamp = new Date(payment.payment_time).getTime();
                            if (!isNaN(timestamp)) {
                              return timestamp;
                            }
                          }

                          // Priority 3: For Sippy payments, use the parseSippyDate function
                          if (payment.payment_time) {
                            const timestamp = parseSippyDate(payment.payment_time).getTime();
                            if (!isNaN(timestamp)) {
                              return timestamp;
                            }
                          }

                          // Priority 4: Return very old timestamp for invalid dates (will appear at bottom)
                          console.warn('No valid date found for payment, using old timestamp:', payment);
                          return 0; // January 1, 1970 - will appear at bottom
                        } catch (error) {
                          console.warn('Error parsing payment date for sorting:', payment.payment_time, error);
                          return 0; // January 1, 1970 - will appear at bottom
                        }
                      };

                      const timestampA = getTimestamp(a);
                      const timestampB = getTimestamp(b);

                      // Sort descending (most recent first)
                      const result = timestampB - timestampA;

                      // Handle NaN cases: if either timestamp is NaN, put it at the bottom
                      if (isNaN(result)) {
                        if (isNaN(timestampA) && isNaN(timestampB)) {
                          return 0; // Both invalid, keep original order
                        } else if (isNaN(timestampA)) {
                          return 1; // A is invalid, put it after B
                        } else if (isNaN(timestampB)) {
                          return -1; // B is invalid, put it after A
                        }
                      }

                      return result;
                    }).map((payment, index) => (
                    <TableRow 
                      key={`${payment.i_payment}-${index}`} 
                      className="hover:bg-muted/30 transition-all duration-200 border-b border-muted/30 group"
