// Payment Form Component
function PaymentForm({ 
  paymentIntent, 
  onSuccess, 
  onError 
}: { 
  paymentIntent: PaymentIntentResponse;
  onSuccess: (paymentData: PaymentSuccessData) => void;
  onError: (error: string, errorCode?: string, errorType?: string, paymentIntentId?: string, cardBrand?: string, cardLast4?: string) => void;
}) {
  const stripe = useStripe();
  const elements = useElements();
  const [isProcessing, setIsProcessing] = useState(false);

  const handleSubmit = async (event: React.FormEvent) => {
    event.preventDefault();

    if (!stripe || !elements) {
      return;
    }

    setIsProcessing(true);

    const cardElement = elements.getElement(CardElement);
    if (!cardElement) {
      onError('Card element not found', 'card_element_missing', 'validation_error', undefined, undefined, undefined);
      setIsProcessing(false);
      return;
    }

    try {
      const { error, paymentIntent: confirmedPayment } = await stripe.confirmCardPayment(
        paymentIntent.clientSecret,
        {
          payment_method: {
            card: cardElement,
          }
        }
      );

      if (error && (error.message || error.code || error.type)) {
        // Extract detailed error information
        const errorMessage = error.message || 'Payment failed';
        const errorCode = error.code;
        const errorType = error.type;
        const paymentIntentId = error.payment_intent?.id || paymentIntent.clientSecret.split('_secret')[0];

        // Get card details if available for error reporting
        let cardBrand, cardLast4;
        if (error.payment_method?.card) {
          cardBrand = error.payment_method.card.brand;
          cardLast4 = error.payment_method.card.last4;
        }

        // Try to get card details from the card element if not available in error
        if (!cardBrand || !cardLast4) {
          try {
            const cardElementValue = (cardElement as any)._element;
            if (cardElementValue && cardElementValue.card) {
              cardBrand = cardBrand || cardElementValue.card.brand;
              cardLast4 = cardLast4 || cardElementValue.card.last4;
            }
          } catch {
            // Ignore errors getting card details from element
          }
        }

        onError(errorMessage, errorCode, errorType, paymentIntentId, cardBrand, cardLast4);
      } else if (confirmedPayment?.status === 'succeeded') {
        // Extract payment method details
        const paymentMethod = confirmedPayment.payment_method;
        const cardDetails = typeof paymentMethod === 'object' ? paymentMethod?.card : undefined;

        const successData: PaymentSuccessData = {
          paymentIntentId: confirmedPayment.id,
          amount: paymentIntent.amount,
          topupAmount: paymentIntent.topupAmount,
          processingFee: paymentIntent.processingFee,
          fixedFee: paymentIntent.fixedFee,
          currency: paymentIntent.currency,
          gatewayName: paymentIntent.gatewayName,
          timestamp: new Date().toISOString(),
          cardBrand: cardDetails?.brand,
          cardLast4: cardDetails?.last4
        };

        onSuccess(successData);
      } else if (error) {
        // Handle empty or malformed error objects
        onError('Payment failed due to an unknown error', 'unknown_error', 'stripe_error');
      } else {
        // Handle unexpected payment states
        onError('Payment completed with unexpected status', 'unexpected_status', 'stripe_error');
      }
    } catch (err) {
      console.error('Payment exception:', err);
      const errorMessage = err instanceof Error ? err.message : 'Payment failed';
      onError(errorMessage, 'exception', 'api_error', undefined, undefined, undefined);
    } finally {
      setIsProcessing(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-6">
      <div className="space-y-4">
        <div className="p-4 bg-muted/30 rounded-lg">
          <h4 className="font-medium mb-2">Payment Summary</h4>
          <div className="space-y-2 text-sm">
            <div className="flex justify-between">
              <span>Top-up Amount:</span>
              <span className="font-medium">
                {paymentIntent.topupAmount.toFixed(2)} {getCurrencySymbol(paymentIntent.currency)}
              </span>
            </div>
            <div className="flex justify-between">
              <span>Processing Fee:</span>
              <span>{paymentIntent.processingFee.toFixed(2)} {getCurrencySymbol(paymentIntent.currency)}</span>
            </div>
            <div className="flex justify-between">
              <span>Fixed Fee:</span>
              <span>{paymentIntent.fixedFee.toFixed(2)} {getCurrencySymbol(paymentIntent.currency)}</span>
            </div>
            <div className="flex justify-between font-medium border-t pt-2">
              <span>Total Charge:</span>
              <span>{paymentIntent.amount.toFixed(2)} {getCurrencySymbol(paymentIntent.currency)}</span>
            </div>
          </div>
        </div>

        <div className="space-y-2">
          <Label>Card Details</Label>
          <div className="p-3 border rounded-md">
            <CardElement
              options={{
                style: {
                  base: {
                    fontSize: '16px',
                    color: '#424770',
                    '::placeholder': {
                      color: '#aab7c4',
                    },
                  },
                },
              }}
            />
          </div>
        </div>
      </div>

      <Button 
        type="submit" 
        disabled={!stripe || isProcessing}
        className="w-full gap-2"
      >
        {isProcessing ? (
          <>
            <Loader2 className="h-4 w-4 animate-spin" />
            Processing Payment...
          </>
        ) : (
          <>
            <CreditCard className="h-4 w-4" />
            Pay {paymentIntent.amount.toFixed(2)} {getCurrencySymbol(paymentIntent.currency)}
          </>
        )}
      </Button>
    </form>
  );
}

export function BalanceTopup({ onPaymentSuccess }: BalanceTopupProps = {}) {
  const { colors } = useBranding();
  const [showDialog, setShowDialog] = useState(false);
  const [showSuccessDialog, setShowSuccessDialog] = useState(false);
  const [showFailureDialog, setShowFailureDialog] = useState(false);
  const [paymentSuccessData, setPaymentSuccessData] = useState<PaymentSuccessData | null>(null);
  const [paymentFailureData, setPaymentFailureData] = useState<PaymentFailureData | null>(null);
  const [gateways, setGateways] = useState<PaymentGateway[]>([]);
  const [selectedGateway, setSelectedGateway] = useState<PaymentGateway | null>(null);
  const [isLoading, setIsLoading] = useState(false);
  const [amount, setAmount] = useState('');
  const [currency, setCurrency] = useState('EUR');
  const [paymentIntent, setPaymentIntent] = useState<PaymentIntentResponse | null>(null);
  const [stripePromise, setStripePromise] = useState<Promise<any> | null>(null);

  // Fetch available payment gateways
  useEffect(() => {
    if (showDialog) {
      fetchGateways();
    }
  }, [showDialog]);

  const fetchGateways = async () => {
    try {
      const response = await fetch('/api/payment-gateways/available');
      if (!response.ok) throw new Error('Failed to fetch gateways');

      const data = await response.json();
      const activeGateways = data.gateways?.filter((g: PaymentGateway) => g.provider === 'stripe') || [];
      setGateways(activeGateways);

      if (activeGateways.length > 0) {
        setSelectedGateway(activeGateways[0]);
      }
    } catch (error) {
      console.error('Error fetching gateways:', error);
      toast.error('Failed to load payment gateways');
    }
  };

  const createPaymentIntent = async () => {
    if (!selectedGateway || !amount) return;

    const amountValue = parseFloat(amount);
    if (isNaN(amountValue) || amountValue <= 0) {
      toast.error('Please enter a valid amount');
      return;
    }

    if (amountValue < selectedGateway.settings.minimumAmount) {
      toast.error(`Minimum amount is ${selectedGateway.settings.minimumAmount} ${getCurrencySymbol(currency)}`);
      return;
    }

    if (amountValue > selectedGateway.settings.maximumAmount) {
      toast.error(`Maximum amount is ${selectedGateway.settings.maximumAmount} ${getCurrencySymbol(currency)}`);
      return;
    }

    try {
      setIsLoading(true);

      const response = await fetch('/api/payments/create-intent', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          amount: amountValue,
          currency,
          provider: 'stripe'
        })
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to create payment intent');
      }

      const data: PaymentIntentResponse = await response.json();
      setPaymentIntent(data);

      // Initialize Stripe with the publishable key
      setStripePromise(loadStripe(data.publishableKey));

    } catch (error) {
      console.error('Error creating payment intent:', error);
      toast.error(error instanceof Error ? error.message : 'Failed to create payment intent');
    } finally {
      setIsLoading(false);
    }
  };

  const handlePaymentSuccess = (paymentData: PaymentSuccessData) => {
    setPaymentSuccessData(paymentData);
    setShowDialog(false);
    setShowSuccessDialog(true);
  };

  const handlePaymentError = (error: string, errorCode?: string, errorType?: string, paymentIntentId?: string, cardBrand?: string, cardLast4?: string) => {
    setPaymentFailureData({
      error,
      errorCode,
      errorType,
      amount: paymentIntent?.amount || 0,
      currency: paymentIntent?.currency || 'EUR',
      timestamp: new Date().toISOString(),
      paymentIntentId,
      cardBrand,
      cardLast4
    });
    setShowDialog(false);
    setShowFailureDialog(true);

    // Also record this failed payment
    recordFailedPayment(error, errorCode, errorType, paymentIntentId, cardBrand, cardLast4);
  };

  const recordFailedPayment = async (error: string, errorCode?: string, errorType?: string, paymentIntentId?: string, cardBrand?: string, cardLast4?: string) => {
    try {
      // Get user agent and IP address for security analysis
      const userAgent = navigator.userAgent;

      // Get client IP (this will be the user's IP as seen by the server)
      let ipAddress = 'unknown';
      try {
        const ipResponse = await fetch('https://api.ipify.org?format=json');
        const ipData = await ipResponse.json();
        ipAddress = ipData.ip;
      } catch (ipError) {
        console.warn('Could not get client IP:', ipError);
      }

      const failureData = {
        paymentIntentId: paymentIntentId || 'unknown',
        error,
        errorCode,
        errorType,
        amount: paymentIntent?.amount || 0,
        currency: paymentIntent?.currency || 'EUR',
        cardBrand: cardBrand,
        cardLast4: cardLast4,
        gatewayName: paymentIntent?.gatewayName || 'Stripe',
        userAgent,
        ipAddress
      };

      const response = await fetch('/api/payments/record-failure', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(failureData)
      });

      if (response.ok) {
        await response.json();
      } else {
        // Failed to record payment failure to server
      }
    } catch {
      // Don't throw - we don't want to break the user experience if recording fails
    }
  };

  const handleFailureDialogClose = () => {
    setShowFailureDialog(false);
    setPaymentFailureData(null);
  };

  const handleRetryPayment = () => {
    setShowFailureDialog(false);
    setPaymentFailureData(null);
    setShowDialog(true);
  };

  const resetDialog = () => {
    setPaymentIntent(null);
    setAmount('');
    setCurrency('EUR');
    setSelectedGateway(null);
  };

  const handleSuccessDialogClose = () => {
    setShowSuccessDialog(false);
    setPaymentSuccessData(null);
  };

  return (
    <>
      {/* Success Dialog */}
      {paymentSuccessData && (
        <PaymentSuccessDialog
          paymentData={paymentSuccessData}
          isOpen={showSuccessDialog}
          onClose={handleSuccessDialogClose}
          onPaymentSuccess={onPaymentSuccess}
        />
      )}

      {/* Failure Dialog */}
      {paymentFailureData && (
        <PaymentFailureDialog
          failureData={paymentFailureData}
          isOpen={showFailureDialog}
          onClose={handleFailureDialogClose}
          onRetry={handleRetryPayment}
        />
      )}

      {/* Main Top-up Dialog */}
      <Dialog open={showDialog} onOpenChange={(open) => {
        setShowDialog(open);
        if (!open) resetDialog();
      }}>
        <DialogTrigger asChild>
          <Button 
            className="gap-2 hover:shadow-lg hover:scale-105 transition-all duration-200 hover:brightness-110" 
            style={{ backgroundColor: colors.primary }}
          >
            <Plus className="h-4 w-4" />
            Top Up Balance
          </Button>
        </DialogTrigger>
        <DialogContent className="max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <DollarSign className="h-5 w-5" style={{ color: colors.primary }} />
              Top Up Balance
            </DialogTitle>
            <DialogDescription>
              Add funds to your account using a secure payment method
            </DialogDescription>
          </DialogHeader>

          {!paymentIntent ? (
            // Payment setup form
            <div className="space-y-4">
              {gateways.length === 0 ? (
                <div className="text-center py-6">
                  <AlertCircle className="h-8 w-8 text-amber-500 mx-auto mb-2" />
                  <p className="text-sm text-muted-foreground">
                    No payment gateways are currently available.
                  </p>
                </div>
              ) : (
                <>
                  <div className="space-y-2">
                    <Label>Amount</Label>
                    <div className="relative">
                      <div className="absolute left-3 top-1/2 transform -translate-y-1/2 text-muted-foreground">
                        {getCurrencySymbol(currency)}
                      </div>
                      <Input
                        type="number"
                        step="0.01"
                        min={selectedGateway?.settings.minimumAmount || 0}
                        max={selectedGateway?.settings.maximumAmount || 10000}
                        value={amount}
                        onChange={(e) => setAmount(e.target.value)}
                        placeholder="Enter amount"
                        className="pl-8"
                      />
                    </div>
                    {selectedGateway && (
                      <p className="text-xs text-muted-foreground">
                        Min: {selectedGateway.settings.minimumAmount} {getCurrencySymbol(currency)} â€¢ 
                        Max: {selectedGateway.settings.maximumAmount} {getCurrencySymbol(currency)} â€¢ 
                        Fee: {selectedGateway.settings.processingFee}% + {selectedGateway.settings.fixedFee} {getCurrencySymbol(currency)}
                      </p>
                    )}
                  </div>

                  <div className="space-y-2">
                    <Label>Currency</Label>
                    <Select value={currency} onValueChange={setCurrency}>
                      <SelectTrigger>
                        <SelectValue />
                      </SelectTrigger>
                      <SelectContent>
                        {selectedGateway?.settings.allowedCurrencies.map(curr => (
                          <SelectItem key={curr} value={curr}>{curr}</SelectItem>
                        ))}
                      </SelectContent>
                    </Select>
                  </div>

                  {selectedGateway && (
                    <div className="p-3 bg-blue-50 border border-blue-200 rounded-lg dark:bg-blue-950/50 dark:border-blue-800">
                      <div className="flex items-center gap-2 text-sm">
                        <Info className="h-4 w-4 text-blue-600 dark:text-blue-400" />
                        <span className="font-medium">Payment Gateway:</span>
                        <Badge variant="secondary">{selectedGateway.name}</Badge>
                      </div>
                    </div>
                  )}

                  <Button 
                    onClick={createPaymentIntent}
                    disabled={isLoading || !amount || !selectedGateway}
                    className="w-full gap-2"
                    style={{ backgroundColor: colors.primary }}
                  >
                    {isLoading ? (
                      <>
                        <Loader2 className="h-4 w-4 animate-spin" />
                        Setting up payment...
                      </>
                    ) : (
                      <>
                        <CreditCard className="h-4 w-4" />
                        Continue to Payment
                      </>
                    )}
                  </Button>
                </>
              )}
            </div>
          ) : (
            // Payment form with Stripe Elements
            stripePromise && (
              <Elements stripe={stripePromise}>
                <PaymentForm
                  paymentIntent={paymentIntent}
                  onSuccess={handlePaymentSuccess}
                  onError={handlePaymentError}
                />
              </Elements>
            )
          )}
        </DialogContent>
      </Dialog>
    </>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle, CardDescription } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import {
  Dialog,
  DialogContent,
  DialogDescription,
  DialogHeader,
  DialogTitle,
} from '@/components/ui/dialog';
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from '@/components/ui/select';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';

import {
  Table,
  TableBody,
  TableCell,
  TableHead,
  TableHeader,
  TableRow,
} from '@/components/ui/table';
import { 
  ChevronLeft,
  ChevronRight,
  Eye,
  Info, 
  Loader2,
  Phone,
  Receipt, 
  RefreshCw,
  X,
  XCircle,
  Zap
} from 'lucide-react';

import { toast } from 'sonner';
import { format } from 'date-fns';

interface PhoneNumberBilling {
  _id: string;
  phoneNumberId: string;
  phoneNumber: {
    _id: string;
    number: string;
    numberType: string;
    monthlyRate: number;
    currency: string;
    country: string;
    countryCode: string;
  };
  userId: string;
  user: {
    _id: string;
    name: string;
    email: string;
    company?: string;
    onboarding?: {
      companyName?: string;
    };
  };
  assignmentId: string;
  assignment: {
    _id: string;
    assignedAt: string;
    unassignedAt?: string;
  };
  billingPeriodStart: string;
  billingPeriodEnd: string;
  amount: number;
  currency: string;
  status: 'pending' | 'paid' | 'failed' | 'cancelled' | 'refunded';
  billingDate: string;
  paidDate?: string;
  failureReason?: string;
  transactionType: 'monthly_fee' | 'setup_fee' | 'prorated_fee' | 'refund';
  sippyTransactionId?: string;
  processedBy?: string;
  notes?: string;
  createdAt: string;
  updatedAt: string;
}

interface PhoneNumberBillingsResponse {
  billings: PhoneNumberBilling[];
  total: number;
  page: number;
  limit: number;
  totalPages: number;
}

interface PhoneNumberBillingSectionProps {
  isAdmin: boolean;
  colors: {
    primary: string;
    secondary: string;
    accent: string;
  };
}

export function PhoneNumberBillingSection({ isAdmin, colors }: PhoneNumberBillingSectionProps) {
  const [billingsData, setBillingsData] = useState<PhoneNumberBillingsResponse | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [showSection, setShowSection] = useState(false);

  // Pagination and filters
  const [currentPage, setCurrentPage] = useState(1);
  const [filters, setFilters] = useState({
    status: 'all',
    transactionType: 'all',
    userId: 'all',
    startDate: '',
    endDate: '',
  });

  // Process billing state
  const [showProcessDialog, setShowProcessDialog] = useState(false);
  const [showDetailsModal, setShowDetailsModal] = useState(false);
  const [selectedBilling, setSelectedBilling] = useState<PhoneNumberBilling | null>(null);
  const [isProcessing, setIsProcessing] = useState(false);

  const fetchBillings = async () => {
    try {
      setIsLoading(true);
      setError(null);

      const params = new URLSearchParams({
        page: currentPage.toString(),
        limit: '20',
        ...(filters.status !== 'all' && { status: filters.status }),
        ...(filters.transactionType !== 'all' && { transactionType: filters.transactionType }),
        ...(filters.userId !== 'all' && { userId: filters.userId }),
        ...(filters.startDate && { startDate: filters.startDate }),
        ...(filters.endDate && { endDate: filters.endDate }),
      });

      const response = await fetch(`/api/phone-numbers/billing?${params}`);

      if (!response.ok) {
        throw new Error('Failed to fetch phone number billings');
      }

      const data = await response.json();
      setBillingsData(data);
    } catch (error) {
      console.error('Error fetching phone number billings:', error);
      setError(error instanceof Error ? error.message : 'Failed to fetch billing data');
    } finally {
      setIsLoading(false);
    }
  };

  const processBilling = async (billingId: string) => {
    try {
      setIsProcessing(true);

      const response = await fetch('/api/phone-numbers/billing', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          billingId,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to process billing');
      }

      toast.success('Billing processed successfully');
      setShowProcessDialog(false);
      setSelectedBilling(null);

      // Add a small delay to ensure database is updated, then refresh
      setTimeout(() => {
        fetchBillings();
      }, 1000);

    } catch (error) {
      console.error('Error processing billing:', error);
      toast.error(error instanceof Error ? error.message : 'Failed to process billing');
    } finally {
      setIsProcessing(false);
    }
  };

  // Auto-refresh effect when section becomes visible
  useEffect(() => {
    if (showSection) {
      fetchBillings();
    }
  }, [showSection]);

  // Auto-refresh every 60 seconds when section is visible to catch changes
  useEffect(() => {
    if (!showSection) return;

    const interval = setInterval(() => {
      fetchBillings();
    }, 60000); // Refresh every 60 seconds

    return () => clearInterval(interval);
  }, [showSection]);

  useEffect(() => {
    fetchBillings();
  }, [currentPage, filters]);

  useEffect(() => {
    if (isAdmin) {
      fetchBillings();
    }
  }, [isAdmin]);

  const formatCurrency = (amount: number, currency: string = 'EUR') => {
    return new Intl.NumberFormat('en-US', {
      style: 'currency',
      currency: currency,
      minimumFractionDigits: 2,
      maximumFractionDigits: 2,
    }).format(amount);
  };

  const formatDate = (dateString: string) => {
    try {
      return format(new Date(dateString), 'MMM dd, yyyy HH:mm');
    } catch {
      return 'Invalid Date';
    }
  };

  const getStatusBadge = (status: string) => {
    switch (status) {
      case 'paid':
        return <Badge className="bg-green-100 text-green-800 border-green-200">Paid</Badge>;
      case 'pending':
        return <Badge className="bg-yellow-100 text-yellow-800 border-yellow-200">Pending</Badge>;
      case 'failed':
        return <Badge variant="destructive">Failed</Badge>;
      case 'cancelled':
        return <Badge variant="secondary">Cancelled</Badge>;
      case 'refunded':
        return <Badge className="bg-blue-100 text-blue-800 border-blue-200">Refunded</Badge>;
      default:
        return <Badge variant="outline">{status}</Badge>;
    }
  };

  const getTransactionTypeBadge = (type: string) => {
    switch (type) {
      case 'monthly_fee':
        return <Badge variant="outline" className="bg-blue-50 text-blue-700 border-blue-200">Monthly Fee</Badge>;
      case 'setup_fee':
        return <Badge variant="outline" className="bg-purple-50 text-purple-700 border-purple-200">Setup Fee</Badge>;
      case 'prorated_fee':
        return <Badge variant="outline" className="bg-orange-50 text-orange-700 border-orange-200">Prorated Fee</Badge>;
      case 'refund':
        return <Badge variant="outline" className="bg-green-50 text-green-700 border-green-200">Refund</Badge>;
      default:
        return <Badge variant="outline">{type}</Badge>;
    }
  };

  return (
    <>
      {/* Phone Number Billing Toggle Card */}
      <Card className="border-0 shadow-sm">
        <CardContent className="p-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div 
                className="p-2 rounded-lg"
                style={{ backgroundColor: `${colors.primary}20` }}
              >
                <Phone className="h-5 w-5" style={{ color: colors.primary }} />
              </div>
              <div>
                <h3 className="font-semibold" style={{ color: colors.primary }}>
                  Phone Number Billing Traceability
                </h3>
                <p className="text-sm text-muted-foreground">
                  Track phone number charges and billing history with Sippy integration
                </p>
              </div>
            </div>
            <Button
              onClick={() => setShowSection(!showSection)}
              variant="outline"
              className="gap-2"
              style={{ 
                borderColor: colors.primary,
                color: colors.primary 
              }}
            >
              {showSection ? (
                <>
                  <X className="h-4 w-4" />
                  Hide Billing
                </>
              ) : (
                <>
                  <Eye className="h-4 w-4" />
                  Show Billing
                </>
              )}
            </Button>
          </div>
        </CardContent>
      </Card>

      {/* Phone Number Billing Section */}
      {showSection && (
        <Card className="border-0 shadow-xl rounded-xl overflow-hidden">
          <div 
            className="h-2 rounded-t-xl"
            style={{ background: `linear-gradient(90deg, ${colors.primary}, ${colors.secondary}, ${colors.accent})` }}
          />
          <CardHeader className="pb-4">
            <div className="flex items-center justify-between">
              <div>
                <CardTitle className="flex items-center gap-2" style={{ color: colors.primary }}>
                  <Phone className="h-5 w-5" />
                  Phone Number Billing Records
                </CardTitle>
                <CardDescription>
                  {isAdmin 
                    ? 'Manage and process phone number billing charges via Sippy accountDebit API'
                    : 'View your phone number billing history and charges'
                  }
                </CardDescription>
              </div>
              <Button
                onClick={fetchBillings}
                disabled={isLoading}
                variant="outline"
                className="gap-2"
              >
                <RefreshCw className={`h-4 w-4 ${isLoading ? 'animate-spin' : ''}`} />
                Refresh
              </Button>
            </div>
          </CardHeader>

          <CardContent className="space-y-4">
            {/* Filters */}
            <div className="grid grid-cols-1 md:grid-cols-5 gap-3">
              <div>
                <Label>Status</Label>
                <Select value={filters.status} onValueChange={(value) => setFilters(prev => ({ ...prev, status: value }))}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Statuses</SelectItem>
                    <SelectItem value="pending">Pending</SelectItem>
                    <SelectItem value="paid">Paid</SelectItem>
                    <SelectItem value="failed">Failed</SelectItem>
                    <SelectItem value="cancelled">Cancelled</SelectItem>
                    <SelectItem value="refunded">Refunded</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label>Type</Label>
                <Select value={filters.transactionType} onValueChange={(value) => setFilters(prev => ({ ...prev, transactionType: value }))}>
                  <SelectTrigger>
                    <SelectValue />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">All Types</SelectItem>
                    <SelectItem value="monthly_fee">Monthly Fee</SelectItem>
                    <SelectItem value="setup_fee">Setup Fee</SelectItem>
                    <SelectItem value="prorated_fee">Prorated Fee</SelectItem>
                    <SelectItem value="refund">Refund</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div>
                <Label>Start Date</Label>
                <Input
                  type="date"
                  value={filters.startDate}
                  onChange={(e) => setFilters(prev => ({ ...prev, startDate: e.target.value }))}
                />
              </div>

              <div>
                <Label>End Date</Label>
                <Input
                  type="date"
                  value={filters.endDate}
                  onChange={(e) => setFilters(prev => ({ ...prev, endDate: e.target.value }))}
                />
              </div>

              <div className="flex items-end">
                <Button
                  onClick={() => setFilters({
                    status: 'all',
                    transactionType: 'all',
                    userId: 'all',
                    startDate: '',
                    endDate: '',
                  })}
                  variant="outline"
                  className="w-full"
                >
                  Clear Filters
                </Button>
              </div>
            </div>

            {/* Billing Table */}
            {isLoading ? (
              <div className="text-center py-8">
                <Loader2 className="h-8 w-8 animate-spin mx-auto mb-4" style={{ color: colors.primary }} />
                <p className="text-muted-foreground">Loading billing records...</p>
              </div>
            ) : error ? (
              <div className="text-center py-8">
                <XCircle className="h-8 w-8 mx-auto mb-4 text-red-500" />
                <p className="text-red-600 mb-4">{error}</p>
                <Button onClick={fetchBillings} variant="outline">
                  Try Again
                </Button>
              </div>
            ) : !billingsData?.billings?.length ? (
              <div className="text-center py-8">
                <Phone className="h-8 w-8 mx-auto mb-4 text-muted-foreground" />
                <p className="text-muted-foreground">No billing records found</p>
              </div>
            ) : (
              <div className="overflow-x-auto">
                <Table>
                  <TableHeader>
                    <TableRow>
                      <TableHead>Phone Number</TableHead>
                      <TableHead>Country</TableHead>
                      <TableHead>User</TableHead>
                      <TableHead>Amount</TableHead>
                      <TableHead>Type</TableHead>
                      <TableHead>Status</TableHead>
                      <TableHead>Billing Date</TableHead>
                      {isAdmin && <TableHead>Actions</TableHead>}
                    </TableRow>
                  </TableHeader>
                  <TableBody>
                    {billingsData.billings.map((billing) => (
                      <TableRow key={billing._id}>
                        <TableCell>
                          <div>
                            <div className="font-medium">{billing.phoneNumber.number}</div>
                            <div className="text-sm text-muted-foreground">
                              {billing.phoneNumber.numberType}
                            </div>
                          </div>
                        </TableCell>
                        <TableCell>
                          <div>
                            <div className="font-medium">{billing.phoneNumber.country}</div>
                          </div>
                        </TableCell>
                        <TableCell>
                          <div>
                            <div className="font-medium">{billing.user.name || billing.user.email}</div>
                            {(billing.user.onboarding?.companyName || billing.user.company) && (
                              <div className="text-sm text-muted-foreground">
                                {billing.user.onboarding?.companyName || billing.user.company}
                              </div>
                            )}
                          </div>
                        </TableCell>
                        <TableCell>
                          <div className="font-medium">
                            {formatCurrency(billing.amount, billing.currency)}
                          </div>
                        </TableCell>
                        <TableCell>
                          {getTransactionTypeBadge(billing.transactionType)}
                        </TableCell>
                        <TableCell>
                          {getStatusBadge(billing.status)}
                        </TableCell>
                        <TableCell>
                          <div>
                            <div className="font-medium">{formatDate(billing.billingDate)}</div>
                            {billing.paidDate && (
                              <div className="text-sm text-muted-foreground">
                                Paid: {formatDate(billing.paidDate)}
                              </div>
                            )}
                          </div>
                        </TableCell>
                        {isAdmin && (
                          <TableCell>
                            <div className="flex gap-2">
                              <Button
                                onClick={() => {
                                  setSelectedBilling(billing);
                                  setShowDetailsModal(true);
                                }}
                                variant="outline"
                                size="sm"
                                className="gap-2"
                              >
                                <Eye className="h-3 w-3" />
                                View details
                              </Button>

                              {billing.status === 'pending' && (
                                <Button
                                  onClick={() => {
                                    setSelectedBilling(billing);
                                    setShowProcessDialog(true);
                                  }}
                                  size="sm"
                                  className="gap-2"
                                  style={{ backgroundColor: colors.primary }}
                                >
                                  <Zap className="h-3 w-3" />
                                  Process
                                </Button>
                              )}
                            </div>
                          </TableCell>
                        )}
                      </TableRow>
                    ))}
                  </TableBody>
                </Table>
              </div>
            )}

            {/* Pagination */}
            {billingsData && billingsData.totalPages > 1 && (
              <div className="flex items-center justify-between">
                <div className="text-sm text-muted-foreground">
                  Page {billingsData.page} of {billingsData.totalPages} 
                  ({billingsData.total} total records)
                </div>
                <div className="flex gap-2">
                  <Button
                    onClick={() => setCurrentPage(prev => Math.max(1, prev - 1))}
                    disabled={currentPage === 1}
                    variant="outline"
                    size="sm"
                  >
                    <ChevronLeft className="h-4 w-4" />
                  </Button>
                  <Button
                    onClick={() => setCurrentPage(prev => Math.min(billingsData.totalPages, prev + 1))}
                    disabled={currentPage === billingsData.totalPages}
                    variant="outline"
                    size="sm"
                  >
                    <ChevronRight className="h-4 w-4" />
                  </Button>
                </div>
              </div>
            )}
          </CardContent>
        </Card>
      )}

      {/* Process Billing Dialog */}
      <Dialog open={showProcessDialog} onOpenChange={setShowProcessDialog}>
        <DialogContent>
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Zap className="h-5 w-5" style={{ color: colors.primary }} />
              Process Billing Charge
            </DialogTitle>
            <DialogDescription>
              This will charge the customer's Sippy account using the accountDebit API.
              Based on billing policy: full monthly rate, no proration, negative balance handling.
            </DialogDescription>
          </DialogHeader>

          {selectedBilling && (
            <div className="space-y-4">
