      gradientMiddleColor: '#a855f7',
      gradientEndColor: '#3b82f6',
      authFormBackgroundColor: '#ffffff',
      darkAuthFormBackgroundColor: '#0f172a',
      fontFamily: 'Inter, sans-serif',
      headingFontFamily: 'Inter, sans-serif',
