      websiteUrl: '',
      socialLinks: {
        twitter: '',
        linkedin: '',
        facebook: '',
        instagram: '',
      },
      customCss: '',
      enableAnimations: true,
      enableGlassMorphism: true,
      enableGradientBackground: true,
    });
  };

  if (isLoading) {
