'use client';

import React, { useState, useEffect, useCallback } from 'react';
import { useDropzone } from 'react-dropzone';
import { useTickets } from '@/hooks/useTickets';
import { 
  TicketReply,
  SERVICE_LABELS, 
  PRIORITY_LABELS, 
  STATUS_LABELS,
  PRIORITY_COLORS,
  STATUS_COLORS,
  TicketAttachment,
  canReopenTicket,
  canReRateTicket,
  getTimeRemaining,
  ACTION_RESTRICTIONS
} from '@/types/ticket';
import { Button } from '@/components/ui/button';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Card, CardContent, CardHeader, CardTitle } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';

import { Avatar, AvatarFallback } from '@/components/ui/avatar';
import { Dialog, DialogContent, DialogDescription, DialogHeader, DialogTitle } from '@/components/ui/dialog';
import { 
  MessageCircle, 
  Paperclip, 
  Download, 
  Clock,
  User,
  FileText,
  Image as ImageIcon,
  Upload,
  X,
  Star,
  AlertCircle,
  CheckCircle2,
  Loader2,
  Send,
  Archive,
  RefreshCw,
  Timer,
  CircleDot,
  Heart,
  Sparkles,
  MessageSquare,
  Building,
  Tag,
  Settings,
  Info,
  Eye
} from 'lucide-react';
import { motion } from 'framer-motion';
import WysiwygEditor from '@/components/ui/WysiwygEditor';
import AttachmentPreview from '@/components/ui/AttachmentPreview';

interface TicketDetailProps {
  ticketId: string;
}

export default function TicketDetail({ ticketId }: TicketDetailProps) {
  const { 
    ticket, 
    loading, 
    error, 
    fetchTicket, 
    updateTicket, 
    uploadFiles,
    clearError,
    clearTicket
  } = useTickets();

  const [newReply, setNewReply] = useState('');
  const [replyAttachments, setReplyAttachments] = useState<File[]>([]);
  const [isSubmittingReply, setIsSubmittingReply] = useState(false);
  const [uploadingFiles, setUploadingFiles] = useState(false);
  const [showSatisfactionRating, setShowSatisfactionRating] = useState(false);
  const [rating, setRating] = useState(0);
  const [ratingComment, setRatingComment] = useState('');
  const [isSubmittingRating, setIsSubmittingRating] = useState(false);
  const [isDialogShaking, setIsDialogShaking] = useState(false);
  const [previewAttachment, setPreviewAttachment] = useState<any>(null);
  const [showPreview, setShowPreview] = useState(false);

  // Check restrictions
  const reopenCheck = ticket ? canReopenTicket(ticket) : { allowed: false };
  const reRateCheck = ticket ? canReRateTicket(ticket) : { allowed: false };

  useEffect(() => {
    if (ticketId) {
      clearError();
      clearTicket();
      setNewReply('');
      setReplyAttachments([]);
      setShowSatisfactionRating(false);
      fetchTicket(ticketId);
    }
  }, [ticketId, fetchTicket, clearError, clearTicket]);

  useEffect(() => {
    return () => {
      clearError();
    };
  }, [clearError]);

  // File upload with drag and drop
  const onDrop = useCallback((acceptedFiles: File[]) => {
    setReplyAttachments(prev => [...prev, ...acceptedFiles].slice(0, 5));
  }, []);

  const { getRootProps, getInputProps, isDragActive } = useDropzone({
    onDrop,
    accept: {
      'image/*': ['.png', '.jpg', '.jpeg', '.gif'],
      'application/pdf': ['.pdf'],
      'application/msword': ['.doc'],
      'application/vnd.openxmlformats-officedocument.wordprocessingml.document': ['.docx'],
      'text/plain': ['.txt']
    },
    maxFiles: 5,
    maxSize: 10 * 1024 * 1024, // 10MB
    disabled: uploadingFiles
  });

  const removeAttachment = (index: number) => {
    setReplyAttachments(prev => prev.filter((_, i) => i !== index));
  };

  const handleReplySubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!newReply.trim()) return;

    setIsSubmittingReply(true);
    clearError();

    try {
      let attachments: TicketAttachment[] = [];

      if (replyAttachments.length > 0) {
        setUploadingFiles(true);
        const fileList = new DataTransfer();
        replyAttachments.forEach(file => fileList.items.add(file));
        attachments = await uploadFiles(fileList.files);
      }

      const success = await updateTicket(ticketId, {
        action: 'add_reply',
        content: newReply,
        attachments,
      });

      if (success) {
        setNewReply('');
        setReplyAttachments([]);
      }
    } catch (err) {
      console.error('Reply submission error:', err);
    } finally {
      setIsSubmittingReply(false);
      setUploadingFiles(false);
    }
  };



  const handleReopenTicket = async () => {
    // Check if reopening is allowed
    if (!reopenCheck.allowed) {
      console.warn('Reopening not allowed:', reopenCheck.reason);
      return;
    }

    try {
      await updateTicket(ticketId, {
        action: 'reopen_ticket',
      });
    } catch (error: any) {
      // Handle specific error codes from API
      if (error?.code === 'REOPEN_TIME_EXPIRED') {
        console.error('Reopen time expired:', error.message);
        // The UI will automatically update to show the restriction
      } else {
        console.error('Error reopening ticket:', error);
      }
    }
  };

  const handleMarkResolved = async () => {
    const success = await updateTicket(ticketId, {
      action: 'update_status',
      status: 'resolved',
    });

    if (success) {
      // Show satisfaction rating modal when ticket is marked as resolved
      setShowSatisfactionRating(true);
    }
  };

  const handleSatisfactionRating = async (selectedRating: number) => {
    setRating(selectedRating);

    // If rating is 2 or less, don't submit yet - let user add comment
    if (selectedRating <= 2) {
      return; // Keep dialog open for comment
    }

    // For ratings 3+, submit immediately
    setIsSubmittingRating(true);

    try {
      await updateTicket(ticketId, {
        action: 'rate_satisfaction',
        rating: selectedRating,
      });

      setShowSatisfactionRating(false);
      setRating(0);
      setRatingComment('');
    } catch (error: any) {
      // Handle specific error codes from API
      if (error?.code === 'RERATING_TIME_EXPIRED') {
        console.error('Re-rating time expired:', error.message);
        setShowSatisfactionRating(false);
        setRating(0);
        setRatingComment('');
      } else {
        console.error('Error submitting rating:', error);
      }
    } finally {
      setIsSubmittingRating(false);
    }
  };

  const handleSubmitRatingWithComment = async () => {
    if (rating <= 2 && !ratingComment.trim()) {
      return; // Don't submit if comment is required but empty
    }

    setIsSubmittingRating(true);

    try {
      await updateTicket(ticketId, {
        action: 'rate_satisfaction',
        rating: rating,
        comment: ratingComment,
      } as any);

      setShowSatisfactionRating(false);
      setRating(0);
      setRatingComment('');
    } catch (error: any) {
      // Handle specific error codes from API
      if (error?.code === 'RERATING_TIME_EXPIRED') {
        console.error('Re-rating time expired:', error.message);
        setShowSatisfactionRating(false);
        setRating(0);
        setRatingComment('');
      } else {
        console.error('Error submitting rating with comment:', error);
      }
