                    dangerouslySetInnerHTML={{ __html: ticket.description }}
                  />
                </div>
              </CardContent>
            </Card>

            {/* Attachments Card */}
            {ticket.attachments && ticket.attachments.length > 0 && (
              <Card className="bg-white/80 dark:bg-slate-900/80 backdrop-blur-sm shadow-lg rounded-2xl border border-slate-200/60 dark:border-slate-700/60">
                <CardHeader className="pb-3">
                  <CardTitle className="text-base flex items-center gap-2">
                    <div className="w-8 h-8 rounded-lg flex items-center justify-center bg-purple-100 dark:bg-purple-900/50">
                      <Paperclip className="h-4 w-4 text-purple-600 dark:text-purple-400" />
                    </div>
                    Attachments ({ticket.attachments?.length || 0})
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="grid grid-cols-1 md:grid-cols-2 gap-2">
                    {ticket.attachments?.map((attachment, index) => (
                      <div key={index} className="flex items-center justify-between p-3 bg-slate-50 dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 hover:bg-slate-100 dark:hover:bg-slate-700/50 transition-colors">
                        <div className="flex items-center space-x-3">
                          <div className="p-2 bg-white dark:bg-slate-700 rounded-lg shadow-sm">
                            {getFileIcon(attachment.mimeType)}
                          </div>
                          <div className="min-w-0">
                            <p className="text-sm font-medium text-slate-900 dark:text-slate-100 truncate">{attachment.originalName}</p>
                            <p className="text-xs text-slate-500">{formatFileSize(attachment.size)}</p>
                          </div>
                        </div>
                        <div className="flex items-center gap-1">
                          <Button 
                            variant="ghost" 
                            size="sm"
                            onClick={() => handlePreviewAttachment(attachment)}
                            title="Preview"
                            className="h-6 w-6 p-0"
                          >
                            <Eye className="h-3 w-3" />
                          </Button>
                          <Button variant="ghost" size="sm" asChild className="h-6 w-6 p-0">
                            <a
                              href={attachment.url || `/uploads/tickets/${attachment.filename}`}
                              download={attachment.originalName}
                              target="_blank"
                              rel="noopener noreferrer"
                              className="text-slate-500 hover:text-blue-600 dark:hover:text-blue-400"
                            >
                              <Download className="h-3 w-3" />
                            </a>
                          </Button>
                        </div>
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}
          </div>

          {/* Right Sidebar */}
          <div className="space-y-4 dark:rounded-2xl">

            {/* Actions Card */}
            <Card className="bg-white/80 dark:bg-slate-900/80 backdrop-blur-sm shadow-lg rounded-2xl border border-slate-200/60 dark:border-slate-700/60">
              <CardHeader className="pb-3">
                <CardTitle className="text-base flex items-center gap-2">
                  <div className="w-8 h-8 rounded-lg flex items-center justify-center bg-green-100 dark:bg-green-900/50">
                    <CircleDot className="h-4 w-4 text-green-600 dark:text-green-400" />
                  </div>
                  Actions
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                {/* Mark as Resolved - Show for all active statuses */}
                {(ticket.status === 'open' || ticket.status === 'in_progress' || ticket.status === 'waiting_admin' || ticket.status === 'waiting_user') && (
                  <Button 
                    onClick={handleMarkResolved} 
                    className="w-full bg-green-600 hover:bg-green-700 text-white shadow-md"
                    size="sm"
                  >
                    <CheckCircle2 className="h-4 w-4 mr-2" />
                    Mark as Resolved
                  </Button>
                )}

                {/* Reopen Ticket - Show when closed or resolved */}
                {(ticket.status === 'closed' || ticket.status === 'resolved') && (
                  <div className="space-y-2">
                    <Button 
                      onClick={handleReopenTicket} 
                      variant="outline"
                      disabled={!reopenCheck.allowed}
                      className={`w-full ${
                        reopenCheck.allowed 
                          ? 'border-amber-300 text-amber-600 hover:bg-amber-50 dark:border-amber-600 dark:text-amber-400 dark:hover:bg-amber-950/20' 
                          : 'border-slate-300 text-slate-400 dark:border-slate-600 dark:text-slate-500 cursor-not-allowed'
                      }`}
                      size="sm"
                    >
                      <RefreshCw className="h-4 w-4 mr-2" />
                      Reopen Ticket
                    </Button>

                    {/* Show restriction message */}
                    {!reopenCheck.allowed && reopenCheck.reason && (
                      <div className="flex items-start gap-2 p-2 bg-slate-50 dark:bg-slate-800 rounded-md text-xs text-slate-600 dark:text-slate-400">
                        <Info className="h-3 w-3 mt-0.5 flex-shrink-0" />
                        <span>{reopenCheck.reason}</span>
                      </div>
                    )}

                    {/* Show time remaining if within restriction period */}
                    {reopenCheck.allowed && ticket.resolvedAt && (
                      <div className="flex items-start gap-2 p-2 bg-amber-50 dark:bg-amber-950/20 rounded-md text-xs text-amber-600 dark:text-amber-400">
                        <Clock className="h-3 w-3 mt-0.5 flex-shrink-0" />
                        <span>
                          {getTimeRemaining(
                            ticket.status === 'resolved' ? ticket.resolvedAt : ticket.closedAt!, 
                            ACTION_RESTRICTIONS.REOPEN_HOURS
                          )} to reopen
                        </span>
                      </div>
                    )}
                  </div>
                )}

                {/* Information text based on status */}
                <div className="text-xs text-slate-500 dark:text-slate-400 pt-2 border-t border-slate-200 dark:border-slate-700">
                  {ticket.status === 'open' && "Mark as resolved when your issue is fixed"}
                  {ticket.status === 'in_progress' && "Mark as resolved when your issue is fixed"}
                  {ticket.status === 'waiting_admin' && "Mark as resolved when your issue is fixed"}
                  {ticket.status === 'waiting_user' && "Mark as resolved when your issue is fixed"}
                  {ticket.status === 'resolved' && (
                    reopenCheck.allowed 
                      ? "Reopen if you need further assistance" 
                      : "Tickets can only be reopened within 48 hours"
                  )}
                  {ticket.status === 'closed' && (
                    reopenCheck.allowed 
                      ? "Reopen if you need further assistance" 
                      : "Tickets can only be reopened within 48 hours"
                  )}
                </div>
              </CardContent>
            </Card>

            {/* Ticket Details Card */}
            <Card className="bg-white/80 dark:bg-slate-900/80 backdrop-blur-sm shadow-lg rounded-2xl border border-slate-200/60 dark:border-slate-700/60">
              <CardHeader className="pb-3">
                <CardTitle className="text-base flex items-center gap-2">
                  <div className="w-8 h-8 rounded-lg flex items-center justify-center bg-slate-100 dark:bg-slate-800">
                    <FileText className="h-4 w-4 text-slate-600 dark:text-slate-400" />
                  </div>
                  Details
                </CardTitle>
              </CardHeader>
              <CardContent className="space-y-3">
                {ticket.estimatedResolutionTime && (
                  <div>
                    <span className="text-xs font-medium text-slate-600 dark:text-slate-400">Estimated Resolution</span>
                    <p className="font-medium text-amber-600 dark:text-amber-400">{formatDate(ticket.estimatedResolutionTime)}</p>
                  </div>
                )}

                <div>
                  <span className="text-xs font-medium text-slate-600 dark:text-slate-400">Resolve Date</span>
                  {ticket.resolvedAt ? (
                    <p className="font-medium text-green-600 dark:text-green-400">{formatDate(ticket.resolvedAt)}</p>
                  ) : (
                    <p className="font-medium text-slate-500 dark:text-slate-400">Not yet resolved</p>
                  )}
                </div>

                <div>
                  <span className="text-xs font-medium text-slate-600 dark:text-slate-400">Rating</span>
                  {ticket.customerSatisfactionRating ? (
                    <div className="space-y-2">
                      <div className="flex items-center justify-between">
                        <div className="flex items-center gap-2">
                          <Star className="h-4 w-4 text-amber-500 fill-current" />
                          <p className="font-medium text-amber-600 dark:text-amber-400">{ticket.customerSatisfactionRating}/5</p>
                        </div>
                        <Button
                          variant="outline"
                          size="sm"
                          onClick={handleReRate}
                          disabled={!reRateCheck.allowed}
                          className={`h-7 px-3 text-xs ${
                            reRateCheck.allowed
                              ? 'bg-amber-50 hover:bg-amber-100 text-amber-700 border-amber-200 hover:border-amber-300 dark:bg-amber-950/20 dark:hover:bg-amber-950/30 dark:text-amber-400 dark:border-amber-800 dark:hover:border-amber-700'
                              : 'bg-slate-50 text-slate-400 border-slate-200 dark:bg-slate-800 dark:text-slate-500 dark:border-slate-600 cursor-not-allowed'
                          }`}
                        >
                          <Star className="h-3 w-3 mr-1" />
                          Re-rate
                        </Button>
                      </div>

                      {/* Show restriction message or time remaining */}
                      {!reRateCheck.allowed && reRateCheck.reason && (
                        <div className="flex items-start gap-2 p-2 bg-slate-50 dark:bg-slate-800 rounded-md text-xs text-slate-600 dark:text-slate-400">
                          <Info className="h-3 w-3 mt-0.5 flex-shrink-0" />
                          <span>{reRateCheck.reason}</span>
                        </div>
                      )}

                      {reRateCheck.allowed && ticket.resolvedAt && (
                        <div className="flex items-start gap-2 p-2 bg-amber-50 dark:bg-amber-950/20 rounded-md text-xs text-amber-600 dark:text-amber-400">
                          <Clock className="h-3 w-3 mt-0.5 flex-shrink-0" />
                          <span>
                            {getTimeRemaining(
                              ticket.status === 'resolved' ? ticket.resolvedAt : ticket.closedAt!, 
                              ACTION_RESTRICTIONS.RERATING_HOURS
                            )} to re-rate
                          </span>
                        </div>
                      )}
                    </div>
                  ) : (
                    <p className="font-medium text-slate-500 dark:text-slate-400">Not yet rated</p>
                  )}
                </div>

                <div>
                  <span className="text-xs font-medium text-slate-600 dark:text-slate-400">Last Updated</span>
                  <p className="font-medium text-slate-900 dark:text-slate-100">{getTimeAgo(ticket.updatedAt)}</p>
                </div>
              </CardContent>
            </Card>

            {/* Tags Card */}
            {ticket.tags && ticket.tags.length > 0 && (
              <Card className="bg-white/80 dark:bg-slate-900/80 backdrop-blur-sm shadow-lg rounded-2xl border border-slate-200/60 dark:border-slate-700/60">
                <CardHeader className="pb-3">
                  <CardTitle className="text-base flex items-center gap-2">
                    <div className="w-8 h-8 rounded-lg flex items-center justify-center bg-blue-100 dark:bg-blue-900/50">
                      <Tag className="h-4 w-4 text-blue-600 dark:text-blue-400" />
                    </div>
                    Tags
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="flex flex-wrap gap-2">
                    {ticket.tags.map((tag, index) => (
                      <Badge key={index} className="bg-blue-100 dark:bg-blue-900/50 text-blue-800 dark:text-blue-200 px-2 py-1 text-xs font-medium border-0 rounded-md">
                        {tag}
                      </Badge>
                    ))}
                  </div>
                </CardContent>
              </Card>
            )}

            {/* Internal Notes (Admin Only) */}
            {ticket.internalNotes && (
              <Card className="bg-purple-50/80 dark:bg-purple-950/20 backdrop-blur-sm shadow-lg rounded-2xl border border-purple-200 dark:border-purple-800/50">
                <CardHeader className="pb-3">
                  <CardTitle className="text-base flex items-center gap-2">
                    <div className="w-8 h-8 rounded-lg flex items-center justify-center bg-purple-100 dark:bg-purple-900/50">
                      <FileText className="h-4 w-4 text-purple-600 dark:text-purple-400" />
                    </div>
                    Internal Notes
                    <Badge variant="secondary" className="text-xs">Admin Only</Badge>
                  </CardTitle>
                </CardHeader>
                <CardContent>
                  <div className="bg-purple-100/50 dark:bg-purple-950/30 rounded-lg p-3 border border-purple-200/50 dark:border-purple-700/50">
                    <div 
                      className="text-purple-900 dark:text-purple-100 whitespace-pre-wrap text-sm leading-relaxed prose prose-sm max-w-none prose-slate dark:prose-invert"
                      dangerouslySetInnerHTML={{ __html: ticket.internalNotes }}
                    />
                  </div>
                </CardContent>
              </Card>
            )}
          </div>
        </div>

        {/* Conversation Section - Full Width */}
        <div className="mt-8 space-y-6 dark:rounded-2xl">

          {/* Conversation */}
          <Card className="bg-white/80 dark:bg-slate-900/80 backdrop-blur-sm shadow-lg rounded-2xl border border-slate-200/60 dark:border-slate-700/60">
            <CardHeader className="pb-3">
              <CardTitle className="text-base flex items-center gap-2">
                <div className="w-8 h-8 rounded-lg flex items-center justify-center bg-blue-100 dark:bg-blue-900/50">
                  <MessageSquare className="h-4 w-4 text-blue-600 dark:text-blue-400" />
                </div>
                <div>
                  <span>Conversation</span>
                  <span className="text-sm font-normal text-slate-500 dark:text-slate-400 ml-2">
                    ({ticket.replies.filter(reply => !reply.isInternal).length} messages)
                  </span>
                </div>
              </CardTitle>
            </CardHeader>
            <CardContent>
              <div className="space-y-4">
                {ticket.replies.filter(reply => !reply.isInternal).length === 0 ? (
                  <div className="text-center py-8">
                    <div className="p-3 bg-slate-100 dark:bg-slate-800 rounded-full w-12 h-12 mx-auto mb-3 flex items-center justify-center">
                      <MessageCircle className="h-6 w-6 text-slate-400" />
                    </div>
                    <p className="font-medium text-slate-600 dark:text-slate-400 mb-1">No messages yet</p>
                    <p className="text-sm text-slate-500 dark:text-slate-500">Start the conversation below</p>
                  </div>
                ) : (
                  ticket.replies
                    .filter(reply => !reply.isInternal)
                    .map((reply, index) => {
                      const isSystem = isSystemMessage(reply);

                      return (
                        <div key={index} className="flex gap-3">
                          <Avatar className="h-8 w-8 border-2 border-slate-200 dark:border-slate-700 shadow-sm">
                            <AvatarFallback className={`text-xs font-medium ${
                              isSystem
                                ? 'bg-amber-100 dark:bg-amber-900/50 text-amber-600 dark:text-amber-400'
                                : reply.authorType === 'admin' 
                                  ? 'bg-blue-100 dark:bg-blue-900/50 text-blue-600 dark:text-blue-400' 
                                  : 'bg-slate-100 dark:bg-slate-800 text-slate-600 dark:text-slate-400'
                            }`}>
                              {isSystem ? (
                                <Settings className="h-4 w-4" />
                              ) : (
                                reply.author 
                                  ? getUserInitials(reply.author.email, reply.author.firstName, reply.author.lastName, reply.author.name)
                                  : reply.authorType === 'admin' ? 'S' : 'Y'
                              )}
                            </AvatarFallback>
                          </Avatar>
                          <div className="flex-1 space-y-2">
                            <div className="flex items-center gap-2 text-sm">
                              <span className="font-medium text-slate-900 dark:text-slate-100">
                                {isSystem ? 'System' : (
                                  reply.author 
                                    ? reply.author.name || (reply.author.firstName && reply.author.lastName ? `${reply.author.firstName} ${reply.author.lastName}` : reply.author.firstName || reply.author.lastName) || reply.author.email
                                    : reply.authorType === 'admin' ? 'Support Team' : 'You'
                                )}
                              </span>
                              <Badge variant="outline" className={`text-xs ${
                                isSystem
                                  ? 'bg-amber-50 dark:bg-amber-950/20 text-amber-600 dark:text-amber-400 border-amber-200 dark:border-amber-800'
                                  : reply.authorType === 'admin' 
                                    ? 'bg-blue-50 dark:bg-blue-950/20 text-blue-600 dark:text-blue-400 border-blue-200 dark:border-blue-800' 
                                    : 'bg-slate-50 dark:bg-slate-800 text-slate-600 dark:text-slate-400'
                              }`}>
                                {isSystem ? 'System' : (reply.authorType === 'admin' ? 'Support' : 'Customer')}
                              </Badge>
                              <span className="text-slate-500 dark:text-slate-500">
                                {formatDate(reply.createdAt)}
                              </span>
                            </div>
                            <div className={`rounded-lg p-3 shadow-sm transition-colors hover:shadow-md ${
                              isSystem
                                ? 'bg-amber-50/80 dark:bg-amber-950/20 border border-amber-200/50 dark:border-amber-800/50'
                                : reply.authorType === 'admin' 
                                  ? 'bg-blue-50/80 dark:bg-blue-950/20 border border-blue-200/50 dark:border-blue-800/50' 
                                  : 'bg-slate-50 dark:bg-slate-800/50 border border-slate-200 dark:border-slate-700'
                            }`}>
                              <div 
                                className="text-slate-800 dark:text-slate-200 text-sm leading-relaxed prose prose-sm max-w-none prose-slate dark:prose-invert"
                                dangerouslySetInnerHTML={{ __html: reply.content }}
                              />

                              {/* Reply Attachments */}
                              {reply.attachments && reply.attachments.length > 0 && (
                                <div className="mt-3 space-y-2">
                                  <div className="flex items-center gap-2">
                                    <Paperclip className="h-3 w-3 text-slate-500" />
                                    <span className="text-xs text-slate-500">Attachments</span>
                                  </div>
                                  {reply.attachments.map((attachment, attachIndex) => (
                                    <div
                                      key={attachIndex}
                                      className="flex items-center justify-between p-2 bg-white dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700 hover:bg-slate-50 dark:hover:bg-slate-700/50 transition-colors"
                                    >
                                      <div className="flex items-center space-x-2">
                                        <div className="p-1 bg-slate-100 dark:bg-slate-700 rounded">
                                          {getFileIcon(attachment.mimeType)}
                                        </div>
                                        <div>
                                          <p className="text-xs font-medium text-slate-900 dark:text-slate-100">
                                            {attachment.originalName}
                                          </p>
                                          <p className="text-xs text-slate-500">
                                            {formatFileSize(attachment.size)}
                                          </p>
                                        </div>
                                      </div>
                                      <div className="flex items-center gap-1">
                                        <Button 
                                          variant="ghost" 
                                          size="sm"
                                          onClick={() => handlePreviewAttachment(attachment)}
                                          title="Preview"
                                          className="h-6 w-6 p-0"
                                        >
                                          <Eye className="h-3 w-3" />
                                        </Button>
                                        <Button variant="ghost" size="sm" asChild className="h-6 w-6 p-0">
                                          <a
                                            href={attachment.url || `/uploads/tickets/${attachment.filename}`}
                                            download={attachment.originalName}
                                            target="_blank"
                                            rel="noopener noreferrer"
                                            className="text-slate-500 hover:text-blue-600 dark:hover:text-blue-400"
                                          >
                                            <Download className="h-3 w-3" />
                                          </a>
                                        </Button>
                                      </div>
                                    </div>
                                  ))}
                                </div>
                              )}
                            </div>
                          </div>
                        </div>
                      );
                    })
                )}
              </div>
            </CardContent>
          </Card>

          {/* Reply Form */}
          {ticket.status !== 'closed' && (
            <Card className="bg-white/80 dark:bg-slate-900/80 backdrop-blur-sm shadow-lg rounded-2xl border border-slate-200/60 dark:border-slate-700/60">
              <CardHeader className="pb-3">
                <CardTitle className="text-base flex items-center gap-2">
                  <div className="w-8 h-8 rounded-lg flex items-center justify-center bg-green-100 dark:bg-green-900/50">
                    <Send className="h-4 w-4 text-green-600 dark:text-green-400" />
                  </div>
                  Add Reply
                </CardTitle>
              </CardHeader>
              <CardContent>
                <form onSubmit={handleReplySubmit} className="space-y-6">
                  <div className="space-y-2">
                    <Label htmlFor="reply-content" className="text-sm font-medium text-slate-700 dark:text-slate-300">
                      Your Message
                    </Label>
                    <WysiwygEditor
                      id="reply-content"
                      value={newReply}
                      onChange={(value) => setNewReply(value)}
                      placeholder="Type your message here..."
                      minHeight="120px"
                      toolbar="standard"
                      className="border-slate-200 dark:border-slate-700 bg-white dark:bg-slate-800 focus:ring-2 focus:ring-blue-500/20 focus:border-blue-500 dark:focus:border-blue-400 rounded-xl transition-all duration-200"
                    />
                  </div>

                  {/* File Upload with Drag & Drop */}
                  <div className="space-y-4">
                    <div className="flex items-center justify-between">
                      <Label className="text-sm font-medium text-slate-700 dark:text-slate-300">Attachments</Label>
                      <div className="text-xs text-slate-500">
                        Max 5 files, 10MB each
                      </div>
                    </div>

                    <div
                      {...getRootProps()}
                      className={`border-2 border-dashed rounded-xl p-8 text-center transition-all duration-300 cursor-pointer ${
                        isDragActive
                          ? 'border-blue-400 bg-blue-50 dark:bg-blue-950/20 shadow-md'
                          : 'border-slate-300 dark:border-slate-600 bg-slate-50 dark:bg-slate-800/50 hover:border-blue-400 hover:bg-blue-50 dark:hover:bg-blue-950/20'
                      }`}
                    >
                      <input {...getInputProps()} />
                      <div className="space-y-4">
                        <div className="mx-auto w-12 h-12 bg-slate-200 dark:bg-slate-700 rounded-full flex items-center justify-center">
                          <Upload className="h-6 w-6 text-slate-500" />
                        </div>
                        <div className="space-y-1">
                          <p className="text-base font-medium text-slate-700 dark:text-slate-300">
                            {isDragActive ? 'Drop files here' : 'Upload attachments'}
                          </p>
                          <p className="text-sm text-slate-500">
                            Drag & drop files here, or click to browse
                          </p>
                        </div>
                      </div>
                    </div>

                    {/* File Preview */}
                    {replyAttachments.length > 0 && (
                      <div className="space-y-3">
                        <div className="flex items-center gap-2">
                          <Paperclip className="h-4 w-4 text-slate-500" />
                          <span className="text-sm font-medium text-slate-700 dark:text-slate-300">
                            Selected Files ({replyAttachments.length})
                          </span>
                        </div>
                        <div className="space-y-2">
                          {replyAttachments.map((file, index) => (
                            <div
                              key={index}
                              className="flex items-center justify-between p-3 bg-slate-50 dark:bg-slate-800 rounded-lg border border-slate-200 dark:border-slate-700"
                            >
                              <div className="flex items-center space-x-3">
                                <div className="p-2 bg-white dark:bg-slate-700 rounded-lg shadow-sm">
                                  {getFileIcon(file.type)}
                                </div>
                                <div className="min-w-0 flex-1">
                                  <p className="text-sm font-medium text-slate-800 dark:text-slate-200 truncate">
                                    {file.name}
                                  </p>
                                  <p className="text-xs text-slate-500">
                                    {formatFileSize(file.size)}
                                  </p>
                                </div>
                              </div>
                              <Button
                                type="button"
                                variant="ghost"
                                size="sm"
                                onClick={() => removeAttachment(index)}
                                className="text-red-500 hover:text-red-700 hover:bg-red-50 dark:hover:bg-red-950/20"
                              >
                                <X className="h-4 w-4" />
                              </Button>
                            </div>
                          ))}
                        </div>
                      </div>
                    )}
                  </div>

                  {/* Submit Button */}
                  <div className="flex justify-end">
                    <Button 
                      type="submit" 
                      disabled={isSubmittingReply || uploadingFiles || !getPlainTextFromHtml(newReply).trim()}
                      className="bg-blue-600 hover:bg-blue-700 text-white shadow-md hover:shadow-lg transition-all duration-200 px-6"
                    >
                      {isSubmittingReply || uploadingFiles ? (
                        <div className="flex items-center gap-2">
                          <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
                          <span>Sending...</span>
                        </div>
                      ) : (
                        <div className="flex items-center gap-2">
                          <Send className="h-4 w-4" />
                          <span>Send Reply</span>
                        </div>
                      )}
                    </Button>
                  </div>
                </form>
              </CardContent>
            </Card>
          )}
        </div>
      </div>

      {/* Attachment Preview Modal */}
      {previewAttachment && (
        <AttachmentPreview
          attachment={previewAttachment}
          isOpen={showPreview}
          onClose={handleClosePreview}
        />
      )}
    </div>
  );
}

'use client';

import React, { useState, useEffect } from 'react';
import Link from 'next/link';
import { useTickets } from '@/hooks/useTickets';
import { 
  TicketFilters, 
  SERVICE_LABELS, 
  PRIORITY_LABELS, 
  STATUS_LABELS,
  PRIORITY_COLORS,
  STATUS_COLORS,
  TicketStatus
} from '@/types/ticket';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { 
  Select, 
  SelectContent, 
  SelectItem, 
  SelectTrigger, 
  SelectValue 
} from '@/components/ui/select';
import { Card, CardContent } from '@/components/ui/card';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { 
  Search, 
  Plus, 
  Filter, 
  RefreshCw, 
  MessageCircle,
  Clock,
  TrendingUp,
  BarChart3,
  Activity,
  CheckCircle2,
  AlertCircle,
  CircleDot,
  Timer,
  Sparkles,
  Zap,
  AlertTriangle
} from 'lucide-react';

interface TicketListProps {
  showCreateButton?: boolean;
  onCreateTicket?: () => void;
}

export default function TicketList({ showCreateButton = true, onCreateTicket }: TicketListProps) {
  const { 
    tickets, 
    loading, 
    error, 
    pagination, 
    stats,
    fetchTickets, 
    clearError 
  } = useTickets();

  const [filters, setFilters] = useState<TicketFilters>({
    page: 1,
    limit: 10,
    search: '',
    status: undefined,
    service: undefined,
    priority: undefined,
  });

  const [showFilters, setShowFilters] = useState(false);

  useEffect(() => {
    fetchTickets(filters);
  }, [fetchTickets, filters]);

  const handleFilterChange = (key: keyof TicketFilters, value: any) => {
    setFilters(prev => ({
      ...prev,
      [key]: value,
      page: 1, // Reset to first page when filtering
    }));
  };

  const handleSearch = (searchTerm: string) => {
    setFilters(prev => ({
      ...prev,
      search: searchTerm,
      page: 1,
    }));
  };

  const handlePageChange = (newPage: number) => {
    setFilters(prev => ({
      ...prev,
      page: newPage,
    }));
  };

  const clearFilters = () => {
    setFilters({
      page: 1,
      limit: 10,
      search: '',
      status: undefined,
      service: undefined,
      priority: undefined,
    });
  };

  const formatDate = (date: Date | string) => {
    return new Date(date).toLocaleDateString('en-US', {
      year: 'numeric',
      month: 'short',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
    });
  };

  const getTimeAgo = (date: Date | string) => {
    const now = new Date();
    const past = new Date(date);
    const diffInMinutes = Math.floor((now.getTime() - past.getTime()) / (1000 * 60));

    if (diffInMinutes < 1) return 'Just now';
    if (diffInMinutes < 60) return `${diffInMinutes}m ago`;
    if (diffInMinutes < 1440) return `${Math.floor(diffInMinutes / 60)}h ago`;
    return `${Math.floor(diffInMinutes / 1440)}d ago`;
  };

  const getStatusIcon = (status: TicketStatus) => {
    switch (status) {
      case 'open':
        return <AlertCircle className="h-4 w-4" />;
      case 'in_progress':
        return <CircleDot className="h-4 w-4" />;
      case 'waiting_admin':
        return <Timer className="h-4 w-4" />;
      case 'resolved':
        return <CheckCircle2 className="h-4 w-4" />;
      case 'closed':
        return <CheckCircle2 className="h-4 w-4" />;
      default:
        return <AlertCircle className="h-4 w-4" />;
    }
  };

  return (
    <div className="space-y-8">
      {/* Enhanced User Stats Dashboard */}
      {stats && (
        <div className="grid grid-cols-2 lg:grid-cols-5 gap-6">
          {/* Total Tickets Card */}
          <Card className="group relative overflow-hidden border-0 bg-gradient-to-br from-slate-50 via-slate-50 to-slate-100 dark:from-slate-950/50 dark:via-slate-900/30 dark:to-slate-800/20 shadow-lg hover:shadow-2xl hover:scale-[1.02] transition-all duration-300">
            <div className="absolute inset-0 bg-gradient-to-br from-slate-500/5 to-transparent" />
            <div className="absolute inset-0 ring-1 ring-inset ring-slate-200/50 dark:ring-slate-700/50 rounded-lg" />
            <CardContent className="relative p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="p-3 bg-slate-100 dark:bg-slate-900/50 rounded-xl shadow-sm">
                  <BarChart3 className="h-6 w-6 text-slate-600 dark:text-slate-400" />
                </div>
                <Sparkles className="h-4 w-4 text-slate-400 opacity-60 group-hover:opacity-100 transition-opacity" />
              </div>
              <div className="space-y-1">
                <p className="text-3xl font-bold text-slate-700 dark:text-slate-300">{stats.total || 0}</p>
                <p className="text-sm font-medium text-slate-600 dark:text-slate-400">Total Tickets</p>
                <p className="text-xs text-slate-500/70 dark:text-slate-400/70">All your requests</p>
              </div>
            </CardContent>
          </Card>

          {/* Open Tickets Card */}
          <Card className="group relative overflow-hidden border-0 bg-gradient-to-br from-red-50 via-red-50 to-red-100 dark:from-red-950/50 dark:via-red-900/30 dark:to-red-800/20 shadow-lg hover:shadow-2xl hover:scale-[1.02] transition-all duration-300">
            <div className="absolute inset-0 bg-gradient-to-br from-red-500/5 to-transparent" />
            <div className="absolute inset-0 ring-1 ring-inset ring-red-200/50 dark:ring-red-700/50 rounded-lg" />
            <CardContent className="relative p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="p-3 bg-red-100 dark:bg-red-900/50 rounded-xl shadow-sm">
                  <AlertCircle className="h-6 w-6 text-red-600 dark:text-red-400" />
                </div>
                <TrendingUp className="h-4 w-4 text-red-400 opacity-60 group-hover:opacity-100 transition-opacity" />
              </div>
              <div className="space-y-1">
                <p className="text-3xl font-bold text-red-700 dark:text-red-300">{stats.open || 0}</p>
                <p className="text-sm font-medium text-red-600 dark:text-red-400">Open</p>
                <p className="text-xs text-red-500/70 dark:text-red-400/70">Need attention</p>
              </div>
            </CardContent>
          </Card>

          {/* In Progress Card */}
          <Card className="group relative overflow-hidden border-0 bg-gradient-to-br from-blue-50 via-blue-50 to-blue-100 dark:from-blue-950/50 dark:via-blue-900/30 dark:to-blue-800/20 shadow-lg hover:shadow-2xl hover:scale-[1.02] transition-all duration-300">
            <div className="absolute inset-0 bg-gradient-to-br from-blue-500/5 to-transparent" />
            <div className="absolute inset-0 ring-1 ring-inset ring-blue-200/50 dark:ring-blue-700/50 rounded-lg" />
            <CardContent className="relative p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="p-3 bg-blue-100 dark:bg-blue-900/50 rounded-xl shadow-sm">
                  <CircleDot className="h-6 w-6 text-blue-600 dark:text-blue-400" />
                </div>
                <Activity className="h-4 w-4 text-blue-400 opacity-60 group-hover:opacity-100 transition-opacity" />
              </div>
              <div className="space-y-1">
                <p className="text-3xl font-bold text-blue-700 dark:text-blue-300">{stats.in_progress || 0}</p>
                <p className="text-sm font-medium text-blue-600 dark:text-blue-400">In Progress</p>
                <p className="text-xs text-blue-500/70 dark:text-blue-400/70">Being worked on</p>
              </div>
            </CardContent>
          </Card>

          {/* Waiting for Support Card */}
          <Card className="group relative overflow-hidden border-0 bg-gradient-to-br from-amber-50 via-amber-50 to-amber-100 dark:from-amber-950/50 dark:via-amber-900/30 dark:to-amber-800/20 shadow-lg hover:shadow-2xl hover:scale-[1.02] transition-all duration-300">
            <div className="absolute inset-0 bg-gradient-to-br from-amber-500/5 to-transparent" />
            <div className="absolute inset-0 ring-1 ring-inset ring-amber-200/50 dark:ring-amber-700/50 rounded-lg" />
            <CardContent className="relative p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="p-3 bg-amber-100 dark:bg-amber-900/50 rounded-xl shadow-sm">
                  <Timer className="h-6 w-6 text-amber-600 dark:text-amber-400" />
                </div>
                <Clock className="h-4 w-4 text-amber-400 opacity-60 group-hover:opacity-100 transition-opacity" />
              </div>
              <div className="space-y-1">
                <p className="text-3xl font-bold text-amber-700 dark:text-amber-300">{stats.waiting_admin || 0}</p>
                <p className="text-sm font-medium text-amber-600 dark:text-amber-400">Waiting Response</p>
                <p className="text-xs text-amber-500/70 dark:text-amber-400/70">Pending support</p>
              </div>
            </CardContent>
          </Card>

          {/* Resolved Card */}
          <Card className="group relative overflow-hidden border-0 bg-gradient-to-br from-emerald-50 via-emerald-50 to-emerald-100 dark:from-emerald-950/50 dark:via-emerald-900/30 dark:to-emerald-800/20 shadow-lg hover:shadow-2xl hover:scale-[1.02] transition-all duration-300">
            <div className="absolute inset-0 bg-gradient-to-br from-emerald-500/5 to-transparent" />
            <div className="absolute inset-0 ring-1 ring-inset ring-emerald-200/50 dark:ring-emerald-700/50 rounded-lg" />
            <CardContent className="relative p-6">
              <div className="flex items-center justify-between mb-4">
                <div className="p-3 bg-emerald-100 dark:bg-emerald-900/50 rounded-xl shadow-sm">
                  <CheckCircle2 className="h-6 w-6 text-emerald-600 dark:text-emerald-400" />
                </div>
                <BarChart3 className="h-4 w-4 text-emerald-400 opacity-60 group-hover:opacity-100 transition-opacity" />
              </div>
              <div className="space-y-1">
                <p className="text-3xl font-bold text-emerald-700 dark:text-emerald-300">{stats.resolved || 0}</p>
                <p className="text-sm font-medium text-emerald-600 dark:text-emerald-400">Resolved</p>
                <p className="text-xs text-emerald-500/70 dark:text-emerald-400/70">Successfully closed</p>
              </div>
            </CardContent>
          </Card>
        </div>
      )}

      {/* Enhanced Search and Filters */}
      <Card className="border-0 bg-card/80 backdrop-blur-md shadow-xl ring-1 ring-border/50 hover:shadow-2xl transition-all duration-300">
        <CardContent className="p-6">
          <div className="space-y-6">
            <div className="flex flex-col lg:flex-row gap-4">
              {/* Enhanced Search */}
              <div className="flex-1 relative group">
                <Search className="absolute left-4 top-1/2 transform -translate-y-1/2 text-muted-foreground h-4 w-4 transition-colors group-focus-within:text-primary" />
                <Input
                  placeholder="Search tickets by title, description, or ticket number..."
                  value={filters.search || ''}
                  onChange={(e) => handleSearch(e.target.value)}
                  className="pl-12 h-12 border-0 bg-background/60 backdrop-blur-sm hover:bg-background/80 focus:bg-background transition-all duration-200 text-base"
                />
              </div>

              {/* Action Buttons */}
              <div className="flex gap-3">
                <Button
                  variant="outline"
                  onClick={() => setShowFilters(!showFilters)}
                  className="h-12 px-6 border-0 bg-background/60 backdrop-blur-sm hover:bg-background/80 transition-all duration-200"
                >
                  <Filter className="h-4 w-4 mr-2" />
                  <span className="hidden sm:inline">Advanced Filters</span>
                  <span className="sm:hidden">Filters</span>
                </Button>

                <Button
                  variant="outline"
                  onClick={() => fetchTickets(filters)}
                  disabled={loading}
                  className="h-12 px-6 border-0 bg-background/60 backdrop-blur-sm hover:bg-background/80 transition-all duration-200"
                >
                  <RefreshCw className={`h-4 w-4 mr-2 ${loading ? 'animate-spin' : ''}`} />
                  <span className="hidden sm:inline">Refresh</span>
                </Button>
              </div>
            </div>

            {/* Enhanced Expandable Filters */}
            {showFilters && (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4 p-6 bg-muted/30 dark:bg-muted/20 rounded-xl border border-border/50">
                <div className="space-y-2">
                  <label className="text-sm font-medium text-muted-foreground">Status</label>
                  <Select
                    value={filters.status || 'all'}
                    onValueChange={(value) => handleFilterChange('status', value === 'all' ? undefined : value)}
                  >
                    <SelectTrigger className="h-10 border-0 bg-background/60">
                      <SelectValue placeholder="All Statuses" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Statuses</SelectItem>
                      {Object.entries(STATUS_LABELS).map(([value, label]) => (
                        <SelectItem key={value} value={value}>
                          <div className="flex items-center gap-2">
                            {getStatusIcon(value as TicketStatus)}
                            {label}
                          </div>
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <label className="text-sm font-medium text-muted-foreground">Service</label>
                  <Select
                    value={filters.service || 'all'}
                    onValueChange={(value) => handleFilterChange('service', value === 'all' ? undefined : value)}
                  >
                    <SelectTrigger className="h-10 border-0 bg-background/60">
                      <SelectValue placeholder="All Services" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Services</SelectItem>
                      {Object.entries(SERVICE_LABELS).map(([value, label]) => (
                        <SelectItem key={value} value={value}>
                          {label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="space-y-2">
                  <label className="text-sm font-medium text-muted-foreground">Priority</label>
                  <Select
                    value={filters.priority || 'all'}
                    onValueChange={(value) => handleFilterChange('priority', value === 'all' ? undefined : value)}
                  >
                    <SelectTrigger className="h-10 border-0 bg-background/60">
                      <SelectValue placeholder="All Priorities" />
                    </SelectTrigger>
                    <SelectContent>
                      <SelectItem value="all">All Priorities</SelectItem>
                      {Object.entries(PRIORITY_LABELS).map(([value, label]) => (
                        <SelectItem key={value} value={value}>
                          {label}
                        </SelectItem>
                      ))}
                    </SelectContent>
                  </Select>
                </div>

                <div className="flex items-end">
