  );
}

'use client';

import React, { useState } from 'react';
import { Button } from '@/components/ui/button';
import { 
  Download, 
  X, 
  ZoomIn, 
  ZoomOut, 
  RotateCw, 
  Eye,
  FileText,
  Image as ImageIcon,
  File
} from 'lucide-react';

interface AttachmentPreviewProps {
  attachment: {
    filename: string;
    originalName: string;
    mimeType: string;
    size: number;
    url?: string;
  };
  onClose: () => void;
  isOpen: boolean;
}

const AttachmentPreview: React.FC<AttachmentPreviewProps> = ({ 
  attachment, 
  onClose, 
  isOpen 
}) => {
  const [zoom, setZoom] = useState(100);
  const [rotation, setRotation] = useState(0);
  const [imageError, setImageError] = useState(false);

  if (!isOpen) return null;

  const fileUrl = attachment.url || `/uploads/tickets/${attachment.filename}`;

  const handleBackdropClick = (e: React.MouseEvent) => {
    if (e.target === e.currentTarget) {
      onClose();
    }
  };

  const handleZoomIn = () => setZoom(prev => Math.min(prev + 25, 300));
  const handleZoomOut = () => setZoom(prev => Math.max(prev - 25, 25));
  const handleRotate = () => setRotation(prev => (prev + 90) % 360);
  const resetTransform = () => {
    setZoom(100);
    setRotation(0);
  };

  const formatFileSize = (bytes: number) => {
    if (bytes === 0) return '0 Bytes';
    const k = 1024;
    const sizes = ['Bytes', 'KB', 'MB', 'GB'];
    const i = Math.floor(Math.log(bytes) / Math.log(k));
    return parseFloat((bytes / Math.pow(k, i)).toFixed(2)) + ' ' + sizes[i];
  };

  const getFileIcon = (mimeType: string) => {
    if (mimeType.startsWith('image/')) {
      return <ImageIcon className="h-8 w-8 text-blue-500" />;
    }
    if (mimeType === 'application/pdf') {
      return <FileText className="h-8 w-8 text-red-500" />;
    }
    if (mimeType.startsWith('text/')) {
      return <FileText className="h-8 w-8 text-green-500" />;
    }
    return <File className="h-8 w-8 text-gray-500" />;
  };

  const isImage = attachment.mimeType.startsWith('image/');
  const isPDF = attachment.mimeType === 'application/pdf';
  const isText = attachment.mimeType.startsWith('text/');
  const isPreviewable = isImage || isPDF || isText;

  return (
    <div 
      className="fixed inset-0 z-[9999] bg-black/80 backdrop-blur-sm flex items-center justify-center"
      onClick={handleBackdropClick}
    >
      <div className="relative w-full h-full max-w-7xl max-h-screen mx-4 flex flex-col">
        {/* Header */}
        <div className="flex items-center justify-between p-4 bg-white dark:bg-slate-800 rounded-t-xl border-b border-slate-200 dark:border-slate-700">
          <div className="flex items-center gap-3 min-w-0 flex-1">
            {getFileIcon(attachment.mimeType)}
            <div className="min-w-0 flex-1">
              <h3 className="text-lg font-semibold text-slate-900 dark:text-slate-100 truncate">
                {attachment.originalName}
              </h3>
              <p className="text-sm text-slate-600 dark:text-slate-400">
                {attachment.mimeType} â€¢ {formatFileSize(attachment.size)}
              </p>
            </div>
          </div>

          <div className="flex items-center gap-2">
            {isImage && !imageError && (
              <>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleZoomOut}
                  disabled={zoom <= 25}
                  title="Zoom Out"
                >
                  <ZoomOut className="h-4 w-4" />
                </Button>
                <span className="text-sm text-slate-600 dark:text-slate-400 min-w-[4rem] text-center">
                  {zoom}%
                </span>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleZoomIn}
                  disabled={zoom >= 300}
                  title="Zoom In"
                >
                  <ZoomIn className="h-4 w-4" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={handleRotate}
                  title="Rotate"
                >
                  <RotateCw className="h-4 w-4" />
                </Button>
                <Button
                  variant="ghost"
                  size="sm"
                  onClick={resetTransform}
                  title="Reset"
                  className="text-xs"
                >
                  Reset
                </Button>
              </>
            )}

            <Button
              variant="ghost"
              size="sm"
              asChild
              title="Download"
            >
              <a
                href={fileUrl}
                download={attachment.originalName}
                target="_blank"
                rel="noopener noreferrer"
              >
                <Download className="h-4 w-4" />
              </a>
            </Button>

            <Button
              variant="ghost"
              size="sm"
              onClick={onClose}
              title="Close"
            >
              <X className="h-4 w-4" />
            </Button>
          </div>
        </div>

        {/* Content */}
        <div className="flex-1 bg-white dark:bg-slate-800 rounded-b-xl overflow-hidden">
          {isPreviewable ? (
            <div className="w-full h-full flex items-center justify-center overflow-auto">
              {/* Image Preview */}
              {isImage && !imageError && (
                <div className="flex items-center justify-center p-4 w-full h-full">
                  <img
                    src={fileUrl}
                    alt={attachment.originalName}
                    className="max-w-full max-h-full object-contain"
                    style={{
                      transform: `scale(${zoom / 100}) rotate(${rotation}deg)`,
                      transition: 'transform 0.2s ease-in-out'
                    }}
                    onError={() => setImageError(true)}
                  />
                </div>
              )}

              {/* PDF Preview */}
              {isPDF && (
                <div className="w-full h-full">
                  <iframe
                    src={`${fileUrl}#view=FitH`}
                    className="w-full h-full border-0"
                    title={attachment.originalName}
                  />
                </div>
              )}

              {/* Text File Preview */}
              {isText && (
                <div className="w-full h-full p-6 overflow-auto">
                  <iframe
                    src={fileUrl}
                    className="w-full h-full border-0 bg-white dark:bg-slate-900 rounded"
                    title={attachment.originalName}
                  />
                </div>
              )}

              {/* Error State for Images */}
              {isImage && imageError && (
                <div className="flex flex-col items-center justify-center p-8 text-center">
                  <ImageIcon className="h-16 w-16 text-slate-400 mb-4" />
                  <h3 className="text-lg font-medium text-slate-900 dark:text-slate-100 mb-2">
                    Cannot preview image
                  </h3>
                  <p className="text-slate-600 dark:text-slate-400 mb-4">
                    The image file could not be loaded or displayed.
                  </p>
                  <Button asChild>
                    <a
                      href={fileUrl}
                      download={attachment.originalName}
                      target="_blank"
                      rel="noopener noreferrer"
                    >
                      <Download className="h-4 w-4 mr-2" />
                      Download File
                    </a>
                  </Button>
                </div>
              )}
            </div>
          ) : (
            /* Non-previewable files */
            <div className="flex flex-col items-center justify-center p-8 text-center h-full">
              {getFileIcon(attachment.mimeType)}
              <h3 className="text-xl font-medium text-slate-900 dark:text-slate-100 mt-4 mb-2">
                {attachment.originalName}
              </h3>
              <p className="text-slate-600 dark:text-slate-400 mb-6">
                This file type cannot be previewed in the browser.
              </p>
              <div className="flex gap-3">
                <Button asChild>
                  <a
                    href={fileUrl}
                    download={attachment.originalName}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <Download className="h-4 w-4 mr-2" />
                    Download File
                  </a>
                </Button>
                <Button 
                  variant="outline"
                  asChild
                >
                  <a
                    href={fileUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                  >
                    <Eye className="h-4 w-4 mr-2" />
                    Open in New Tab
                  </a>
                </Button>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default AttachmentPreview;

'use client';

import { useEditor, EditorContent } from '@tiptap/react';
import StarterKit from '@tiptap/starter-kit';
import Underline from '@tiptap/extension-underline';
import TextAlign from '@tiptap/extension-text-align';
import TextStyle from '@tiptap/extension-text-style';
import Color from '@tiptap/extension-color';
import Highlight from '@tiptap/extension-highlight';
import Link from '@tiptap/extension-link';
import Placeholder from '@tiptap/extension-placeholder';
import Table from '@tiptap/extension-table';
import TableRow from '@tiptap/extension-table-row';
import TableCell from '@tiptap/extension-table-cell';
import TableHeader from '@tiptap/extension-table-header';
import Image from '@tiptap/extension-image';
import HorizontalRule from '@tiptap/extension-horizontal-rule';
import Superscript from '@tiptap/extension-superscript';
import Subscript from '@tiptap/extension-subscript';
import FontFamily from '@tiptap/extension-font-family';
import ListKeymap from '@tiptap/extension-list-keymap';
import { forwardRef, useCallback, useEffect, useMemo, useState } from 'react';
import { Button } from '@/components/ui/button';
import { 
  Bold, 
  Italic, 
  Underline as UnderlineIcon, 
  Strikethrough,
  List,
  ListOrdered,
  Quote,
  Code,
  Code2,
  AlignLeft,
  AlignCenter,
  AlignRight,
  AlignJustify,
  Link as LinkIcon,
  Type,
  Undo,
  Redo,
  Heading1,
  Heading2,
  Heading3,
  Table as TableIcon,
  Image as ImageIcon,
  Minus,
  Highlighter,
  Superscript as SuperscriptIcon,
  Subscript as SubscriptIcon,
  Indent,
  Outdent,
  Trash2,
  Maximize2,
  Minimize2
} from 'lucide-react';

interface WysiwygEditorProps {
  value: string;
  onChange: (value: string) => void;
  placeholder?: string;
  className?: string;
  disabled?: boolean;
  minHeight?: string;
  toolbar?: 'basic' | 'standard' | 'full';
  onKeyDown?: (event: any) => void;
  id?: string;
}

const WysiwygEditor = forwardRef<any, WysiwygEditorProps>(
  ({ 
    value, 
    onChange, 
    placeholder = "Type your message here...", 
    className = "",
    disabled = false,
    minHeight = "120px",
    toolbar = "standard",
    onKeyDown,
    id
  }) => {

    const [isFullscreen, setIsFullscreen] = useState(false);
    const [showColorPicker, setShowColorPicker] = useState(false);
    const [showHighlightPicker, setShowHighlightPicker] = useState(false);

    const extensions = useMemo(() => [
      StarterKit.configure({
        heading: {
          levels: [1, 2, 3, 4, 5, 6],
        },
      }),
      Underline,
      TextStyle,
      Color.configure({
        types: ['textStyle'],
      }),
      Highlight.configure({
        multicolor: true,
      }),
      TextAlign.configure({
        types: ['heading', 'paragraph'],
      }),
      Link.configure({
        openOnClick: false,
        HTMLAttributes: {
          class: 'text-blue-600 dark:text-blue-400 underline cursor-pointer',
        },
      }),
      Placeholder.configure({
        placeholder,
      }),
      Table.configure({
        resizable: true,
      }),
      TableRow,
      TableHeader,
      TableCell,
      Image.configure({
        HTMLAttributes: {
          class: 'max-w-full h-auto rounded-lg',
        },
      }),
      HorizontalRule,
      Superscript,
      Subscript,
      FontFamily.configure({
        types: ['textStyle'],
      }),
      ListKeymap,
    ], [placeholder]);

    const editor = useEditor({
      extensions,
      content: value,
      editable: !disabled,
      onUpdate: ({ editor }) => {
        const html = editor.getHTML();
        if (html !== value) {
          onChange(html);
        }
      },
      editorProps: {
        attributes: {
          class: 'prose prose-sm sm:prose lg:prose-lg xl:prose-2xl mx-auto focus:outline-none min-h-[' + minHeight + '] p-4',
          style: `min-height: ${minHeight}`,
        },
        handleKeyDown: (view, event) => {
          if (onKeyDown) {
            onKeyDown(event);
          }
          return false;
        },
      },
    });

    // Update editor content when value prop changes
    useEffect(() => {
      if (editor && value !== editor.getHTML()) {
        editor.commands.setContent(value);
      }
    }, [value, editor]);

    // Handle disabled state
    useEffect(() => {
      if (editor) {
        editor.setEditable(!disabled);
      }
    }, [disabled, editor]);

    const setLink = useCallback(() => {
      if (!editor) return;

      const previousUrl = editor.getAttributes('link').href;
      const url = window.prompt('URL', previousUrl);

      if (url === null) {
        return;
      }

      if (url === '') {
        editor.chain().focus().extendMarkRange('link').unsetLink().run();
        return;
      }

      editor.chain().focus().extendMarkRange('link').setLink({ href: url }).run();
    }, [editor]);

    const addImage = useCallback(() => {
      if (!editor) return;

      const url = window.prompt('Image URL');

      if (url) {
        editor.chain().focus().setImage({ src: url }).run();
      }
    }, [editor]);

    const insertTable = useCallback(() => {
      if (!editor) return;

      editor.chain().focus().insertTable({ rows: 3, cols: 3, withHeaderRow: true }).run();
    }, [editor]);

    const colors = [
      '#000000', '#434343', '#666666', '#999999', '#b7b7b7', '#cccccc', '#d9d9d9', '#efefef', '#f3f3f3', '#ffffff',
      '#980000', '#ff0000', '#ff9900', '#ffff00', '#00ff00', '#00ffff', '#4a86e8', '#0000ff', '#9900ff', '#ff00ff',
      '#e6b8af', '#f4cccc', '#fce5cd', '#fff2cc', '#d9ead3', '#d0e0e3', '#c9daf8', '#cfe2f3', '#d9d2e9', '#ead1dc',
      '#dd7e6b', '#ea9999', '#f9cb9c', '#ffe599', '#b6d7a8', '#a2c4c9', '#a4c2f4', '#9fc5e8', '#b4a7d6', '#d5a6bd',
      '#cc4125', '#e06666', '#f6b26b', '#ffd966', '#93c47d', '#76a5af', '#6d9eeb', '#6fa8dc', '#8e7cc3', '#c27ba0',
      '#a61c00', '#cc0000', '#e69138', '#f1c232', '#6aa84f', '#45818e', '#3c78d8', '#3d85c6', '#674ea7', '#a64d79',
      '#85200c', '#990000', '#b45f06', '#bf9000', '#38761d', '#134f5c', '#1155cc', '#0b5394', '#351c75', '#741b47',
      '#5b0f00', '#660000', '#783f04', '#7f6000', '#274e13', '#0c343d', '#1c4587', '#073763', '#20124d', '#4c1130'
    ];

    const ToolbarButton = ({ 
      onClick, 
      isActive = false, 
      disabled = false, 
      children, 
      title,
      variant = 'default'
    }: {
      onClick: (e?: React.MouseEvent) => void;
      isActive?: boolean;
      disabled?: boolean;
      children: React.ReactNode;
      title: string;
      variant?: 'default' | 'dropdown';
    }) => (
      <Button
        type="button"
        variant="ghost"
        size="sm"
        onClick={onClick}
        disabled={disabled}
        title={title}
        className={`h-8 ${variant === 'dropdown' ? 'w-auto px-2' : 'w-8'} p-0 ${
          isActive 
            ? 'bg-slate-200 dark:bg-slate-700 text-slate-900 dark:text-slate-100' 
            : 'text-slate-600 dark:text-slate-400 hover:text-slate-900 dark:hover:text-slate-100'
        }`}
      >
        {children}
      </Button>
    );

    const ColorPickerModal = ({ 
      isOpen, 
      onClose, 
      onColorSelect, 
      colors: colorPalette,
      title 
    }: { 
      isOpen: boolean;
      onClose: () => void;
      onColorSelect: (color: string) => void;
      colors: string[];
      title: string;
    }) => {
      if (!isOpen) return null;

      const handleBackdropClick = (e: React.MouseEvent) => {
        if (e.target === e.currentTarget) {
          onClose();
        }
      };

      const handleModalClick = (e: React.MouseEvent) => {
        e.stopPropagation();
      };

      return (
        <div 
          className="fixed inset-0 z-[9999] flex items-center justify-center"
          onClick={handleBackdropClick}
        >
          {/* Backdrop */}
          <div className="absolute inset-0 bg-black/50 backdrop-blur-sm" />

          {/* Modal */}
          <div 
            className="relative bg-white dark:bg-slate-800 rounded-xl shadow-2xl border border-slate-200 dark:border-slate-700 p-6 max-w-md w-full mx-4 animate-in zoom-in-95 duration-200"
            onClick={handleModalClick}
          >
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-lg font-semibold text-slate-900 dark:text-slate-100">
                {title}
              </h3>
              <button
                type="button"
                onClick={(e) => {
                  e.stopPropagation();
                  onClose();
                }}
                className="text-slate-400 hover:text-slate-600 dark:hover:text-slate-300 transition-colors p-1 hover:bg-slate-100 dark:hover:bg-slate-700 rounded"
              >
                <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M6 18L18 6M6 6l12 12" />
                </svg>
              </button>
            </div>

            <div className="grid grid-cols-10 gap-3 mb-6">
              {colorPalette.map((color) => (
                <button
                  key={color}
                  type="button"
                  className="w-8 h-8 rounded-lg border-2 border-slate-300 dark:border-slate-600 hover:scale-110 hover:shadow-lg transition-all duration-200 focus:ring-2 focus:ring-blue-500"
                  style={{ backgroundColor: color }}
                  onClick={(e) => {
                    e.stopPropagation();
                    onColorSelect(color);
                    onClose();
                  }}
                  title={color}
                />
              ))}
            </div>

            <div className="flex gap-3">
              <button
                type="button"
                className="flex-1 px-4 py-2 text-sm font-medium text-slate-700 dark:text-slate-300 bg-slate-100 dark:bg-slate-700 hover:bg-slate-200 dark:hover:bg-slate-600 rounded-lg transition-colors border border-slate-300 dark:border-slate-600"
                onClick={(e) => {
                  e.stopPropagation();
                  onColorSelect('');
                  onClose();
                }}
              >
                Remove Color
              </button>
              <button
                type="button"
                className="px-4 py-2 text-sm font-medium text-slate-500 dark:text-slate-400 hover:text-slate-700 dark:hover:text-slate-300 transition-colors hover:bg-slate-100 dark:hover:bg-slate-700 rounded-lg"
                onClick={(e) => {
                  e.stopPropagation();
                  onClose();
                }}
              >
                Cancel
              </button>
            </div>
          </div>
        </div>
      );
    };

    const renderToolbar = () => {
      if (!editor) return null;

      const basicTools = (
        <>
          <ToolbarButton
            onClick={() => editor.chain().focus().toggleBold().run()}
