                      >
                        <SelectTrigger className="col-span-3">
                          <SelectValue placeholder="Select role" />
                        </SelectTrigger>
                        <SelectContent>
                          <SelectItem value="client">Client</SelectItem>
                          <SelectItem value="admin">Admin</SelectItem>
                        </SelectContent>
                      </Select>
                    </div>
                    <div className="grid grid-cols-4 items-center gap-4">
                      <Label htmlFor="sippyAccountId" className="text-right">
                        Sippy ID
                      </Label>
                      <Input
                        id="sippyAccountId"
                        name="sippyAccountId"
                        type="number"
                        value={formData.sippyAccountId}
                        onChange={handleChange}
                        className="col-span-3"
                        placeholder="Optional"
                      />
                    </div>
                  </div>
                </form>
              </TabsContent>

              <TabsContent value="notifications" className="mt-6 overflow-y-auto max-h-[400px]">
                <UserNotificationSettingsComponent
                  userId={userId}
                  userName={formData.name}
                  userEmail={formData.email}
                />
              </TabsContent>

              <TabsContent value="ratedecks" className="mt-6 overflow-y-auto max-h-[400px]">
                <UserRateDecks userId={userId} />
              </TabsContent>
            </Tabs>
          </div>
        )}

        <DialogFooter className="mt-6">
          <Button variant="outline" type="button" onClick={onClose} disabled={isSubmitting}>
            Cancel
          </Button>
          {activeTab === 'details' && (
            <Button 
              type="button" 
              onClick={handleSubmit}
              disabled={isSubmitting}
            >
              {isSubmitting && <Loader2 className="mr-2 h-4 w-4 animate-spin" />}
              {isSubmitting ? 'Updating...' : 'Update User'}
            </Button>
          )}
        </DialogFooter>
      </DialogContent>
    </Dialog>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Input } from '@/components/ui/input';
import { Label } from '@/components/ui/label';
import { Switch } from '@/components/ui/switch';
import { Loader2, Bell, User } from 'lucide-react';
import { toast } from 'sonner';
import { UserNotificationSettings } from '@/models/UserNotificationSettings';

interface UserNotificationSettingsProps {
  userId: string;
  userName: string;
  userEmail: string;
}

export function UserNotificationSettingsComponent({ 
  userId, 
  userName, 
  userEmail 
}: UserNotificationSettingsProps) {
  const [settings, setSettings] = useState<Partial<UserNotificationSettings>>({
    lowBalanceThreshold: 10.0000,
    zeroBalanceThreshold: 0.0000,
    negativeBalanceThreshold: -0.0001,
    enableLowBalanceNotifications: true,
    enableZeroBalanceNotifications: true,
    enableNegativeBalanceNotifications: true,
    notificationFrequencyHours: 24,
    currency: 'EUR'
  });
  const [isLoading, setIsLoading] = useState(false);

  useEffect(() => {
    fetchSettings();
  }, [userId]);

  const fetchSettings = async () => {
    try {
      setIsLoading(true);
      const response = await fetch(`/api/users/${userId}/notification-settings`);
      if (response.ok) {
        const data = await response.json();
        setSettings({
          lowBalanceThreshold: data.lowBalanceThreshold || 10.0000,
          zeroBalanceThreshold: data.zeroBalanceThreshold || 0.0000,
          negativeBalanceThreshold: data.negativeBalanceThreshold || -0.0001,
          enableLowBalanceNotifications: data.enableLowBalanceNotifications ?? true,
          enableZeroBalanceNotifications: data.enableZeroBalanceNotifications ?? true,
          enableNegativeBalanceNotifications: data.enableNegativeBalanceNotifications ?? true,
          notificationFrequencyHours: data.notificationFrequencyHours || 24,
          currency: data.currency || 'EUR'
        });
      }
    } catch (error) {
      console.error('Error fetching user notification settings:', error);
      toast.error('Failed to load notification settings');
    } finally {
      setIsLoading(false);
    }
  };

  const handleSave = async () => {
    try {
      setIsLoading(true);
      const response = await fetch(`/api/users/${userId}/notification-settings`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(settings),
      });

      if (response.ok) {
        toast.success('Notification settings saved successfully');
        fetchSettings(); // Refresh to get updated data
      } else {
        const error = await response.json();
        toast.error(error.error || 'Failed to save notification settings');
      }
    } catch (error) {
      console.error('Error saving notification settings:', error);
      toast.error('Failed to save notification settings');
    } finally {
      setIsLoading(false);
    }
  };

  const handleInputChange = (field: keyof UserNotificationSettings, value: string | number | boolean) => {
    setSettings(prev => ({
      ...prev,
      [field]: value
    }));
  };

  if (isLoading && !settings.lowBalanceThreshold) {
    return (
      <div className="flex justify-center py-8">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Header Info */}
      <div className="pb-4 border-b">
        <h3 className="text-lg font-medium">Notification Settings for {userName}</h3>
        <p className="text-sm text-muted-foreground">
          Configure notification thresholds and preferences for {userEmail}
        </p>
      </div>

      {/* Notification Frequency */}
      <div className="space-y-2">
        <Label htmlFor="frequency">Notification Frequency (hours)</Label>
        <Input
          id="frequency"
          type="number"
          min="1"
          max="168"
          value={settings.notificationFrequencyHours}
          onChange={(e) => handleInputChange('notificationFrequencyHours', parseInt(e.target.value))}
        />
        <p className="text-sm text-muted-foreground">
          How often to send notifications for the same condition (1-168 hours)
        </p>
      </div>

      {/* Low Balance Notifications */}
      <div className="space-y-4 p-4 border rounded-lg">
        <div className="flex items-center justify-between">
          <div className="space-y-1">
            <Label className="text-base">Low Balance Alert</Label>
            <p className="text-sm text-muted-foreground">
              Send notifications when balance falls below threshold
            </p>
          </div>
          <Switch
            checked={settings.enableLowBalanceNotifications}
            onCheckedChange={(checked: boolean) => handleInputChange('enableLowBalanceNotifications', checked)}
          />
        </div>

        {settings.enableLowBalanceNotifications && (
          <div className="space-y-2">
            <Label htmlFor="lowThreshold">Low Balance Threshold (EUR)</Label>
            <Input
              id="lowThreshold"
              type="number"
              step="0.0001"
              min="0"
              value={settings.lowBalanceThreshold}
              onChange={(e) => handleInputChange('lowBalanceThreshold', parseFloat(e.target.value))}
            />
            <p className="text-xs text-muted-foreground">
              Send alert when balance drops below this amount
            </p>
          </div>
        )}
      </div>

      {/* Zero Balance Notifications */}
      <div className="space-y-4 p-4 border rounded-lg">
        <div className="flex items-center justify-between">
          <div className="space-y-1">
            <Label className="text-base">Zero Balance Alert</Label>
            <p className="text-sm text-muted-foreground">
              Send notifications when balance reaches zero
            </p>
          </div>
          <Switch
            checked={settings.enableZeroBalanceNotifications}
            onCheckedChange={(checked: boolean) => handleInputChange('enableZeroBalanceNotifications', checked)}
          />
        </div>

        {settings.enableZeroBalanceNotifications && (
          <div className="space-y-2">
            <Label htmlFor="zeroThreshold">Zero Balance Threshold (EUR)</Label>
            <Input
              id="zeroThreshold"
              type="number"
              step="0.0001"
              value={settings.zeroBalanceThreshold}
              onChange={(e) => handleInputChange('zeroBalanceThreshold', parseFloat(e.target.value))}
            />
            <p className="text-xs text-muted-foreground">
              Usually set to 0.0000, but can be adjusted for early warning
            </p>
          </div>
        )}
      </div>

      {/* Negative Balance Notifications */}
      <div className="space-y-4 p-4 border rounded-lg">
        <div className="flex items-center justify-between">
          <div className="space-y-1">
            <Label className="text-base">Negative Balance Alert</Label>
            <p className="text-sm text-muted-foreground">
              Send critical notifications when balance goes negative
            </p>
          </div>
          <Switch
            checked={settings.enableNegativeBalanceNotifications}
            onCheckedChange={(checked: boolean) => handleInputChange('enableNegativeBalanceNotifications', checked)}
          />
        </div>

        {settings.enableNegativeBalanceNotifications && (
          <div className="space-y-2">
            <Label htmlFor="negativeThreshold">Negative Balance Threshold (EUR)</Label>
            <Input
              id="negativeThreshold"
              type="number"
              step="0.0001"
              value={settings.negativeBalanceThreshold}
              onChange={(e) => handleInputChange('negativeBalanceThreshold', parseFloat(e.target.value))}
            />
            <p className="text-xs text-muted-foreground">
              Negative value that triggers critical alerts (e.g., -0.0001)
            </p>
          </div>
        )}
      </div>

      {/* Save Button */}
      <div className="flex justify-end pt-4">
        <Button onClick={handleSave} disabled={isLoading}>
          {isLoading ? (
            <>
              <Loader2 className="h-4 w-4 mr-2 animate-spin" />
              Saving...
            </>
          ) : (
            'Save Settings'
          )}
        </Button>
      </div>
    </div>
  );
}

'use client';

import { useState, useEffect } from 'react';
import { Badge } from '@/components/ui/badge';
import { Button } from '@/components/ui/button';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Loader2, ExternalLink, Hash, MessageSquare, Calendar, User } from 'lucide-react';
import { toast } from 'sonner';

interface RateDeckAssignment {
  id: string;
  rateDeckId: string;
  rateDeckType: 'number' | 'sms';
  rateDeckName: string;
  rateDeckDescription: string;
  assignedBy: string;
  assignedAt: string;
}

interface UserRateDecksProps {
  userId: string;
}

export function UserRateDecks({ userId }: UserRateDecksProps) {
  const [assignments, setAssignments] = useState<RateDeckAssignment[]>([]);
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    if (userId) {
      fetchAssignments();
    }
  }, [userId]);

  const fetchAssignments = async () => {
    try {
      setIsLoading(true);
      const response = await fetch(`/api/users/${userId}/assignments`);

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.error || 'Failed to fetch assignments');
      }

      const data = await response.json();
      setAssignments(data.assignments || []);
    } catch (error) {
      console.error('Error fetching assignments:', error);
      toast.error(error instanceof Error ? error.message : 'Failed to load rate deck assignments');
    } finally {
      setIsLoading(false);
    }
  };

  const getRateDeckIcon = (type: string) => {
    return type === 'number' ? Hash : MessageSquare;
  };

  const getRateDeckBadgeVariant = (type: string) => {
    return type === 'number' ? 'default' : 'secondary';
  };

  const openRateDeck = (assignment: RateDeckAssignment) => {
    const rateDeckPath = assignment.rateDeckType === 'number' ? 'numbers' : 'sms';
    const url = `/rates/${rateDeckPath}/decks/${assignment.rateDeckId}/rates`;
    window.open(url, '_blank');
  };

  const formatDate = (dateString: string) => {
    const date = new Date(dateString);
    return {
      date: date.toLocaleDateString(),
      time: date.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }),
    };
  };

  if (isLoading) {
    return (
      <div className="flex justify-center p-8">
        <Loader2 className="h-8 w-8 animate-spin text-primary" />
      </div>
    );
  }

  if (assignments.length === 0) {
    return (
      <div className="text-center py-12">
        <div className="flex flex-col items-center space-y-4">
          <div className="p-4 bg-muted rounded-full">
            <Hash className="h-8 w-8 text-muted-foreground" />
          </div>
          <div className="space-y-2">
            <p className="text-lg font-medium">No Rate Decks Assigned</p>
            <p className="text-sm text-muted-foreground">
              This user is not currently assigned to any rate decks
            </p>
          </div>
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <div className="flex items-center justify-between">
        <div>
          <h3 className="text-lg font-medium">Assigned Rate Decks</h3>
          <p className="text-sm text-muted-foreground">
            Rate decks currently assigned to this user ({assignments.length})
          </p>
        </div>
      </div>

      <div className="grid gap-4">
        {assignments.map((assignment) => {
          const Icon = getRateDeckIcon(assignment.rateDeckType);
          const dateInfo = formatDate(assignment.assignedAt);

          return (
            <Card key={assignment.id} className="hover:shadow-md transition-shadow">
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between">
                  <div className="flex items-center space-x-3">
                    <div className="p-2 bg-primary/10 rounded-lg">
                      <Icon className="h-5 w-5 text-primary" />
                    </div>
                    <div>
                      <CardTitle className="text-base">{assignment.rateDeckName}</CardTitle>
                      <CardDescription className="mt-1">
                        {assignment.rateDeckDescription || 'No description available'}
                      </CardDescription>
                    </div>
                  </div>
                  <div className="flex items-center space-x-2">
                    <Badge variant={getRateDeckBadgeVariant(assignment.rateDeckType)}>
                      {assignment.rateDeckType === 'number' ? 'Number Rates' : 'SMS Rates'}
                    </Badge>
                    <Button
                      variant="outline"
                      size="sm"
                      onClick={() => openRateDeck(assignment)}
                      className="flex items-center space-x-1"
                    >
                      <ExternalLink className="h-3 w-3" />
                      <span>Open</span>
                    </Button>
                  </div>
                </div>
              </CardHeader>
              <CardContent className="pt-0">
                <div className="flex items-center justify-between text-sm text-muted-foreground">
                  <div className="flex items-center space-x-4">
                    <div className="flex items-center space-x-1">
                      <User className="h-3 w-3" />
                      <span>Assigned by {assignment.assignedBy}</span>
                    </div>
                    <div className="flex items-center space-x-1">
                      <Calendar className="h-3 w-3" />
                      <span>{dateInfo.date} at {dateInfo.time}</span>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          );
        })}
      </div>
    </div>
  );
}

'use client';

import React, { createContext, useContext, useState, useEffect, useCallback } from 'react';
import { useAuth } from '@/lib/AuthContext';
import { CdrRow } from '@/lib/sippy/core/types';

// Lightweight CDR interface for dashboard widgets - only essential fields
interface CdrWidget {
  cost: string;                    // For Cost of Day calculations
  duration: number;                // For ACD and Total Minutes calculations
  result: number;                  // For ASR (success/failure) calculations
  payment_currency?: string;       // For currency display
  connect_time: string;           // For time filtering
}



interface CdrKpis {
  costOfDay: number;
  asr: number; // Answer Seizure Ratio (Success Rate)
  acd: number; // Average Call Duration
  totalMinutes: number;
  totalCalls: number;
  successfulCalls: number;
  failedCalls: number;
  currency: string;
}

interface ProgressState {
  phase: 'initial' | 'background' | 'complete';
  currentBatch: number;
  totalBatches: number;
  recordsLoaded: number;
  estimatedTotal: number;
  isComplete: boolean;
}

interface CdrContextType {
  cdrs: CdrWidget[];  // Use lightweight interface for dashboard widgets
  kpis: CdrKpis | null;
  isLoading: boolean;
  error: string | null;
  lastRefresh: Date | null;
  selectedDate: Date;
  progress: ProgressState;
  hasDataLoaded: boolean; // New: Track if data has been manually loaded
  refetch: () => void;
  setSelectedDate: (date: Date) => void;
  loadMetrics: () => void; // New: Manual trigger function
}

const CdrContext = createContext<CdrContextType | undefined>(undefined);

// Account-specific configuration for unlimited fetching
const ACCOUNT_CONFIG = {
  '3': {
    // Account 3 is significantly slower (22s vs 1.5s for same request)
    initialTimeout: 60000,    // 60 seconds for initial requests
    backgroundTimeout: 90000, // 90 seconds for background requests
    initialBatchSize: 500,    // First batch size for quick display
    unlimitedBatchSize: 10000, // Large batches for unlimited fetching
    batchDelay: 2000,        // Longer delays between requests
    description: 'Slow performance account - requires longer timeouts'
  },
  '27': {
    // Account 27 has normal performance
    initialTimeout: 45000,    // 45 seconds for initial requests
    backgroundTimeout: 60000, // 60 seconds for background requests
    initialBatchSize: 500,    // First batch size for quick display
    unlimitedBatchSize: 10000, // Large batches for unlimited fetching
    batchDelay: 500,         // Normal delays
    description: 'Normal performance account'
  },
  'default': {
    // Default configuration for unknown accounts
    initialTimeout: 60000,    // 60 seconds
    backgroundTimeout: 90000, // 90 seconds
    initialBatchSize: 500,    // First batch size for quick display
    unlimitedBatchSize: 10000, // Large batches for unlimited fetching
    batchDelay: 1000,        // Medium delay
    description: 'Default configuration for unknown accounts'
  }
};

const getAccountConfig = (accountId: string) => {
  return ACCOUNT_CONFIG[accountId as keyof typeof ACCOUNT_CONFIG] || ACCOUNT_CONFIG.default;
};

export function CdrProvider({ children }: { children: React.ReactNode }) {
  const { user } = useAuth();

  const [cdrs, setCdrs] = useState<CdrWidget[]>([]);  // Use lightweight interface
  const [kpis, setKpis] = useState<CdrKpis | null>(null);
  const [isLoading, setIsLoading] = useState(false); // Changed: Start as false, no auto-loading
  const [error, setError] = useState<string | null>(null);
  const [lastRefresh, setLastRefresh] = useState<Date | null>(null);
  const [hasDataLoaded, setHasDataLoaded] = useState(false); // New: Track manual loading
  const [progress, setProgress] = useState<ProgressState>({
    phase: 'initial',
    currentBatch: 0,
    totalBatches: 0,
    recordsLoaded: 0,
    estimatedTotal: 0,
    isComplete: false
  });

  // Initialize with today's date in local timezone
  const [selectedDate, setSelectedDate] = useState(() => {
    const today = new Date();
    // Reset to start of day in local timezone
    return new Date(today.getFullYear(), today.getMonth(), today.getDate());
  });

  const calculateKpis = useCallback((cdrs: CdrWidget[]): CdrKpis => {
    if (!cdrs || cdrs.length === 0) {
      return {
        costOfDay: 0,
        asr: 0,
        acd: 0,
        totalMinutes: 0,
        totalCalls: 0,
        successfulCalls: 0,
        failedCalls: 0,
        currency: '',
      };
    }

    let totalCost = 0;
    let totalDuration = 0;
    let successfulCalls = 0;
    let failedCalls = 0;
    let currency = '';

    cdrs.forEach(cdr => {
      const cost = parseFloat(cdr.cost) || 0;
      const duration = cdr.duration || 0;

      totalCost += cost;
      totalDuration += duration;

      // Get currency from first CDR that has it
      if (!currency && cdr.payment_currency) {
        currency = cdr.payment_currency;
      }

      // Count successful vs failed calls based on result code
      // According to Sippy documentation: result 0 or 200 = successful
      if (cdr.result === 0 || cdr.result === 200) {
        successfulCalls++;
      } else {
        failedCalls++;
      }
    });

    const totalCalls = cdrs.length;
    const asr = totalCalls > 0 ? (successfulCalls / totalCalls) * 100 : 0;
    const acd = successfulCalls > 0 ? totalDuration / successfulCalls : 0;
    const totalMinutes = totalDuration / 60;

    return {
      costOfDay: totalCost,
      asr,
      acd,
      totalMinutes,
      totalCalls,
      successfulCalls,
      failedCalls,
      currency: currency || 'USD',
    };
  }, []);

  // Update KPIs when CDRs change
  useEffect(() => {
    const kpisToUse = calculateKpis(cdrs);
    setKpis(kpisToUse);

    if (cdrs.length > 0) {
      setLastRefresh(new Date());
    }
  }, [cdrs, calculateKpis]);

  const fetchCDRBatch = async (
    accountId: string,
    offset: number,
    limit: number,
    startDate?: string,
    endDate?: string,
    isBackground = false
  ): Promise<{ cdrs: CdrWidget[]; hasMore: boolean }> => {
    const config = getAccountConfig(accountId);
    const timeout = isBackground ? config.backgroundTimeout : config.initialTimeout;

    try {
      const controller = new AbortController();
      const timeoutId = setTimeout(() => controller.abort(), timeout);

      // Use the same request pattern as the working CDR test page
      const requestBody = {
        i_account: accountId,
        limit: limit.toString(),
        offset: offset.toString(),
        type: 'all', // Get all CDRs for comprehensive KPI calculations
        mode: 'optimized', // Use optimized parsing for dashboard widgets (essential fields only)
        ...(startDate && { start_date: startDate }),
        ...(endDate && { end_date: endDate })
      };

      // Use the working CDR API endpoint (same as test page)
      const response = await fetch(`/api/sippy/account/${accountId}/cdrs`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(requestBody),
        signal: controller.signal,
      });

      clearTimeout(timeoutId);

      if (!response.ok) {
        const errorData = await response.json().catch(() => ({ error: 'Unknown error' }));
        throw new Error(`HTTP ${response.status}: ${errorData.error || 'Failed to fetch CDRs'}`);
      }

      const data = await response.json();

      // Handle both old and new API response formats
      const fullCdrs = data.success ? (data.cdrs || []) : (data.cdrs || []);

      // Convert full CDR objects to lightweight CdrWidget objects
      const cdrs: CdrWidget[] = fullCdrs.map((cdr: CdrRow) => ({
        cost: cdr.charged_amount?.toString() || '0',
        duration: cdr.duration || 0,
        result: cdr.disconnect_cause ? parseInt(cdr.disconnect_cause) || 0 : 0,
        payment_currency: undefined, // CdrRow doesn't have payment_currency, will be set from account info
        connect_time: cdr.connect_time || ''
      }));

      // Determine if there are more CDRs to fetch
      const hasMore = cdrs.length === limit;

      return { cdrs, hasMore };
    } catch (error) {
      // Handle timeout errors gracefully
      if (error instanceof Error) {
        if (error.name === 'AbortError') {
          throw new Error(`CDR fetch timed out after ${timeout}ms. Try selecting a shorter date range.`);
        }

        // Re-throw with context
        throw new Error(`Failed to fetch CDRs: ${error.message}`);
      }

      throw new Error('An unknown error occurred while fetching CDRs');
    }
  };

  const fetchCDRsUnlimited = async (accountId: string, startDate?: string, endDate?: string) => {
    const config = getAccountConfig(accountId);

    setIsLoading(true);
    setError(null);
    setProgress({
      phase: 'initial',
      currentBatch: 0,
      totalBatches: 1, // Start with 1, will update as we go
      recordsLoaded: 0,
      estimatedTotal: 0, // No estimation for unlimited
      isComplete: false
    });

    let allCdrs: CdrWidget[] = [];
    let offset = 0;
    let batchCount = 0;
    let hasMore = true;

    try {
      // Phase 1: Initial batch for quick display
      if (hasMore) {
        const result = await fetchCDRBatch(accountId, offset, config.initialBatchSize, startDate, endDate, false);

        allCdrs = [...allCdrs, ...result.cdrs];
        hasMore = result.hasMore;
        offset += result.cdrs.length;
        batchCount++;

        setProgress({
          phase: 'initial',
          currentBatch: 1,
          totalBatches: hasMore ? 2 : 1,
          recordsLoaded: allCdrs.length,
          estimatedTotal: 0,
          isComplete: !hasMore
        });

        setCdrs(allCdrs);

        if (hasMore) {
          await new Promise(resolve => setTimeout(resolve, config.batchDelay));
        }
      }

      // Phase 2: UNLIMITED background fetching
      if (hasMore) {
        setProgress(prev => ({
          ...prev,
          phase: 'background'
        }));

        let consecutiveEmptyBatches = 0;
        const maxConsecutiveEmpty = 3; // Safety check

        while (hasMore && consecutiveEmptyBatches < maxConsecutiveEmpty) {
          const result = await fetchCDRBatch(accountId, offset, config.unlimitedBatchSize, startDate, endDate, true);

          if (result.cdrs.length === 0) {
            consecutiveEmptyBatches++;
          } else {
            consecutiveEmptyBatches = 0; // Reset counter
          }

          allCdrs = [...allCdrs, ...result.cdrs];
          hasMore = result.hasMore && result.cdrs.length > 0;
          offset += result.cdrs.length;
          batchCount++;

          setProgress({
            phase: 'background',
            currentBatch: batchCount,
            totalBatches: hasMore ? batchCount + 1 : batchCount,
            recordsLoaded: allCdrs.length,
            estimatedTotal: 0, // No estimation for unlimited
            isComplete: !hasMore
          });

          setCdrs(allCdrs);

          if (hasMore && consecutiveEmptyBatches === 0) {
            await new Promise(resolve => setTimeout(resolve, config.batchDelay));
          }
        }
      }

      const finalProgress = {
        phase: 'complete' as const,
        currentBatch: batchCount,
        totalBatches: batchCount,
        recordsLoaded: allCdrs.length,
        estimatedTotal: 0,
        isComplete: true
      };

      setProgress(finalProgress);

    } catch (error) {
      console.error('âŒ Error in unlimited CDR fetch:', error);
      setError(error instanceof Error ? error.message : 'Failed to fetch CDRs');
      setProgress(prev => ({ ...prev, isComplete: true }));
    } finally {
      setIsLoading(false);
    }
  };

  const fetchCdrsForDate = useCallback(async (targetDate?: Date) => {
    if (!user?.sippyAccountId) {
      setError('No account ID available');
      setIsLoading(false);
      return;
    }

    // Use the provided targetDate or the current selectedDate
    const dateToFetch = targetDate || selectedDate;

    try {
      setIsLoading(true);
      setError(null);
      setHasDataLoaded(true); // Mark that data has been manually loaded

      // Create date boundaries for the selected date
      const startOfDay = new Date(dateToFetch.getFullYear(), dateToFetch.getMonth(), dateToFetch.getDate(), 0, 0, 0);
      const endOfDay = new Date(dateToFetch.getFullYear(), dateToFetch.getMonth(), dateToFetch.getDate(), 23, 59, 59);

      // Fetch CDRs for the specific date
      await fetchCDRsUnlimited(
        user.sippyAccountId.toString(), 
        startOfDay.toISOString(), 
        endOfDay.toISOString()
      );

      setLastRefresh(new Date());

    } catch (error) {
      console.error('âŒ Error in CDR fetch for date:', error);
      setError(error instanceof Error ? error.message : 'Failed to fetch CDRs');
    } finally {
      setIsLoading(false);
    }
  }, [user, selectedDate]);

  // Manual date change handler that resets loading state
  const handleDateChange = useCallback((date: Date) => {
    setSelectedDate(date);
    setHasDataLoaded(false); // Reset loading state when date changes
    setCdrs([]); // Clear previous data
    setKpis(null);
    setError(null);
  }, []);

  return (
    <CdrContext.Provider
      value={{
        cdrs,
        kpis,
        isLoading,
        error,
        lastRefresh,
        selectedDate,
        progress,
        hasDataLoaded,
        refetch: () => fetchCdrsForDate(),
        setSelectedDate: handleDateChange,
        loadMetrics: () => fetchCdrsForDate()
      }}
    >
      {children}
    </CdrContext.Provider>
  );
}

export function useCdr() {
  const context = useContext(CdrContext);
  if (context === undefined) {
    throw new Error('useCdr must be used within a CdrProvider');
  }
  return context;
}

'use client';

import React, { createContext, useContext, useState, useEffect, useRef, ReactNode } from 'react';
import { useAuth } from '@/lib/AuthContext';
import { 
  NotificationContextType,
  InternalNotification,
  NotificationPreferences,
  NotificationStats,
  NotificationFilter,
  NotificationType
} from '@/types/notifications';
import InternalNotificationService from '@/services/InternalNotificationService';
import NotificationSoundService from '@/services/NotificationSoundService';
import { useNotificationToast } from '@/components/notifications/NotificationToastContainer';
import NotificationToastContainer from '@/components/notifications/NotificationToastContainer';

const NotificationContext = createContext<NotificationContextType | undefined>(undefined);

interface NotificationProviderProps {
  children: ReactNode;
}

export function NotificationProvider({ children }: NotificationProviderProps) {
  // State
  const [notifications, setNotifications] = useState<InternalNotification[]>([]);
  const [preferences, setPreferences] = useState<NotificationPreferences | null>(null);
  const [stats, setStats] = useState<NotificationStats>({
    total: 0,
    unread: 0,
    byType: {} as Record<NotificationType, number>,
    byPriority: {} as Record<string, number>,
    todayCount: 0,
    weekCount: 0
  });
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState<string | null>(null);
  const [isNotificationCenterOpen, setNotificationCenterOpen] = useState(false);

  // Filter state
  const [filter, setFilter] = useState<NotificationFilter>({});
  const [filteredNotifications, setFilteredNotifications] = useState<InternalNotification[]>([]);

  // Services
  const notificationService = useRef(InternalNotificationService.getInstance());
  const soundService = useRef(NotificationSoundService.getInstance());

  // Custom toast system
  const { showToast } = useNotificationToast();

  // Auth context
  const { user, isLoading: authLoading } = useAuth();

  // Polling interval for real-time updates
  const pollInterval = useRef<NodeJS.Timeout | null>(null);
  const POLL_INTERVAL = 10000; // 10 seconds

  // Track last notification count for detecting new notifications
  const lastNotificationCount = useRef(0);
  const isInitialized = useRef(false);
  const lastNotificationTimestamp = useRef<Date | null>(null);

  // Initialize when user is authenticated
  useEffect(() => {
    if (!authLoading && user) {
      initializeNotifications();
      startPolling();
    } else if (!authLoading && !user) {
      // Reset state when user logs out
      resetNotificationState();
      stopPolling();
    }

    return stopPolling;
  }, [user, authLoading]);

  // Update filtered notifications when notifications or filter changes
  useEffect(() => {
    applyFilters();
  }, [notifications, filter]);

  // Handle new notifications for toast display (only for real-time updates, not initial load)
  useEffect(() => {
    if (!isInitialized.current || !preferences?.showToasts) {
      return;
    }

    // Only show toasts for notifications that are genuinely new
    // We add a small buffer (30 seconds) to account for slight timing differences
    const bufferTime = 30 * 1000; // 30 seconds in milliseconds
    const cutoffTime = lastNotificationTimestamp.current ? 
      new Date(lastNotificationTimestamp.current.getTime() - bufferTime) : 
      new Date();

    const newNotifications = notifications.filter(notification => {
      // Only show notifications created after our last known timestamp
      const notificationTime = new Date(notification.createdAt);
      return notificationTime > cutoffTime &&
             notification.showToast &&
             notification.status === 'unread'; // Only show unread notifications as toasts
    });

    if (newNotifications.length > 0) {
      // Update the timestamp to the most recent notification
      const mostRecentTime = new Date(Math.max(...newNotifications.map(n => new Date(n.createdAt).getTime())));

      // Only update timestamp if this notification is actually newer
      if (!lastNotificationTimestamp.current || mostRecentTime > lastNotificationTimestamp.current) {
        lastNotificationTimestamp.current = mostRecentTime;

        if (process.env.NODE_ENV === 'development') {
          console.log(`Showing ${newNotifications.length} new notification toasts. Updated timestamp to:`, mostRecentTime);
        }

        // Show toasts for new notifications
        newNotifications.forEach(notification => {
          showNotificationToast(notification);
        });
      }
    }
  }, [notifications, preferences?.showToasts, showToast]);

  const resetNotificationState = () => {
    setNotifications([]);
    setPreferences(null);
    setStats({
      total: 0,
      unread: 0,
      byType: {} as Record<NotificationType, number>,
      byPriority: {} as Record<string, number>,
      todayCount: 0,
      weekCount: 0
    });
    setError(null);
    setIsLoading(false);
    lastNotificationCount.current = 0;
    isInitialized.current = false;
    lastNotificationTimestamp.current = null;
  };

  const initializeNotifications = async () => {
    if (!user) return;

    try {
      setIsLoading(true);
      setError(null);

      // Load user preferences
      const userPrefs = await notificationService.current.getUserPreferences(user.id);
      setPreferences(userPrefs);

      // Apply preferences to sound service
      if (userPrefs) {
        soundService.current.setEnabled(userPrefs.enableSounds);
        soundService.current.setVolume(userPrefs.soundVolume);
      }

      // Load notifications
      await refreshNotifications();

      // Load stats
      const userStats = await notificationService.current.getUserStats(user.id);
      setStats(userStats);

      // Mark as initialized and set the timestamp to now
      // This prevents existing notifications from being shown as toasts
      isInitialized.current = true;
      lastNotificationTimestamp.current = new Date();

      if (process.env.NODE_ENV === 'development') {
        console.log('Notifications initialized. Timestamp set to:', lastNotificationTimestamp.current);
      }

    } catch (err) {
      console.error('Error initializing notifications:', err);
      setError('Failed to load notifications');
    } finally {
      setIsLoading(false);
    }
  };

  const refreshNotifications = async () => {
    if (!user) return;

    try {
      const { notifications: userNotifications } = await notificationService.current.getUserNotifications(
        user.id,
        { limit: 100 }
      );

      setNotifications(userNotifications as InternalNotification[]);

      // Update stats
      const userStats = await notificationService.current.getUserStats(user.id);
      setStats(userStats);

    } catch (err) {
      console.error('Error refreshing notifications:', err);
    }
  };

  const startPolling = () => {
    if (pollInterval.current) {
      clearInterval(pollInterval.current);
    }

    pollInterval.current = setInterval(() => {
      if (!document.hidden && user) {
        refreshNotifications();
      }
    }, POLL_INTERVAL);
  };

  const stopPolling = () => {
    if (pollInterval.current) {
      clearInterval(pollInterval.current);
      pollInterval.current = null;
    }
  };

  const applyFilters = () => {
    let filtered = [...notifications];

    if (filter.types?.length) {
      filtered = filtered.filter(n => filter.types!.includes(n.type));
    }

    if (filter.priorities?.length) {
      filtered = filtered.filter(n => filter.priorities!.includes(n.priority));
    }

    if (filter.status?.length) {
      filtered = filtered.filter(n => filter.status!.includes(n.status));
    }

    if (filter.search) {
      const searchLower = filter.search.toLowerCase();
      filtered = filtered.filter(n => 
        n.title.toLowerCase().includes(searchLower) ||
        n.message.toLowerCase().includes(searchLower)
      );
    }

    if (filter.dateFrom) {
      filtered = filtered.filter(n => new Date(n.createdAt) >= filter.dateFrom!);
    }

    if (filter.dateTo) {
      filtered = filtered.filter(n => new Date(n.createdAt) <= filter.dateTo!);
    }

    setFilteredNotifications(filtered);
  };

  const showNotificationToast = (notification: InternalNotification) => {
    if (!preferences?.showToasts) return;

    // Use our custom toast system
    showToast(notification, {
      position: preferences.toastPosition,
      duration: (preferences.toastDuration || 5) * 1000
    });
  };

  // Context actions
  const addNotification = async (notification: Omit<InternalNotification, 'id' | 'createdAt'>) => {
    if (!user) {
      console.warn('Cannot add notification: user not authenticated');
      return;
    }

    try {
      const newNotification = await notificationService.current.createNotification(
        user.id,
        notification.type,
        notification.data
      );

      if (newNotification) {
        setNotifications(prev => [newNotification as InternalNotification, ...prev]);

        setStats(prev => ({
          ...prev,
          total: prev.total + 1,
          unread: notification.status === 'unread' ? prev.unread + 1 : prev.unread
        }));

        // Update the timestamp for this new notification
        const notificationTime = new Date(newNotification.createdAt);
        lastNotificationTimestamp.current = notificationTime;

        // Show toast if enabled (this will be handled by the useEffect)
        // No need to manually show toast here as the useEffect will catch it

        // Play sound if enabled
        if (preferences?.enableSounds && newNotification.sound) {
          await soundService.current.playSound(
            newNotification.sound,
            preferences.soundVolume
          );
        }
      }
    } catch (err) {
      console.error('Error adding notification:', err);
      throw err;
    }
  };

  const markAsRead = async (notificationId: string) => {
    if (!user) {
      console.warn('Cannot mark notification as read: user not authenticated');
      return;
    }

    try {
      const success = await notificationService.current.markAsRead(notificationId, user.id);

      if (success) {
        setNotifications(prev => prev.map(n => 
          n.id === notificationId 
            ? { ...n, status: 'read' as const, readAt: new Date() }
            : n
        ));

        setStats(prev => ({
          ...prev,
          unread: Math.max(0, prev.unread - 1)
        }));
      }
    } catch (err) {
      console.error('Error marking notification as read:', err);
      throw err;
    }
  };

  const markAllAsRead = async () => {
    if (!user) {
      console.warn('Cannot mark all notifications as read: user not authenticated');
      return;
    }

    try {
      const count = await notificationService.current.markAllAsRead(user.id);

      if (count > 0) {
        setNotifications(prev => prev.map(n => 
          n.status === 'unread' 
            ? { ...n, status: 'read' as const, readAt: new Date() }
            : n
        ));

        setStats(prev => ({
          ...prev,
          unread: 0
        }));
      }
    } catch (err) {
      console.error('Error marking all notifications as read:', err);
      throw err;
    }
  };

  const archiveNotification = async (notificationId: string) => {
    if (!user) {
      console.warn('Cannot archive notification: user not authenticated');
      return;
    }

    try {
      const success = await notificationService.current.archiveNotification(notificationId, user.id);

      if (success) {
        setNotifications(prev => prev.map(n => 
          n.id === notificationId 
            ? { ...n, status: 'archived' as const, archivedAt: new Date() }
            : n
        ));
      }
    } catch (err) {
      console.error('Error archiving notification:', err);
      throw err;
    }
  };

  const deleteNotification = async (notificationId: string) => {
    if (!user) {
      console.warn('Cannot delete notification: user not authenticated');
      return;
    }

    try {
      const success = await notificationService.current.deleteNotification(notificationId, user.id);

      if (success) {
        setNotifications(prev => prev.filter(n => n.id !== notificationId));

        setStats(prev => ({
          ...prev,
          total: Math.max(0, prev.total - 1)
        }));
      }
    } catch (err) {
      console.error('Error deleting notification:', err);
      throw err;
    }
  };

  const clearAllNotifications = async () => {
    if (!user) {
      console.warn('Cannot clear notifications: user not authenticated');
      return;
    }

    try {
      const count = await notificationService.current.clearAllNotifications(user.id);

      if (count > 0) {
        setNotifications([]);
        setStats({
          total: 0,
          unread: 0,
          byType: {} as Record<NotificationType, number>,
          byPriority: {} as Record<string, number>,
          todayCount: 0,
          weekCount: 0
        });
      }
    } catch (err) {
      console.error('Error clearing all notifications:', err);
      throw err;
    }
  };

  const updatePreferences = async (updates: Partial<NotificationPreferences>) => {
    if (!user) {
      console.warn('Cannot update preferences: user not authenticated');
      return;
    }

    try {
      const updated = await notificationService.current.updateUserPreferences(user.id, updates);

      if (updated) {
        setPreferences(updated as NotificationPreferences);
      }
    } catch (err) {
      console.error('Error updating preferences:', err);
      throw err;
    }
  };

  const requestPushPermission = async () => {
    try {
      return await notificationService.current.requestPushPermission();
    } catch (err) {
      console.error('Error requesting push permission:', err);
      return false;
    }
  };

  const testNotification = async (type: NotificationType) => {
    if (!user) {
      console.warn('Cannot test notification: user not authenticated');
      return;
    }

    try {
      await notificationService.current.testNotification(user.id, type);
    } catch (err) {
      console.error('Error testing notification:', err);
      throw err;
    }
  };

  const contextValue: NotificationContextType = {
    // State
    notifications,
    preferences,
    stats,
    isLoading,
    error,

    // Notification Center
    isNotificationCenterOpen,
    setNotificationCenterOpen,

    // Actions
    addNotification,
    markAsRead,
    markAllAsRead,
    archiveNotification,
    deleteNotification,
    clearAllNotifications,

    // Preferences
    updatePreferences,

    // Filtering
    filter,
    setFilter,
    filteredNotifications,

    // Utility
    refreshNotifications,
    requestPushPermission,
    testNotification
  };

  return (
    <NotificationContext.Provider value={contextValue}>
      {children}
      <NotificationToastContainer 
        position={preferences?.toastPosition || 'top-right'}
        maxToasts={5}
        defaultDuration={(preferences?.toastDuration || 5) * 1000}
      />
    </NotificationContext.Provider>
  );
}

export function useNotifications() {
  const context = useContext(NotificationContext);
  if (context === undefined) {
    throw new Error('useNotifications must be used within a NotificationProvider');
  }
  return context;
}

export default NotificationContext;

'use client';

import { useState, useCallback } from 'react';
import { 
  Ticket, 
  TicketFilters,
  TicketAttachment,
  TicketStats,
  TicketStatus,
  TicketPriority
} from '@/types/ticket';

interface AdminTicketFilters extends TicketFilters {
  assignedTo?: string;
  customerEmail?: string;
  userId?: string;
  dateRange?: string;
}

interface AdminUser {
  id: string;
  email: string;
  name?: string;
  firstName?: string;
  lastName?: string;
  role: string;
  companyName?: string | null;
}

interface AdminTicketStats extends TicketStats {
  total: number;
  unassigned: number;
  urgent: number;
  overdue: number;
}

interface BulkUpdateRequest {
  ticketIds: string[];
  action: 'assign' | 'update_status' | 'update_priority' | 'delete';
  assignTo?: string;
  status?: TicketStatus;
  priority?: TicketPriority;
  internalNote?: string;
}

interface AssignmentRequest {
  assignTo: string;
  internalNote?: string;
}

interface StatusUpdateRequest {
  status: TicketStatus;
  internalNote?: string;
}

interface PriorityUpdateRequest {
  priority: TicketPriority;
  internalNote?: string;
}

interface AddReplyRequest {
  content: string;
  attachments?: TicketAttachment[];
  isInternal?: boolean;
}



export function useAdminTickets() {
  const [tickets, setTickets] = useState<Ticket[]>([]);
  const [ticket, setTicket] = useState<Ticket | null>(null);
  const [admins, setAdmins] = useState<AdminUser[] | null>(null);
  const [users, setUsers] = useState<AdminUser[] | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [pagination, setPagination] = useState({
    page: 1,
    limit: 10,
    total: 0,
    totalPages: 0,
  });
  const [stats, setStats] = useState<AdminTicketStats | null>(null);

  const clearError = useCallback(() => {
    setError(null);
  }, []);

  const fetchTickets = useCallback(async (filters: AdminTicketFilters) => {
    try {
      setLoading(true);
      clearError();

      const params = new URLSearchParams();
      Object.entries(filters).forEach(([key, value]) => {
        if (value !== undefined && value !== null && value !== '') {
          params.append(key, value.toString());
        }
      });

      const response = await fetch(`/api/admin/tickets?${params.toString()}`);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to fetch tickets');
      }

      setTickets(data.tickets);
      setPagination(data.pagination);
      setStats(data.stats);

      // Fetch admins if not already loaded
      if (!admins) {
        await fetchAdmins();
      }

      // Fetch users if not already loaded
      if (!users) {
        await fetchUsers();
      }
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to fetch tickets');
    } finally {
      setLoading(false);
    }
  }, [admins, users, clearError]);

  const fetchTicket = useCallback(async (ticketId: string) => {
    try {
      setLoading(true);
      clearError();

      const response = await fetch(`/api/admin/tickets/${ticketId}`);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch ticket');
      }

      const ticketData = await response.json();

      // The admin API returns ticket data directly, not wrapped in { ticket: ... }
      setTicket(ticketData);

      // Fetch admins if not already loaded
      if (!admins) {
        await fetchAdmins();
      }
    } catch (err: unknown) {
      console.error('Admin fetchTicket: Error:', err);
      setError(err instanceof Error ? err.message : 'Failed to fetch ticket');
      setTicket(null);
    } finally {
      setLoading(false);
    }
  }, [admins, clearError]);

  const fetchAdmins = useCallback(async () => {
    try {
      const response = await fetch('/api/admin/users?role=admin&limit=100');
      const data = await response.json();

      if (response.ok) {
        setAdmins(data.users);
      }
    } catch (err) {
      console.error('Failed to fetch admins:', err);
    }
  }, []);

  const fetchUsers = useCallback(async () => {
    try {
      const response = await fetch('/api/admin/users?limit=100');
      const data = await response.json();

      if (response.ok) {
        setUsers(data.users);
      }
    } catch (err) {
      console.error('Failed to fetch users:', err);
    }
  }, []);

  const addInternalNote = useCallback(async (ticketId: string, content: string) => {
    try {
      setLoading(true);
      clearError();

      const response = await fetch(`/api/admin/tickets/${ticketId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'add_reply',
          content,
          isInternal: true,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to add internal note');
      }

      // Update current ticket if viewing details
      if (ticket && ticket._id === ticketId) {
        await fetchTicket(ticketId); // Refetch to get latest data
      }

      return true;
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to add internal note');
      return false;
    } finally {
      setLoading(false);
    }
  }, [ticket, fetchTicket]);

  const assignTicket = useCallback(async (ticketId: string, request: AssignmentRequest) => {
    try {
      setLoading(true);
      clearError();

      const response = await fetch(`/api/admin/tickets/${ticketId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'assign_ticket',
          assignedTo: request.assignTo,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to assign ticket');
      }

      // Add internal note if provided
      if (request.internalNote && request.internalNote.trim()) {
        await addInternalNote(ticketId, request.internalNote);
      }

      // Update current ticket if viewing details
      if (ticket && ticket._id === ticketId) {
        await fetchTicket(ticketId); // Refetch to get latest data
      }

      // Update in tickets list
      setTickets(prev => prev.map(t => 
        t._id === ticketId ? { ...t, ...data.ticket } : t
      ));

      return true;
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to assign ticket');
      return false;
    } finally {
      setLoading(false);
    }
  }, [ticket, addInternalNote, fetchTicket]);

  const updateTicketStatus = useCallback(async (ticketId: string, request: StatusUpdateRequest) => {
    try {
      setLoading(true);
      clearError();

      const response = await fetch(`/api/admin/tickets/${ticketId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'update_status',
          status: request.status,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to update ticket status');
      }

      // Add internal note if provided
      if (request.internalNote && request.internalNote.trim()) {
        await addInternalNote(ticketId, request.internalNote);
      }

      // Update current ticket if viewing details
      if (ticket && ticket._id === ticketId) {
        await fetchTicket(ticketId); // Refetch to get latest data
      }

      // Update in tickets list
      setTickets(prev => prev.map(t => 
        t._id === ticketId ? { ...t, ...data.ticket } : t
      ));

      return true;
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to update ticket status');
      return false;
    } finally {
      setLoading(false);
    }
  }, [ticket, addInternalNote, fetchTicket]);

  const updateTicketPriority = useCallback(async (ticketId: string, request: PriorityUpdateRequest) => {
    try {
      setLoading(true);
      clearError();

      const response = await fetch(`/api/admin/tickets/${ticketId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'update_priority',
          priority: request.priority,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to update ticket priority');
      }

      // Add internal note if provided
      if (request.internalNote && request.internalNote.trim()) {
        await addInternalNote(ticketId, request.internalNote);
      }

      // Update current ticket if viewing details
      if (ticket && ticket._id === ticketId) {
        await fetchTicket(ticketId);
      }

      // Update in tickets list
      setTickets(prev => prev.map(t => 
        t._id === ticketId ? { ...t, ...data.ticket } : t
      ));

      return true;
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to update ticket priority');
      return false;
    } finally {
      setLoading(false);
    }
  }, [ticket, addInternalNote, fetchTicket]);

  const bulkUpdateTickets = useCallback(async (request: BulkUpdateRequest) => {
    try {
      setLoading(true);
      clearError();

      const response = await fetch('/api/admin/tickets/bulk', {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify(request),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to update tickets');
      }

      return true;
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to update tickets');
      return false;
    } finally {
      setLoading(false);
    }
  }, []);

  const deleteTicket = useCallback(async (ticketId: string) => {
    try {
      setLoading(true);
      clearError();

      const response = await fetch(`/api/admin/tickets/${ticketId}`, {
        method: 'DELETE',
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error || 'Failed to delete ticket');
      }

      return true;
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to delete ticket');
      return false;
    } finally {
      setLoading(false);
    }
  }, []);

  const addReply = useCallback(async (ticketId: string, request: AddReplyRequest) => {
    try {
      setLoading(true);
      clearError();

      const response = await fetch(`/api/admin/tickets/${ticketId}`, {
        method: 'PUT',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          action: 'add_reply',
          content: request.content,
          attachments: request.attachments || [],
          isInternal: request.isInternal || false,
        }),
      });

      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to add reply');
      }

      // Update current ticket if viewing details
      if (ticket && ticket._id === ticketId) {
        await fetchTicket(ticketId);
      }

      // Update in tickets list
      setTickets(prev => prev.map(t => 
        t._id === ticketId ? { ...t, ...data.ticket } : t
      ));

      return true;
    } catch (err: unknown) {
      setError(err instanceof Error ? err.message : 'Failed to add reply');
      return false;
    } finally {
      setLoading(false);
    }
  }, [ticket, fetchTicket]);

  const uploadFiles = useCallback(async (files: FileList): Promise<TicketAttachment[]> => {
    try {
      const formData = new FormData();
      Array.from(files).forEach((file) => {
        formData.append('files', file);
      });

      const response = await fetch('/api/tickets/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error || 'Failed to upload files');
      }

      const data = await response.json();
      return data.files || [];
    } catch (err: unknown) {
      const errorMessage = err instanceof Error ? err.message : 'Failed to upload files';
      setError(errorMessage);
      throw new Error(errorMessage);
    }
  }, []);

  return {
    // State
    tickets,
    ticket,
    admins,
    users,
    loading,
    error,
    pagination,
    stats,

    // Actions
    fetchTickets,
    fetchTicket,
    assignTicket,
    updateTicketStatus,
    updateTicketPriority,
    addInternalNote,
    addReply,
    uploadFiles,
    bulkUpdateTickets,
    deleteTicket,
    clearError,
  };
}

'use client';

import { useState, useCallback } from 'react';

export interface CannedResponse {
  _id: string;
  title: string;
  content: string;
  category: string;
  services?: string[];
  keywords?: string[];
  isActive: boolean;
  usageCount: number;
  createdAt: Date;
  updatedAt: Date;
}

export function useCannedResponses() {
  const [responses, setResponses] = useState<CannedResponse[]>([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const fetchResponses = useCallback(async (filters?: {
    category?: string;
    service?: string;
    search?: string;
  }) => {
    try {
      setLoading(true);
      setError(null);

      const params = new URLSearchParams();
      if (filters?.category) params.append('category', filters.category);
      if (filters?.service) params.append('service', filters.service);
      if (filters?.search) params.append('search', filters.search);
      params.append('isActive', 'true'); // Only fetch active responses

      const url = `/api/admin/support/canned-responses?${params.toString()}`;
      const response = await fetch(url);
      const data = await response.json();

      if (!response.ok) {
        throw new Error(data.error || 'Failed to fetch canned responses');
      }

      setResponses(data.responses || []);
      return data.responses || [];
    } catch (err: any) {
      setError(err.message);
      return [];
    } finally {
      setLoading(false);
    }
  }, []);

  const incrementUsage = useCallback(async (responseId: string) => {
    try {
      await fetch(`/api/admin/support/canned-responses/${responseId}/usage`, {
        method: 'POST',
      });

      // Update local state
      setResponses(prev => prev.map(response => 
        response._id === responseId 
          ? { ...response, usageCount: response.usageCount + 1 }
          : response
      ));
    } catch (err) {
      console.error('Failed to increment usage count:', err);
    }
  }, []);

  return {
    responses,
    loading,
    error,
    fetchResponses,
    incrementUsage,
  };
}

import { useState, useEffect } from 'react';
import { useAuth } from '@/lib/AuthContext';

export function useOnboarding() {
  const { user } = useAuth();
  const [hasCompletedOnboarding, setHasCompletedOnboarding] = useState<boolean | null>(null);
  const [isLoading, setIsLoading] = useState(true);

  const checkOnboardingStatus = async () => {
    if (!user?.id) {
      setIsLoading(false);
      return;
    }

    try {
      const response = await fetch('/api/user/onboarding');

      if (response.ok) {
        const data = await response.json();
        setHasCompletedOnboarding(data.onboarding?.completed || false);
      } else if (response.status === 404) {
        // No onboarding data found
        setHasCompletedOnboarding(false);
      } else {
        console.error('Failed to check onboarding status');
        setHasCompletedOnboarding(false);
      }
    } catch (error) {
      console.error('Error checking onboarding status:', error);
      setHasCompletedOnboarding(false);
    } finally {
      setIsLoading(false);
    }
  };

  useEffect(() => {
    checkOnboardingStatus();
  }, [user?.id]);

  const markOnboardingComplete = () => {
    setHasCompletedOnboarding(true);
  };

  // Determine if user needs onboarding
  const needsOnboarding = user && 
    user.isEmailVerified && 
    !user.sippyAccountId && 
    hasCompletedOnboarding === false;

  // Determine if user needs verification (after onboarding)
  const needsVerification = user && 
    user.isEmailVerified && 
    !user.sippyAccountId && 
    hasCompletedOnboarding === true;

  return {
    needsOnboarding,
    needsVerification,
    hasCompletedOnboarding,
    isLoading,
    markOnboardingComplete,
    refetch: checkOnboardingStatus
  };
}

import { useState, useEffect } from 'react';
import { AccountInfo } from '@/lib/sippyClient';
import { useAuth } from '@/lib/AuthContext';
import { toast } from 'sonner';

interface UseSippyAccountResult {
  accountInfo: AccountInfo | null;
  isLoading: boolean;
  error: string | null;
  refetch: () => Promise<void>;
}

/**
 * Parse XML string into a JavaScript object
 */
function parseXmlResponse(xmlString: string): AccountInfo | null {
  const parser = new DOMParser();
  const xmlDoc = parser.parseFromString(xmlString, 'text/xml');

  // Get the struct element which contains all the account data
  const struct = xmlDoc.getElementsByTagName('struct')[0];
  if (!struct) return null;

  const result: Partial<AccountInfo> = {};

  // Iterate through all member elements
  const members = struct.getElementsByTagName('member');
  for (let i = 0; i < members.length; i++) {
    const member = members[i];
    const name = member.getElementsByTagName('name')[0]?.textContent;
    const value = member.getElementsByTagName('value')[0];

    if (!name || !value) continue;

    // Get the first child element of value to determine the type
    const valueType = value.firstElementChild?.tagName;
    let parsedValue: any;

    switch (valueType) {
      case 'int':
        parsedValue = parseInt(value.textContent || '0', 10);
        break;
      case 'double':
        parsedValue = parseFloat(value.textContent || '0');
        break;
      case 'boolean':
        parsedValue = value.textContent === '1';
        break;
      case 'string':
        parsedValue = value.textContent || '';
        break;
      case 'array':
        const data = value.getElementsByTagName('data')[0];
        parsedValue = Array.from(data.getElementsByTagName('value')).map(v => v.textContent);
        break;
      case 'nil':
        parsedValue = null;
        break;
      default:
        parsedValue = value.textContent;
    }

    // Convert field names to match the AccountInfo interface
    const fieldName = name as keyof AccountInfo;
    result[fieldName] = parsedValue;
  }

  return result as AccountInfo;
}

/**
 * Hook to fetch Sippy account data for a specific account ID
 * @param accountId The Sippy account ID to fetch data for
 */
export function useSippyAccount(accountId?: number): UseSippyAccountResult {
  const [accountInfo, setAccountInfo] = useState<AccountInfo | null>(null);
  const [isLoading, setIsLoading] = useState<boolean>(false);
  const [error, setError] = useState<string | null>(null);
  const { user } = useAuth();

  // Use the current user's Sippy account ID if none is provided
  const targetAccountId = accountId ?? user?.sippyAccountId;

  const fetchAccountInfo = async () => {
    // If no account ID is available, return early
    if (!targetAccountId) {
      setError('No account ID available');
      return;
    }

    try {
      setIsLoading(true);
      setError(null);

      const response = await fetch(`/api/sippy/account/${targetAccountId}`);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch account data');
      }

      const data = await response.json();

      // Parse the XML response
      const parsedAccountInfo = parseXmlResponse(data.accountInfo);

      if (!parsedAccountInfo) {
        throw new Error('Failed to parse account information');
      }

      setAccountInfo(parsedAccountInfo);
    } catch (err) {
      console.error('Error fetching Sippy account data:', err);
      const errorMessage = err instanceof Error ? err.message : 'Failed to fetch account data';
      setError(errorMessage);
      toast.error(errorMessage);
    } finally {
      setIsLoading(false);
    }
  };

  // Fetch account info on component mount and when targetAccountId changes
  useEffect(() => {
    fetchAccountInfo();
  }, [targetAccountId]);

  return {
    accountInfo,
    isLoading,
    error,
    refetch: fetchAccountInfo
  };
}

import { useState, useCallback } from 'react';
import { 
  Ticket, 
  TicketFilters, 
  TicketListResponse, 
  CreateTicketRequest, 
  UpdateTicketRequest,
  UploadResponse,
  TicketAttachment
} from '@/types/ticket';

interface UseTicketsReturn {
  tickets: Ticket[];
  ticket: Ticket | null;
  loading: boolean;
  error: string | null;
  pagination: TicketListResponse['pagination'] | null;
  stats: TicketListResponse['stats'] | null;
  fetchTickets: (filters?: TicketFilters) => Promise<void>;
  fetchTicket: (id: string) => Promise<void>;
  createTicket: (data: CreateTicketRequest) => Promise<Ticket | null>;
  updateTicket: (id: string, data: UpdateTicketRequest) => Promise<boolean>;
  uploadFiles: (files: FileList) => Promise<TicketAttachment[]>;
  clearError: () => void;
  clearTicket: () => void;
}

export function useTickets(): UseTicketsReturn {
  const [tickets, setTickets] = useState<Ticket[]>([]);
  const [ticket, setTicket] = useState<Ticket | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [pagination, setPagination] = useState<TicketListResponse['pagination'] | null>(null);
  const [stats, setStats] = useState<TicketListResponse['stats'] | null>(null);

  const clearError = useCallback(() => {
    setError(null);
  }, []);

  const clearTicket = useCallback(() => {
    setTicket(null);
  }, []);

  const fetchTickets = useCallback(async (filters: TicketFilters = {}) => {
    setLoading(true);
    setError(null);

    try {
      const searchParams = new URLSearchParams();

      Object.entries(filters).forEach(([key, value]) => {
        if (value !== undefined && value !== null && value !== '') {
          searchParams.append(key, value.toString());
        }
      });

      const response = await fetch(`/api/tickets?${searchParams.toString()}`);

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch tickets');
      }

      const data: TicketListResponse = await response.json();
      setTickets(data.tickets);
      setPagination(data.pagination);
      setStats(data.stats);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
      setTickets([]);
      setPagination(null);
      setStats(null);
    } finally {
      setLoading(false);
    }
  }, []);

  const fetchTicket = useCallback(async (id: string) => {
    setLoading(true);
    setError(null);

    try {
      console.log('Fetching ticket with ID:', id);
      const response = await fetch(`/api/tickets/${id}`);

      console.log('Response status:', response.status);
      console.log('Response ok:', response.ok);

      if (!response.ok) {
        const errorData = await response.json();
        console.log('Error response data:', errorData);
        throw new Error(errorData.error || 'Failed to fetch ticket');
      }

      const ticketData: Ticket = await response.json();
      console.log('Received ticket data:', ticketData);
      console.log('Setting ticket to state...');
      setTicket(ticketData);
      console.log('Ticket set successfully');
    } catch (err) {
      console.error('Error in fetchTicket:', err);
      setError(err instanceof Error ? err.message : 'An error occurred');
      setTicket(null);
    } finally {
      setLoading(false);
      console.log('fetchTicket finished, loading set to false');
    }
  }, []);

  const createTicket = useCallback(async (data: CreateTicketRequest): Promise<Ticket | null> => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch('/api/tickets', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to create ticket');
      }

      const result = await response.json();
      return result.ticket;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
      return null;
    } finally {
      setLoading(false);
    }
  }, []);

  const updateTicket = useCallback(async (id: string, data: UpdateTicketRequest): Promise<boolean> => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`/api/tickets/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(data),
      });

      if (!response.ok) {
        const errorData = await response.json();
        // Create an error object with the code if available
        const error = new Error(errorData.error || 'Failed to update ticket');
        if (errorData.code) {
          (error as any).code = errorData.code;
        }
        throw error;
      }

      // Refresh the current ticket if it's the one being updated
      if (ticket?._id === id) {
        await fetchTicket(id);
      }

      return true;
    } catch (err: any) {
      const errorMessage = err instanceof Error ? err.message : 'An error occurred';
      setError(errorMessage);

      // Re-throw the error with the code so components can handle specific cases
      const enhancedError = new Error(errorMessage);
      if (err.code) {
        (enhancedError as any).code = err.code;
      }
      throw enhancedError;
    } finally {
      setLoading(false);
    }
  }, [ticket, fetchTicket]);

  const uploadFiles = useCallback(async (files: FileList): Promise<TicketAttachment[]> => {
    setLoading(true);
    setError(null);

    try {
      const formData = new FormData();
      Array.from(files).forEach(file => {
        formData.append('files', file);
      });

      const response = await fetch('/api/tickets/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to upload files');
      }

      const result: UploadResponse = await response.json();
      return result.files;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
      return [];
    } finally {
      setLoading(false);
    }
  }, []);

  return {
    tickets,
    ticket,
    loading,
    error,
    pagination,
    stats,
    fetchTickets,
    fetchTicket,
    createTicket,
    updateTicket,
    uploadFiles,
    clearError,
    clearTicket,
  };
}

// Admin version of the hook
export function useAdminTickets(): UseTicketsReturn & {
  assignTicket: (id: string, assignedTo: string) => Promise<boolean>;
  updateTicketStatus: (id: string, status: string) => Promise<boolean>;
} {
  const baseHook = useTickets();
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const assignTicket = useCallback(async (id: string, assignedTo: string): Promise<boolean> => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`/api/admin/tickets/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'assign_ticket',
          assignedTo,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to assign ticket');
      }

      return true;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
      return false;
    } finally {
      setLoading(false);
    }
  }, []);

  const updateTicketStatus = useCallback(async (id: string, status: string): Promise<boolean> => {
    setLoading(true);
    setError(null);

    try {
      const response = await fetch(`/api/admin/tickets/${id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          action: 'update_status',
          status,
        }),
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to update ticket status');
      }

      return true;
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An error occurred');
      return false;
    } finally {
      setLoading(false);
    }
  }, []);

  return {
    ...baseHook,
    loading: loading || baseHook.loading,
    error: error || baseHook.error,
    assignTicket,
    updateTicketStatus,
  };
}

'use client';

import { createContext, useContext, useState, useEffect, ReactNode } from 'react';
import { useRouter } from 'next/navigation';
import { toast } from 'sonner';
import { SippyClient } from './sippyClient';
import { UserData } from './authService';

interface AuthContextType {
  user: UserData | null;
  isLoading: boolean;
  sippyClient: SippyClient | null;
  login: (email: string, password: string) => Promise<void>;
  register: (
    name: string, 
    email: string, 
    password: string, 
    sippyAccountId: number
  ) => Promise<void>;
  logout: () => void;
}

const AuthContext = createContext<AuthContextType | undefined>(undefined);

export function AuthProvider({ children }: { children: ReactNode }) {
  const [user, setUser] = useState<UserData | null>(null);
  const [isLoading, setIsLoading] = useState(true);
  const [sippyClient, setSippyClient] = useState<SippyClient | null>(null);
  const router = useRouter();

  // Check for user on component mount
  useEffect(() => {
    const checkAuth = async () => {
      try {
        const response = await fetch('/api/auth/me', {
          credentials: 'include',
        });

        if (response.ok) {
          const data = await response.json();
          setUser(data.user);

          // Initialize SippyClient if credentials are available
          if (data.sippyCredentials) {
            const client = new SippyClient({
              username: data.sippyCredentials.username,
              password: data.sippyCredentials.password,
              host: data.sippyCredentials.host,
            });
            setSippyClient(client);
          }
        }
      } catch (error) {
        // User not authenticated or other error, this is normal
      } finally {
        setIsLoading(false);
      }
    };

    checkAuth();
  }, []);

  // Login function
  const login = async (email: string, password: string) => {
    try {
      setIsLoading(true);

      const response = await fetch('/api/auth/login', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email, password }),
      });

      if (!response.ok) {
        let errorData;
        try {
          errorData = await response.json();
        } catch {
          // If response is not JSON, create a generic error
          errorData = {
            error: 'Login failed',
            message: `HTTP ${response.status}: ${response.statusText}`,
            code: 'REQUEST_FAILED'
          };
        }

        // Throw structured error with status and code
        const structuredError = new Error(errorData.message || 'Login failed');
        Object.assign(structuredError, {
          ...errorData,
          status: response.status
        });

        throw structuredError;
      }

      const data = await response.json();
      setUser(data.user);

      // Initialize SippyClient
      if (data.sippyCredentials) {
        const client = new SippyClient({
          username: data.sippyCredentials.username,
          password: data.sippyCredentials.password,
          host: data.sippyCredentials.host,
        });
        setSippyClient(client);
      }

      toast.success('Logged in successfully');

      // Navigate to dashboard
      router.push('/dashboard');
    } catch (error) {
      throw error; // Re-throw so LoginForm can handle it
    } finally {
      setIsLoading(false);
    }
  };

  // Register function
  const register = async (
    name: string, 
    email: string, 
    password: string, 
    sippyAccountId: number
  ) => {
    try {
      setIsLoading(true);

      const response = await fetch('/api/auth/register', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ name, email, password, sippyAccountId }),
      });

      if (!response.ok) {
        const error = await response.json();
        throw new Error(error.message || 'Registration failed');
      }

      const data = await response.json();
      setUser(data.user);

      // Initialize SippyClient
      if (data.sippyCredentials) {
        const client = new SippyClient({
          username: data.sippyCredentials.username,
          password: data.sippyCredentials.password,
          host: data.sippyCredentials.host,
        });
        setSippyClient(client);
      }

      toast.success('Registered successfully');

      // Navigate to dashboard
      router.push('/dashboard');
    } catch (error) {
      toast.error(error instanceof Error ? error.message : 'Registration failed');
    } finally {
      setIsLoading(false);
    }
  };

  // Logout function
  const logout = async () => {
    try {
      await fetch('/api/auth/logout', {
        method: 'POST',
      });

      setUser(null);
      setSippyClient(null);
      router.push('/');
      toast.success('Logged out successfully');
    } catch (error) {
      // Silent error handling for logout
    }
  };

  return (
    <AuthContext.Provider
      value={{
        user,
        isLoading,
        sippyClient,
        login,
        register,
        logout,
      }}
    >
      {children}
    </AuthContext.Provider>
  );
}

export function useAuth() {
  const context = useContext(AuthContext);
  if (context === undefined) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
}

