  secondaryColor: '--brand-secondary',
  accentColor: '--brand-accent',
  backgroundColor: '--brand-background',
  textColor: '--brand-text',
  surfaceColor: '--brand-surface',
  gradientStartColor: '--brand-gradient-start',
  gradientMiddleColor: '--brand-gradient-middle',
  gradientEndColor: '--brand-gradient-end',
  fontFamily: '--brand-font-family',
  headingFontFamily: '--brand-heading-font-family',
  borderRadius: '--brand-border-radius',
};

// Branding provider component
export const BrandingProvider: React.FC<BrandingProviderProps> = ({ children }) => {
  const [settings, setSettings] = useState<Partial<IBrandingSettings>>(defaultBrandingSettings);
  const [isLoading, setIsLoading] = useState(true); // Show loading until branding is ready
  const [error, setError] = useState<string | null>(null);

  // Apply branding immediately after mount
  React.useLayoutEffect(() => {
    // Apply default branding synchronously before any render
    applyAllBranding(defaultBrandingSettings);

    // Fetch actual branding settings
    fetchSettings();
  }, []);

  // Fetch branding settings from API
  const fetchSettings = async (): Promise<void> => {
    try {
      setError(null);

      const response = await fetch('/api/settings/branding');

      if (!response.ok) {
        throw new Error('Failed to fetch branding settings');
      }

      const data = await response.json();
      const newSettings = { ...defaultBrandingSettings, ...data };

      // Apply settings immediately when received
      setSettings(newSettings);
      applyAllBranding(newSettings);

      // Add minimal loading time to ensure smooth experience
      await new Promise(resolve => setTimeout(resolve, 500));

    } catch (err) {
      setError(err instanceof Error ? err.message : 'Unknown error');
      // Use default settings on error
      setSettings(defaultBrandingSettings);
      applyAllBranding(defaultBrandingSettings);

      // Add minimum loading time even on error
      await new Promise(resolve => setTimeout(resolve, 500));
    } finally {
      setIsLoading(false); // Hide loading when done
    }
  };

  // Update branding settings
  const updateSettings = async (updates: Partial<IBrandingSettings>): Promise<void> => {
    try {
      setError(null);

      const response = await fetch('/api/settings/branding', {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(updates),
      });

      let responseData;

      // Try to parse JSON response
      try {
        responseData = await response.json();
      } catch (parseError) {
        // If JSON parsing fails, get the text response for debugging
        const textResponse = await response.text();

        throw new Error('Server returned invalid response format. Please check the server logs.');
      }

      if (!response.ok) {
        throw new Error(responseData.error || `Server error: ${response.status}`);
      }

      // Update local state
      setSettings(prev => ({ ...prev, ...updates }));

      // Apply changes immediately
      applyBrandingToPage();
    } catch (err) {
      const errorMessage = err instanceof Error ? err.message : 'Unknown error';
      setError(errorMessage);
      throw new Error(errorMessage);
    }
  };

  // Apply branding to page via CSS variables
  const applyBrandingToPage = (): void => {
    applyAllBranding(settings);
  };

  // Refresh settings
  const refreshSettings = async (): Promise<void> => {
    await fetchSettings();
  };

  const contextValue: BrandingContextType = {
    settings,
    isLoading,
    error,
    refreshSettings,
    updateSettings,
    applyBrandingToPage,
  };

  return (
    <BrandingContext.Provider value={contextValue}>
      {children}
    </BrandingContext.Provider>
  );
};

import { toast } from 'sonner';

export interface ApiError {
  error: string;
  message: string;
  code?: string;
  status?: number;
}

export interface ApiErrorResponse {
  error: string;
  message?: string;
  code?: string;
}

/**
 * Handle API errors with user-friendly messages
 */
export function handleApiError(error: any, customMessage?: string): void {
  console.error('API Error:', error);

  let errorMessage = customMessage || 'An unexpected error occurred';
  let errorTitle = 'Error';

  // Handle fetch errors or network issues
  if (error instanceof TypeError && error.message.includes('fetch')) {
    errorMessage = 'Network error. Please check your connection and try again.';
    errorTitle = 'Connection Error';
  }
  // Handle structured API error responses
  else if (error?.status || error?.code) {
    const apiError = error as ApiError;

    switch (apiError.status || apiError.code) {
      case 401:
      case 'AUTH_REQUIRED':
        errorMessage = apiError.message || 'You need to be logged in to access this feature';
        errorTitle = 'Authentication Required';
        // Optionally redirect to login after showing error
        setTimeout(() => {
          if (typeof window !== 'undefined') {
            window.location.href = '/';
          }
        }, 2000);
        break;

      case 403:
      case 'FORBIDDEN':
        errorMessage = apiError.message || 'You don\'t have permission to perform this action';
        errorTitle = 'Access Denied';
        break;

      case 400:
      case 'BAD_REQUEST':
        errorMessage = apiError.message || 'Invalid request. Please check your input and try again.';
        errorTitle = 'Invalid Request';
        break;

      case 404:
      case 'NOT_FOUND':
        errorMessage = apiError.message || 'The requested resource was not found';
        errorTitle = 'Not Found';
        break;

      case 422:
      case 'VALIDATION_ERROR':
        errorMessage = apiError.message || 'Please check your input and try again';
        errorTitle = 'Validation Error';
        break;

      case 500:
      case 'INTERNAL_ERROR':
        errorMessage = apiError.message || 'A server error occurred. Please try again later.';
        errorTitle = 'Server Error';
        break;

      default:
        errorMessage = apiError.message || apiError.error || errorMessage;
        break;
    }
  }
  // Handle error objects with message property
  else if (error?.message) {
    errorMessage = error.message;
  }
  // Handle string errors
  else if (typeof error === 'string') {
    errorMessage = error;
  }

  // Show toast notification
  toast.error(errorMessage, {
    description: errorTitle !== 'Error' ? errorTitle : undefined,
    duration: 5000,
  });
}

/**
 * Wrapper for fetch requests with error handling
 */
export async function apiRequest<T = any>(
  url: string, 
  options: RequestInit = {}
): Promise<T> {
  try {
    const response = await fetch(url, {
      headers: {
        'Content-Type': 'application/json',
        ...options.headers,
      },
      ...options,
    });

    // Handle successful responses
    if (response.ok) {
      // Handle responses that might not have JSON content
      const contentType = response.headers.get('content-type');
      if (contentType && contentType.includes('application/json')) {
        return await response.json();
      } else {
        return response as any;
      }
    }

    // Handle error responses
    let errorData: ApiErrorResponse;
    try {
      errorData = await response.json();
    } catch {
      // If response is not JSON, create a generic error
      errorData = {
        error: 'Request failed',
        message: `HTTP ${response.status}: ${response.statusText}`,
      };
    }

    // Create structured error object
    const apiError: ApiError = {
      error: errorData.error,
      message: errorData.message || errorData.error,
      code: errorData.code,
      status: response.status,
    };

    throw apiError;
  } catch (error) {
    // If the error is already an ApiError, re-throw it
    if (error && typeof error === 'object' && 'status' in error) {
      throw error;
    }

    // Handle network errors or other unexpected errors
    throw {
      error: 'Network error',
      message: 'Unable to connect to the server. Please check your connection.',
      code: 'NETWORK_ERROR',
    } as ApiError;
  }
}

/**
 * Handle authentication errors specifically
 */
export function handleAuthError(error: any): { title: string; message: string; description: string } {
  let title = 'Authentication Error';
  let message = 'Authentication failed';
  let description = 'Please try again or contact support if the problem persists.';

  if (error?.status === 401 || error?.code === 'INVALID_CREDENTIALS') {
    title = 'Invalid Credentials';
    message = 'Invalid email or password';
    description = 'Please check your credentials and try again. Make sure your email and password are correct.';
  } else if (error?.code === 'USER_NOT_FOUND') {
    title = 'Account Not Found';
    message = 'No account found with this email address';
    description = 'Please check your email address or create a new account if you don\'t have one yet.';
  } else if (error?.code === 'MISSING_CREDENTIALS') {
    title = 'Missing Information';
    message = 'Please enter both email and password';
    description = 'Both email and password fields are required to sign in.';
  } else if (error?.code === 'INVALID_EMAIL') {
    title = 'Invalid Email Format';
    message = 'Please enter a valid email address';
    description = 'The email address format appears to be incorrect. Please check and try again.';
  } else if (error?.message) {
    message = error.message;
  }

  return { title, message, description };
}

/**
 * Show success message
 */
export function showSuccess(message: string, description?: string): void {
  toast.success(message, {
    description,
    duration: 4000,
  });
}

/**
 * Show info message  
 */
export function showInfo(message: string, description?: string): void {
  toast.info(message, {
    description,
    duration: 4000,
  });
}

'use server';

import jwt from 'jsonwebtoken';
import { cookies } from 'next/headers';
import User, { UserRole } from '@/models/User';
import { connectToDatabase } from './db';
import { SippyClient, SippyCredentials } from './sippyClient';

// Constants
const JWT_SECRET = process.env.JWT_SECRET || 'your-secret-key';
const JWT_EXPIRES_IN = '7d';
const COOKIE_NAME = 'auth_token';

// Interface for registration data
export interface RegisterData {
  name: string;
  email: string;
  password: string;
  sippyAccountId: number;
  role?: UserRole;
}

// Interface for login data
export interface LoginData {
  email: string;
  password: string;
}

// Interface for user data returned to the client
export interface UserData {
  id: string;
  name: string;
  email: string;
  role: UserRole;
  sippyAccountId?: number;
  isEmailVerified: boolean;
  isImpersonating?: boolean;
  originalUserId?: string;
}

// Interface for authentication result
export interface AuthResult {
  user: UserData;
  token: string;
}

// Create a JWT token
const createToken = (userId: string): string => {
  return jwt.sign({ userId }, JWT_SECRET, {
    expiresIn: JWT_EXPIRES_IN,
  });
};

// Convert user document to safe user data
const sanitizeUser = (user: any): UserData => {
  return {
    id: typeof user._id === 'object' && user._id !== null ? user._id.toString() : String(user._id),
    name: user.name,
    email: user.email,
    role: user.role,
    sippyAccountId: user.sippyAccountId,
    isEmailVerified: user.isEmailVerified,
  };
};

// Register a new user
export const registerUser = async (data: RegisterData): Promise<AuthResult> => {
  await connectToDatabase();

  // Check if user already exists
  const existingUser = await User.findOne({ email: data.email });
  if (existingUser) {
    throw new Error('User already exists');
  }

  // Create new user
  const user = await User.create({
    name: data.name,
    email: data.email,
    password: data.password,
    sippyAccountId: data.sippyAccountId,
    role: data.role || UserRole.CLIENT,
  });

  // Create token
  const userId = typeof user._id === 'object' && user._id !== null ? 
    user._id.toString() : String(user._id);
  const token = createToken(userId);

  return {
    user: sanitizeUser(user),
    token,
  };
};

// Login a user
export const loginUser = async (data: LoginData): Promise<AuthResult> => {
  await connectToDatabase();

  // Find user by email with password
  const user = await User.findByEmail(data.email);
  if (!user) {
    throw new Error('Invalid credentials');
  }

  // Check password
  const isPasswordValid = await user.comparePassword(data.password);
  if (!isPasswordValid) {
    throw new Error('Invalid credentials');
  }

  // Check if email is verified
  if (!user.isEmailVerified) {
    throw new Error('Email not verified. Please check your email for verification code.');
  }

  // Create token
  const userId = typeof user._id === 'object' && user._id !== null ? 
    user._id.toString() : String(user._id);
  const token = createToken(userId);

  return {
    user: sanitizeUser(user),
    token,
  };
};

// Get current user from token
export const getCurrentUser = async (): Promise<UserData | null> => {
  try {
    await connectToDatabase();

    // Get token from cookies
    const cookieStore = await cookies();
    const token = cookieStore.get(COOKIE_NAME)?.value;

    if (!token) {
      return null;
    }

    // Verify token
    const decoded = jwt.verify(token, JWT_SECRET) as { 
      userId: string;
      originalUserId?: string;
      isImpersonating?: boolean;
    };

    // Get user by ID
    const user = await User.findById(decoded.userId);
    if (!user) {
      return null;
    }

    // Create user data with impersonation info if applicable
    const userData = sanitizeUser(user);

    // Add impersonation data if present in token
    if (decoded.isImpersonating && decoded.originalUserId) {
      userData.isImpersonating = true;
      userData.originalUserId = decoded.originalUserId;
    }

    return userData;
  } catch {
    return null;
  }
};

// Get Sippy client for a user
export const getSippyClientForUser = async (
  userId: string, 
  credentials: SippyCredentials
): Promise<SippyClient> => {
  await connectToDatabase();

  // Get user
  const user = await User.findById(userId);
  if (!user) {
    throw new Error('User not found');
  }

  // Create Sippy client
  return new SippyClient(credentials);
};

// Set authentication cookie
export const setAuthCookie = async (token: string): Promise<void> => {
  const cookieStore = await cookies();
  cookieStore.set(COOKIE_NAME, token, {
    httpOnly: true,
    secure: process.env.NODE_ENV === 'production',
    maxAge: 7 * 24 * 60 * 60, // 7 days
    path: '/',
  });
};

// Clear authentication cookie
export const clearAuthCookie = async (): Promise<void> => {
  const cookieStore = await cookies();
  cookieStore.delete(COOKIE_NAME);
};

import mongoose from 'mongoose';

const MONGODB_URI = process.env.MONGODB_URI || 'mongodb://localhost:27017/sippy-dashboard';

// Define the mongoose connection cache interface
interface MongooseConnection {
  conn: typeof mongoose | null;
  promise: Promise<typeof mongoose> | null;
}

// Declare the global variable for mongoose connection
declare global {
  var mongooseConnection: MongooseConnection;
}

// Initialize the global connection cache
if (!global.mongooseConnection) {
  global.mongooseConnection = {
    conn: null,
    promise: null,
  };
}

export async function connectToDatabase(): Promise<typeof mongoose> {
  if (global.mongooseConnection.conn) {
    return global.mongooseConnection.conn;
  }

  if (!global.mongooseConnection.promise) {
    const opts = {
      bufferCommands: false,
    };

    global.mongooseConnection.promise = mongoose.connect(MONGODB_URI, opts).then(() => {
      console.log('Connected to MongoDB');
      return mongoose;
    });
  }

  try {
    const connectedMongoose = await global.mongooseConnection.promise;
    global.mongooseConnection.conn = connectedMongoose;
  } catch (e) {
    global.mongooseConnection.promise = null;
    throw e;
  }

  return global.mongooseConnection.conn as typeof mongoose;
}

export default connectToDatabase;

import { connectToDatabase } from '@/lib/db';
import { NotificationLog as NotificationLogModel } from '@/models/NotificationLog';

export interface EmailLogData {
  userId: string;
  userEmail: string;
  userName: string;
  sippyAccountId?: number;
  notificationType: 'low_balance' | 'zero_balance' | 'negative_balance' | 'email_verification' | 'account_activation' | 'high_cost_alert' | 'low_asr_alert' | 'extreme_usage_alert' | 'payment_success_gateway' | 'payment_success_admin' | 'payment_debit_admin' | 'password_reset' | 'number_purchase_single' | 'number_purchase_bulk' | 'number_assignment' | 'number_unassignment' | 'backorder_approved' | 'backorder_rejected' | 'cancellation_approved' | 'cancellation_rejected' | 'admin_user_purchase_single' | 'admin_user_purchase_bulk' | 'admin_backorder_request' | 'admin_cancellation_request';
  emailSubject: string;
  emailBody: string;

  // Optional fields based on notification type
  balanceAmount?: number;
  thresholdAmount?: number;
  currency?: string;
  otpCode?: string;
  activationData?: any;
  alertData?: any;
  paymentData?: {
    amount: number;
    currency: string;
    paymentMethod?: string;
    transactionId?: string;
    fees?: {
      processingFee?: number;
      fixedFee?: number;
    };
    gateway?: string;
    notes?: string;
    processedBy?: string;
  };
  metadata?: any; // Add metadata field for password reset and other flexible data
}

export interface EmailLogResponse {
  logId: string;
  success: boolean;
  error?: string;
}

/**
 * Create a notification log entry for an email that is about to be sent
 */
export async function createEmailLog(data: EmailLogData): Promise<EmailLogResponse> {
  try {
    await connectToDatabase();

    const logEntry = new NotificationLogModel({
      userId: data.userId,
      userEmail: data.userEmail,
      userName: data.userName,
      sippyAccountId: data.sippyAccountId,
      notificationType: data.notificationType,
      emailSubject: data.emailSubject,
      emailBody: data.emailBody,
      status: 'pending',

      // Optional fields
      ...(data.balanceAmount !== undefined && { balanceAmount: data.balanceAmount }),
      ...(data.thresholdAmount !== undefined && { thresholdAmount: data.thresholdAmount }),
      ...(data.currency && { currency: data.currency }),
      ...(data.otpCode && { otpCode: data.otpCode }),
      ...(data.activationData && { activationData: data.activationData }),
      ...(data.alertData && { alertData: data.alertData }),
      ...(data.paymentData && { paymentData: data.paymentData }),
    });

    const savedLog = await logEntry.save();

    return {
      logId: (savedLog._id as any).toString(),
      success: true
    };
  } catch (error) {
    console.error('Error creating email log:', error);
    return {
      logId: '',
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error'
    };
  }
}

/**
 * Update a notification log entry when email is successfully sent
 */
export async function markEmailAsSent(logId: string, fromEmail?: string): Promise<boolean> {
  try {
    await connectToDatabase();

    const updateData: any = {
      status: 'sent',
      sentAt: new Date()
    };

    if (fromEmail) {
      updateData.fromEmail = fromEmail;
    }

    await NotificationLogModel.findByIdAndUpdate(logId, updateData);

    return true;
  } catch (error) {
    console.error('Error marking email as sent:', error);
    return false;
  }
}

/**
 * Update a notification log entry when email fails to send
 */
export async function markEmailAsFailed(logId: string, errorMessage: string): Promise<boolean> {
  try {
    await connectToDatabase();

    await NotificationLogModel.findByIdAndUpdate(logId, {
      status: 'failed',
      errorMessage: errorMessage
    });

    return true;
  } catch (error) {
    console.error('Error marking email as failed:', error);
    return false;
  }
}

/**
 * Log and send email in one operation with automatic status tracking
 */
export async function logAndSendEmail(
  data: EmailLogData, 
  sendEmailFunction: () => Promise<any>
): Promise<boolean> {
  const logResult = await createEmailLog(data);

  if (!logResult.success) {
    console.error('Failed to create email log:', logResult.error);
    return false;
  }

  try {
    const emailResult = await sendEmailFunction();

    // Extract fromEmail from the email service response
    let fromEmail: string | undefined;
    if (emailResult && typeof emailResult === 'object') {
      // Check if it's an SMTP service response with accountUsed
      if (emailResult.accountUsed && emailResult.accountUsed.fromEmail) {
        fromEmail = emailResult.accountUsed.fromEmail;
      }
      // Check if it's a direct response with fromEmail
      else if (emailResult.fromEmail) {
        fromEmail = emailResult.fromEmail;
      }
    }

    await markEmailAsSent(logResult.logId, fromEmail);
    return true;
  } catch (error) {
    const errorMessage = error instanceof Error ? error.message : 'Unknown error';
    await markEmailAsFailed(logResult.logId, errorMessage);
    throw error; // Re-throw to maintain existing error handling
  }
}

