import { logAndSendEmail } from './emailLogger';
import { generatePasswordResetEmail, PasswordResetEmailData } from './emailTemplates/authNotifications';
import { sendEmail } from './emailService';
import BrandingSettings from '@/models/BrandingSettings';
import User from '@/models/User';
import { connectToDatabase } from './db';

// Send password reset email
export const sendPasswordResetEmail = async (email: string): Promise<{ success: boolean; message: string }> => {
  try {
    await connectToDatabase();

    // Find user by email
    const user = await User.findByEmail(email);
    if (!user) {
      // Don't reveal if email exists or not for security
      return {
        success: true,
        message: 'If an account with this email exists, you will receive a password reset link shortly.'
      };
    }

    // Generate reset token
    const resetToken = user.createPasswordResetToken();
    await user.save({ validateBeforeSave: false });

    // Get branding settings
    const brandingSettings = await BrandingSettings.findOne({});
    const branding = {
      companyName: brandingSettings?.companyName || 'OVOKY',
      companySlogan: brandingSettings?.companySlogan || 'Your trusted communications partner',
      primaryColor: brandingSettings?.primaryColor || '#667eea',
      fontFamily: brandingSettings?.fontFamily || 'Arial, sans-serif',
      supportEmail: brandingSettings?.supportEmail || 'support@ovoky.com',
      websiteUrl: brandingSettings?.websiteUrl || 'https://ovoky.com'
    };

    // Create reset URL
    const baseUrl = process.env.NEXT_PUBLIC_APP_URL || 'http://localhost:3000';
    const resetUrl = `${baseUrl}/reset-password?token=${resetToken}`;

    // Prepare email data
    const emailData: PasswordResetEmailData = {
      name: user.name,
      email: user.email,
      resetToken,
      resetUrl,
      branding
    };

    // Generate email content
    const htmlContent = generatePasswordResetEmail(emailData);

    // Send email with logging
    const emailLogData = {
      userId: (user._id as any).toString(),
      userEmail: user.email,
      userName: user.name,
      notificationType: 'password_reset' as const,
      emailSubject: `Reset Your Password - ${branding.companyName}`,
      emailBody: htmlContent.html,
      metadata: {
        resetTokenSent: true,
        expiresAt: user.passwordResetExpires
      }
    };

    await logAndSendEmail(emailLogData, async () => {
      // Send the actual email using the email service
      await sendEmail({
        to: user.email,
        subject: emailLogData.emailSubject,
        html: htmlContent.html,
        text: htmlContent.text
      });
    });

    return {
      success: true,
      message: 'Password reset email sent successfully. Please check your email for instructions.'
    };

  } catch (error) {
    console.error('Password reset email error:', error);
    throw new Error('Failed to send password reset email. Please try again later.');
  }
};

// Verify password reset token
export const verifyPasswordResetToken = async (token: string): Promise<{ valid: boolean; user?: any; message: string }> => {
  try {
    await connectToDatabase();

    // Find user with valid reset token
    const user = await User.findOne({
      passwordResetToken: { $exists: true },
      passwordResetExpires: { $gt: Date.now() }
    });

    if (!user || !user.isPasswordResetTokenValid(token)) {
      return {
        valid: false,
        message: 'Invalid or expired password reset token. Please request a new password reset.'
      };
    }

    return {
      valid: true,
      user: {
        id: user._id.toString(),
        email: user.email,
        name: user.name
      },
      message: 'Token is valid'
    };

  } catch (error) {
    console.error('Token verification error:', error);
    return {
      valid: false,
      message: 'Error verifying token. Please try again.'
    };
  }
};

// Reset password with token
export const resetPasswordWithToken = async (
  token: string, 
  newPassword: string
): Promise<{ success: boolean; message: string }> => {
  try {
    await connectToDatabase();

    // Verify token first
    const verification = await verifyPasswordResetToken(token);
    if (!verification.valid) {
      return {
        success: false,
        message: verification.message
      };
    }

    // Find user and update password
    const user = await User.findById(verification.user!.id);
    if (!user) {
      return {
        success: false,
        message: 'User not found. Please request a new password reset.'
      };
    }

    // Update password and clear reset token
    user.password = newPassword;
    user.passwordResetToken = undefined;
    user.passwordResetExpires = undefined;
    await user.save();

    return {
      success: true,
      message: 'Password reset successfully. You can now log in with your new password.'
    };

  } catch (error) {
    console.error('Password reset error:', error);
    return {
      success: false,
      message: 'Failed to reset password. Please try again later.'
    };
  }
};

import { generatePaymentSuccessTemplate, PaymentSuccessData } from '@/lib/emailTemplates';
import { sendEmail } from '@/lib/emailService';
import { logAndSendEmail } from '@/lib/emailLogger';
import User from '@/models/User';
import BrandingSettings from '@/models/BrandingSettings';
import SmtpService from '@/services/SmtpService';

export interface PaymentEmailData {
  userId: string;
  paymentAmount: number;
  currency: string;
  paymentType: 'gateway_payment' | 'admin_credit' | 'admin_debit';
  paymentMethod?: string;
  transactionId?: string;
  fees?: {
    processingFee?: number;
    fixedFee?: number;
  };
  gateway?: string;
  notes?: string;
  processedBy?: string; // Admin name for admin credits
  currentBalance?: number; // Updated balance after payment
}

export async function sendPaymentSuccessEmail(data: PaymentEmailData): Promise<boolean> {
  try {
    // Get user details
    const user = await User.findById(data.userId);
    if (!user) {
      console.error('User not found for payment notification:', data.userId);
      return false;
    }

    // Get branding settings
    const brandingSettings = await BrandingSettings.findOne({});
    if (!brandingSettings) {
      console.error('Branding settings not found');
      return false;
    }

    // Prepare email template data
    const templateData: PaymentSuccessData = {
      account: {
        name: user.name,
        email: user.email,
        sippyAccountId: user.sippyAccountId || 0,
        balance: data.currentBalance,
        currency: data.currency,
      },
      payment: {
        amount: data.paymentAmount,
        currency: data.currency,
        paymentMethod: data.paymentMethod,
        transactionId: data.transactionId,
        fees: data.fees,
        gateway: data.gateway,
        notes: data.notes,
      },
      paymentType: data.paymentType,
      processedBy: data.processedBy,
      branding: {
        companyName: brandingSettings.companyName,
        companySlogan: brandingSettings.companySlogan,
        primaryColor: brandingSettings.primaryColor,
        fontFamily: brandingSettings.fontFamily || 'Arial, sans-serif',
      },
    };

    // Generate email content
    const emailContent = generatePaymentSuccessTemplate(templateData);

    // Determine notification type
    const notificationType: 'payment_success_gateway' | 'payment_success_admin' = data.paymentType === 'gateway_payment' 
      ? 'payment_success_gateway' 
      : data.paymentType === 'admin_credit' ? 'payment_success_admin' : 'payment_success_admin';

    // Send email using logAndSendEmail for proper logging
    const emailLogResult = await logAndSendEmail(
      {
        userId: data.userId,
        userEmail: user.email,
        userName: user.name,
        sippyAccountId: user.sippyAccountId,
        notificationType,
        emailSubject: emailContent.subject,
        emailBody: emailContent.html,
        paymentData: {
          amount: data.paymentAmount,
          currency: data.currency,
          paymentMethod: data.paymentMethod || 'Unknown',
          transactionId: data.transactionId,
          fees: data.fees,
          gateway: data.gateway,
          notes: data.notes,
          processedBy: data.processedBy
        }
      },
      async () => {
        const smtpService = SmtpService.getInstance();
        const result = await smtpService.sendBillingEmail({
          to: user.email,
          subject: emailContent.subject,
          html: emailContent.html,
          text: emailContent.text
        });

        if (!result.success) {
          throw new Error(`Failed to send payment notification: ${result.error}`);
        }

        return result;
      }
    );

    if (emailLogResult) {
      console.log(`âœ… Payment success email sent to ${user.email} for ${data.paymentType}`);
      return true;
    } else {
      console.error(`âŒ Failed to send payment success email to ${user.email}`);
      return false;
    }
  } catch (error) {
    console.error('Error sending payment success email:', error);
    return false;
  }
}

export async function sendGatewayPaymentSuccessEmail(params: {
  userId: string;
  amount: number;
  currency: string;
  paymentMethod?: string;
  transactionId?: string;
  fees?: {
    processingFee?: number;
    fixedFee?: number;
  };
  gateway?: string;
  currentBalance?: number;
}): Promise<boolean> {
  return sendPaymentSuccessEmail({
    ...params,
    paymentAmount: params.amount,
    paymentType: 'gateway_payment',
  });
}

export async function sendAdminCreditSuccessEmail(params: {
  userId: string;
  amount: number;
  currency: string;
  processedBy: string;
  notes?: string;
  currentBalance?: number;
}): Promise<boolean> {
  return sendPaymentSuccessEmail({
    ...params,
    paymentAmount: params.amount,
    paymentType: 'admin_credit',
    paymentMethod: 'Admin Credit',
  });
}

export async function sendAdminDebitNotificationEmail(params: {
  userId: string;
  amount: number;
  currency: string;
  processedBy: string;
  notes?: string;
  currentBalance?: number;
}): Promise<boolean> {
  // For debit operations, use the admin_debit payment type
  return sendPaymentSuccessEmail({
    ...params,
    paymentAmount: params.amount, // Keep positive amount, template will handle display
    paymentType: 'admin_debit', // Use new admin_debit type
    paymentMethod: 'Admin Debit',
  });
}

