   * Get payment details for a specific payment
   * @param opts Options with payment ID and account/customer ID
   * @returns Payment information
   */
  async getPaymentInfo(opts: {
    i_payment: number;
    i_account?: number;
    i_customer?: number;
  }): Promise<PaymentInfo> {
    return this.makeRequest<PaymentInfo>('getPaymentInfo', opts);
  }

  /**
   * Get list of payments for an account or customer
   * @param opts Options for filtering payments
   * @returns List of payments
   */
  async getPaymentsList(opts: {
    i_account?: number;
    i_customer?: number;
    offset?: number;
    limit?: number;
    start_date?: string; // '%H:%M:%S.000 GMT %a %b %d %Y' format
    end_date?: string; // '%H:%M:%S.000 GMT %a %b %d %Y' format
    type?: 'credit' | 'debit';
  } = {}): Promise<PaymentsList> {
    return this.makeRequest<PaymentsList>('getPaymentsList', opts);
  }

  /**
   * Add funds to an account (credit the balance)
   * @param opts Options with account ID, amount, and currency
   * @returns Operation result
   */
  async accountAddFunds(opts: {
    i_account: number;
    amount: number;
    currency: string;
    payment_notes?: string;
    payment_time?: string; // '%H:%M:%S.000 GMT %a %b %d %Y' format
  }): Promise<BalanceOperation> {
    return this.makeRequest<BalanceOperation>('accountAddFunds', opts);
  }

  /**
   * Credit an account's balance
   * @param opts Options with account ID, amount, and currency
   * @returns Operation result
   */
  async accountCredit(opts: {
    i_account: number;
    amount: number;
    currency: string;
    payment_notes?: string;
    payment_time?: string; // '%H:%M:%S.000 GMT %a %b %d %Y' format
  }): Promise<BalanceOperation> {
    return this.makeRequest<BalanceOperation>('accountCredit', opts);
  }

  /**
   * Debit an account's balance
   * @param opts Options with account ID, amount, and currency
   * @returns Operation result
   */
  async accountDebit(opts: {
    i_account: number;
    amount: number;
    currency: string;
    payment_notes?: string;
    payment_time?: string; // '%H:%M:%S.000 GMT %a %b %d %Y' format
  }): Promise<BalanceOperation> {
    return this.makeRequest<BalanceOperation>('accountDebit', opts);
  }

  /**
   * Add credit to an account's balance (alias for accountCredit)
   * Used by payment processing webhooks
   * @param opts Options with account ID, amount, and currency
   * @returns Operation result
   */
  async addAccountCredit(opts: {
    i_account: number;
    amount: number;
    currency: string;
    payment_notes?: string;
    payment_time?: string; // '%H:%M:%S.000 GMT %a %b %d %Y' format
  }): Promise<BalanceOperation> {
    return this.accountCredit(opts);
  }
}

import { InternalNotification, NotificationType, NotificationTemplates } from '@/types/notifications';

/**
 * Utility functions for triggering custom toast notifications
 */

// Global function to show toast (will be attached by ToastContainer)
declare global {
  interface Window {
    showNotificationToast?: (
      notification: InternalNotification,
      options?: {
        position?: 'top-right' | 'top-left' | 'bottom-right' | 'bottom-left' | 'top-center' | 'bottom-center';
        duration?: number;
      }
    ) => void;
  }
}

interface ToastOptions {
  position?: 'top-right' | 'top-left' | 'bottom-right' | 'bottom-left' | 'top-center' | 'bottom-center';
  duration?: number;
  priority?: 'low' | 'medium' | 'high' | 'urgent';
  actionUrl?: string;
  data?: Record<string, any>;
  userId?: string;
}

/**
 * Generate a unique ID for toast notifications
 */
function generateToastId(): string {
  return `toast-${Date.now()}-${Math.random().toString(36).substr(2, 9)}`;
}

/**
 * Show a quick toast notification
 */
export function showToast(
  type: NotificationType,
  title?: string,
  message?: string,
  options: ToastOptions = {}
) {
  if (typeof window === 'undefined' || !window.showNotificationToast) {
    console.warn('Toast system not initialized');
    return;
  }

  const template = NotificationTemplates[type];

  const notification: InternalNotification = {
    id: generateToastId(),
    userId: options.userId || 'anonymous',
    type,
    title: title || template.getTitle(options.data),
    message: message || template.getMessage(options.data),
    priority: options.priority || 'medium',
    status: 'unread',
    data: options.data,
    actionUrl: options.actionUrl,
    icon: template.icon,
    sound: template.sound,
    showToast: true,
    showPush: false,
    persistent: false,
    createdAt: new Date()
  };

  window.showNotificationToast(notification, {
    position: options.position,
    duration: options.duration
  });
}

/**
 * Show a success toast
 */
export function showSuccessToast(
  message: string,
  title?: string,
  options: ToastOptions = {}
) {
  showToast(
    'payment_success', // Using payment_success as a generic success type
    title || 'Success',
    message,
    { ...options, priority: 'medium' }
  );
}

/**
 * Show an error toast
 */
export function showErrorToast(
  message: string,
  title?: string,
  options: ToastOptions = {}
) {
  showToast(
    'payment_failed', // Using payment_failed as a generic error type
    title || 'Error',
    message,
    { ...options, priority: 'high' }
  );
}

/**
 * Show a warning toast
 */
export function showWarningToast(
  message: string,
  title?: string,
  options: ToastOptions = {}
) {
  showToast(
    'low_balance', // Using low_balance as a generic warning type
    title || 'Warning',
    message,
    { ...options, priority: 'high' }
  );
}

/**
 * Show an info toast
 */
export function showInfoToast(
  message: string,
  title?: string,
  options: ToastOptions = {}
) {
  showToast(
    'system_maintenance', // Using system_maintenance as a generic info type
    title || 'Information',
    message,
    { ...options, priority: 'low' }
  );
}

/**
 * Show an urgent toast
 */
export function showUrgentToast(
  message: string,
  title?: string,
  options: ToastOptions = {}
) {
  showToast(
    'security_alert', // Using security_alert as a generic urgent type
    title || 'Urgent',
    message,
    { ...options, priority: 'urgent' }
  );
}

/**
 * Show a ticket-related toast
 */
export function showTicketToast(
  action: 'created' | 'updated' | 'assigned' | 'resolved' | 'reply',
  ticketNumber: string,
  options: ToastOptions = {}
) {
  const typeMap = {
    created: 'ticket_created',
    updated: 'ticket_updated',
    assigned: 'ticket_assigned',
    resolved: 'ticket_resolved',
    reply: 'ticket_reply'
  } as const;

  showToast(
    typeMap[action],
    undefined,
    undefined,
    {
      ...options,
      data: { ticketNumber, ...options.data }
    }
  );
}

/**
 * Show a payment-related toast
 */
export function showPaymentToast(
  success: boolean,
  amount: string,
  currency: string = 'USD',
  options: ToastOptions = {}
) {
  showToast(
    success ? 'payment_success' : 'payment_failed',
    undefined,
    undefined,
    {
      ...options,
      priority: success ? 'medium' : 'high',
      data: { amount, currency, ...options.data }
    }
  );
}

/**
 * Show a phone number related toast
 */
export function showPhoneNumberToast(
  action: 'approved' | 'rejected' | 'purchased' | 'assigned',
  phoneNumber: string,
  options: ToastOptions = {}
) {
  const typeMap = {
    approved: 'phone_number_approved',
    rejected: 'phone_number_rejected',
    purchased: 'phone_number_purchased',
    assigned: 'phone_number_assigned'
  } as const;

  showToast(
    typeMap[action],
    undefined,
    undefined,
    {
      ...options,
      data: { phoneNumber, ...options.data }
    }
  );
}

/**
 * Show a balance-related toast
 */
export function showBalanceToast(
  balance: number,
  threshold: number,
  currency: string = 'USD',
  options: ToastOptions = {}
) {
  const type = balance <= 0 ? 'zero_balance' : 'low_balance';
  const priority = balance <= 0 ? 'urgent' : 'high';

  showToast(
    type,
    undefined,
    undefined,
    {
      ...options,
      priority,
      data: { balance: balance.toFixed(2), threshold: threshold.toFixed(2), currency, ...options.data }
    }
  );
}

/**
 * Hook to easily show toasts from React components
 */
export function useToast() {
  return {
    showToast,
    showSuccessToast,
    showErrorToast,
    showWarningToast,
    showInfoToast,
    showUrgentToast,
    showTicketToast,
    showPaymentToast,
    showPhoneNumberToast,
    showBalanceToast
  };
}

import { connectToDatabase } from '@/lib/db';
import DashboardPreferencesModel from '@/models/DashboardPreferences';
import { NotificationPreferencesModel } from '@/models/InternalNotification';
import { createDefaultDashboardPreferences } from '@/lib/defaultDashboardPreferences';
import { createDefaultPreferences } from '@/types/notifications';

/**
 * Initialize default preferences for a new user
 * This should be called during user registration/signup
 */
export async function initializeUserDefaults(
  userId: string, 
  userRole: 'admin' | 'user' = 'user'
): Promise<{
  dashboardPreferences: any;
  notificationPreferences: any;
  success: boolean;
  error?: string;
}> {
  try {
    await connectToDatabase();

    // Initialize default dashboard preferences
    const defaultDashboardPrefs = createDefaultDashboardPreferences(userId, userRole);

    const dashboardPreferences = await DashboardPreferencesModel.findOneAndUpdate(
      { userId },
      { $setOnInsert: defaultDashboardPrefs },
      { 
        new: true, 
        upsert: true 
      }
    );

    // Initialize default notification preferences
    const defaultNotificationPrefs = createDefaultPreferences(userId);

    const notificationPreferences = await NotificationPreferencesModel.findOneAndUpdate(
      { userId },
      { $setOnInsert: defaultNotificationPrefs },
      { 
        new: true, 
        upsert: true 
      }
    );

    console.log(`âœ… Initialized default preferences for user ${userId} (role: ${userRole})`);

    return {
      dashboardPreferences,
      notificationPreferences,
      success: true
    };

  } catch (error) {
    console.error('âŒ Error initializing user defaults:', error);
    return {
      dashboardPreferences: null,
      notificationPreferences: null,
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error'
    };
  }
}

/**
 * Reset user preferences to defaults
 * Useful for troubleshooting or when a user requests a reset
 */
export async function resetUserPreferences(
  userId: string,
  userRole: 'admin' | 'user' = 'user',
  options: {
    resetDashboard?: boolean;
    resetNotifications?: boolean;
  } = { resetDashboard: true, resetNotifications: true }
): Promise<{
  success: boolean;
  error?: string;
  reset: string[];
}> {
  try {
    await connectToDatabase();
    const reset: string[] = [];

    if (options.resetDashboard) {
      const defaultDashboardPrefs = createDefaultDashboardPreferences(userId, userRole);

      await DashboardPreferencesModel.findOneAndUpdate(
        { userId },
        defaultDashboardPrefs,
        { upsert: true }
      );

      reset.push('dashboard');
    }

    if (options.resetNotifications) {
      const defaultNotificationPrefs = createDefaultPreferences(userId);

      await NotificationPreferencesModel.findOneAndUpdate(
        { userId },
        defaultNotificationPrefs,
        { upsert: true }
      );

      reset.push('notifications');
    }

    console.log(`âœ… Reset preferences for user ${userId}: ${reset.join(', ')}`);

    return {
      success: true,
      reset
    };

  } catch (error) {
    console.error('âŒ Error resetting user preferences:', error);
    return {
      success: false,
      error: error instanceof Error ? error.message : 'Unknown error',
      reset: []
    };
  }
}

/**
 * Check if user has default preferences set up
 * Useful for determining if initialization is needed
 */
export async function checkUserPreferences(userId: string): Promise<{
  hasDashboardPreferences: boolean;
  hasNotificationPreferences: boolean;
  needsInitialization: boolean;
}> {
  try {
    await connectToDatabase();

    const [dashboardPrefs, notificationPrefs] = await Promise.all([
      DashboardPreferencesModel.findOne({ userId }),
      NotificationPreferencesModel.findOne({ userId })
    ]);

    const hasDashboardPreferences = !!dashboardPrefs;
    const hasNotificationPreferences = !!notificationPrefs;
    const needsInitialization = !hasDashboardPreferences || !hasNotificationPreferences;

    return {
      hasDashboardPreferences,
      hasNotificationPreferences,
      needsInitialization
    };

  } catch (error) {
    console.error('Error checking user preferences:', error);
    return {
      hasDashboardPreferences: false,
      hasNotificationPreferences: false,
      needsInitialization: true
    };
  }
}

/**
 * Bulk initialize preferences for multiple users
 * Useful for migrating existing users or batch operations
 */
export async function bulkInitializeUsers(
  users: Array<{ userId: string; role: 'admin' | 'user' }>
): Promise<{
  success: number;
  failed: number;
  errors: Array<{ userId: string; error: string }>;
}> {
  const results = {
    success: 0,
    failed: 0,
    errors: [] as Array<{ userId: string; error: string }>
  };

  for (const user of users) {
    try {
      const result = await initializeUserDefaults(user.userId, user.role);

      if (result.success) {
        results.success++;
      } else {
        results.failed++;
        results.errors.push({
          userId: user.userId,
          error: result.error || 'Unknown error'
        });
      }
    } catch (error) {
      results.failed++;
      results.errors.push({
        userId: user.userId,
        error: error instanceof Error ? error.message : 'Unknown error'
      });
    }
  }

  console.log(`Bulk initialization complete: ${results.success} success, ${results.failed} failed`);
  return results;
}

