      console.log(`ðŸ“ Updating nextCheck to: ${nextCheckTime.toISOString()}`);

      const result = await SchedulerSettingsModel.findOneAndUpdate(
        {},
        { 
          lastCheck: now,
          nextCheck: nextCheckTime,
          updatedAt: now
        },
        { 
