  }

  /**
   * Perform KPI alert check for all users
   */
  private async performKpiAlertCheck(): Promise<void> {
    try {
      console.log('ðŸ“Š [KPI] Starting KPI data collection for all users...');

      // Get Sippy API credentials
      const credentials = await getSippyApiCredentials();
      if (!credentials) {
        console.error('âŒ [KPI] Sippy API credentials not configured');
        return;
      }

      // Get all admin users with Sippy accounts
      await connectToDatabase();
      const adminUsers = await UserModel.find({ 
        role: 'admin', 
        sippyAccountId: { $exists: true, $ne: null } 
      }).lean();

      if (adminUsers.length === 0) {
        console.log('â„¹ï¸ [KPI] No admin users with Sippy accounts found');
        return;
      }

      console.log(`ðŸ“Š [KPI] Processing ${adminUsers.length} admin users...`);

      const sippyClient = new SippyClient(credentials);
      const today = new Date();
      const startOfDay = new Date(today.getFullYear(), today.getMonth(), today.getDate());
      const endOfDay = new Date(today.getFullYear(), today.getMonth(), today.getDate(), 23, 59, 59);

      const kpiDataByUser = new Map();

      // Collect KPI data for each user
      for (const user of adminUsers) {
        try {
          console.log(`ðŸ“Š [KPI] Fetching CDR data for user ${user.name} (${user.email})...`);

          let allCdrs: any[] = [];
          let offset = 0;
          const limit = 1000;
          let hasMore = true;

          // Fetch all CDRs for today
          while (hasMore) {
            try {
              const response = await sippyClient.getAccountCDRs({
                i_account: user.sippyAccountId,
                start_date: startOfDay.toISOString().split('T')[0],
                end_date: endOfDay.toISOString().split('T')[0],
                limit: limit,
                offset: offset
              });

              if (response && Array.isArray(response) && response.length > 0) {
                allCdrs = allCdrs.concat(response);
                offset += limit;

                if (response.length < limit) {
                  hasMore = false;
                }
              } else {
                hasMore = false;
              }
            } catch (error) {
              console.error(`âŒ [KPI] Error fetching CDRs for user ${user.name}:`, error);
              hasMore = false;
            }
          }

          // Calculate KPIs from CDR data
          let totalCost = 0;
          let totalDuration = 0;
          let successfulCalls = 0;
          let failedCalls = 0;
          let currency = 'EUR';

          for (const cdr of allCdrs) {
            const cost = parseFloat(cdr.cost) || 0;
            const duration = parseInt(cdr.duration) || 0;
            const result = parseInt(cdr.result);

            totalCost += cost;
            totalDuration += duration;

            if (result === 0 || result === 200) {
              successfulCalls++;
            } else {
              failedCalls++;
            }

            if (cdr.currency) {
              currency = cdr.currency;
            }
          }

          const totalCalls = successfulCalls + failedCalls;
          const asr = totalCalls > 0 ? (successfulCalls / totalCalls) * 100 : 0;
          const totalMinutes = totalDuration / 60;

          const kpiData = {
            costOfDay: totalCost,
            asr,
            totalMinutes,
            totalCalls,
            successfulCalls,
            failedCalls,
            currency
          };

          console.log(`ðŸ“Š [KPI] KPI data for ${user.name}:`, {
            costOfDay: kpiData.costOfDay.toFixed(2),
            asr: kpiData.asr.toFixed(1) + '%',
            totalMinutes: kpiData.totalMinutes.toFixed(0),
            totalCalls: kpiData.totalCalls,
            cdrsProcessed: allCdrs.length
          });

          kpiDataByUser.set(user._id.toString(), kpiData);

        } catch (error) {
          console.error(`âŒ [KPI] Error processing KPI data for user ${user.name}:`, error);
        }
      }

      // Reset daily alerts at start of day
      kpiAlertService.resetDailyAlerts();

      // Check KPI alerts for all users
      console.log(`ðŸ“§ [KPI] Checking alerts for ${kpiDataByUser.size} users...`);
      await kpiAlertService.checkAllUsersKpiAlerts(kpiDataByUser);

      console.log('âœ… [KPI] KPI alert check completed successfully');

    } catch (error) {
      console.error('âŒ [KPI] Error in performKpiAlertCheck:', error);
    }
  }

  /**
   * Manually trigger KPI alert check
   */
  public async triggerKpiAlertCheck(): Promise<void> {
    console.log('ðŸ“Š [MANUAL] Triggering manual KPI alert check...');
    const startTime = new Date();

    try {
      await this.performKpiAlertCheck();

      const endTime = new Date();
      const duration = endTime.getTime() - startTime.getTime();
      console.log(`âœ… [MANUAL] Manual KPI alert check completed successfully in ${duration}ms`);
    } catch (error) {
      console.error('âŒ [MANUAL] Error during manual KPI alert check:', error);
      throw error;
    }
  }

  /**
   * Start customer notification execution task
   */
  private async startCustomerNotificationTask(): Promise<void> {
    const settings = await this.getSchedulerSettings();

    if (!settings.enabled) {
      console.log('Customer notification task disabled in settings');
      return;
    }

    // Customer notifications run every 5 minutes to check for due notifications
    const cronSchedule = '*/5 * * * *';

    console.log(`â° Setting up customer notification execution task:`);
    console.log(`   Schedule: Every 5 minutes`);
    console.log(`   Cron: ${cronSchedule}`);
    console.log(`   Timezone: ${settings.timezone}`);

    const task = cron.schedule(cronSchedule, async () => {
      console.log('\nðŸ“§ [CRON] Starting customer notification execution...');
      const startTime = new Date();

      try {
        const customerNotificationService = CustomerNotificationService.getInstance();
        await customerNotificationService.executeScheduledNotifications();

        const endTime = new Date();
        const duration = endTime.getTime() - startTime.getTime();
        console.log(`âœ… [CRON] Customer notification execution completed in ${duration}ms`);

      } catch (error) {
        console.error('âŒ [CRON] Error during customer notification execution:', error);
      }
    }, {
      timezone: settings.timezone
    });

    this.tasks.set('customer-notifications', task);

    console.log(`âœ… Customer notification task scheduled and started`);
  }
}

import SmtpSettings from '@/models/SmtpSettings';
import { EmailCategory, ISmtpSettings } from '@/types/smtp';
import { connectToDatabase } from '@/lib/db';
import nodemailer from 'nodemailer';

export interface EmailOptions {
  to: string;
  subject: string;
  html: string;
  text?: string;
  from?: string;
  replyTo?: string;
}

export interface EmailResult {
  success: boolean;
  messageId?: string;
  error?: string;
  accountUsed?: {
    name: string;
    category: EmailCategory;
    fromEmail: string;
  };
}

export class SmtpService {
  private static instance: SmtpService;

  private constructor() {}

  public static getInstance(): SmtpService {
    if (!SmtpService.instance) {
      SmtpService.instance = new SmtpService();
    }
    return SmtpService.instance;
  }

  /**
   * Get SMTP account for a specific email category
   */
  public async getSmtpAccountForCategory(category: EmailCategory): Promise<any> {
    try {
      await connectToDatabase();

      // First try to find an enabled account for the specific category
      let account = await SmtpSettings.findOne({
        category,
        enabled: true
      })
      .select('+password')
      .sort({ priority: 1, createdAt: 1 });

      // If no specific category account found, try to get the default account
      if (!account && category !== 'default') {
        account = await SmtpSettings.findOne({
          isDefault: true,
          enabled: true
        })
        .select('+password') as any;
      }

      // If still no account, get any enabled account as last resort
      if (!account) {
        account = await SmtpSettings.findOne({
          enabled: true
        })
        .select('+password')
        .sort({ priority: 1, createdAt: 1 });
      }

      return account;
    } catch (error) {
      console.error('Error getting SMTP account for category:', error);
      return null;
    }
  }

  /**
   * Send email using appropriate SMTP account based on category
   */
  public async sendEmail(category: EmailCategory, emailOptions: EmailOptions): Promise<EmailResult> {
    try {
      const smtpAccount = await this.getSmtpAccountForCategory(category);

      if (!smtpAccount) {
        return {
          success: false,
          error: `No SMTP account available for category: ${category}`
        };
      }

      const transporter = this.createTransporter(smtpAccount as any);

      // Verify connection first
      await transporter.verify();

      const mailOptions = {
        from: emailOptions.from || `"${smtpAccount.fromName}" <${smtpAccount.fromEmail}>`,
        to: emailOptions.to,
        subject: emailOptions.subject,
        html: emailOptions.html,
        text: emailOptions.text,
        replyTo: emailOptions.replyTo
      };

      const info = await transporter.sendMail(mailOptions);

      return {
        success: true,
        messageId: info.messageId,
        accountUsed: {
          name: smtpAccount.name,
          category: smtpAccount.category,
          fromEmail: smtpAccount.fromEmail
        }
      };
    } catch (error) {
      console.error('Error sending email:', error);
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Unknown error'
      };
    }
  }

  /**
   * Send billing-related emails (payments, balance alerts, invoices)
   */
  public async sendBillingEmail(emailOptions: EmailOptions): Promise<EmailResult> {
    return this.sendEmail('billing', emailOptions);
  }

  /**
   * Send authentication-related emails (signup, login, password reset, OTP)
   */
  public async sendAuthenticationEmail(emailOptions: EmailOptions): Promise<EmailResult> {
    return this.sendEmail('authentication', emailOptions);
  }

  /**
   * Send support-related emails (KPI alerts, system notifications)
   */
  public async sendSupportEmail(emailOptions: EmailOptions): Promise<EmailResult> {
    return this.sendEmail('support', emailOptions);
  }

  /**
   * Send general emails using default SMTP account
   */
  public async sendDefaultEmail(emailOptions: EmailOptions): Promise<EmailResult> {
    return this.sendEmail('default', emailOptions);
  }

  /**
   * Get all available SMTP accounts by category
   */
  public async getAllSmtpAccounts(): Promise<Record<EmailCategory, ISmtpSettings[]>> {
    try {
      await connectToDatabase();

      const accounts = await SmtpSettings.find({ enabled: true })
        .select('-password')
        .sort({ category: 1, priority: 1, createdAt: 1 });

      const categorizedAccounts: Record<EmailCategory, ISmtpSettings[]> = {
        billing: [],
        authentication: [],
        support: [],
        default: []
      };

      accounts.forEach(account => {
        categorizedAccounts[account.category].push(account);
      });

      return categorizedAccounts;
    } catch (error) {
      console.error('Error getting all SMTP accounts:', error);
      return {
        billing: [],
        authentication: [],
        support: [],
        default: []
      };
    }
  }

  /**
   * Test SMTP connection for a specific account
   */
  public async testSmtpAccount(accountId: string): Promise<{ success: boolean; error?: string }> {
    try {
      await connectToDatabase();

      const account = await SmtpSettings.findById(accountId).select('+password');
      if (!account) {
        return { success: false, error: 'Account not found' };
      }

      const transporter = this.createTransporter(account as any);
      await transporter.verify();

      return { success: true };
    } catch (error) {
      return {
        success: false,
        error: error instanceof Error ? error.message : 'Connection failed'
      };
    }
  }

  /**
   * Create nodemailer transporter for an SMTP account
   */
  private createTransporter(account: ISmtpSettings) {
    return nodemailer.createTransport({
      host: account.host,
      port: account.port,
      secure: account.secure,
      auth: account.username && account.password ? {
        user: account.username,
        pass: account.password,
      } : undefined,
      connectionTimeout: 10000,
      socketTimeout: 10000,
      tls: {
        rejectUnauthorized: false
      }
    });
  }

  /**
   * Determine email category based on email type or content
   */
  public static categorizeEmail(emailType: string): EmailCategory {
    const billingTypes = [
      'topup-success', 'payment-confirmation', 'balance-low', 'balance-critical', 
      'payment-failed', 'invoice-generated', 'billing-update', 'subscription-renewal'
    ];

    const authTypes = [
      'signup-welcome', 'email-verification', 'forgot-password', 'password-reset', 
      'password-changed', 'otp-code', 'account-activation', 'login-alert', 
      'security-notification', 'account-locked'
    ];

    const supportTypes = [
      'kpi-alert', 'system-maintenance', 'performance-report', 'error-notification',
      'usage-report', 'quota-exceeded', 'service-degradation', 'support-ticket'
    ];

    if (billingTypes.includes(emailType.toLowerCase())) {
      return 'billing';
    }

    if (authTypes.includes(emailType.toLowerCase())) {
      return 'authentication';
    }

    if (supportTypes.includes(emailType.toLowerCase())) {
      return 'support';
    }

    return 'default';
  }
}

export default SmtpService;
